Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (150,1,to_timestamp('10-AUG-15 11.16.39.909000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tal-r&icirc;shtar',null,null,'The black moon of Puell&aelig;, associated with the Essence of Shadow.  Because it is invisible against the sky, Tal-r&icirc;shtar has been historically unknown to the people of Puell&aelig;.  During the Second &AElig;ea, V&oelig;n-M&aelig;gar was mistaken as the "true" black moon (Tal-r&icirc;shtar is smaller, more distant, and generally difficult to detect due to its revolutionary orbit).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (151,1,to_timestamp('10-AUG-15 11.26.36.221000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ene',null,null,'A pet name by which both Malig Narz and Malnos have referred to Eleanor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (153,1,to_timestamp('10-AUG-15 11.28.14.117000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Essence',null,null,'A collective name for the four "astral" attributes of Da''kol:  Light, Shadow, Death, and Life.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (155,1,to_timestamp('10-AUG-15 11.33.18.453000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gift, The',null,null,'A term used for the ability to Shape, the ability to Sing, the ability to Name, and the ability to See, depending on the context.  The first (the ability to Shape) is the most common and is also referred to as the Spark or the the Blood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (156,1,to_timestamp('10-AUG-15 11.34.04.640000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arbitrator',null,null,'An alternative name for the Neraphin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (158,1,to_timestamp('10-AUG-15 11.38.02.983000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Llineveh',null,null,'A world far removed from Puell&aelig;, once home to the Neraphin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (159,1,to_timestamp('10-AUG-15 11.39.24.999000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shaping Stone',null,null,'A rare stone that becomes temporarily malleable through the application of heat, pressure, or friction.  Also known as a Changeling Stone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (162,1,to_timestamp('10-AUG-15 11.49.01.345000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valley of the Cursed',null,null,'A valley in the Death''s Blades Mountains where persons of the Brown Plains are exiled to die for either being born Twisted or practicing forbidden arts, such as writing or magick.  The pass to the Valley is located near the town of Camaar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (163,1,to_timestamp('10-AUG-15 11.49.46.970000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sphinx',null,null,'A mythological creature and symbol of the Gamon Ju''g&aelig;.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (164,1,to_timestamp('10-AUG-15 11.51.20.187000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Summer Tree',null,null,'An apple tree located on a farm near the village of Camaar.  It is green and fruitful despite the season.  The bark is carved with the name, Memar Korain.',0);
