Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (177,1,to_timestamp('11-AUG-15 12.20.42.702000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arastel',null,null,'The name assumed by Xatran M&ucirc;r during the Great War, meaning "The Grand Strategist".  He also referred to himself as Death''s Warlord.  Currently, he refers to himself as the God.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (178,1,to_timestamp('11-AUG-15 12.23.04.016000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'White Gull',null,null,'A deep sea Malkaran vessel, captained by Myrake.  It was employed by Malnos and Eleanor to travel between Namar and the Grey Tower.',0);
