Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (182,1,to_timestamp('11-AUG-15 12.26.49.248000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Es Ber Canaal, Erk&icirc;l Der Snaal Durain Duretr&ecirc;',null,null,'A mysterious phrase in the Pure Tongue, spoken primarily in dreams.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (186,1,to_timestamp('11-AUG-15 07.04.57.364000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Seven, The',null,null,'An alternate name for the Neraphin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (188,1,to_timestamp('11-AUG-15 07.05.22.973000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nine, The',null,null,'An alternate name for the Neraphin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (193,1,to_timestamp('11-AUG-15 07.11.08.010000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Baragur',null,null,'The Neraphin associated with the Essence of Death.  Also known as Shiro, The Enemy, The Child, The Lonely God, The Dark One, and the Lord of Death.  Known as the Great Lord among his servants.  Due to his ill-understood conflict with the other Neraphin, Baragur is not considered one of the Seven.  During the Second &AElig;gan Empire, he was not considered a Neraphin at all but rather as a dark, antithetical god.  Baragur has an unknown relationship with the Sacred Mount of Udmar D&ucirc;marg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (201,1,to_timestamp('11-AUG-15 07.28.49.399000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Stars'' Vigil',null,null,'An alternate name for the Neraphin, Elurin.',0);
