Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (506,1,to_timestamp('12-AUG-15 10.40.26.088000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sral',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (112,1,to_timestamp('10-AUG-15 04.45.52.596000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Malkar',null,null,'A nation of modern Puell&aelig; which lies south of the Death''s Blades Mountains on the western edge of the Great Island.  It consists of eleven poleis, or city-states, each of which has its own government and lands.  This number was once thirteen.  The former poleis of Calyth and Atheos have seceded and fallen, respectively.  According to some historical records, Malkar was once part of of Her-lak Merg&ouml;l, an eastern empire.  The current poleis include Ithia, Nithia, Threte, Namar, Phoebes, Ler, Me''din, Dyal, Kourinth, Coreyra, and Spira.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (284,1,to_timestamp('11-AUG-15 09.21.15.897000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tillers'' City, The',null,null,'An alternative name for the Malkaran polis of Ithia.',0);
