Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (295,1,to_timestamp('11-AUG-15 09.25.17.036000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Slaves'' City, The',null,null,'An alternative name for the seceded Malkaran polis of Calyth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (296,1,to_timestamp('11-AUG-15 09.25.26.406000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Traitors'' City, The',null,null,'An alternative name for the seceded Malkaran polis of Calyth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (303,1,to_timestamp('11-AUG-15 09.48.16.247000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Green Lord, The',null,null,'The name by which the Shining Ones refer the Triumvir, Ym du''Seraa, better known as Calik Twiceborn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (103,1,to_timestamp('10-AUG-15 04.21.59.753000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'At&ucirc;n',null,null,'The Sacred Mounts, lone peaks whose origins are lost to time.  Also known as Cielan Agur or Sky Spears.  Examples include Cielin-al&ucirc;n, Udmar D&ucirc;marg, Udmar D&ucirc;sere, and Udmar Cajar.  Tai''shar is considered by some to be one of the At&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (107,1,to_timestamp('10-AUG-15 04.29.31.319000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Swiftdeep, The',null,null,'A river which runs from the Death''s Blade mountains south through the nation of Malkar, emptying into the western and eastern seas.  Its northern half is famed for its rapids and swift current.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (279,1,to_timestamp('11-AUG-15 09.20.21.081000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City of the Oracles, The',null,null,'An alternative name for the Malkaran polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (230,1,to_timestamp('11-AUG-15 07.34.56.401000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fickle God, The',null,null,'An alternate name for the Neraphin, Thiassi.',0);
