Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (200,1,to_timestamp('11-AUG-15 07.28.35.956000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Champion, The',null,null,'An alternate name for the Neraphin, Elurin.',0);
