Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (253,1,to_timestamp('11-AUG-15 08.47.13.464000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Coreyra',null,null,'A polis of Malkar, dedicated to the god, Valeus.',0);
