Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (199,1,to_timestamp('11-AUG-15 07.21.24.784000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ajzagal',null,null,'The eldest of the Neraphin, associated with the Gift of Earth Song and the Gift of Naming.  He did not accompany his younger brethren to Puell&aelig;, and his fate is unknown.  He has never been known or worshiped among mortals.  Also known as Namma, The Grandfather, The First Or&uacute;, and The Mentor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (453,1,to_timestamp('12-AUG-15 01.55.02.301000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Token',null,null,'An object possessed by each student at the University of Xaln, acting as a memento of their prior lives.  Each child was permitted one and only one Token.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (169,1,to_timestamp('10-AUG-15 11.58.25.802000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Providenta',null,null,'The name of staves borne by the members of the Triumvirate:  Gai''lal, Ki''lan, and Calik. Crafted of blue stone, their surfaces danced with gray runes according to the will of their bearers.  The touch of a Providenta caused immediate, painful death for most persons, transforming their flesh to crystal and stone.  The staves are believed to be Tal''Shiahd and involved in some way in the relationship between the Triumvirs and their sere''marg.  pl. Provident&aelig;.  The connection between the Provident&aelig; and the Oryoni, if any, is unknown.  Also known as a Staff of Three.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (127,1,to_timestamp('10-AUG-15 09.40.53.208000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aenea',null,null,'A small village in Malkar, located between the Grimwood and Ithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (131,1,to_timestamp('10-AUG-15 09.47.41.464000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Crimson Vine',null,null,'An inn in the polis if Ithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (113,1,to_timestamp('10-AUG-15 08.59.39.375000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grimwood',null,null,'A dark, elder forest located in the heart of Malkar.  It is a source of many rumors and ill-explored.  It was once part of Sol Arb&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (101,1,to_timestamp('10-AUG-15 04.16.12.948000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cielin-al&ucirc;n',null,null,'One of the At&ucirc;n, a lone peak located at the heart of Sol Arb&icirc;n.  Also known as the Spear of the Sky.  Wrought of emerald stone, this mountain is overgrown with trees and vegetation, except its summit, which is a caldera featuring a crystalline lake and a dead plain where &AElig;thel''Mierre once grew.  This peak has been historically associated with the Neraphin, Irien, but the connection is ill-understood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (102,1,to_timestamp('10-AUG-15 04.20.05.962000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Spear of the Sky, The',null,null,'An alternative name for Cielin-al&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (114,1,to_timestamp('10-AUG-15 09.01.42.378000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Greenweald, The',null,null,'A place of legend supposedly located on a peninsula within the Grimwood.  According to tales, the veil between the earthly realm and the realm of the Malkaran gods is thin in the Greenweald, allowing servants of the heavens to enter the world.  These servants, in the stories, take forms similar to the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (115,1,to_timestamp('10-AUG-15 09.03.24.158000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dorica',null,null,'A democratic state during the time of the First &AElig;gan Empire.  Also a city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (116,1,to_timestamp('10-AUG-15 09.04.48.536000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Heul',null,null,'A river which runs south from the Death''s Blades Mountains through the Holy Lands of Ki''mar and the Empire of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (117,1,to_timestamp('10-AUG-15 09.05.27.949000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Murk, The',null,null,'A swamp in northern Malkar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (106,1,to_timestamp('10-AUG-15 04.26.29.245000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Old Forest',null,null,'An alternative name for Sol Arb&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (108,1,to_timestamp('10-AUG-15 04.33.49.283000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tempest Sea',null,null,'The great sea at the former heart of the Great Island of &AElig;gea, separating Malkar, Serinin, and Her-lak Merg&ouml;l.  Storms rage throughout the area, granting the sea its name creating vast whirlpools that are considered impassable.  Rumor claims that an island lies at the sea''s heart.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (110,1,to_timestamp('10-AUG-15 04.39.43.331000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gardenlands',null,null,'An alternative name for Ashan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (111,1,to_timestamp('10-AUG-15 04.40.02.490000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shade, The',null,null,'An alternative name for Ashan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (120,1,to_timestamp('10-AUG-15 09.20.17.193000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eyes of N&aelig;yt, The',null,null,'An alternative name for the moons of Puell&aelig;, used among the People of the Brown Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (121,1,to_timestamp('10-AUG-15 09.20.28.570000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gifts of Serin',null,null,'An alternative name for the moons of Puell&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (122,1,to_timestamp('10-AUG-15 09.25.40.049000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Puell&aelig;',null,null,'The proper name of central world of the story.  Its etymology is lost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (123,1,to_timestamp('10-AUG-15 09.31.17.632000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ki''mar',null,null,'A nation of modern Puell&aelig; located on the northeastern coast of the Great Island, south of the Death''s Blade Mountains and north of Her-lak Merg&ouml;l.  Geographically isolated by the Sanara desert, little is known of this land or its peoples except that they worship a monotheistic deity known as the One God.  The capital of Ki''mar shares the same name as the nation-state but is more commonly known as the Holy City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (124,1,to_timestamp('10-AUG-15 09.33.36.636000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sanara',null,null,'A desert in the modern Great Island of Puell&aelig;, which geographically separates the nations of Ki''mar and Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (119,1,to_timestamp('10-AUG-15 09.17.00.969000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Moon',null,null,'Puell&aelig; has nine moons:  Naudar, Ishgar, Gamar, Kovar, Vidar, Augar, Axtar, V&oelig;n-M&aelig;gar, and Tal-r&icirc;shtar.  The last two are dark and invisible against the sky and have been unknown to both scholars and the common man throughout various periods of time (particularly Tal-r&icirc;shtar).  During the modern era, Puell&aelig; is considered to have seven moons.  During the Second &AElig;gea, it was considered to have eight (Tal-r&icirc;shtar was unknown and V&oelig;n-M&aelig;gar was considered the "black" moon.)  The moons have been known by various names by different cultures throughout history, including the Eyes of N&aelig;yt and the Gifts of Serin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (125,1,to_timestamp('10-AUG-15 09.37.10.436000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Her-lak Merg&ouml;l',null,null,'A militaristic empire that lies on the eastern coast of the Great Island.  Once, the Empire ruled the lands now occupied by Ki''mar, Ashan, and Malkar, but its power was broken in historical times.  Recently, it has resumed its campaign to reoccupy its former territories.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (130,1,to_timestamp('10-AUG-15 09.46.38.213000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'One God, The',null,null,'Various peoples and religions of Puell&aelig; have been monotheistic in their beliefs.  Among them are the modern priests of Ki''mar, the Holy City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (134,1,to_timestamp('10-AUG-15 09.51.47.760000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;nus',null,null,'An ancient township of Malkar, now fallen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (136,1,to_timestamp('10-AUG-15 09.55.46.137000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Exalted Library',null,null,'The great library of the polis of Namar, located atop the Acropolis.  Also known as the Bibliotethum.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (149,1,to_timestamp('10-AUG-15 11.16.22.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'V&oelig;n-M&aelig;gar',null,null,'The violet moon of Puell&aelig;, associated with the Pillar of Time.  At some point in history, the moon was darkened, rendering it black.  Because it is invisible against the sky, V&oelig;n-M&aelig;gar has been unknown to the people of Puell&aelig; throughout periods of its history. During the Second &AElig;gan Empire, V&oelig;n-M&aelig;gar was mistakenly known as the black moon (Tal-r&icirc;shtar is smaller, more distant, and generally difficult to detect due to its revolutionary orbit). Also called the Dark Moon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (152,1,to_timestamp('10-AUG-15 11.27.40.703000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Element',null,null,'A collective name for the four "physical" attributes of Da''kol:  Water, Air, Fire, and Earth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (154,1,to_timestamp('10-AUG-15 11.29.50.418000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Pillar',null,null,'A collective name for the attributes of Da''kol, including both the Elements and Essences:  Earth, Air, Water, Fire, Light, Darkness, Death, and Life.  Time can also be considered a Pillar, though it is neither and Element nor an Essence.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (157,1,to_timestamp('10-AUG-15 11.37.05.981000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;gea',null,null,'A reference to the &AElig;gan Empire or the land mass it occupied.  Occasionally used to refer to the Great Island.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (161,1,to_timestamp('10-AUG-15 11.44.37.016000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'K&aring;n',null,null,'A board game common among the lords of the Second &AElig;gan Empire.  It was considered to accurately capture the complexities of war.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (698,1,to_timestamp('14-AUG-15 01.07.46.509000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kylur',null,null,'A brown stone worn as a pendant among the priests of the Die Nearal.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (170,1,to_timestamp('11-AUG-15 12.07.28.649000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Owl Guard',null,null,'Elite Warriors of Namar, employed primarily to protect sacred persons and places, such as the Shrine of the Oracle and the Temples atop the Acropolis.  The Owl Guard are not employed for military purposes, as Namar has no standing army or militia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (426,1,to_timestamp('12-AUG-15 01.18.54.376000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ikamukatika',null,null,'A village of the Ba-kwe on the Brown Plains.  It was visited by the na''k&ouml;r on their journey between the Summer Island and Uragar.  It lies on the shores of Wa-mu-male.  The name translates to "The Spirit of Ka-tiak."  So called because the sound of waves against pebbles on the beach supposedly sounded like the god Ka-tiak beating on his drums.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (428,1,to_timestamp('12-AUG-15 01.23.28.713000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ama-tu',null,null,'A river which runs through the Brown Plains.  The villages of Amatuluktu and Kanalokamu lie on its shores.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (431,1,to_timestamp('12-AUG-15 01.28.14.619000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sevinutuki',null,null,'A river that runs through the Brown Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (433,1,to_timestamp('12-AUG-15 01.35.48.546000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Road of the Damned, The',null,null,'The path between Camaar and the Valley of the Cursed in the Death''s Blade mountains, consisting of perilous trails and valleys.  It is a journey of several days under optimal conditions and is not considered passable in the winter months.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (437,1,to_timestamp('12-AUG-15 01.43.53.418000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Children of the Silver Gift, The',null,null,'Literal translation of Kalit''edon Emari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (438,1,to_timestamp('12-AUG-15 01.44.23.669000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'True-Blooded, The',null,null,'An alternative name for the Kalit’edon Emari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (441,1,to_timestamp('12-AUG-15 01.46.27.772000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Clan of the White Rabbit, The',null,null,'One of the seven clans of the Ipu''qat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (443,1,to_timestamp('12-AUG-15 01.48.05.697000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sevenfold People, The',null,null,'An alternative name for the Ipu''qat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (445,1,to_timestamp('12-AUG-15 01.49.14.000000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Salt Born, The',null,null,'An alternative name for the Ku-kuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (447,1,to_timestamp('12-AUG-15 01.49.58.049000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Still Folk, The',null,null,'An alternative name for the Ba-kwe.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (448,1,to_timestamp('12-AUG-15 01.50.08.571000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'People of the Stone Roof, The',null,null,'An alternative name for the Ba-kwe.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (452,1,to_timestamp('12-AUG-15 01.53.44.461000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Haanduer Hall',null,null,'A lecture hall in the University of Xaln.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (173,1,to_timestamp('11-AUG-15 12.11.33.735000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Az-Azaal',null,null,'The name assumed by Merik do''Rehny when he became one of the Mortuani.  It means literally "Father of Monsters" in the Pure Tongue.  (According to legend, the name was first associated with Merik by the people of H''zith during the Gh&ucirc;l War.)',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (217,1,to_timestamp('11-AUG-15 07.32.20.247000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lord of Death, The',null,null,'An alternate name for the Neraphin, Baragur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (222,1,to_timestamp('11-AUG-15 07.33.31.450000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Siren, The',null,null,'An alternate name for the Neraphin, Asara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (271,1,to_timestamp('11-AUG-15 09.02.19.430000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Merchant Council, The',null,null,'An alternative name for the Ercanti.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (415,1,to_timestamp('12-AUG-15 11.56.02.030000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Star-fallen, The',null,null,'The name given to Kain by the Shining Ones after he descended in the Sphere.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (410,1,to_timestamp('12-AUG-15 11.45.08.937000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hanging Gardens, The',null,null,'A district of the polis of Ithia, reserved for members of the Ercanti.  Its winding streets are thought to represent one of the great beauties of the modern world.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (174,1,to_timestamp('11-AUG-15 12.15.22.724000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'H''zith',null,null,'An island kingdom during the time of the First &AElig;gan Empire.  According to legend, its people were subjected to experiments by Merik do''Rehny during the Gh&ucirc;l War and were the first to dub him Az-Azaal, the "Father of Monsters," a title that he later adopted during the Great War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (183,1,to_timestamp('11-AUG-15 12.29.55.158000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Camaar',null,null,'A town of the Brown Plains, located in the shadows of the Death Blades'' mountains.  It is thought, based upon its old construction, to have been constructed by the Zimuit.  It is currently occupied by the Ba-kwe.  Camaar is the nearest habitation to both the Valley of the Cursed and Uragar.  It, or a nearby farm, was likely the birthplace of Memar Korain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (272,1,to_timestamp('11-AUG-15 09.03.04.808000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Qintax, The',null,null,'The ruling body of the Malkaran city of Threte, comprised of five generals.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (197,1,to_timestamp('11-AUG-15 07.16.16.730000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Doitean',null,null,'The Neraphin associated with the Element of Fire.  Also known as Vulcan, The Flamebringer, The Chariot, and Wroth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (198,1,to_timestamp('11-AUG-15 07.17.27.270000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Noimead',null,null,'The Neraphin associated with the Pillar of Time.  Due to his inability to extend his Pillar to mortals, Noimead has been largely forgotten by the people of Puell&aelig;.  He was not considered among the Seven and was unknown to the lords of the First and Second &AElig;gan Empires.  Also known as Roimh, The Ancient, The Dreamweaver, and The All-Seer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (207,1,to_timestamp('11-AUG-15 07.30.17.241000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Madrar',null,null,'An alternate name for the Neraphin, Irien.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (280,1,to_timestamp('11-AUG-15 09.20.30.926000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City of a Thousand Voices, The',null,null,'An alternative name for the Malkaran polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (212,1,to_timestamp('11-AUG-15 07.31.28.692000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shiro',null,null,'An alternate name for the Neraphin, Baragur.  This name has not been used since the Neraphin considered Llineveh home.',0);
