Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (223,1,to_timestamp('11-AUG-15 07.33.41.831000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Patient, The',null,null,'An alternate name for the Neraphin, Asara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (241,1,to_timestamp('11-AUG-15 07.38.01.224000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grandfather, The',null,null,'An alternate name for the Neraphin, Ajzagal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (554,1,to_timestamp('13-AUG-15 10.02.13.567000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dark Moon, The',null,null,'An alternative (and inapt) name for the moon, V&oelig;n-m&aelig;gar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (557,1,to_timestamp('13-AUG-15 10.04.41.557000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tower Room, The',null,null,'An alternative name for the Whistle Chamber.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (249,1,to_timestamp('11-AUG-15 08.44.09.401000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ler',null,null,'A polis of Malkar, dedicated to the goddess, Lesix.  Governed by the Te''garch.  Also known as the City of the Pure.  Recently, Ler has fallen under the influence of a foreign lord, Bartholomew.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (252,1,to_timestamp('11-AUG-15 08.46.52.530000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kourinth',null,null,'A polis of Malkar, dedicated to the god, Kourix.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (254,1,to_timestamp('11-AUG-15 08.48.12.696000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Spira',null,null,'A polis of Malkar, dedicated to the goddess, Spictalia.  Governed by the High Priestess of Spictalia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (226,1,to_timestamp('11-AUG-15 07.34.20.530000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Four Winds, The',null,null,'An alternate name for the Neraphin, Thiassi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (261,1,to_timestamp('11-AUG-15 08.55.51.268000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Phoebos',null,null,'A god of the people of Malkar, associated with Wind/Speed/Change.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (264,1,to_timestamp('11-AUG-15 08.57.11.774000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dyalia',null,null,'A goddess of the people of Malkar, associated with Sea/Weather.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (266,1,to_timestamp('11-AUG-15 08.57.59.069000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Valeus',null,null,'A god of the people of Malkar, associated with Love/Lust/Hunger.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1228,1,to_timestamp('18-AUG-15 08.43.45.045000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;engill-rikr-rikr',null,null,'A Phaelen chosen to be &thorn;engill-rikr.  Only one has ever held this title, the original, son of Erland Hjalmar-s&oslash;n of magnr Khazin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (274,1,to_timestamp('11-AUG-15 09.04.12.883000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Council of Ler, The',null,null,'An alternative name for the Te''garch.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (301,1,to_timestamp('11-AUG-15 09.46.03.346000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pure Tongue',null,null,'The language of the First and Second &AElig;gan Empires (and likely earlier times).  Its written form includes both hieroglyphic and syllabic variants, often combined.  The language is considered very elegant, having a very precise and broad vocabulary with fine-tuned connotative variations and complex syntax/declensions/conjugations not found among other tongues.  Also known as Er-l&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (104,1,to_timestamp('10-AUG-15 04.22.29.906000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sacred Mount',null,null,'An alternative name for the At&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (105,1,to_timestamp('10-AUG-15 04.26.10.608000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sol Arb&icirc;n',null,null,'An ancient forest north of Ashan.  The largest forest of the modern world.  It was ancient even in the times of the Second &AElig;gea and is thought to be the oldest forest in Puell&aelig;.  The mountain known as Cilien-al&ucirc;n lies at its heart.  Also known as the Old Forest.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (109,1,to_timestamp('10-AUG-15 04.37.42.862000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ashan',null,null,'A land of modern Puell&aelig; which lies to the south of Sol Arb&icirc;n.  It is a largely inhospitable and infertile region between the forest and the Tempest Sea, featuring several small villages and cities.  It has no formal government, and most of its inhabitants are subsistence farmers who follow the monotheistic faith of Ki''mar.  A hard land which has been an area of contention between Malkar, Ki''mar, and the empire of Her-lak Merg&ouml;l for centuries.  Also known as the Shade for its proximity to the Old Forest and, ironically, as the Gardenlands.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (245,1,to_timestamp('11-AUG-15 08.39.40.783000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ithia',null,null,'A polis of Malkar, dedicated to the goddess, Ithiax.  Governed by the Ercanti, a Merchants'' council.  Also known as the Green City, the Tillers'' City, and Aeris'' Terrace.  The northern sister-city of Nithia, Ithia is one of the breadbaskets of Malkar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1225,1,to_timestamp('18-AUG-15 08.19.48.170000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rikr',null,null,'A lord of one of the houses of the magnr, the twenty-one high clans of I&aring;rth.  Less frequently used to refer to the lord of a minor house.  Also used as a predicate indicating leadership or primacy of something, i.e. &thorn;engill-rikr.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (168,1,to_timestamp('10-AUG-15 11.55.49.198000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Undying, The',null,null,'An alternative name for the members of the Council of Twenty-one during the First &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (275,1,to_timestamp('11-AUG-15 09.04.26.310000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Captain''s Council, The',null,null,'An alternative name for the She''garch.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (175,1,to_timestamp('11-AUG-15 12.19.06.782000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grand Strategist, The',null,null,'The literal translation of "Arastel," the name assumed by Xatran M&ucirc;r during the Great War.  He also referred to himself as Death''s Warlord.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1229,1,to_timestamp('18-AUG-15 08.45.41.263000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iron King',null,null,'An alternative name for the &thorn;engill-rikr of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (176,1,to_timestamp('11-AUG-15 12.20.04.627000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Death''s Warlord',null,null,'A title assumed by Xatran M&ucirc;r during the Great War.  He also referred to himself as Arastel, "The Grand Strategist."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (294,1,to_timestamp('11-AUG-15 09.24.51.003000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lost Polis, The',null,null,'An alternative name for the ruined Malkaran polis of Atheo.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (246,1,to_timestamp('11-AUG-15 08.40.51.227000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nithia',null,null,'A polis of Malkar, dedicated to the god, Nithiax.  Governed by the Ercanti, a Merchants'' council.  Also known as the Autumn City.  The southern sister-city of Ithia, Nithia is one of the breadbaskets of Malkar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (247,1,to_timestamp('11-AUG-15 08.42.13.578000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Threte',null,null,'A polis of Malkar, dedicated to the god, Tallo.  Governed by the Qintax, a council of generals.  Also known as the City of Spears and the Unfallen City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (251,1,to_timestamp('11-AUG-15 08.46.15.655000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dyal',null,null,'A polis of Malkar, dedicated to the goddess, Dyalia.  Governed by the She''garch, a council of shipping captains.  Also known as the Waterbasket of Malkar and the Mermans'' Polis.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (267,1,to_timestamp('11-AUG-15 08.58.27.134000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Spictalia',null,null,'A goddess of the people of Malkar, associated with Moon/Night.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (270,1,to_timestamp('11-AUG-15 09.01.59.806000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ercanti',null,null,'The name of the ruling bodies of the Malkaran cities of both Ithia and Sithia.  Also known as the Merchant Council.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (257,1,to_timestamp('11-AUG-15 08.53.59.452000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nama',null,null,'A goddess of the people of Malkar, associated with Wisdom/Learning.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (263,1,to_timestamp('11-AUG-15 08.56.54.763000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Medynos',null,null,'A god of the people of Malkar, associated with Craft/Labor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (281,1,to_timestamp('11-AUG-15 09.20.40.043000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City Without Walls, The',null,null,'An alternative name for the Malkaran polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (283,1,to_timestamp('11-AUG-15 09.21.07.380000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Green City, The',null,null,'An alternative name for the Malkaran polis of Ithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (306,1,to_timestamp('11-AUG-15 11.01.40.700000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Malkaran',null,null,'An adjective referring to the nation-state of Malkar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (401,1,to_timestamp('12-AUG-15 11.06.42.077000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Udmar D&ucirc;marg',null,null,'One of the At&ucirc;n, a lone mountain that rises amidst the ruins of Braal on the Summer Island.  The stone of Udmar D&ucirc;marg is a deep brown, but its exterior is veiled by frost.  This mountain has been associated with the Neraphin, Baragur, but the relationship is not understood.  The interior of the mountain is carved with circular layers of rooms that appear to have been carved by different peoples and cultures over the course of millennia.  The Ipu''qat call the mountain Ya-we-nerat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (402,1,to_timestamp('12-AUG-15 11.16.47.444000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Udmar Cajar',null,null,'One of the At&ucirc;n, a lone mountain that rises amidst the ruins of Uragar in the Death''s Blades Mountains.  The stone of Udmar Cajar is a deep terra cotta.  This mountain has historically been associated with the Neraphin, Urag, but the relationship is not understood.  The interior of the mountain is available from a gate located in the Mas-Agar district of Urag and is currently inhabited by the Shining Ones.  The extent of the interior tunnels is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (404,1,to_timestamp('12-AUG-15 11.36.50.661000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Golden Walls, The',null,null,'A popular name for the walls of the polis of Me''din.  Though the walls are tall and strong, they are in fact wrought from granite which has been painted gold.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (405,1,to_timestamp('12-AUG-15 11.37.37.671000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Golden Warriors, The',null,null,'The popular name of the soldiers of the polis of Me''din.  Their armor and weapons are in fact bronze rather than gold.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (406,1,to_timestamp('12-AUG-15 11.38.12.477000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Innumerable Spears, The',null,null,'The popular name for the warriors of the polis of Threte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (414,1,to_timestamp('12-AUG-15 11.53.15.861000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shala-kuku',null,null,'A Ba-kwe village on the shores of the Frost Sea.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (422,1,to_timestamp('12-AUG-15 01.13.17.497000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stone Forest, The',null,null,'A petrified forest on the eastern coast of the Summer Island.  The trees are tall and lifeless, their breed obscured by layers of frost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (693,1,to_timestamp('14-AUG-15 01.00.54.521000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seven Faiths, The',null,null,'An alternative name for the Caal Anuur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (302,1,to_timestamp('11-AUG-15 09.47.02.980000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Er-l&ucirc;n;',null,null,'The word for the Pure Tongue in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (282,1,to_timestamp('11-AUG-15 09.20.51.922000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City of Treaties, The',null,null,'An alternative name for the Malkaran polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (304,1,to_timestamp('11-AUG-15 09.48.42.640000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Storm Lord, The',null,null,'The name by which the Shining Ones (and others) refer the Triumvir, Neatham do''Sema''ar, better known as Ki''lan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (305,1,to_timestamp('11-AUG-15 09.49.00.863000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Great Lord, The',null,null,'The name by which the Shining Ones (and others) refer the Triumvir, Ardran Myramar, better known as Gai''lal.  Also among the Die Nearal and the Mortuani to describe Baragur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (118,1,to_timestamp('10-AUG-15 09.06.54.344000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Polis',null,null,'A city-state of the nation of Malkar, pl. poleis.  Modern poleis include Namar, Ler, Ithia, Nithia, Dyal, Threte, Phoebes, Me''din, Kourinth, Coreyra, and Spira.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (132,1,to_timestamp('10-AUG-15 09.48.59.344000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aeneum',null,null,'A gathering place where the Assembly meets to argue and cast their votes in the Malkaran polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (135,1,to_timestamp('10-AUG-15 09.55.10.237000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bibliotethum',null,null,'An alternative name for the Exalted Library of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (137,1,to_timestamp('10-AUG-15 09.56.33.277000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Acropolis',null,null,'A plateau in the heart of the polis of Namar where many of the great buildings and temples of the city are located.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (142,1,to_timestamp('10-AUG-15 11.13.21.139000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naudar',null,null,'The yellow moon of Puell&aelig;, associated with the Element of Air.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (145,1,to_timestamp('10-AUG-15 11.14.33.122000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vidar',null,null,'The green moon of Puell&aelig;, associated with the Essence of Life.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (148,1,to_timestamp('10-AUG-15 11.15.23.813000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Axtar',null,null,'The red moon of Puell&aelig;, associated with the Element of Fire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (160,1,to_timestamp('10-AUG-15 11.41.00.333000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fermoor',null,null,'The title taken by Ardran Myramar when he became High King of &AElig;gea during the Gh&ucirc;l War.  Also a (deeply flawed) modern strategy game played by the Ba-kwe, superficially similar to k&aring;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (171,1,to_timestamp('11-AUG-15 12.07.49.987000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Owl Sentinels',null,null,'An alternative name for the Owl Guard.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (694,1,to_timestamp('14-AUG-15 01.02.16.203000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'T&icirc;nara',null,null,'One of the seven cities where the Shul-nos were located.  Also called the Seven Cities.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (129,1,to_timestamp('10-AUG-15 09.43.24.998000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silt, The',null,null,'A name for the mouth of the Swiftdeep river as it enters the Tempest Sea near Threte, so-called because the current becomes shallow, thick, and impassable to traffic by large vessels.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (184,1,to_timestamp('11-AUG-15 12.32.28.953000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kithen',null,null,'A small village located in Ashan.',0);
