Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (225,1,to_timestamp('11-AUG-15 07.33.58.946000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mermaid, The',null,null,'An alternate name for the Neraphin, Asara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (166,1,to_timestamp('10-AUG-15 11.53.49.366000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great One, The',null,null,'The literal translation of "Malnos".  The name given to himself by Memar Korain when he came to Gai''lal''s Tower.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (208,1,to_timestamp('11-AUG-15 07.30.24.579000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mother',null,null,'An alternate name for the Neraphin, Irien.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (209,1,to_timestamp('11-AUG-15 07.30.30.967000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wild Mother, The',null,null,'An alternate name for the Neraphin, Irien.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (126,1,to_timestamp('10-AUG-15 09.38.57.522000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dryhten',null,null,'A town of Ashan, located in the shadow of the Death''s Blade mountains near the source of the Swiftdeep river.  It represents the far northwestern border of the Gardenlands.  Also the name of the warrior-priests of the Al''Sinyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (462,1,to_timestamp('12-AUG-15 03.45.41.677000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valley of E''no, The',null,null,'A valley in eastern Her-lak Merg&ouml;l, located in the desert of Go''shan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (189,1,to_timestamp('11-AUG-15 07.05.37.120000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Or&uacute;',null,null,'An alternate name for the Neraphin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (190,1,to_timestamp('11-AUG-15 07.06.50.381000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Elurin',null,null,'The Neraphin associated with the Essence of Light.  Also known as The Champion, the Stars'' Vigil, and Lightbringer.  He has an unknown relationship with the Sacred Mount of Udmar D&ucirc;sere.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (411,1,to_timestamp('12-AUG-15 11.46.27.006000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ice, The',null,null,'A northern area of the Great Island of modern Puell&aelig;.  Some consider the Ice to comprise only the Summer Island north of the Summer Sea, but other peoples also consider it to include the Brown Plains (i.e., all lands north of the Death''s Blades Mountains.)  The Ice, despite its inhosipitable nature, is home to a variety of peoples, including the Drak-hnai, the Ba-kwe, the Zimuit, the Ka-kuit, and the Children of the Silver Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (205,1,to_timestamp('11-AUG-15 07.29.43.320000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Queen of Shadows, The',null,null,'An alternate name for the Neraphin, Meril.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (194,1,to_timestamp('11-AUG-15 07.14.16.452000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Urag',null,null,'The Neraphin associated with the Element of Earth.  Also known as The Smith, The Deep Father, and the Anvil.  He has an unknown connection with the Sacred Mount of Udmar Cajar. The du&aring;rg of I&aring;rth worship a god known as Urag, but His connection to the Neraphin is uncertain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (216,1,to_timestamp('11-AUG-15 07.32.12.537000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dark One, The',null,null,'An alternate name for the Neraphin, Baragur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (221,1,to_timestamp('11-AUG-15 07.33.24.278000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Maiden, The',null,null,'An alternate name for the Neraphin, Asara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (224,1,to_timestamp('11-AUG-15 07.33.50.822000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Bride, The',null,null,'An alternate name for the Neraphin, Asara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (229,1,to_timestamp('11-AUG-15 07.34.47.602000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Storm, The',null,null,'An alternate name for the Neraphin, Thiassi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (232,1,to_timestamp('11-AUG-15 07.35.27.666000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ancient, The',null,null,'An alternate name for the Neraphin, Noimead.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (234,1,to_timestamp('11-AUG-15 07.35.54.657000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Chariot, The',null,null,'An alternate name for the Neraphin, Doitean.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (133,1,to_timestamp('10-AUG-15 09.50.42.021000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Assembly',null,null,'The name of the voting body in the Malkaran city-state of Namar, which is open to all landed citizens.  The Assembly gathers in the Aeneum and is guided by the Archons.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (191,1,to_timestamp('11-AUG-15 07.07.19.783000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Meril',null,null,'The Neraphin associated with the Essence of Shadow.  Also known as Merysh, The Unseen, the Queen of Shadows, and The Night Curtain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (172,1,to_timestamp('11-AUG-15 12.09.38.353000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pirakides',null,null,'An early philosopher and scholar of Malkar.  His writings underlie the current democratic model of Namar, and according to legend he was one of the founders of the polis.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (514,1,to_timestamp('12-AUG-15 10.47.57.917000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Serinin',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.  The City of Serinin is still inhabited in the modern age, the heart of the southern half of the broken Great Island, now a separate empire and continent that shares the city''s name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (515,1,to_timestamp('12-AUG-15 10.52.56.519000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cielan Agur',null,null,'An alternative name for the At&ucirc;n, literally the "Sky Spears".',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (516,1,to_timestamp('12-AUG-15 10.53.59.918000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sky Spears, The',null,null,'Translation of Cielan Agur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (518,1,to_timestamp('12-AUG-15 10.58.01.448000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aul''dyn',null,null,'A village among the Death''s Blades Mountains where the Shining Ones shelter children (excepting the Twisted Ones) who have been abandoned by the People to die in the Valley of the Cursed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (519,1,to_timestamp('12-AUG-15 11.01.12.301000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Broodmother, The',null,null,'A title given to the woman who leads the Shining Ones.  Also a name applied to a deity worshiped among the People of the Brown Plains, N&aelig;yt.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (520,1,to_timestamp('12-AUG-15 11.02.55.995000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silver Legion, The',null,null,'Warriors of the city of Uragar during the Second &AElig;gan Empire.  The Shining Ones believe themselves to be descendants of the Legion, cursed for failing the Great Lord.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (521,1,to_timestamp('12-AUG-15 11.05.23.496000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blossom Spire of Erinin, The',null,null,'The great tower at the heart of the city of Erinin, considered to be one the great wonders of the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (524,1,to_timestamp('13-AUG-15 07.41.12.001000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Phylactery',null,null,'An alternative name for a Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (525,1,to_timestamp('13-AUG-15 07.41.41.882000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Artifact',null,null,'An alternative name for a Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (528,1,to_timestamp('13-AUG-15 07.47.10.087000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Great Shiahd',null,null,'An alternative name for a Tal''Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (531,1,to_timestamp('13-AUG-15 07.52.52.265000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Maker',null,null,'An alternative name for a Wyrd-smith.  Also an alternative term for the Muatha isch Lluhahdi among the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (532,1,to_timestamp('13-AUG-15 07.53.06.920000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Binder',null,null,'An alternative name for a Wyrd-smith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (533,1,to_timestamp('13-AUG-15 07.54.32.433000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hrest',null,null,'A strange and rare crystal that has the affect of augmenting wyrds and, in some circumstances, manifesting will.  It is also used in the creation of Shiahd and, apparently, Tal''Shiahd.  Serrik Vey''ron was a scholar of hrest during the time of the Second &AElig;gan Empire.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (534,1,to_timestamp('13-AUG-15 07.59.09.349000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Echtz',null,null,'A shortened term for the Dust Plains of Echtz.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (537,1,to_timestamp('13-AUG-15 08.06.55.938000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ba-nuit',null,null,'A rare flower found on the Brown Plains and the Ice, used in alchemical and shamanic preparations.  It only grows in the snows above subterranean springs.  Also known as the Summer Flower.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (539,1,to_timestamp('13-AUG-15 08.10.27.790000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;gan Council',null,null,'An alternative name for the Council of Twenty-one during both the First &AElig;gan Empire and the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (541,1,to_timestamp('13-AUG-15 08.11.15.864000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Council of Tai''shar, The',null,null,'An alternative name for the Council of Twenty-one during the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (512,1,to_timestamp('12-AUG-15 10.41.24.688000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Heram',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (546,1,to_timestamp('13-AUG-15 09.46.23.620000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Triumvir',null,null,'A member of the Triumvirate:  Ki''lan, Gai''lal, or Calik.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (547,1,to_timestamp('13-AUG-15 09.48.13.674000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Triumvirate',null,null,'A faction during the Great War, consisting of the Triumvirs:  Gai''lal, Ki''lan, and Calik.  The Triumvirs were allied with the churches of the Seven Spears and the greater body of the N&aelig; Kisal.  They set aside the Oryoni in favor of blue staves called the Provident&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (548,1,to_timestamp('13-AUG-15 09.49.49.967000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Semaari, The',null,null,'The people of Semaar, located in the Valley of Sema, a race of legendary warriors long secluded from the rest of Puell&aelig; before Lukifan rediscovered their hidden kingdom.  The most of famous Semaari of the Second &AElig;gan Empire was Neatham do''Sema''ar, the Councilor who later became known as Ki''lan.  The fate of the Semaari after the Sundering is unknown, although some believe that the people of Zamoch may be descendants of this stock.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (550,1,to_timestamp('13-AUG-15 09.51.26.838000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eternal Peaks',null,null,'A mountain range during the Second &AElig;gan Empire and location of the Valley of Sema, now thought to be located within the broader Death''s Blades Mountains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (552,1,to_timestamp('13-AUG-15 09.57.19.221000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Crown of Straw, The',null,null,'A symbol of the king of Semaar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (553,1,to_timestamp('13-AUG-15 09.57.51.316000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Edelhaum',null,null,'The great hall where the king of Semaar ruled, located in the Valley of Sema.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (555,1,to_timestamp('13-AUG-15 10.04.09.604000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mem''mari',null,null,'A great cathedral at the University of Xaln, used as a dining and lecture hall.  Also called the Hall of Faces.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (556,1,to_timestamp('13-AUG-15 10.04.22.907000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Faces, The',null,null,'An alternative name for Mem''mari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (558,1,to_timestamp('13-AUG-15 10.04.57.016000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Bell Room, The',null,null,'An alternative name for the Whistle Chamber.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (560,1,to_timestamp('13-AUG-15 10.10.13.026000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Saamsday',null,null,'A calendar day of the People of the Brown Plains.  It lies outside the standard week, falling twice per month.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (563,1,to_timestamp('13-AUG-15 10.23.31.034000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Provident&aelig;',null,null,'Plural of Providenta.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (530,1,to_timestamp('13-AUG-15 07.51.09.824000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrd-smith',null,null,'One who can bind a wyrd into a physical object (Shiahd).  This is a very rare Talent, with fewer than a dozen wyrd-smiths having been discovered during the Second &AElig;gan Empire.  Wyrd-smiths are universally also Artificers.  Wyrd-smiths in the Empire managed to create only a small number of Shiahd and never recreated a Tal''Shiahd.  Also known as a Maker or Binder.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (496,1,to_timestamp('12-AUG-15 09.33.10.345000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kay''sar',null,null,'An alternative name for the one boy among the Oracles of Namar considered to have prophetic powers.  Also called the High Oracle.  The servants in the Shrine of the Oracle believe that the High Oracle is periodically ritually sacrificed and replaced when he comes of age, using children selected for their physical similarity, but Eleanor has evidence that Kay''sar is a mentej who has secretly ruled Namar for centuries under the guise of different children.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (465,1,to_timestamp('12-AUG-15 03.48.44.114000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Namaran',null,null,'A person from the polis of Namar or an adjective referring to an object related to Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (466,1,to_timestamp('12-AUG-15 03.49.06.170000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nithian',null,null,'A person from the polis of Nitha or an adjective referring to an object related to Nithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (468,1,to_timestamp('12-AUG-15 03.50.00.522000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Paradein',null,null,'The mythical home of the gods of Malkar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (469,1,to_timestamp('12-AUG-15 03.51.14.684000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dal-jadar',null,null,'A town of Ashan.  It represents the eastern border of the Gardenlands.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (470,1,to_timestamp('12-AUG-15 03.51.55.362000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Medynos'' Finger',null,null,'An alternative name for the Tower of Gai''lal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (471,1,to_timestamp('12-AUG-15 03.52.31.908000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tower of the Ghost, The',null,null,'An alternative name for the Tower of Gai''lal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (472,1,to_timestamp('12-AUG-15 03.53.15.578000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stone Plain, The',null,null,'An alternative name for the Stone Desert.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (474,1,to_timestamp('12-AUG-15 03.54.48.147000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Sorcerer, The',null,null,'A name by which the people of Malkar refer to the master of Medynos'' Finger, Malnos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (477,1,to_timestamp('12-AUG-15 04.03.50.668000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mergol&iuml; Lancers',null,null,'Elite warriors of the Empire of Her-lak Merg&ouml;l, feared throughout the Great Island.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (479,1,to_timestamp('12-AUG-15 04.10.55.423000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Oryoni',null,null,'Grey stone staves possessed by the Undying of the First &AElig;gan Empire and later by the Councillors of the Second &AElig;gan Empire.  Although evidently Tal''Shiahd of great power, their nature was ill understood.  Their touch led to immediate and painful death for most mortals, causing their flesh to transform to crystal and stone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (480,1,to_timestamp('12-AUG-15 04.12.00.217000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tytan',null,null,'Your guess is as good as mine?',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (481,1,to_timestamp('12-AUG-15 04.12.50.348000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stone Desert, The',null,null,'A broad plain of slate and granite in the southern portions of Malkar.  Nothing grows or lives in this region and it is thought among the locals to be cursed.  The Tower of Gai''lal lies against the far southern edge of the Desert on a cliff set against the Tempest Sea.  Also known as Medynos'' Hand and the Stone Plain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (482,1,to_timestamp('12-AUG-15 04.18.26.980000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Tower, The',null,null,'An alternative name for the Tower of Gai''lal''.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (483,1,to_timestamp('12-AUG-15 04.27.05.316000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Solstice Games, The',null,null,'A series of twenty-one trials in which the students of the University of Xaln competed for glory and fame.  The games tested intellect and skill with wyrds.  Each game awarded one Star for its victor.  A competitor who earned twenty-one stars in a single Game was awarded with the Hestilion Chalice.  Only Ardran Myramar ever earned this honor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (486,1,to_timestamp('12-AUG-15 04.36.42.852000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unborn Horrors, The',null,null,'Translation of Bel’caj Ged’don.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (488,1,to_timestamp('12-AUG-15 09.05.10.484000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Izlie',null,null,'A four-legged, winged lizard approximately the size of a house cat.  Izlie are known to come in two types:  emerald with blue eyes and red with green eyes.  It is unknown whether these types represent breeds or separate species, but two flavors have been known to react violently when brought into contact.  Izlie are rare and generally only seen around sacred places and during times of import, resulting in their being associated with superstition and legend.  Many believe them to be mythical creatures.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (489,1,to_timestamp('12-AUG-15 09.08.26.847000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lardle',null,null,'A tree-dwelling rodent with a long, furry tail.  They are found through Llua''la and also in the cavern of Alanon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (485,1,to_timestamp('12-AUG-15 04.30.47.098000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Karileon Chalice',null,null,'The reward given to a participant victorious in all twenty-one challenges of the Equinox Games. Never achieved during the time of the Second &AElig;gan Empire, with Neatham do''Sema''ar coming closest at nineteen Stars.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (185,1,to_timestamp('11-AUG-15 06.58.31.140000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Neraphin',null,null,'The Or&uacute;, created by D&uacute;vinar to limit and govern the Gifts offered by Istaron to his mortal children.  The known Neraphin are Elurin, Meril, Irien, Baragur, Urag, Asara, Thiassi, Doitean, Noimead, and Ajzagal.  The first nine govern the Pillars of Da''kol, while Ajzagal governed the Song and Naming.  Originally, the Neraphin warded creation from the world of Llineveh but at some point departed that realm to come to Puell&aelig;.  Azjagal was once the lord and mentor of the younger Neraphin, but he disappeared, perished, or did not accompany his children to Puell&aelig.  Baragur is currently at conflict with his brethren.  At alternate times in Puell&aelig;n history, the people have considered there to be seven, eight, or nine Neraphin; or have been unaware of their presence altogether.  Due to their ability to grant influence over Da''kol to those without the Gift, the Neraphin have often been considered gods.  Also called The Arbitrators, The Seven, The Seven Spears, The Nine, The Seven Spears, the Or&uacute;, and the Lords of Llineveh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (195,1,to_timestamp('11-AUG-15 07.15.00.601000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Asara',null,null,'The Neraphin associated with the Element of Water.  Also known as The Maiden, The Siren, The Patient, The Bride, and The Mermaid.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (204,1,to_timestamp('11-AUG-15 07.29.35.326000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Unseen, The',null,null,'An alternate name for the Neraphin, Meril.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (213,1,to_timestamp('11-AUG-15 07.31.41.236000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Enemy, The',null,null,'An alternate name for the Neraphin, Baragur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (214,1,to_timestamp('11-AUG-15 07.31.49.667000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Child, The',null,null,'An alternate name for the Neraphin, Baragur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (219,1,to_timestamp('11-AUG-15 07.32.50.447000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Anvil, The',null,null,'An alternate name for the Neraphin, Urag.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (237,1,to_timestamp('11-AUG-15 07.36.40.869000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'All-Seer, The',null,null,'An alternate name for the Neraphin, Noimead.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (240,1,to_timestamp('11-AUG-15 07.37.53.129000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Namma',null,null,'An alternate name for the Neraphin, Ajzagal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (258,1,to_timestamp('11-AUG-15 08.54.19.986000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ithiax',null,null,'A goddess of the people of Malkar, associated with Spring/Birth/Life.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (490,1,to_timestamp('12-AUG-15 09.11.45.820000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Leidhle',null,null,'A creature that superficially resembles a large lardle (approximately twice the size) but with six legs.  A leidhle has an undercoat of violet fur running along its spine that can be exposed by touch.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (273,1,to_timestamp('11-AUG-15 09.03.44.748000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Te''garch',null,null,'The ruling body of the Malkaran polis of Ler, comprised of an oligarchic council of seven.  Also known as the Council of Ler.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (277,1,to_timestamp('11-AUG-15 09.05.50.690000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Golden King, The',null,null,'The monarch of the Malkaran polis of Me''din.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (285,1,to_timestamp('11-AUG-15 09.21.25.928000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aeris'' Terrace',null,null,'An alternative name for the Malkaran polis of Ithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (288,1,to_timestamp('11-AUG-15 09.22.09.835000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Unfallen City, The',null,null,'An alternative name for the Malkaran polis of Threte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (617,1,to_timestamp('13-AUG-15 09.34.47.476000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Prethaanor',null,null,'A secondary training site for the Meduri, located in the city of Il''yar.  Founded late in the Second &AElig;gan Empire.  Also known as the Hall of Effacing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (293,1,to_timestamp('11-AUG-15 09.24.30.357000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mermans'' Polis, The',null,null,'An alternative name for the Malkaran polis of Dyal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (297,1,to_timestamp('11-AUG-15 09.25.36.297000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City of Flesh Eaters, The',null,null,'An alternative name for the seceded Malkaran polis of Calyth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (403,1,to_timestamp('12-AUG-15 11.18.13.699000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Udmar D&ucirc;sere',null,null,'One of the At&ucirc;n, a lone mountain that rises amidst the jungles of the continent of Serinin along the southwestern coast.  The stone of Udmar D&ucirc;sere is a pure white.  This mountain has been associated with the Neraphin, Elurin, but the relationship is not understood.  A pair of golden doors leads into the base of the mountain, sized to accommodate giants.  What lies beyond those doors is not known.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (420,1,to_timestamp('12-AUG-15 12.23.45.951000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Forever Sea, The',null,null,'The sea that lies to the west of Malkar in modern Puell&aelig;.  It is relatively tranquil and warm, particularly its southern regions, and its many reefs are a plentiful source of fish and shell-fish.  The domain of the goddess, Dyalia, is thought to be located in one of the sea''s deep ravines.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (427,1,to_timestamp('12-AUG-15 01.22.29.673000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Amatuluktu',null,null,'A village of the Ba-kwe on the Brown Plains.  It was visited by the na''k&ouml;r on their journey between the Summer Island and Uragar.  It lies on the river Ama-tu, from which it takes its name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (491,1,to_timestamp('12-AUG-15 09.15.23.497000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bartholomew',null,null,'A man who recently came to power in the polis of Ler.  This is unusual both because he is rumored to be a foreigner of Merk&ouml;li descent and because Ler is a traditional society which has for centuries been ruled by the Te''garch.  According to rumor, the seven families have accepted his rule willingly.  For several years, he has been predicting invasion by Her-lak Merg&ouml;l and has been preparing the city-state for war with his former homeland.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (715,1,to_timestamp('14-AUG-15 02.34.20.775000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Black Feather',null,null,'A derogatory term used to describe a priest or initiate of the Massra Dein.  Also called a Charred or Scalded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (716,1,to_timestamp('14-AUG-15 02.35.50.738000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shaabah',null,null,'A religious order that predated the Second &AElig;gan Empire.  They believed in a cycle of reincarnation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (432,1,to_timestamp('12-AUG-15 01.31.01.683000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Yetn',null,null,'Large, bipedal beasts of the Death''s Blades Mountains.  Little is known of the yetn or their origins except that they live in small societies and possess rudimentary intelligence and language.  The yetn and the Shining Ones exist in a fragile state of detente, sharing access to the ruins of Uragar.  In appearance, the yetn superficially resemble standing ursines with human features.  Except in legend, the yetn are unknown among the People of the Brown Plains.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (434,1,to_timestamp('12-AUG-15 01.38.34.431000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Brown Plains, The',null,null,'An area of the Great Island of Puell&aelig; that lies between the Death''s Blades Mountains and the Summer Sea.  It is home to the People and is an inhospitable tundra caught in state of winter or near-winter.  The na''k&ouml;r passed through this land on their journey between Udmar D&ucirc;marg and Uragar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (442,1,to_timestamp('12-AUG-15 01.47.20.466000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Clan of the Brown Elk, The',null,null,'One of the seven clans of the Ipu''qat.  They are the only clan to permanently inhabit the Summer Island, not departing even during the Long Night.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (457,1,to_timestamp('12-AUG-15 02.03.50.787000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hwe''leri',null,null,'The title of one of two leaders of the corti.  It translates to ''Keeper of the Staff'' and was thought to be possessed by a single man, Jan&icirc;l Hwe''leri, throughout much of the history of Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (463,1,to_timestamp('12-AUG-15 03.46.02.343000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Go''shan',null,null,'A desert in eastern Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (467,1,to_timestamp('12-AUG-15 03.49.22.010000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ithian',null,null,'A person from the polis of Ithia or an adjective referring to an object related to Ithia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (473,1,to_timestamp('12-AUG-15 03.53.40.044000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Medynos'' Hand',null,null,'An alternative name for the Stone Desert.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (475,1,to_timestamp('12-AUG-15 03.55.03.220000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ghost, The',null,null,'A name by which the people of Malkar refer to the master of Medynos'' Finger, Malnos.  Also known as the Grey Sorcerer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (510,1,to_timestamp('12-AUG-15 10.41.08.282000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Salidar',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (408,1,to_timestamp('12-AUG-15 11.42.01.218000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Drak-hvallen',null,null,'A series of islands located in the Black Sea off the northwestern coast of the Brown Plains.  These islands are home to the villages of the Drak-hnai.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (495,1,to_timestamp('12-AUG-15 09.32.43.328000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'High Oracle, The',null,null,'An alternative name for Kay''sar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (511,1,to_timestamp('12-AUG-15 10.41.16.839000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Il''yar',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (513,1,to_timestamp('12-AUG-15 10.44.13.174000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dangracht',null,null,'A vast desert during the time of the Second &AElig;gan Empire and home of the warrior people known as the Grek''Maka.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (721,1,to_timestamp('14-AUG-15 02.49.18.913000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shahra',null,null,'A western island city of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (722,1,to_timestamp('14-AUG-15 02.49.38.269000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ta-shara',null,null,'A city of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (723,1,to_timestamp('14-AUG-15 02.50.01.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Persyan',null,null,'A city of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (729,1,to_timestamp('14-AUG-15 03.00.29.264000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lih''tin',null,null,'A visible current of electricity, generally azure in hue.  Lih''tin is summoned when the na''k&ouml;r attempt to harm to one another, preventing them from doing so.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (497,1,to_timestamp('12-AUG-15 09.39.42.815000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shrine of the Oracles, The',null,null,'A subterranean complex in Namar and home to the Oracles of Namar and their servants and wardens.  The cave is carved from Dragon Stone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (498,1,to_timestamp('12-AUG-15 09.41.20.022000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Synern',null,null,'A Shiahd used to create a copula among Shapers who are not Builders.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (499,1,to_timestamp('12-AUG-15 09.45.13.805000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Death''s Blades Mountains, The',null,null,'A great mountain range that separates the Brown Plains from the southern lands of the Great Island, essentially dividing the land into two geographically isolated masses.  The mountains were raised during the Sundering, the cataclysm that ended the Second &AElig;gan Empire.  Also known as N&aelig;yt''s End.  The mountains are among the tallest in the world and are generally considered impassable.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (501,1,to_timestamp('12-AUG-15 09.47.40.688000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Death''s Blades, The',null,null,'And alternative name for the Death Blade Mountains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (503,1,to_timestamp('12-AUG-15 09.51.33.585000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sevenfold Moot',null,null,'A rare meeting of all seven clans of the Ipu''qat, held in the holy city of Arikh-nu.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (504,1,to_timestamp('12-AUG-15 09.53.41.239000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seven Tribes, The',null,null,'An alternative name for the Ipu''qat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (505,1,to_timestamp('12-AUG-15 09.57.05.219000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Holy Plain, The',null,null,'The ice plains surrounding the holy city of Arikh-nu.  Violence is forbidden within the Holy Plains among all the Peoples of the Brown Plains, even the Drak-hnai.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (416,1,to_timestamp('12-AUG-15 12.05.46.033000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Summer Sea, The',null,null,'The sea which lies north of the Brown Plains.  It is frozen during the Long Night but is navigable by ship during the summer months.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (417,1,to_timestamp('12-AUG-15 12.08.26.342000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Long Night, The',null,null,'A three-month period of permanent night that descends over the northern areas of the Great Island during the winter months.  The Long Night affects Drak-hvallen, the Summer Sea, and the Summer Island.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (425,1,to_timestamp('12-AUG-15 01.17.48.289000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wa-mu-male',null,null,'A lake in the Brown Plains.  The village of Ikamukatika lies on its shore.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (561,1,to_timestamp('13-AUG-15 10.12.47.076000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yriena',null,null,'The Broodmother of the Shining Ones at the time the na''k&ouml;r visited Uragar.  She is very old but not old enough that she was the Broodmother when Memar Korain visited Udmar Cajar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (418,1,to_timestamp('12-AUG-15 12.11.50.389000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Summer Island, The',null,null,'The northernmost known area of the Great Island of modern Puell&aelig;, an island separated from the Brown Plains by the Summer Sea.  This inhospitable, largely barren island is visited only by the Ipu''qat, and only the Brown Elk Tribe remain when the Long Night has fallen (thus the origin of the name).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (566,1,to_timestamp('13-AUG-15 10.36.13.735000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ku-kuit',null,null,'One of the Peoples of the Brown Plains.  They live in simple coastal villages and rely upon fishing and seacraft to survive.  Also called the Salt Born and the Salt Folk.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (603,1,to_timestamp('13-AUG-15 09.20.13.527000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Erudite',null,null,'A graduate of one of the four colleges of Bal Dannan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (542,1,to_timestamp('13-AUG-15 08.12.06.658000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Undying Council, The',null,null,'An alternative name for the Council of Twenty-one during the First &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (278,1,to_timestamp('11-AUG-15 09.06.25.421000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'High Priestess of Spictalia, The',null,null,'Leader of the faith of Spictalia and also ruler of the Malkaran polis of Spira.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (298,1,to_timestamp('11-AUG-15 09.29.47.907000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Father of Monsters, The',null,null,'Translation of Az-Azaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (300,1,to_timestamp('11-AUG-15 09.36.25.391000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Argi',null,null,'A young woman descended of the Shining Ones.  She was among those who discovered and saved the life of Kain after his Sphere descended.  Later, against the guidance of her people, she led the na''k&ouml;r (Malnos, Malig, Kain, and Eleanor) to Uragar, to the resting place of the Sphere, and eventually to her people at Udmar Cajar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (409,1,to_timestamp('12-AUG-15 11.43.11.727000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black Sea, The',null,null,'A sea located northwest of the Brown Plains and west of the Ice.  It is frozen or partially frozen during much of the Long Night.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (419,1,to_timestamp('12-AUG-15 12.21.18.116000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mas-Agar',null,null,'A district of the city of Uragar, separated from the city proper by a great Tal''Shiahd shaped as a bridge.  Udmar Cajar and several great edifices are located here, suggesting its importance.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (424,1,to_timestamp('12-AUG-15 01.16.42.591000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'White Mountains, The',null,null,'A mountain range along the northeastern shore of the Brown Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (535,1,to_timestamp('13-AUG-15 08.01.44.383000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dust Plains of Echtz, The',null,null,'An area of the Brown Plains nestled against the Death''s Blades Mountains.  Those who enter this region are never seen again, and the People believe that the Dust Plains are peopled with monsters bound here during a mythological conflict.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (449,1,to_timestamp('12-AUG-15 01.50.39.180000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wilden',null,null,'An alternative name for the Zimuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (455,1,to_timestamp('12-AUG-15 02.00.43.868000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gres''Mahar',null,null,'The title of one of two leaders of the corti.  It translates to ''Grey Sentinel'' and was thought to be possessed by a single man, Kisel Gres''Mehar, throughout much of the history of Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (536,1,to_timestamp('13-AUG-15 08.05.25.118000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'N&aelig;yt',null,null,'The primordial goddess of the Faith of the People.  She was the first goddess from which all else was born.  Eventually she turned upon and attempted to devour her children.  N&aelig;yt was defeated in a great war that nearly destroyed creation, and her eyes became the moons.  Also known as the Broodmother.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (543,1,to_timestamp('13-AUG-15 08.18.07.448000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tai''shar',null,null,'A great crystal mountain near the city of Aldamar, considered by some to be one of the At&ucirc;n.  During the Second &AElig;gan Empire, it was a symbol closely  associated with the Council of Twenty-one, who convened regularly in the Hall of Tai''shar, a chamber located in the heart of the mountain.  Many believe that the entire mountain is crafted of Hrest, although if so the crystal does not respond as one would expect.  The location of the mountain is unknown in modern Puell&aelig;.  Some believe it may have sunk into the sea during the sundering.  Others believe it may be at the heart of the Maelstrom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1230,1,to_timestamp('18-AUG-15 08.45.49.538000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'High Thane',null,null,'An alternative name for the &thorn;engill-rikr of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (585,1,to_timestamp('13-AUG-15 08.31.21.189000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blessed, The',null,null,'An alternative name for the Kalit’edon Emari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (593,1,to_timestamp('13-AUG-15 09.15.09.337000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ishar&aelig;',null,null,'A school of Bal Dannan devoted to algebra and numismatics (all mathematics).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (594,1,to_timestamp('13-AUG-15 09.15.37.088000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aman',null,null,'A school of Bal Dannan devoted to matter and physics (earth sciences, physics, anatomy).<br/><br/>Also one of the cara-aleach and sworn partner to Enwi.  He accompanied Delsar to I&aring;rth, where Enwi was tortured and disfigured by the du&aring;rg and then killed in Arastel''s assault on the Mountain Home.  Aman himself was horribly disfigured, rendered mute, deaf, and blind.  Amber restored his sight and hearing, but he remains mute, acting as Delsar''s silent protector.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (595,1,to_timestamp('13-AUG-15 09.16.06.116000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Katar',null,null,'A school of Bal Dannan devoted to life sciences (biology, chemistry, and neuroscience).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (596,1,to_timestamp('13-AUG-15 09.16.39.327000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ynara',null,null,'A school of Bal Dannan devoted to applied sciences (applied mathematics, medicine, engineering, alchemy, etc.).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (598,1,to_timestamp('13-AUG-15 09.18.05.432000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Numen',null,null,'A professor of Bal Dannan, also known as a Tutor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (599,1,to_timestamp('13-AUG-15 09.18.19.158000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tutor',null,null,'An alternative name for a Numen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (600,1,to_timestamp('13-AUG-15 09.18.41.755000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Magnate',null,null,'A master of the four Lyceum of Bal Dannan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (601,1,to_timestamp('13-AUG-15 09.19.22.076000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lord Magnate',null,null,'The title of the steward of Bal Dannan and overseer of all four colleges.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (602,1,to_timestamp('13-AUG-15 09.19.50.184000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Danathaan',null,null,'The great university of Bal Danaan located in the city of Il''yar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (608,1,to_timestamp('13-AUG-15 09.28.05.923000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Effaced, The',null,null,'An alternative name for an acolyte of the Meduri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (609,1,to_timestamp('13-AUG-15 09.28.16.328000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Selfless, The',null,null,'An alternative name for an acolyte of the Meduri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (610,1,to_timestamp('13-AUG-15 09.28.42.477000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ash''Medur',null,null,'An alternative name for an acolyte of the Meduri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (648,1,to_timestamp('13-AUG-15 11.00.21.554000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keepers of Account, The',null,null,'An alternative name for the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (653,1,to_timestamp('13-AUG-15 11.02.05.667000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sage',null,null,'A rank among the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (654,1,to_timestamp('13-AUG-15 11.02.11.890000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sapient',null,null,'A rank among the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (655,1,to_timestamp('13-AUG-15 11.02.28.605000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'High Sapient',null,null,'The highest rank among the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (549,1,to_timestamp('13-AUG-15 09.50.12.773000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Semaar',null,null,'The kingdom of the people of the Valley of Sema.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (551,1,to_timestamp('13-AUG-15 09.52.46.761000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Valley of Sema, The',null,null,'A valley within the Eternal Peaks, thought lost or mythical before it was rediscovered by Lukifan during the prior age.  Sema is home to the Semaari, a race of legendary warriors long secluded from the rest of Puell&aelig;.  Also called more simply the Valley.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (407,1,to_timestamp('12-AUG-15 11.40.13.884000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Drak-hnai',null,null,'A people of the Ice, known as sea-faring marauders.  Their kraken-themed vessels are feared throughout the north, and they are even known to pillage as far south as Malkar.  The villages of the Drak-hnai are located among the Drak-hvallen islands in the Black Sea.  It is thought be some that a second clan of the Drak-hnai hail from islands on the eastern edge of the Brown Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (662,1,to_timestamp('13-AUG-15 11.30.22.084000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Flesh Armor',null,null,'An alternative name for qadr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (664,1,to_timestamp('13-AUG-15 11.31.03.524000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shug',null,null,'A warrior of the Shugda Legion.  Also known as the Blooded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (667,1,to_timestamp('13-AUG-15 11.32.33.333000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Os-Shug',null,null,'A master of a "school" of the Shugda Legion.  The Os-Shug always numbered exactly 100.  Also known as the Twice Blooded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (668,1,to_timestamp('13-AUG-15 11.33.06.660000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shil',null,null,'A term in the language of the Shug, meaning "frivolous emotions."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (649,1,to_timestamp('13-AUG-15 11.00.31.900000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chroniclers, The',null,null,'An alternative name for the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (652,1,to_timestamp('13-AUG-15 11.01.59.116000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Scholar',null,null,'A rank among the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (565,1,to_timestamp('13-AUG-15 10.35.22.942000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ba-kwe',null,null,'One of the Peoples of the Brown Plains.  They live in simple villages and have developed rudimentary agriculture.  Also called the Still Folk and the People of the Stone Roof.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (579,1,to_timestamp('13-AUG-15 06.44.12.303000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cora-ladumduur',null,null,'A cloud-born landmass claimed by Merik do''Rehny and N''koth Velar at some point during the Second &AElig;gan Empire.  Az-Azaal and N&aelig;yla ruled the armies of the Bel''caj Ged''don from the citadel of Dagar Morduum atop Cora-ladumduur during the Great War; and the Unborn Horrors themselves were birthed in Black Waters at its base.  The location and fate of the floating isle in the current age is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (575,1,to_timestamp('13-AUG-15 05.46.56.525000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great War, The',null,null,'The war which broke the Second &AElig;gan Empire.  For unknown reasons, the Council of Twenty-one was sundered.  Some became the Mortuani, others the Triumvirate, and still others remained neutral.  The war waged for over a century, eventually ending when the Sundering broke the world.  Once the War and the Cataclysm were finished, over ninety-nine percent of the world''s population had been lost, and most of the remainder had fled into the First Domain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (574,1,to_timestamp('13-AUG-15 05.44.51.558000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Second Seat',null,null,'An alternative name for Silent Seat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (562,1,to_timestamp('13-AUG-15 10.19.11.142000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mortuani, The',null,null,'A faction during the Great War, consisting of: Arastel, Balthasar, T''van, Az-Azaal, N&aeligyla, Mariel, Kitira, Izmorah, Sarahl, Malracht, Anezra, and Roggarok.  The Mortuani were allied with the Neraphin, Baragur.  Some consider Zaryth dar Mistral to have stood among the Mortuani, but she had long since been banished to the Void when the War broke.  Members of the Mortuani were also referred to as Mortuani or Dread Lords.  Among themselves, they were known as the Elegari.  They set aside the Oryoni in favor of dark, twisted staves for which the name, if it ever existed, has been lost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (650,1,to_timestamp('13-AUG-15 11.01.23.293000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sa',null,null,'An honorific applied to a Sapient of the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (564,1,to_timestamp('13-AUG-15 10.30.50.547000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wemi',null,null,'A man of the Ba-kwe, inhabitant of Kanalokamu, and father of Ana.  He invited Kain into his home after he descended from the Death''s Blades Mountains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (639,1,to_timestamp('13-AUG-15 10.47.25.756000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kurt',null,null,'A courthouse of the Jesur&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (640,1,to_timestamp('13-AUG-15 10.47.51.583000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Scaldar',null,null,'An advocate of the state during a Jesur&aelig; trial.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (641,1,to_timestamp('13-AUG-15 10.48.05.897000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Faldar',null,null,'An advocate of the accused during a Jesur&aelig; trial.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (643,1,to_timestamp('13-AUG-15 10.49.26.435000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jesutarum',null,null,'A judge of the Jesur&aelig; (pl.Jesutari).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (645,1,to_timestamp('13-AUG-15 10.50.43.050000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Os-Aryd-Jesutarum',null,null,'Traditionally, the Os-Jesutarum stood as equals among the Jesur&aelig;, without rank.  Jeamoch N&icirc;el assumed this title, “Chief High Justice”, when he reformed the court.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (578,1,to_timestamp('13-AUG-15 06.11.18.106000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bliss Stone',null,null,'A Shiadh which magnifies the positive emotional response of those who touch it, inducing a state of intense, blissful intoxication.  It was one of few Shiahd created and mass produced during the Second &AElig;gan Empire.  Due to their crippling and addictive nature, Bliss Stones were eventually declared as forbidden by the Council of Twenty-one.  During the Gh&ucirc;l War, they were employed as lures.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (704,1,to_timestamp('14-AUG-15 02.26.53.337000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Confessor',null,null,'The title of a priest of the Massra Dein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (705,1,to_timestamp('14-AUG-15 02.27.07.817000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'High Confessor',null,null,'The title of a high priest of the Massra Dein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (707,1,to_timestamp('14-AUG-15 02.28.42.306000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Children of God, The',null,null,'Literal translation of Massra Dein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (582,1,to_timestamp('13-AUG-15 06.53.19.453000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'M&ucirc;r Denaagra',null,null,'A black lake at the base of Dagar Morduum atop the isle of Cora-ladumduur.  The monsters of the Bel''caj Ged''don were spawned in this foul pit.  Literally, "Dark Womb".  Also known as the Black Waters.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (573,1,to_timestamp('13-AUG-15 05.44.39.322000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silent Seat',null,null,'Name for members of a Copula other than the Builder binding the wyrd.  Such Shapers are bound to the wyrd and are unable to withdraw but cannot control its pattern.  Also called Second Seat, Second Chair, Spare, or Battery.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1231,1,to_timestamp('18-AUG-15 08.47.45.484000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;engill',null,null,'The collective name for the eight leaders of the Iron Born, chosen from among the rikr of the twenty-one magnr.  Also the name of a singular member.  They rule I&aring;rth from the Mountain Home.  Only seven of the seats are currently occupied, by the magnr:  Banama&thorn;r, F&oacute;lk, Haldororf, Hlorn, Kojungr, Skjotla, and Vald.  No replacement was selected for  magnr Undlflos after their disgrace.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1419,1,to_timestamp('21-AUG-15 06.28.59.228000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mar''jak',null,null,'A word in the tongue of the Dar''lak with several meanings, including "hunt", "track," "eat," and "kill."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (709,1,to_timestamp('14-AUG-15 02.30.27.140000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Emminar',null,null,'The god of the Massra Dein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (711,1,to_timestamp('14-AUG-15 02.31.29.946000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vy''shin',null,null,'"Trespass" in the Pure Tongue, used by the Massra Dein to describe any sin of action committed against the laws of Emminar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (607,1,to_timestamp('13-AUG-15 09.27.42.919000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Medur',null,null,'An island and city during the Second &AElig;gan Empire.  The Meduri were trained here and originally were comprised exclusively of the ethnic stock of the island.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (233,1,to_timestamp('11-AUG-15 07.35.45.556000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Flamebringer, The',null,null,'An alternate name for the Neraphin, Doitean.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (238,1,to_timestamp('11-AUG-15 07.37.22.959000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vulcan',null,null,'An alternate name for the Neraphin, Doitean.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (239,1,to_timestamp('11-AUG-15 07.37.33.821000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Roimh',null,null,'An alternate name for the Neraphin, Noimead.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (243,1,to_timestamp('11-AUG-15 07.38.22.729000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mentor, The',null,null,'An alternate name for the Neraphin, Ajzagal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (244,1,to_timestamp('11-AUG-15 08.37.19.418000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Namar',null,null,'A polis of Malkar, dedicated to the goddess, Nama.  Governed by The Assembly, a direct democracy comprised of all landed citizens.  Also known as the City of the Oracle, the City of a Thousand Voices, the City Without Walls, and the City of Treaties.  Namar has no standing army or walls and is a place of truce/peace within Malkar.  It is also home to the Oracle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (250,1,to_timestamp('11-AUG-15 08.45.11.790000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Me''din',null,null,'A polis of Malkar, dedicated to the god, Medynos.  Governed by the Golden King.  Also known as the Golden City and renowned for its smiths, mines, and high walls.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (256,1,to_timestamp('11-AUG-15 08.52.03.643000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Calyth',null,null,'A former polis of Malkar, dedicated to the goddess, Calysos.  The monarchy of Calyth was overthrown by its slave class some two centuries before the modern day, and it is no longer considered a city-state of the kingdom.  Also known as the Slaves'' City, the Traitors'' City, and the City of Flesh Eaters.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (265,1,to_timestamp('11-AUG-15 08.57.27.500000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kourix',null,null,'A god of the people of Malkar, associated with Sun/Fire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (500,1,to_timestamp('12-AUG-15 09.47.12.348000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'N&aelig;yt''s End',null,null,'An alternative name for the Death''s Blade Mountains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (502,1,to_timestamp('12-AUG-15 09.49.27.066000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arikh-nu',null,null,'A city considered holy to all the Peoples of the Brown Plains and Ice, including the Ku-kuit, Ipu''qat, Ba-kwe, and Drak-hnai.  It is located on the Holy Plain in the eastern areas of the Brown Plains.  Violence is forbidden in the city, and all the Peoples meet in peace here, on very rare occasions together.  Malnos believes that Arikh-nu may be built near the ravine leading to Chamoch.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (508,1,to_timestamp('12-AUG-15 10.40.49.386000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Erinin',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.  Malnos speculates that the modern city of Haldar is built near or atop the ruins of Erinin.  One of the T&icirc;nara, dedicated to Irien, and location of the Blossom Spire of Erinin.  It was the site of a battle between Az-Azaal and Calik during the Great War in which both perished and the city itself was destroyed.  The elithi consider Erinin to be their ancestral home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (710,1,to_timestamp('14-AUG-15 02.30.48.887000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'K''emminar',null,null,'The enemy, or devil, of the Massra Dein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (517,1,to_timestamp('12-AUG-15 10.55.40.521000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Uragar',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.  During the Great War, it lay under the dominion of the Triumvir, Ki''lan, but was also evidently of some import to Gai''lal, whose wife, Marianthala, had a sanctuary in the city.  It was destroyed by Arastel.  One of the T&icirc;nara, dedicated to Urag, and location of Udmar Cajar.  The modern ruins lie among the Death''s Blades Mountains and are occupied by the Shining Ones and yetn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (567,1,to_timestamp('13-AUG-15 10.38.09.023000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ipu''qat',null,null,'One of the Peoples of the Brown Plains.  They also visit the Summer Island (and one clan, the Clan of the Brown Elk, exclusively hunts these lands).  They are migrant hunters who do not build permanent structures, construct boats, or farm the land.  There are seven clans, which include the Clan of the Brown Elk Clan, the Clan of the Muskrat (now extinct), and the Clan of the White Rabbit.  Also called the Sevenfold People and the Seven Tribes.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (568,1,to_timestamp('13-AUG-15 10.41.56.171000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Zimuit',null,null,'One of the Peoples of the Brown Plains, who once lived in or near the Death''s Blades Mountains.  They are the supposed architects of the city of Camaar and may well be extinct in the modern day.  It is also possible that they are the Shining Ones.  Also called the Wilden and the Mountain Kin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (569,1,to_timestamp('13-AUG-15 05.27.34.719000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Egra Door',null,null,'Stone portals found throughout the world of Puell&aelig;, most frequently among the ruins of ancient cities.  The portals are marked with runes that map to Pillar, which when activated in a sequence allow travel between the Doors.  These rune matrices are named Sigilia, and aach door has its own combination.  The origin of the doors is unknown, and they are virtually indestructible.  Zaryth dar Mistral discovered how to manipulate the doors and uncovered many of the matrices during the Second &AElig;gan Empire, but she was never able to change their paths or add new Gates to the network.  She was, however, able to create Oilear and i''Srin l&ucirc;n based on study of the Gate''s design.  Also called Zaryth''s Gate and Gods'' Door.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (570,1,to_timestamp('13-AUG-15 05.36.59.849000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sigilia',null,null,'A combination used to "unlock" an Egra Door, thus choosing a destination.  Each door is marked with twenty-four runes (each Pillar three times) in a fixed pattern.  To mark a Sigilia, first nine runes are selected, then each is activated by touching it with a Pillar.  Essentially, it is like a door with twenty-four locks where each lock takes eight different keys.  The proper nine locks must be paired with the appropriate key in order for the door to open to the desired location.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (571,1,to_timestamp('13-AUG-15 05.40.57.455000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Zaryth''s Gate',null,null,'An alternative name for an Egra Door.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (572,1,to_timestamp('13-AUG-15 05.41.05.996000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gods'' Door',null,null,'An alternative name for an Egra Door.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (523,1,to_timestamp('13-AUG-15 07.41.03.117000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Madstone',null,null,'An alternative name for a Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (526,1,to_timestamp('13-AUG-15 07.41.51.436000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fetish',null,null,'An alternative name for a Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (484,1,to_timestamp('12-AUG-15 04.28.08.965000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Equinox Games, The',null,null,'A series of twenty-one trials in which the students of the University of Xaln competed for glory and fame.  The games tested physical skills.  Each game awarded one Star for its victor.  A competitor who earned twenty-one stars in a single Game was awarded with the Karileon Chalice.  This honor was never achieved during the time of the Second &AElig;gan Empire, with Neatham do''Sema''ar coming closest at nineteen Stars.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (493,1,to_timestamp('12-AUG-15 09.26.02.230000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Child Oracles',null,null,'Alternative name for the Oracles of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (538,1,to_timestamp('13-AUG-15 08.07.10.901000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Summer Flower',null,null,'An alternative name for Ba-nuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (544,1,to_timestamp('13-AUG-15 08.18.29.536000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Maelstrom',null,null,'A name for the storms that rage throughout the Tempest Sea.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (545,1,to_timestamp('13-AUG-15 08.21.13.280000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Council of Twenty-one, The',null,null,'The name given to the bodies which governed the First &AElig;gan Empire and Second &AElig;gan Empire.  In both cases, they were marked (and possibly selected) by their ability to wield the Oryoni.  During the Second &AElig;gan Empire, the members of the council changed over time.  Lukifan was replaced by N''koth Velar after the former perished.  Zaryth dar Mistral was replaced by Marhiar after the former was banished to the Void for her crimes.  Division among the council is what precipitated the Great War.  Also known as the &AElig;gan Council, Unbreakable Council, Undying Council, and the Council of Tai''shar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (576,1,to_timestamp('13-AUG-15 06.00.03.825000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chamoch Party, The',null,null,'The group which rediscovered Chamoch during the last age, consisting of Elaina J&aelig;s''n&aelig;, Kerin Farryn-lar, Johra Kail, Antoni do''Ch&aelig;ron, Davian Lamar, Merik do''Rehny, Alvain Nu&icirc;n, Serrik Vey''ron, and Ariel durahl Durain.  Led by Headmaster Lukifan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (577,1,to_timestamp('13-AUG-15 06.06.08.638000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chamoch',null,null,'Ancient ruins discovered by students of Xaln during the last age, led by Headmaster Lukifan.  The lost city was found through a portal at the base of a ravine in the frozen north and consisted of what appeared to be layers of ruins reaching back millennia.  Artifacts and secrets discovered here, including the Grey Gem and knowledge of the Neraphin, contributed to the foundation of the Second &AElig;gan Empire.  The group which discovered the ruins was known as the Chamoch Party.  The modern city of Arikh-nu is thought by Malnos to have lie near or atop the ravine that leads to the ruins.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (581,1,to_timestamp('13-AUG-15 06.52.31.798000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dagar Morduum',null,null,'The name of a black mountain that rises atop Cora-ladumduur and also a citadel wrought from the mountain''s surface.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (583,1,to_timestamp('13-AUG-15 06.54.53.493000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dark Womb, The',null,null,'An alternative name for M&ucirc;r Denaagra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (584,1,to_timestamp('13-AUG-15 06.55.03.568000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Black Waters, The',null,null,'An alternative name for M&ucirc;r Denaagra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (540,1,to_timestamp('13-AUG-15 08.10.52.507000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Unbreakable Council, The',null,null,'An alternative name for the Council of Twenty-one during the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (587,1,to_timestamp('13-AUG-15 08.45.12.711000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unredeemed, The',null,null,'An alternative name for the Kalit’edon Emari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (588,1,to_timestamp('13-AUG-15 08.45.43.399000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Redeemed, The',null,null,'The Shining Ones believe that they will become the Redeemed when the Great Lord returns and forgives them for their cowardice.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (589,1,to_timestamp('13-AUG-15 08.55.38.021000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Marianthala',null,null,'According to the Shining Ones, she was the wife of the Great Lord and perished during the final assault of Uragar by Arastel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (590,1,to_timestamp('13-AUG-15 08.56.48.321000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Returning to the Gift',null,null,'Upon their deaths, the bodies of the Shining Ones are delivered to the pool at the base of Udmar Cajar and joined with its essence.  They call this Returning to the Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (591,1,to_timestamp('13-AUG-15 08.57.54.390000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Birthing Pool, The',null,null,'The silver pool at the base of Udmar Cajar in Uragar, where the Shining Ones are conceived, born, and Return to the Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (612,1,to_timestamp('13-AUG-15 09.31.40.292000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ash''Tara',null,null,'An acolyte of the Meduri trained for beauty or sexual allure.  Translates to "Celestial."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (613,1,to_timestamp('13-AUG-15 09.32.39.984000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ash''Yassein',null,null,'An acolyte of the Meduri trained to provide intellectual guidance or specialized knowledge.  Translates to "Attendant."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (615,1,to_timestamp('13-AUG-15 09.33.50.521000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Unmaking, The',null,null,'An alternative name for Ganaanor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (616,1,to_timestamp('13-AUG-15 09.34.09.259000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Effacing, The',null,null,'An alternative name for Prethaanor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (618,1,to_timestamp('13-AUG-15 09.35.13.484000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lumen',null,null,'The traditional names by which acolytes of the Meduri referred to their masters.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (620,1,to_timestamp('13-AUG-15 09.40.52.023000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lower Council, The',null,null,'An unofficial, derogatory name for the Azagai Janaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (621,1,to_timestamp('13-AUG-15 09.41.02.153000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Council of Vulgars, The',null,null,'An unofficial, derogatory name for the Azagai Janaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (623,1,to_timestamp('13-AUG-15 09.41.35.916000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Realm Confederacy, The',null,null,'An alternative name for the Azagai Janaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (624,1,to_timestamp('13-AUG-15 09.42.02.279000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ambassador',null,null,'The title of a senator of the Azagai Janaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (630,1,to_timestamp('13-AUG-15 09.51.22.367000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Healer',null,null,'A member of the Ter Graz Daan.  Generally only used to refer to lesser members without the ability to Shape.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (632,1,to_timestamp('13-AUG-15 09.52.28.750000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lord Mender',null,null,'A rank within the Ter Graz Daan, reserved for those with the ability to Shape.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (634,1,to_timestamp('13-AUG-15 09.53.35.857000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'H&aelig;fen',null,null,'A healing hall of the Ter Graz Daan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (228,1,to_timestamp('11-AUG-15 07.34.38.723000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Navigator, The',null,null,'An alternate name for the Neraphin, Thiassi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (235,1,to_timestamp('11-AUG-15 07.36.01.628000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wroth',null,null,'An alternate name for the Neraphin, Doitean.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (242,1,to_timestamp('11-AUG-15 07.38.13.993000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'First Or&uacute;, The',null,null,'An alternate name for the Neraphin, Ajzagal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (509,1,to_timestamp('12-AUG-15 10.41.01.362000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aldamar',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (559,1,to_timestamp('13-AUG-15 10.05.37.666000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Whistle Chamber, The',null,null,'A chamber in a tower atop Mem''mari, a cathedral located at the University of Xaln.  It was a common gathering place of the members of the Council of Twenty-one while they were students.  The legend is that the room earned its name in jest after the tower''s bell collapsed and Merik do''Rehny hummed its tune.  It is suspected that Merik is among those responsible for the bell''s untimely demise.  The bell, for whatever reason, was never replaced.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (606,1,to_timestamp('13-AUG-15 09.25.11.696000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Meduri, The',null,null,'A class of servants trained for utter obedience and denial of self.  They adhered to a strict code of conduct and a self-effacing mode of speech.  By custom, the Meduri were unclad, though this custom was not observed if it conflicted with the customs of the land or the whims of the lord they served.  By similar custom, the Meduri were known to self-mutilate during their training, usually to deny or punish a fault of vanity or desire.  This was not universal.  By custom, the Meduri did not own property, although occasionally they did carry some symbol of their master.  Upon completion of training, a Meduri was marked a tattoo at the base of the neck, showing a kneeling man.  This mark was drawn in soul ink, and the application was incredibly painful – and generally lethal if done outside the order.  It could be recognized by the manner in which it mirrored the servant’s pulse.  The Meduri were not property.  Although frequently appointed to their masters by the order, they had the right to leave or disobey.  This choice reflected great shame upon the master and was exceptionally rare.  It was forbidden under the laws of &AElig;gea to harm or compel one of the Meduri.  It was considered bad taste to employ one of the Meduri in a commercial or base manner; or to extend their service outside the direct family.  The Meduri predated the foundation of the second &AElig;gan Empire.  “Meduri” also applies to a race of people from the island of Medur, and the Selfless were originally drawn exclusively from this people.  This was expanded in the early years after Xaln’s restoration, before the foundation of the Council.  The Meduri were trained in a “hand-talk”, which was a guarded secret of the guild and used only among members of the order.  Teaching a “master” the Talk was very rare.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (631,1,to_timestamp('13-AUG-15 09.52.00.328000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mender',null,null,'A member of the Ter Graz Daan with the ability to Shape.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (622,1,to_timestamp('13-AUG-15 09.41.21.664000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ashen Symposium, The',null,null,'An alternative name for the Azagai Janaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (699,1,to_timestamp('14-AUG-15 01.10.08.880000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Die Nearal',null,null,'The collective name given to the priests of the Neraphin, Baragur.  The Die Nearal can be identified by the possession of a kylur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (423,1,to_timestamp('12-AUG-15 01.15.55.479000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Breaking Hills, The',null,null,'Hills in the northwestern area of Malkar, between the Grimwood and the Forever Sea.  The Breaking HIlls are replete with natural minerals and gems, making them home to several mines and small villages beholden to the polis of Me''din.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (430,1,to_timestamp('12-AUG-15 01.26.36.616000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Old Place, The',null,null,'The name by which the Shining Ones refer to the ruins of the city of Uragar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (629,1,to_timestamp('13-AUG-15 09.50.48.814000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Suffering is Our Foe',null,null,'The motto of the Ter Graz Daan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (461,1,to_timestamp('12-AUG-15 02.12.40.308000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hestil',null,null,'An ancient symbol meaning Unity.  It consisted of a twenty-one pointed star where each point was itself a star.  Hestil was used as the symbol of the First &AElig;gan Empire and the Second &AElig;gan Empire as well by the councils which guided them.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (635,1,to_timestamp('13-AUG-15 09.53.52.265000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Healing Hall',null,null,'An alternative name for a H&aelig;fen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (435,1,to_timestamp('12-AUG-15 01.41.27.687000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'People, The',null,null,'The self-identified name of the native peoples of the Brown Plains. The Zimuit, the Ku-kuit, the Ba-kwe, and the Ipu''qat each refer to their own ethnicity as the People.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (647,1,to_timestamp('13-AUG-15 11.00.11.558000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keepers, The',null,null,'An alternative name for the Gamon Ju''g&aelig;.  Also a collective name group of important roles within the Durem Erim&aelig;.  In times of succession, the next Mrek''Sera is most often selected from among the Keepers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (742,1,to_timestamp('14-AUG-15 08.59.54.316000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Munk',null,null,'Small rodents endemic to the First Domain, with three long, furred tails. Munks were originally creatures of the deep, fungal forests and were small and fast, with black fur and giant, keen eyes that could see in the most spectral light.  None have survived in the wild, but they can still be found in the gardens of Rolar.  Over thousands of years in the dappled shadows of Diris Alonne, they had kept their shape and size. Their eyes are still like vast saucers in their mousy skulls. But their fur had changed to become striped with color: a circus of hues without pattern.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (619,1,to_timestamp('13-AUG-15 09.39.02.902000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Azagai Janaal',null,null,'The world government of the Second &AElig;gan Empire, founded by the edict of Gai''lal when he surrendered the crown of the Fermoor and restored the Council of Twenty-one.  It consisted of Vulgar representatives of the Ten Thousand Islands, and each member nation was granted a single vote, regardless of size or influence.  It had no executive or judicial branches and was largely considered to be a puppet arm of the Council and the Hall of Halls.  Obedience to its edicts was by consent and relied on the enforcement of its members.  It had no jurisdiction over the Council, the Jesur&aelig;, or the N&aelig; Kisal.  Also known as the Ashen Symposium and the Realm Confederacy; and more derogatorily as the Lower Council or the Council of Vulgars.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (633,1,to_timestamp('13-AUG-15 09.53.08.832000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Salubriant',null,null,'A member of the ruling council of the Ter Graz Daan.  Their station was marked by a serpentine staff that was only carried in ceremonial circumstances.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (756,1,to_timestamp('14-AUG-15 09.05.53.591000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Glowbug',null,null,'Insects the size of a thumbnail, with extended, bulbous abdomens, native to the First Domain.  These animals gravitate to wyrds during their active periods, seeming to gather sustenance on the Gift.  When not feeding, they roost on the upper surface of the Roofs, glowing like small stars.  In Bel Can’nar, glowbugs often swarm the Star Gem during its active hours.  In Alanon, there are glowbugs alight at all hours.  They are rare in the other Roofs.  In its larval form, a glowbug is called a glow worm and has similar properties.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (775,1,to_timestamp('14-AUG-15 09.17.43.835000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ajorrii',null,null,'An ancient people of Bel Can’nar, long ago conquered by the Empire of Demarge.  Their descendants are now the lords of the Milita, Lin''Ajor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (736,1,to_timestamp('14-AUG-15 04.12.26.340000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Cavern, The',null,null,'A Roof of the First Domain, home to Aljerbach, Calikar, Berak, Irongate, and the Free Cities.  Also called Uahd isch Calikahd and the Great Roof.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (592,1,to_timestamp('13-AUG-15 09.13.14.996000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bal Dannan',null,null,'An academic society dedicated to the study of mathematics and the physical sciences.  It predated the discovery of Xaln and the re-institution of the N&aelig; Kisal.  Membership was open to Vulgars.  The four schools of Bal Dannan were Ishar&aelig;, Aman, Katar, and Ynara.  The symbol of the Bal Dannan was a two dimensional spiral without beginning or end, traditionally rendered in a red or ruby hue against a white background.  An alternative symbol was a turtle shell, based on an old parable of a turtle that trusted nothing it could not see with its own eyes.  As a consequence, it never emerged from its shell before the end of time.  Their motto was “Veryre säl,” which translates as “Only on the word of nature,” or “All things are to be examined/tested.”',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (734,1,to_timestamp('14-AUG-15 04.10.41.700000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bel Can’nar',null,null,'A Roof of the First Domain.  Bel Can’nar was formerly the seat of several kingdoms but is currently dominated by the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (738,1,to_timestamp('14-AUG-15 04.13.21.546000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Uahd isch Calikahd',null,null,'An alternative title for both the kingdom of Calikar and the Great Cavern in which it is located.  It means "The Grave of Calik" in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1303,1,to_timestamp('20-AUG-15 03.19.00.646000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Divine Father, The',null,null,'An alternative name for D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1304,1,to_timestamp('20-AUG-15 03.19.11.128000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Creator, The',null,null,'An alternative name for D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1305,1,to_timestamp('20-AUG-15 03.19.34.033000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'All-Father, The',null,null,'An alternative name for D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1306,1,to_timestamp('20-AUG-15 03.19.44.719000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'R&uacute;',null,null,'An alternative name for D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1308,1,to_timestamp('20-AUG-15 03.25.15.087000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Istaron',null,null,'The firstborn of the Er&uacute;, per the Sundering of Anda Adalai&auml;, associated with the Prime Virtues of Ambition and Strength.  Also known as the Child of the Dawn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1241,1,to_timestamp('18-AUG-15 08.52.30.276000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mannrit',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1243,1,to_timestamp('18-AUG-15 08.52.52.128000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rokgtoll',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1246,1,to_timestamp('18-AUG-15 08.53.28.347000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Malmri&eth;',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1074,1,to_timestamp('15-AUG-15 08.22.54.008000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Orbit',null,null,'A category of wyrd, such as healing or weather-working.  Kinships were often organized around Orbits.  Also known as a Discipline or a Demesne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1087,1,to_timestamp('15-AUG-15 08.30.05.653000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'MIrroring',null,null,'To make wyrds appear to originate at a position other than the Shaper''s location.  A very difficult task, not generally possible at more than a few dozen feet.  Also called Shifting and Displacing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (751,1,to_timestamp('14-AUG-15 09.03.22.896000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cat',null,null,'Feline animals in Llua’la and a breed of reptiles in the First Domain.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (439,1,to_timestamp('12-AUG-15 01.45.27.442000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silver Gift, The',null,null,'A virtue or attribute claimed to be possessed by the Shining Ones.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (444,1,to_timestamp('12-AUG-15 01.49.02.468000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seafolk, The',null,null,'An alternative name for the Ku-kuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (450,1,to_timestamp('12-AUG-15 01.50.47.609000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mountain Kin, The',null,null,'An alternative name for the Zimuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (451,1,to_timestamp('12-AUG-15 01.52.52.229000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Haldar',null,null,'A modern city of Her-lak Merg&ouml;l.  Malnos has conjectured that it may have been built near or atop the ruins of the ancient city of Erinin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (458,1,to_timestamp('12-AUG-15 02.04.17.369000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keeper of the Staff, The',null,null,'Translation of the corti title, Hwe''leri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (460,1,to_timestamp('12-AUG-15 02.11.00.934000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hestilion Chalice',null,null,'The reward given to a participant victorious in all twenty-one challenges of the Solstice Games.  Only ever achieved by Ardran Myramar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (757,1,to_timestamp('14-AUG-15 09.06.13.060000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Liver-worm',null,null,'Parasites native to the First Domain that enter their prey through exposed flesh, often the foot.  They can survive for extended periods outside a host in both soil and open water.  The worm does not grow significantly larger than six inches, but they reproduce quickly.  In the larger animals of the Domain, the parasites achieve a balance, but in men the result is almost always fatal.  Though called liver-worms, the worm actually devours the tissues of the liver, heart, eyes, and spleen.  Death usually occurs within a month and is excruciating.  In the end stages, the worms can be seen swimming in the victim’s eyes.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (758,1,to_timestamp('14-AUG-15 09.06.37.851000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silver-coil',null,null,'Venomous serpents of the First Domain.  The size of a grass snake, silver-coils have a vestigial rattle with silver stripes.  They are very aggressive and nearly always bite a man who comes in striking distance.  Death occurs within ten minutes.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (760,1,to_timestamp('14-AUG-15 09.07.21.591000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Meraki',null,null,'Wild creatures of the First Domain that dwell in wyrm caverns, feeding from the wyrm-resin.  The meraki are not natural predators of humanoids, but they will attack a man if threatened, and some have developed a taste for the Aiki.  The meraki have bodies similar to a lion, although hairless, with beaked, eyeless heads.  The beak is used as a weapon, not for ingestion.  They have four prehensile tentacles, which include razor-like spikes along their length, which they use to ingest liquids:  resin or blood.  A merak is capable of releasing a shriek which will repel a wyrm or an aiki, or in some cases incapacitate the latter.  This sound is barely audible to men.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (763,1,to_timestamp('14-AUG-15 09.10.49.211000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-shoat',null,null,'Domesticated animals of the First Domain, now very rare in the wilds.  Roughly the size of a pig, ka-shoats are tailless lizards with flesh colored scales.  They are renowned for their willingness to eat anything.  Although ka-shoat fed pure water are safe to consume, their meat is considered unsavory.  Their hides and bones, however, are considered valuable, and their meat is frequently used as feed for other animals.  Their eggs are a staple of Aljer cuisine, but animals intended to be harvested for their eggs are raised in special environments where they cannot accidentally exposed to the Grim.  Translates literally to “Not Pig.”',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (478,1,to_timestamp('12-AUG-15 04.07.14.585000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tower of Gai''lal, The',null,null,'A tower located in the Stone Desert of southern Malkar, set against a cliff of the Tempest Sea and surrounded by a walled garden.  The tower, as well as the stone plains that surround it, is shrouded in myth and tales of a curse.  It was claimed by Malnos several hundred years before the modern day, and he has since become known by the local Malkarans as the Ghost or Grey Sorcerer.  Prior to Malnos, the Tower was abandoned for centuries and rejected the approach of all vistors.  Malnos'' dreams suggest that Ardran Myramar was the tower''s master during the time of the Second &AElig;gan Empire, although the tower itself predates that age.  The tower itself appears to be a Tal''Shiahd and responds to the will of its chosen master.  Also known as the Tower of the Ghost, the Grey Tower, and Medynos'' Finger.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (492,1,to_timestamp('12-AUG-15 09.21.45.832000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Oracles of Namar',null,null,'Child prophets who dwell in the city of Namar, considered sacred by all Malkarans.  The presence of the Oracles is what lends Namar its status as a sacred, neutral place, excepted from the usual violence and politicking of the poleis.  The Oracles are young boys selected for their physical similarity.  One among them is considered the High Oracle and always bears the name Kay''sar.  Secretly, within the confines of the Shrine of the Oracles, only the High Oracle is considered to have prophetic powers.  He is ritually sacrificed when he reaches maturity and one of the younger boys is chosen as a replacement.  This is at least the belief of the servants in the Shrine, but Eleanor has evidence that Kay''sar is in fact a Mentej who has ruled Namar for centuries, using  the myth of the Oracles to veil his presence.  Also known as Child Oracles.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (625,1,to_timestamp('13-AUG-15 09.42.24.017000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Symposiarch',null,null,'The title of the “speaker” of the Azagai Janaal, appointed by the Council of Twenty-one.  He or she had no vote but was charged with maintaining rules of order.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (636,1,to_timestamp('13-AUG-15 09.54.19.090000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nurse',null,null,'An alternative name for a Healer of the Ter Graz Daan.  Generally only used to refer to Vulgars.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (811,1,to_timestamp('14-AUG-15 10.12.03.275000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keeper of the Ways, The',null,null,'The magus responsible for Oilear and the kbai among the Durem Erim&aelig;.  Usually one of the a''Serr&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (745,1,to_timestamp('14-AUG-15 09.01.14.223000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Howler',null,null,'Wild six-legged felines of the Deep Dark, native to the First Domain.  They are roughly twice the size of a man, hunt in large packs, and are covered in black or silver fur.  The eyes of a howler are red, and they are adapted to seeing in the complete darkness.  Howlers are known to be highly intelligent.  They will not attack armed or grouped men unless desperate.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (522,1,to_timestamp('13-AUG-15 07.32.16.583000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shiahd',null,null,'A physical object bound such that it can augment or produce a wyrd (always a single wyrd in the latter case). Often, Shiahd are shaped in a manner that reflects their purpose, but this is not universally true.  Activation may be via touch, a word/phrase, a wyrd, or some other factor.  Some Shiahd do not cause the Shivers.  The use of Hrest is required in the creation of Shiahd, though the exact mechanism is not understood.  Those who can "see" the nature of a Shiahd are referred to as Artificers.  Those who can craft Shiahd are known as wyrd-smiths.  Both are exceedingly rare Talents.  Note that wyrds bound to a Shiahd can be responsive/adaptive.  Birthing a wyrd in this manner has never been accomplished with one Crowned to a living creature.  Also known as an Artifact, Madstone, Fetish, or Phylactery.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (527,1,to_timestamp('13-AUG-15 07.42.50.314000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tal''Shiahd',null,null,'Literally, a "Great Shiahd" in the Pure Tongue.  The distinction between a Shiahd and a Tal''Shiahd is not well defined, but a Tal''Shiahd has been generally accepted as one that can be identified but not further analyzed by an Artificer.  It is also possible for a Tal''Shiahd to be more versatile than a Shiahd, bound to multiple wyrds or accomplishing feats that seem impossible via Shaping. There is no term for one capable of creating a Tal''Shiahd, since it was never accomplished during the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (529,1,to_timestamp('13-AUG-15 07.49.19.746000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Artificer',null,null,'One who can “see” the nature of Shiahd, often by instinct or metaphor.  Occasionally (and inaccurately) used to describe those who could make Shiahd, but this is more accurately referred to as wyrd-smith or Maker.  Artificing a rare Talent, with only a few hundred discovered Artificers during the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2480,1,to_timestamp('19-SEP-15 07.16.53.861000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Blood Legions, The',null,null,'The private armies of the Sangus in the Empire of Demarge, i.e. those warriors not given as tribute to the Divine Legions.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2481,1,to_timestamp('19-SEP-15 07.17.12.331000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Divine Host, The',null,null,'An alternative name for the Divine Legions of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2482,1,to_timestamp('19-SEP-15 07.17.38.625000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Imperial Host, The',null,null,'An alternative name for the Divine Legions of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (248,1,to_timestamp('11-AUG-15 08.42.51.830000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Phoebes',null,null,'A polis of Malkar, dedicated to the god, Phoebos.  Also known as the City of Storms.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (260,1,to_timestamp('11-AUG-15 08.55.02.646000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tallo',null,null,'A god of the people of Malkar, associated with War/Honor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (269,1,to_timestamp('11-AUG-15 08.59.53.903000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Atheo',null,null,'A god of the people of Malkar, associated with Famine/Betrayal/Ill-fortune.  Atheo was the patron of the Lost City of Atheos and is no longer worshiped among modern Malkarans.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (276,1,to_timestamp('11-AUG-15 09.05.27.691000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'She''garch',null,null,'The name of the ruling body of the Malkaran polis of Dyalia, comprised of three ship captains selected for their deeds.  Also known as the Captains'' Council.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (743,1,to_timestamp('14-AUG-15 09.00.39.946000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bk&aring;r',null,null,'Domesticated animals of the First Domain, resembling oversized, haired oxen (roughly twice the size).  They have horns similar to a yak and bones and teeth comprised largely of iron.  Similarly, their blood is metallic.  Bk&aring;r are employed as beasts of burden throughout the Domain, but in I&aring;rth they are also employed as a source of milk.  Bk&aring;r live in large herds.  The females are  docile, but bulls can be aggressive and will not abide the presence of another bull.  Bk&aring;r cannot survive the Grim and thus no longer are found in the wild.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (605,1,to_timestamp('13-AUG-15 09.21.34.218000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Veryre s&auml;l',null,null,'The motto of Bal Dannan, translating literally as "Only on the word of nature," or "All things are to be examined/tested."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (611,1,to_timestamp('13-AUG-15 09.30.30.705000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ash''Avan',null,null,'An acolyte of the Meduri who is of the original, ethnic stock of the island of Medur.  Translates to "Unpolluted."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (290,1,to_timestamp('11-AUG-15 09.23.34.300000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'City of the Pure, The',null,null,'An alternative name for the Malkaran polis of Ler.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (292,1,to_timestamp('11-AUG-15 09.24.19.166000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Waterbasket of Malkar, The',null,null,'An alternative name for the Malkaran polis of Dyal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (299,1,to_timestamp('11-AUG-15 09.32.08.706000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ana',null,null,'A girl encountered by Kain in the Ba-kwe village of Kanalokamu while he lived as a guest in the household of her father, Wemi.  She bore a strong physical resemblance to the girl Natalie encountered by the na''k&ouml;r in Ajer-D&aelig;n but appeared several years older (approximately twelve).  She was unique in that she did not respond to Kain''s Sight.  Her fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (412,1,to_timestamp('12-AUG-15 11.51.09.002000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Braal',null,null,'Ruins of an ancient city or town that lie beneath Udmar D&ucirc;marg.  The ruins are covered in millennia of frost and snow, and its history and scale are lost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (421,1,to_timestamp('12-AUG-15 01.11.26.563000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Garden District, The',null,null,'An area of the polis of Namar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3118,1,to_timestamp('12-OCT-15 01.00.52.116000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Plon',null,null,'A fungal plant of the First Domain which grows like a moss on larger fungi.  According to the legends of the Thrice Kingdom, their people were once dying of a terrible plague.  They were near extinction when the gods took pity upon their plight and descended from Erdrion.  Barthur guided their ancestors to the urda, G&auml;r taught the making of the arenne tools, and Kaidor guided them to plon and educated them on how to prepare both the meat and plant.  Only then did the illness pass.  The Aljer continue to eat urda and plon on the holy day of Talinth; and only on the Talinth.  The illness does not return for those who observe the ritual.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (769,1,to_timestamp('14-AUG-15 09.13.49.309000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Narth',null,null,'Wild animals and cousins of the urda, native to the Great Roof.  Narth are normally docile.  However, they will enter berserk rages at the injury of a pack member, trampling and biting the intruder even at great risk to themselves.  Unlike their cousins, narth have claws rather than hooves, which are used primarily in defense.  Narth can survive in the Blight, but can also be safe to consume if raised on pure water.  Such meat, narth steak, is considered a delicacy.  The narth have none of the earthen or iron qualities of either the urda or bk&aring;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (679,1,to_timestamp('14-AUG-15 12.45.07.516000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Vuq',null,null,'A Nera of the Syna''a de''Naan, devoted to form (pottery, statuary, architecture, sculpture).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (680,1,to_timestamp('14-AUG-15 12.45.50.867000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Kosor',null,null,'A Nera of the Syna''a de''Naan, devoted to color (painting, mosaics, weaving).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (681,1,to_timestamp('14-AUG-15 12.46.40.707000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Baat''e',null,null,'A Nera of the Syna''a de''Naan, devoted to prose and written word (style, short story, short plays).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (682,1,to_timestamp('14-AUG-15 12.47.32.684000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Baat''a',null,null,'A Nera of the Syna''a de''Naan, devoted to story composition (novels, playwrights, plot, setting, characters).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (683,1,to_timestamp('14-AUG-15 12.48.29.386000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Nos',null,null,'A Nera of the Syna''a de''Naan, devoted to multi-sensory illusions created via Da''kol (always illusions, never Coerced).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (684,1,to_timestamp('14-AUG-15 12.56.05.446000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Caal Anuur',null,null,'The collective name of the churches of the Neraphin. Each faith had its own name and hierarchy and acted independently.  Also called the Seven Faiths.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (685,1,to_timestamp('14-AUG-15 12.56.39.338000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shul',null,null,'A church of the Seven Spears.  Also called a Basilica.  pl. Shuln.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (686,1,to_timestamp('14-AUG-15 12.56.50.629000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shuln',null,null,'Pluralization of Shul.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (687,1,to_timestamp('14-AUG-15 12.57.56.034000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shul-nos',null,null,'A Great Shul of the Seven.  Each Neraphin had one Shul-nos, located in a T&icirc;nara.  pl. Shuln-nos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (689,1,to_timestamp('14-AUG-15 12.58.27.856000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shuln-nos',null,null,'Pluralization of Shul-nos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (690,1,to_timestamp('14-AUG-15 12.59.24.130000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sancta',null,null,'A follower/devotee/priest of one of the Seven Spears or their faiths, i.e. the Caal Anuur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (766,1,to_timestamp('14-AUG-15 09.12.41.628000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mat''ton',null,null,'A shortened form of To''mat''ton.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (767,1,to_timestamp('14-AUG-15 09.13.14.689000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Adur-&auml;l',null,null,'The steeds of the Calikari.  Like the capaill, the adur-&auml;l were descended from horses of Llua’la (though a lesser breed).  They are now extinct, none having surviving the coming of the Grim.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (770,1,to_timestamp('14-AUG-15 09.14.06.155000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cunyculus',null,null,'Rabbits native to the Roof of Bel Can’nar, descended from specimens preserved from Llua’la.   Their flesh is considered a delicacy and their fur a luxury.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (797,1,to_timestamp('14-AUG-15 09.54.26.514000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sen''Serr&aelig;n',null,null,'A group of seven a''Serr&aelig; who counsel the Mrek''Sera.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (799,1,to_timestamp('14-AUG-15 09.55.35.315000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Spire, The',null,null,'An alternative name for the Sea Spire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (800,1,to_timestamp('14-AUG-15 09.55.47.556000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Spire of Alanon, The',null,null,'An alternative name for the Sea Spire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (801,1,to_timestamp('14-AUG-15 09.55.57.671000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Spire, The',null,null,'An alternative name for the Sea Spire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (802,1,to_timestamp('14-AUG-15 09.56.26.875000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Spire, The',null,null,'An alternative name for the Sea Spire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (659,1,to_timestamp('13-AUG-15 11.28.15.165000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Memoriam Stone',null,null,'A vial placed into the pommel of the blade of a Shug of the Shugda Legion (Blade of the Seven), containing the blood of his fallen competitors.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (661,1,to_timestamp('13-AUG-15 11.30.02.777000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blade of the Twice Seven',null,null,'The blade of an Os-Shug of the Shugda Legion.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (665,1,to_timestamp('13-AUG-15 11.31.22.779000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blooded, The',null,null,'An alternative name for a Shug of the Shugda Legion.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (670,1,to_timestamp('13-AUG-15 11.33.33.634000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gal',null,null,'A term in the language of the Shug, meaning "stillness."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (752,1,to_timestamp('14-AUG-15 09.04.06.562000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Do''Drak',null,null,'Wild beasts of the First Domain with the seeming of wingless dragons (usually with gray or black scales) which can grow to very nearly the size of a dragon.  They have only rudimentary intelligence and can neither fly nor breathe flame.  Nonetheless, do’Drak are considered among the most dangerous of the beasts of the Deep Dark (second only to a wyrm).  Do’Drak are unique in that they can purify the Grim in their bodies.  Their flesh is always safe to consume.  For this reason, many have tried to domesticate do’Drak, to terrible result.  Due to their dependence on expansive breeding and hunting grounds, do’Drak are rare and only found in the larger fungal woods.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (759,1,to_timestamp('14-AUG-15 09.06.54.552000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ugyr',null,null,'Wild beast of the First Domain, found primarily in the Deep Dark.  They are roughly the size of a small elephant.  Their bodies are divided into three segments in the manner of an insect, with six total legs.  They are mammalian in appearance, furred with a fleshy hide, but a thick carapace of solid bone runs underneath their skin.  Their blood and saliva are caustic.  Ugyr have two heads, one on either side, which roughly resemble the skulls of bears.  They  hunt and live in pair-bonds, and are known to torment their prey, seemingly for pleasure.  On rare occasions, larger packs of ugyr have been seen, generally to attack a do’Drak, indicating some form of rudimentary intelligence.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (762,1,to_timestamp('14-AUG-15 09.10.17.435000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Heppin',null,null,'Small hairless and tailless rodents endemic to the First Domain.  They have three eye sockets but no eyes.  Heppin have a seemingly preternatural ability to find the nearest location of civilization (actually, light).  They are valued by the duårg, particularly the Visa.  The heppin are known to occasional make a soft, singing noise, though what leads them to do so is unknown.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (764,1,to_timestamp('14-AUG-15 09.11.07.072000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrm',null,null,'The largest natural creature of the First Domain, growing to a length of several hundred yards.  A wyrm will Rise in response to Da’kol, striking out with a mad rage that will destroy anything in the vicinity.  Wyrms can devour earth and stone alike, but they have a general aversion to harder stones (ie, granite) and volcanic rock, which is why Demarge was constructed across the seven hills.  Wyrms will not pass through large volumes of water, and generally avoid areas with large subterranean aquifers, like the Great Cavern.  Wyrms are most numerous in the areas surrounding Alanon (Demarge and Frelham), but they also pose a danger in Zamoch and Bel Can’nar; and to a lesser extent in Kailar.  A wyrm most often kills by thrashing and crushing, although it also has a maw with numerous rows of teeth capable of lacerating stone.  Among the Aiki, wyrms are worshiped as gods and are (individually) known as the Great Father.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (768,1,to_timestamp('14-AUG-15 09.13.28.831000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Quillen',null,null,'Wild beasts native to Alune, employed as pack and riding animals by the elithi before the rise of Dubh Gaoth.  They are slender quadrupeds, roughly the size of a horse.  They are lower to the ground, and walk at an uneven gait, turning into a fluid loping motion.  Quillen are hairless and feline in body-structure, with tails nearly the length of their body.  They have no eyes and large, twitching ears.  Quillen are known to play dead when endangered, emitting a terrible, rotting odor.  They cannot survive the Grim.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (812,1,to_timestamp('14-AUG-15 10.12.58.590000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keeper of Names, The',null,null,'The magus responsible for keeping and assigning ranks among Durem Erim&aelig;.  Usually one of the a''Serr&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (813,1,to_timestamp('14-AUG-15 10.13.47.398000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sha-tar&icirc;n',null,null,'An ambassador among the Durem Erim&aelig;, generally chosen from among the mal-heru and val-heru.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (814,1,to_timestamp('14-AUG-15 10.14.15.912000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'M''h&aelig;n',null,null,'An instructor among the Durem Erim&aelig;, generally chosen from among the tal-heru and mal-heru.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (771,1,to_timestamp('14-AUG-15 09.14.21.351000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Caynus',null,null,'The war-dogs of Demarge.  They possess wolfish features and are approximately twice the size of the standard dogs of the First Domain.  The caynus are the property of the house of Garus, who provide specimens to the Empire in lieu of the customary legions.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (773,1,to_timestamp('14-AUG-15 09.15.58.821000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ajestum',null,null,'Great lizards native to the cavern of Bel Can’nar and smaller cousins to the do’Drak.  The largest grow to approximately twice the size of an elephant.  Much smarter than their cousins, they can be trained and are fanatically devoted to their master, known as an ajestorum.  Generally, an ajestum will not allow itself be mounted, but there are exceptions.   Do’drak generally have scales of the same color, most often black or gray, but ajestum frquently have scales of two colors.  Rarely, they have three.  Ajestum also have a vestigial socket for a third eye.  Very rarely, this eye is present, but does not seem to function.  Beasts born with the third eye are always sterile.  Ajestum were originally acquired by the Empire through the conquest of the Ajorrii (now the House of Lin''Ajor, one of the Milita).  More than half of the stock still belong to that house, including the most eminent lines.  The rarest of the ajestum, the serestum, are born with the third eye and scales of gold, red, and blue:  the colors of the Empire.  By custom, these rare beasts are offered to the imperial house, where they are kept as detoothed and declawed “pets”.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (779,1,to_timestamp('14-AUG-15 09.21.25.704000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Capaill',null,null,'Horses of the Zamochi, preserved from the stock of Sema.  Their existence is a closely held secret of the lords of Zamoch.  They are bred by the Great Clans but are occasionally offered as gift to the lesser.  There are several breeds, including An Eyr, An B&aacute;n, and An Cr&iacute;.  The highest steeds are the An Morga.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (786,1,to_timestamp('14-AUG-15 09.23.48.547000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Carenne',null,null,'Spectral, wisp-like  creatures of blue or emerald hue which travelers have claimed to see in the wild northlands of the Roof of Welst.  Also known as a will-o-wisp.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (790,1,to_timestamp('14-AUG-15 09.24.45.382000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tortle',null,null,'Creatures endemic to the Shoreless Sea of Alanon, descended from the turtles of Llua’la.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1083,1,to_timestamp('15-AUG-15 08.28.01.548000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Echoing',null,null,'To create false of signs of a chara using wyrds.  Depends on intimate knowledge of the other party.  Also called Beguiling.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1085,1,to_timestamp('15-AUG-15 08.28.50.716000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gilding',null,null,'To add extraneious elements to wyrd in order to veil its true purpose.  Also called Clothing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1088,1,to_timestamp('15-AUG-15 08.30.18.625000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shifting',null,null,'An alternative term for Mirroring.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1089,1,to_timestamp('15-AUG-15 08.30.44.325000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Displacing',null,null,'An alternative term for Mirroring.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (815,1,to_timestamp('14-AUG-15 10.15.07.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'T''h&aelig;n',null,null,'A high instructor among the Durem Erim&aelig;, generally chosen from among the mal-heru and zal-heru.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (816,1,to_timestamp('14-AUG-15 10.25.10.673000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jal-kuru',null,null,'A title among the Durem Erim&aelig;, indicating strength (12).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (817,1,to_timestamp('14-AUG-15 10.25.19.624000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nal-kuru',null,null,'A title among the Durem Erim&aelig;, indicating strength (11).',1);
