Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (820,1,to_timestamp('14-AUG-15 10.25.47.977000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sal-naru',null,null,'A title among the Durem Erim&aelig;, indicating strength (8).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (822,1,to_timestamp('14-AUG-15 10.26.04.040000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kal-heru',null,null,'A title among the Durem Erim&aelig;, indicating strength (6).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (823,1,to_timestamp('14-AUG-15 10.26.11.263000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tal-heru',null,null,'A title among the Durem Erim&aelig;, indicating strength (5).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (826,1,to_timestamp('14-AUG-15 10.26.33.329000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Zal-heru',null,null,'A title among the Durem Erim&aelig;, indicating strength (2).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (827,1,to_timestamp('14-AUG-15 10.26.46.523000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seral-heru',null,null,'A title among the Durem Erim&aelig;, indicating strength (1).  Reserved for the Mrek''Sera.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1098,1,to_timestamp('15-AUG-15 08.37.13.162000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Birthing',null,null,'The theoretical creation of a Crowned wyrd that is responsive/adaptive to a changing environment.  Also called Breathing Life, Siring, or Inception.  This has only ever been accomplished by attaching a wyrd to a Shiahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (753,1,to_timestamp('14-AUG-15 09.04.31.744000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'L&ucirc;r',null,null,'Wild beasts of the First Domain which resemble large porcupines.  They can fold their spines to resemble stones and can remain still for periods of hours.  Two large stalks rise from the their backs.  They resemble eyes and are luminescent, attracting prey.  Despite this appearance, l&ucirc;r in fact have no eyes, nor any other apparent sensory organs, or a skull.  Their mouth are located beneath their abdomens.  L&ucirc;r can fire spines at their prey, causing paralysis but not dulling sensory perception.  Human-sized prey can require days to digest, during which time the victim often remains alive.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (807,1,to_timestamp('14-AUG-15 10.08.03.882000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'D''rin',null,null,'The title of a regional (Roof or kingdom) administrator of the Durem Erim&aelig;.  Generally one of the val-heru or zal-heru.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (809,1,to_timestamp('14-AUG-15 10.10.09.959000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keeper of the Scrolls, The',null,null,'The librarian/artificer of the Durem Erim&aelig;.  Usually one of the a''Serr&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1082,1,to_timestamp('15-AUG-15 08.27.14.417000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Drowning',null,null,'An alternative term for Muddling.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (597,1,to_timestamp('13-AUG-15 09.17.39.829000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lyceum',null,null,'A college of Bal Dannan, for example Ishar&aelig;, Aman, Katar, or Ynara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (792,1,to_timestamp('14-AUG-15 09.25.08.857000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Deep Snake',null,null,'Eels endemic to the Shoreless Sea of Alanon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1100,1,to_timestamp('15-AUG-15 08.37.42.885000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Siring',null,null,'An alternative term for Birthing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1101,1,to_timestamp('15-AUG-15 08.37.52.378000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Inception',null,null,'An alternative term for Birthing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (507,1,to_timestamp('12-AUG-15 10.40.35.762000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Miyok',null,null,'A city of the Second &AElig;gan Empire, built atop the ruins of an older city of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (791,1,to_timestamp('14-AUG-15 09.24.56.535000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Toothfish',null,null,'Carnivorous fish that hunts in packs, endemic to the Shoreless Sea of Alanon',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (604,1,to_timestamp('13-AUG-15 09.20.36.190000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Asathaan',null,null,'The lecture hall of the Bal Dannan at the University of Xaln.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (701,1,to_timestamp('14-AUG-15 02.25.36.315000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shamrin',null,null,'A flightless, burrowing bird with black feathers, able to imitate human speech.  It was native to the Great Island in the time of the Second &AElig;gan Empire.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1107,1,to_timestamp('15-AUG-15 10.48.45.443000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sema',null,null,'A shortened form of Semaar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1201,1,to_timestamp('16-AUG-15 07.44.20.444000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ghaldor',null,null,'The ancestral blade of Ryche Orain, thought to be among the original M&aelig;lorwi crafted by M&aelig;lorwen yn Ash''shwl of Shuon Ashhal.  After Delsar claimed Anlocht, Ghaldor was gifted to Pryderi yr Erybin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (626,1,to_timestamp('13-AUG-15 09.47.00.628000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ter Graz Daan',null,null,'An order of healers during the Second &AElig;gan Empire.  Their seal was an emerald scale with three equally weighted arms:  a red tome, a blue scythe, and a yellow crown.  Their symbol was a twisted leaf with a gold side and a silver side.  This was derived from a well-known folk belief that the sap of &AElig;thel’Mierre could cure an ailment or heal any injury.  Acolytes of the Ter Graz Daan typically wore two rings on the ring finger of their right hand.  One was gold and silver intertwined.  The second was red, blue, yellow, and green intertwined.  Their order was forbidden from carrying weapons or practicing violence.  Their motto was “Suffering is our foe.”  A hall of the Ter Graz Daan was referred to as H&aelig;fen or Healing Hall.  There was a hierarchy within the Ter Graz Daan.  The lords of the order were called Menders or Lord Menders and were technically members of the N&aelig; Kisal.  Lesser members practiced mundane methods and were called Healers (or Nurses).  Only the Lord Menders wore the gold/silver ring, but they all wore the Ring of Four.  The highest ruling council of the Ter Graz Daan, known as Salubriants, were also given serpentine staves, which were carried only in ceremonial circumstances.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (673,1,to_timestamp('14-AUG-15 12.40.23.923000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nera',null,null,'A guild of the Syna''a de''Naan.  Translates literally as "Quest."  The eight primary Nera were Naan e''S''he, Naan e''S''sha, Naan e''Mer, Naan e''Vuq, Naan e''Kosor, Naan e''Baat''e, Naan e''Baat''a, and Naan e''Nos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (772,1,to_timestamp('14-AUG-15 09.15.09.973000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jantum',null,null,'The primary war-beasts of the God Legions of Demarge.  With horns similar to a triceratops and plates and tail spikes similar to a stegosaurus, the jantum are slow-moving and naturally herbivorous, but can be terrifying in battle.  They are employed in the manner of war elephants, with platforms attached to their backs.  Jantum once grazed naturally in the cavern of Bel Can’nar, but they are no longer found in the wild.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (774,1,to_timestamp('14-AUG-15 09.16.54.392000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lin''Ajor',null,null,'One of the houses of the Milita in the Empire of Demarge, famed for breeding the ajestum.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (776,1,to_timestamp('14-AUG-15 09.18.58.147000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Serestum',null,null,'A rare genetic mutation of the ajestum, born with a third, vestigial eye and scales of gold, red, and blue.  Bye custom, these rare gifts are offered as a gift to the imperial house.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (777,1,to_timestamp('14-AUG-15 09.19.56.684000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gimh',null,null,'Bipedal monsters native to the Roof of Zamoch.  The gimh are short but appear tall due to their stretched, emaciated appearance.  They are roughly humanoid, except that their mouths are toothless and their eyes are faceted.  Their arms have two joints rather than one and are as long as their legs.  Their joints articulate at odd, seeming impossible angles, offering them incredible flexibility.  They can fit in tiny spaces, and they are excellent climbers.  The gimh can survive falls from great heights.  They feed by drawing blood through “teeth” in the palms of their hands, feet, and torso, which under normal circumstances lie beneath the skin.  They emerge during feeding and also during periods of excitement.  To feed, the gimh generally wraps itself around its prey for maximal surface area.  The touch of a “tooth” leads to quick paralysis.  The gimh are cowardly by nature, attacking through ambush, and although quick and agile, are not fighters.  They do not wield weapons or wear clothes.  Also known as soul-drinkers.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (778,1,to_timestamp('14-AUG-15 09.20.28.858000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Soul-drinkers',null,null,'An alternative name for gimh.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (780,1,to_timestamp('14-AUG-15 09.21.49.574000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'An Eyr',null,null,'A breed of the capaill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (781,1,to_timestamp('14-AUG-15 09.22.01.398000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'An B&aacute;n',null,null,'A breed of the capaill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (782,1,to_timestamp('14-AUG-15 09.22.22.731000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'An Cr&iacute;',null,null,'A breed of the capaill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (783,1,to_timestamp('14-AUG-15 09.22.45.964000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'An Morga',null,null,'The rarest and most valuable breed of the capaill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (785,1,to_timestamp('14-AUG-15 09.23.27.574000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fire-serpent',null,null,'An alternative name for a pym.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (787,1,to_timestamp('14-AUG-15 09.24.01.779000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Will-o-wisp',null,null,'An alternative name for a carenne.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (788,1,to_timestamp('14-AUG-15 09.24.16.445000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gygon',null,null,'Large, flightless birds endemic to Welst, similar to an ostrich but with razors along their legs and a toothed beak.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (789,1,to_timestamp('14-AUG-15 09.24.32.463000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Leviathon',null,null,'Vast creatures endemic to the deep waters of the Shoreless Sea in Alanon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (794,1,to_timestamp('14-AUG-15 09.25.52.486000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ox',null,null,'Domesticated animals found in both Llua’la and Alanon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (706,1,to_timestamp('14-AUG-15 02.28.21.876000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grand Confessor',null,null,'The title of the highest priest of the Massra Dein.  Following the Gh&ucirc;l War and the split of the Children of God, the Heirs of God and the Children of God had separate Grand Confessors.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (674,1,to_timestamp('14-AUG-15 12.41.21.101000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Of Something, Nothing',null,null,'The literal translation of the Syna''a de''Naan.  Also their motto.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (741,1,to_timestamp('14-AUG-15 04.17.32.274000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shoreless Sea, The',null,null,'A great sea that occupies most of the Roof of Alanon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (672,1,to_timestamp('14-AUG-15 12.38.19.356000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Syna''a de''Naan',null,null,'The artists of the Second &AElig;gan Empire.  Their name meant literally “Of Nothing, Something.”  The Syna''a de''Naan included several guilds, called Nera (“Quests”).  The eight primary Nera were Naan e''S''he, Naan e''S''sha, Naan e''Mer, Naan e''Vuq, Naan e''Kosor, Naan e''Baat''e, Naan e''Baat''a, and Naan e''Nos.  Mixing of the Nera (such as creating a theatrical musical) was considered variably in the Empire.  Some viewed it with scorn, others as an evolutionary step in the arts.  There was a natural snobbery from musical composers towards performers much like Builders vs. Shapers.  The masters of the Syna''a de''Naan were given the title of Savant.  Savants were ranked based on their mastery of the Nera.  Each sect could assign up to 10 Stars, at which juncture the savant earned a Moon.  (A savant required a minimum of twenty-five stars; a moon was worth 12.)  The stars, and eventually the crescent, for each Nera were color-coded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (675,1,to_timestamp('14-AUG-15 12.42.11.720000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Quest',null,null,'Literal translation of Nera, a guild of the Syna''a de''Naan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (413,1,to_timestamp('12-AUG-15 11.52.32.289000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Frozen Sea, The',null,null,'An inland sea located in the northwestern area of the Brown Plains.  It has been frozen solid throughout recorded history (at least several centuries).  The Ba-kwe village of Shala-kuku lies on its shores.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (688,1,to_timestamp('14-AUG-15 12.58.09.777000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Shul',null,null,'Alternative name for a Shul-nos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (614,1,to_timestamp('13-AUG-15 09.33.24.091000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ganaanor',null,null,'The hall where the Meduri are trained, located in the city of Medur.  Also known as the Hall of Unmaking.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (749,1,to_timestamp('14-AUG-15 09.02.55.861000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-sheep',null,null,'Large herbivorous rodents native to the First Domain.  They have long tails and four eyes, two black and two red.  They are similar to sheep in that their fur is used in clothing; and that they make a noise roughly like a baa.  Unlike sheep, ka-sheep are very fast and can climb nearly vertical surfaces.  In the wild, they prefer fungal forests (but are very rare:  few survived the Blight). Translates to “Not Sheep.”',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (429,1,to_timestamp('12-AUG-15 01.24.14.409000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kanalokamu',null,null,'A village of the Ba-kwe on the Brown Plains.  Kain lived in this village for several months with Wemi and his family.  It was visited by the na''k&ouml;r on their journey between the Summer Island and Uragar.  It lies where two rivers converge (its name means literally "Meeting of Waters"), one of which is the Ama-tu, and is one of the largest villages of the People of the Stone Roof.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (464,1,to_timestamp('12-AUG-15 03.47.09.711000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sons of the Kraken, The',null,null,'An alternative name for the Drak-hnai.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (627,1,to_timestamp('13-AUG-15 09.49.42.938000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ring of Four',null,null,'A ring which marked an acolyte of the Ter Graz Daan, consisting of red, blue, yellow, and green bands intertwined.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (676,1,to_timestamp('14-AUG-15 12.43.01.751000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e’S’ha',null,null,'A Nera of the Syna''a de''Naan, devoted to musical performance.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (677,1,to_timestamp('14-AUG-15 12.43.16.756000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''S''sha',null,null,'A Nera of the Syna''a de''Naan, devoted to musical composition.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (678,1,to_timestamp('14-AUG-15 12.44.00.800000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Naan e''Mer',null,null,'A Nera of the Syna''a de''Naan, devoted to motion (dance, performance art, mechanical art).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (730,1,to_timestamp('14-AUG-15 04.07.30.058000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Welst',null,null,'A Roof of the First Domain, home to the kingdoms of Dwie and Rien.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (731,1,to_timestamp('14-AUG-15 04.07.50.494000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Zamoch',null,null,'A Roof of the First Domain and also a kingdom of the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (732,1,to_timestamp('14-AUG-15 04.09.13.075000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Frelham',null,null,'A Roof of the First Domain and a theocratic kingdom of the same name.  It currently lies under the dominion of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (733,1,to_timestamp('14-AUG-15 04.10.06.542000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Alanon',null,null,'A Roof of the First Domain as well as a kingdom and island of the same name.  Alanon is the dominion of the Durem Erim&aelig;.  Most of the Roof is occupied by the Shoreless Sea, one of few sources of pure water in the First Domain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (735,1,to_timestamp('14-AUG-15 04.11.27.750000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kailar',null,null,'A Roof of the First Domain and a kingdom and city of the same name, ruled by the d&aelig;n''shee.  The city is also known as the Black City.  It lies to the east of the Great Cavern, along unmapped paths in the Deep Dark.  The Roof was claimed after the d&aelig;n''shee were driven from their ancestral homes in the Great Cavern and their ancient city of Aljaberin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (737,1,to_timestamp('14-AUG-15 04.12.42.665000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Roof, The',null,null,'An alternative name for the Great Cavern.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (739,1,to_timestamp('14-AUG-15 04.14.39.457000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grave of Calik, The',null,null,'A literal translation of Uahd isch Calikahd in Tuindil.  Can also be used to refer to the Tomb of Calik.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (740,1,to_timestamp('14-AUG-15 04.14.52.294000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tuindil',null,null,'The language of the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (744,1,to_timestamp('14-AUG-15 09.00.56.978000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ondile',null,null,'Wild, reptilian creatures of the First Domain, superficially similar to crocodiles.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (747,1,to_timestamp('14-AUG-15 09.02.08.295000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-wolf',null,null,'Wild, reptilian creatures of the First Domain.  Translates literally to “Not Wolf.”',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (748,1,to_timestamp('14-AUG-15 09.02.24.443000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-lion',null,null,'Wild, reptilian creatures of the First Domain.  Translates literally to “Not Lion.”',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (755,1,to_timestamp('14-AUG-15 09.05.11.587000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Clutcher',null,null,'An alternative name for a Mogril.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (440,1,to_timestamp('12-AUG-15 01.46.17.370000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Clan of the Muskcat, The',null,null,'One of the seven clans of the Ipu''qat, now extinct.  The Ipu''qat believe that they are the ancestors of the Ba-kwe, who abandoned their ways after having been deceived by Ai-Ani into hunting the Muskcat into extinction.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (628,1,to_timestamp('13-AUG-15 09.50.27.521000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ring of Two',null,null,'A ring which marked a Mender or Lord Mender of the Ter Graz Daan (an acolyte who can Shape), consisting of gold and silver bands intertwined.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1472,1,to_timestamp('22-AUG-15 05.51.39.410000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taun, The',null,null,'A shorted form of the Taunland.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (946,1,to_timestamp('15-AUG-15 10.20.14.857000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hungry Ones',null,null,'D&aelig;mons in the Faith of the People, spawned by N&aelig;yt to punish her children.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (947,1,to_timestamp('15-AUG-15 10.20.49.217000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'He-Of-Many-Faces',null,null,'An alternative name for Inyu-Akan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (948,1,to_timestamp('15-AUG-15 10.22.29.405000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ya-we-nerat',null,null,'The name by which the Ipu''qat refer to Udmar D&ucirc;marg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (949,1,to_timestamp('15-AUG-15 10.26.02.513000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Riku',null,null,'A god of the Faith of the People and the son of N&aelig;yt and Aku.  He was stolen from his mother and replaced by a great walrus whom she consumed.  Riku was given to Niku as a consort, and although they initially shared no love, the race of men were eventually born from their union.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (950,1,to_timestamp('15-AUG-15 10.36.05.028000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ai-Ani',null,null,'A cosmological figure of the Faith of the People, known as the Clever God.  According to the Ipu''qat, he was a god who deceived the Clan of the Muskcat into betraying their traditions and hunting the musckat to near extinction, resulting in the ruin of the clan (who became the Ba-kwe).  According to the Ba-kwe, he was a mortal man, a guise of Ki''tin''ya.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (952,1,to_timestamp('15-AUG-15 10.44.43.115000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yuni',null,null,'A man of the Ba-kwe, inhabitant of Kanalokamu, friend to Kain, and cordwainer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (953,1,to_timestamp('15-AUG-15 10.47.03.140000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Asuki',null,null,'A man who acted as guide for the na''k&ouml;r on their journey between Camaar and Kain''s Sphere.  He perished on the trail.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (954,1,to_timestamp('15-AUG-15 10.50.26.032000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grutu',null,null,'A boy of the Ba-kwe, born on a farm near the Dust Plains of Echtz.  He was among those who joined the na''k&ouml;r on their journey to find Kain''s Sphere in the Death''s Blades Mountains, motivated by grief and guilt over his sister''s death.  He perished on the trail.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (957,1,to_timestamp('15-AUG-15 12.17.58.420000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Island, The',null,null,'The largest of the Ten Thousand Islands of Puell&aelig;, equaling the scale of the next hundred combined.  Before it was broken during the Sundering, the Great Island was roughly the size of Eurasia.  Also called the Great Isle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (958,1,to_timestamp('15-AUG-15 12.18.13.030000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Isle, The',null,null,'An alternative name for the Great Island.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (964,1,to_timestamp('15-AUG-15 12.46.08.616000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Void-Born, The',null,null,'And alternative name for the gh&ucirc;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (967,1,to_timestamp('15-AUG-15 02.47.44.140000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Quil Borgan',null,null,'The given name of Ym du''Seraa, abandoned when he was inducted into the University of Xaln.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (720,1,to_timestamp('14-AUG-15 02.48.23.829000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sheltered Sea, The',null,null,'The (relatively) calm strip of waters which lie between the Gardenlands and the Tempest Sea, including the area around Phoebes.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (724,1,to_timestamp('14-AUG-15 02.50.25.503000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shan''non',null,null,'A city of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (728,1,to_timestamp('14-AUG-15 02.59.41.644000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Domain',null,null,'A collection of connected caverns beneath Puell&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (808,1,to_timestamp('14-AUG-15 10.09.04.744000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'C''rin',null,null,'The title of a municipal administrator of the Durem Erim&aelig;.  Generally one of the tal-heru, mal-heru, or val-heru.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1221,1,to_timestamp('17-AUG-15 04.07.07.795000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lord Sapient',null,null,'A rank among the Gamon Ju''g&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1030,1,to_timestamp('15-AUG-15 03.59.57.180000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Winds, The',null,null,'One of the great halls of the N&aelig; Kisal, located in Uragar.  Also known as the Aerie.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1032,1,to_timestamp('15-AUG-15 04.00.27.868000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Swells, The',null,null,'One of the great halls of the N&aelig; Kisal, located in Il''yar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1033,1,to_timestamp('15-AUG-15 04.01.35.653000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chamber',null,null,'A political body within the N&aelig; Kisal.  In the Pure Tongue, W''harra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1034,1,to_timestamp('15-AUG-15 04.01.50.338000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'W''harra',null,null,'"Chamber" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1036,1,to_timestamp('15-AUG-15 04.02.28.611000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shara',null,null,'"Kinship" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1037,1,to_timestamp('15-AUG-15 04.03.17.655000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Circle',null,null,'A "rank" within the N&aelig; Kisal, based on strength in Da''kol.  In the Pure Tongue, Yyara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1038,1,to_timestamp('15-AUG-15 04.03.29.188000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Yyara',null,null,'"Circle" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1039,1,to_timestamp('15-AUG-15 04.04.00.217000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Emissary',null,null,'A representative to one of the Chambers of the N&aelig; Kisal.  Also known as a Legate, Herald, or Speaker.',0);
