Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (997,1,to_timestamp('15-AUG-15 03.44.16.668000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Builder',null,null,'A Shaper with the ability to perceive the wyrds of others others according to his own chara, thus allowing cooperative and interfering patterns.  Also known as an Architect or Master Shaper.  In the Pure Tongue, s''Erim&aelig;.',0);
