Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1237,1,to_timestamp('18-AUG-15 08.51.56.432000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ginnung',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1238,1,to_timestamp('18-AUG-15 08.52.04.157000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ginnheilag',null,null,'A clan of I&aring;rth, one of the magnr.  The last &thorn;engill-rikr, Agnard Edvill-s&oslash;n was a son of Magnr Ginnheilag.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1239,1,to_timestamp('18-AUG-15 08.52.12.804000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Himmin',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1240,1,to_timestamp('18-AUG-15 08.52.21.205000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iotunn',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1242,1,to_timestamp('18-AUG-15 08.52.39.719000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ofrior',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1783,1,to_timestamp('31-AUG-15 10.02.01.482000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Amity Ring',null,null,'One of a set of rings given to kings of Calikar by the lords of Sorain which possessed the power to pierce the Unfinding.  Also called the Five Rings.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1245,1,to_timestamp('18-AUG-15 08.53.07.393000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Umgjord',null,null,'A clan of I&aring;rth, one of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1247,1,to_timestamp('18-AUG-15 08.54.06.157000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Banama&thorn;r',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1249,1,to_timestamp('18-AUG-15 08.54.30.855000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Haldororf',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.  The current head is Einmyri Aldolf-s&oslash;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1250,1,to_timestamp('18-AUG-15 08.54.36.494000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hlorn',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1251,1,to_timestamp('18-AUG-15 08.54.46.845000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kojungr',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1253,1,to_timestamp('18-AUG-15 08.55.12.933000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vald',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1371,1,to_timestamp('20-AUG-15 09.18.05.636000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Pattern of Creation, The',null,null,'An alternative name for Jh&aelig;''va&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1409,1,to_timestamp('21-AUG-15 04.03.52.270000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grim, The',null,null,'A curse laid upon the waters of the First Domain which causes painful death to those who imbibe it.  Its mere touch causes the flesh to burn and peel.  Found the Roofs of Demarge, the Great Cavern, and Kailar, as well as the southern regions of Frelham and the northern sections of Zamoch.  It is by providing water free of the Blight via the kbai-stations that Alanon maintains its power in the First Domain.  The source of the Grim, the "venom", is known as naroch, while the effect is known as the Grim, Blight, or Taint.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1410,1,to_timestamp('21-AUG-15 04.04.02.819000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taint, The',null,null,'An alternative name for the Grim.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1411,1,to_timestamp('21-AUG-15 04.04.46.153000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blight, The',null,null,'An alternative name for the Grim.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1412,1,to_timestamp('21-AUG-15 04.07.44.516000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Oracle''s Road,The',null,null,'A series of simple, earthen roads in Malkar that connect Namar with poleis throughout the kingdom.  They are maintained by laborers from the City of the Oracle but are not patrolled.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1413,1,to_timestamp('21-AUG-15 04.09.53.802000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Throne of Nara, The',null,null,'A throne that sits in Urek-Un&icirc;n in Ire''nurine.  Formerly of white wood, it has been blackened since the Grey War.  Also called Nara''s Seat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1414,1,to_timestamp('21-AUG-15 04.10.06.114000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nara''s Throne',null,null,'See the Throne of Nara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1415,1,to_timestamp('21-AUG-15 04.11.54.786000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Harbingers of Eta&ucirc;r, The',null,null,'The name given by the Aljer to those who descended in the Sphere above Aljerbach:  Malnos, Malig, Delsar, Kisel, Drolock''harka, Diarin, and Natalie.  It is a reference to the god of death, Eta&ucirc;r.  Also known as the Travelers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1417,1,to_timestamp('21-AUG-15 04.14.17.548000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Therylin y Marel',null,null,'The prince of Ryche Marel, husband to Diarin yn Marel yn Ast and father to Aneiron y Marel.  He was last seen searching for his wife in Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1435,1,to_timestamp('21-AUG-15 08.47.18.906000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Diarin',null,null,'See Diarin yn Marel yn Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1436,1,to_timestamp('21-AUG-15 08.47.27.327000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Malig',null,null,'See Malig Narz.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1437,1,to_timestamp('21-AUG-15 08.47.44.996000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kain',null,null,'A name assumed by Argoel y Rhuaidh sometime after he departed Lluagh Maguin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1439,1,to_timestamp('21-AUG-15 08.49.27.401000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Amber',null,null,'The name assumed by Eliryndil yn Orain after she was stolen from her people.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1440,1,to_timestamp('21-AUG-15 08.50.29.950000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kisel',null,null,'See Kisel Gres''Mehar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1442,1,to_timestamp('22-AUG-15 08.22.26.168000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche',null,null,'A "house" or family among the elithin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1445,1,to_timestamp('22-AUG-15 08.30.18.003000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Orain',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Its current head is Cyngen yr Orain.  The line of Orain became the regents of Calikar after the Gloaming and eventually named themselves the new line of kings.  Their symbol is a hawk.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1446,1,to_timestamp('22-AUG-15 08.32.10.372000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Eldwr',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Its current head is Anarawd yr Eldwr.  Their symbol is a raven.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1463,1,to_timestamp('22-AUG-15 10.16.21.967000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'D&aelig;mon',null,null,'An ambiguous term by used by various peoples throughout Puell&aelig; to refer to creatures or things which they consider to be "evil" or inhuman.  Some are fantastical.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1465,1,to_timestamp('22-AUG-15 02.22.15.462000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gatherer',null,null,'An alternative name for one of the Muuri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1466,1,to_timestamp('22-AUG-15 02.22.37.320000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Birther',null,null,'An alternative name for one of the Waakar''Mawlid.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1467,1,to_timestamp('22-AUG-15 02.33.34.199000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Muuri',null,null,'A race of the Bel''caj Ged''don, created by Az-Azaal and N&aelig;lya in the Black Waters beneath Dagar Morduum.  Muuri appear as bipedal insectoids with reversely hinged limbs.  Black, white, and blue feathers ring their wrists and ankles.  They have eight, humanoid eyes which offer three hundred sixty degree vision but no mouths or nostrils.  Each eye is a different hue.  Round, fleshy mounds cover their torsos and backs.  The mounds on the front can open to reveal layers of teeth, expelling locusts with a paralyzing toxin.  Those on the back conceal long tentacles with grappling jaws on the end.  Excepting their queens, the Muuri have minimal intellect or volition and cannot communicate.  Their purpose is to incapacitate possible prey and drag  them to the Black Waters for conversion by the Waakar''Mawlid.  Frequently, they will consume parts of the victim on the path (via their tentacles), preferring sensory organs. One in a hundred victims is delivered to the queen for consumption rather than to the Birthers.  Each individual muuri lives an endless existence of moving back and forth between the Black Waters and the outer world, seeking prey.  The queen can produce new offspring by laying eggs, requiring only sustenance to do so.  All muuri are bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  At least one hive of the muuri survives in modern Sorain.  The queen has descended into madness over the millennia, compelling her children to drag humans, animals, and Bel’caj Ged’don alike to the Black Waters.  Also called Gatherers.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1468,1,to_timestamp('22-AUG-15 02.43.10.584000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Waakar''Mawlid',null,null,'A race of the Bel''caj Ged''don, created by Merik do''Rehny and N''koth Velar in the laboratories of Cora-ladumduur and then submerged in the Black Waters beneath Dagar Morduum.  Waakar''Mawlid appear as giant, pale humanoids with the qualities of aquatic creatures, including scales, gills, and tentacles.  Crustaceans and coral grow from their flesh, which is bloated and rotting.  All are female, with distended mammary glands.  The Waakar''Mawlid are apparently insensate and immobile.  Their eyes never open.  However, their tentacles will defend them if endangered will move to pull nearby living creatures into their wombs, where, after several hours, they are transformed.  Each of the Waakar''Mawlid changes its victims into a specific breed of the Bel''caj Ged''don.  There were once one hundred and one Waakar''Mawlid, but it is unknown how many have survived.  When one of the Unborn Horrors is taken into a Waakar''Mawlid, the outcome can be unpredictable.  All Waakar''Mawlid are theoretically bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  Also called Birthers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1448,1,to_timestamp('22-AUG-15 08.42.41.168000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arianrhod yn Orain',null,null,'An elithin of Ryche of Orain and queen of Rolar, considered exceptionally fair and wise even for one of the elithin.  Wife of Cynyr yr Orain, sister to Searah yn Orain (who was considered her equal in temperament and fairness), daughter to Bathwrwy yn Orain, and mother to Cyngen yr Orain and Eliryndil yn Orain.  She perished of despair after the loss of her daughter on the Day of Orihieyte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1449,1,to_timestamp('22-AUG-15 08.50.20.184000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cynyr yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Husband of Aranrhiod yn Orain, son of Eldeain yr Orain and Athorea yn Dalornmn, and mother to Cyngen yr Orain and Eliryndil yn Orain.  He was taken during the War of East and West, becoming a slave in the court of Eshrella Dao''Ast.  After ten years, he escaped and was eventually found by his cousin, Cadeyrn yr Orain.  Thereafter, he was considered a changed man:  perhaps for his torment, perhaps for his long separation from the Song, perhaps for the death of his wife or daughter.  Nonetheless, he was a beloved king.  He was murdered years later by the Stealer of Lives, Kisel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1450,1,to_timestamp('22-AUG-15 08.58.04.813000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Urek-Un&icirc;n',null,null,'The hall of kings in Ire''nurine.  Formerly, it was the seat of Ryche Senyar, but Ryche Orain has ruled in Rolar since the Gloaming.  The hall is open to the spires above, surrounded by (glassless) windows, and contains the Throne of Nara.  A mosaic of the Great Tree is worked into the floor.  The signs of the events of the Grey War, including the petrified corpse of Suon y Re''nan, the tree grown from Erilys yn Re''nan, the blackened state of the throne, the shattered doors, and the cracked mosaic.  Also known as the Chamber of Kings.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1451,1,to_timestamp('22-AUG-15 09.00.40.202000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Re''nan',null,null,'A house of the elithi in Rolar, once accounted among the Tiaryche.  Prior to the Grey War, their head was Erilys yn Re''nan.  Their line is now considered extinct.  Their symbol was a heron.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1454,1,to_timestamp('22-AUG-15 09.51.04.039000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Chamber of Kings, The',null,null,'An alternative name for Urek-Un&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1455,1,to_timestamp('22-AUG-15 09.56.30.809000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nursing Stone, The',null,null,'An inn in the hurtt of Heilvir, visited by the na''k&ouml;r as they traveled through I&aring;rth on the way to the Iron City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1457,1,to_timestamp('22-AUG-15 09.58.18.050000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Workk',null,null,'A hurtt in sourthern I&aring;rth, which turned away the na''k&ouml;r on their journey toward the Iron City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1458,1,to_timestamp('22-AUG-15 09.58.37.737000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Holmestrand',null,null,'A hurtt in sourthern I&aring;rth, which turned away the na''k&ouml;r on their journey toward the Iron City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1469,1,to_timestamp('22-AUG-15 03.06.37.675000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Imkhan',null,null,'A race of the Bel''caj Ged''don, created by Az-Azaal and N&aelig;lya in the Black Waters beneath Dagar Morduum.  In appearance, they seem to be normal humanoids, except that they universally move with a crippled gait.  Each experiences constant, rheumatoid agony.  They maintain the intellect and memories of their life but are unable to speak.  The contortion of their fingers renders them unable to write.  Each Imkhan was once a Shaper, but although they can sense Da''kol, they cannot summon its touch or Shape according to their own volition.  During the Great War, they were used as involuntary sources for Copulas, often resulting in their deaths.  All Imkhan are bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  It is unknown whether any survived the Sundering, but considering that they have mortal lifespans, they would not have survived to the modern day.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1383,1,to_timestamp('21-AUG-15 08.42.04.488000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Great Tree',null,null,'One of the trees which lie at the heart of the great cities of Calikar and to which the Songs are bound.  Many are surrounded by Holy Groves born of their seeds.  Each of the Great Trees is descended of Mile''&ucirc;r.  The trees provide periodic seeds which grow to become luminescent, lesser trunks but only yield replacement seeds when they sense their death is imminent.  In such a case, three seeds are always provided, of which only one will become a new Great Tree.  Ryche Senyar, and specifically Leuen y Senyar, discovered a mechanism by which a Tree could be encouraged to yield a seed without its death, thereby siring a new Great Tree and a new Song.  Thus were the Eight PIllars of Calikar born.  A Great Tree was also grown in Sorain, a symbol of friendship between the two kingdoms.  Known Great Trees of Calikar include D&ucirc;n Calithar, D&ucirc;n &AElig;mac&ucirc;r, D&ucirc;n Mile''&ucirc;r, and D&ucirc;n Nuchalan.  In the First &AElig;gan Empire, the term was applied to &AElig;thel''Mierre.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1462,1,to_timestamp('22-AUG-15 10.14.04.081000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'W''herra',null,null,'An abandoned tower of the Durem Erim&aelig; located in the Inner Plains near I&aring;rth.  Its i''Srin l&ucirc;n was used by the na''k&ouml;r to travel between a''Tiernya and the Iron Kingdom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1517,1,to_timestamp('23-AUG-15 12.36.36.656000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Demarge',null,null,'See City of Demarge or Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2474,1,to_timestamp('19-SEP-15 06.52.56.913000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Senate, The',null,null,'See the Imperial Senate.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2686,1,to_timestamp('23-SEP-15 09.38.50.818000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Toril Torin-s&oslash;n',null,null,'An ancient  du&aring;rg and lord of magnr Undlflos, former &thorn;engill-rikr of I&aring;arth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1447,1,to_timestamp('22-AUG-15 08.37.01.583000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gairdin',null,null,'A wilderness area within the confines of Rolar, known for its untamed quality and natural beauty.  All manner of flora and fauna grow here, including creatures thought extinct.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1406,1,to_timestamp('21-AUG-15 11.35.37.118000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Free Cities, The',null,null,'A collection of cities in the southwestern region of the Great Cavern.  They once fell under the dominion of Aljerbach but won their independence in the years during and after the Harnese Rebellion.  Each city has its own government, but they operate as a loose coalition to guarantee their collective sovereignty, warding against both Aljerbach and Harn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1423,1,to_timestamp('21-AUG-15 06.47.37.328000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Glimmer Vine',null,null,'A vine endemic to Calikar.  It has the quality that it can absorb light, remaining bio-luminescent for a short time thereafter.  It is frequently used as ornamentation on elithin ships and in elithin constructions.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1424,1,to_timestamp('21-AUG-15 06.51.59.787000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Modeira, The',null,null,'A labyrinthine set of cramped tunnels and caverns carved into the wall that separates the Great Cavern into its western and eastern sections.  It is thought cursed and/or haunted, and both men and elithi have avoided the Moderia since its discovery.  Its geography is not accurately mapped, nor does anyone know what lies within.  Also called the Labyrinth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1425,1,to_timestamp('21-AUG-15 06.52.13.891000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Labyrinth, The',null,null,'An alternate name for the Modeira.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1426,1,to_timestamp('21-AUG-15 07.01.15.756000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sphere',null,null,'One of six vessels in which the na''k&ouml;r descended.  They are perfect spheres measuring approximately fifteen feet in radius, silver in color but as hard as iron.  They have no apparent entrance.  Two fell in Aljerbach, one in I&aring;rth, one in Calikar, one in Demarge, and one near Uragar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1427,1,to_timestamp('21-AUG-15 07.04.58.467000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arsshtra',null,null,'A shadowy, reptilian creature, apparently one of the kabaal, who pursued the na''k&ouml;r aboard the Sihte Alonne, evidently to serve some purpose or prophecy of his people.  Later, he followed and served Diarin when she fled the ship at Gh&icirc;lar.  He perished in Rolar under unclear circumstances, still in her service.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1428,1,to_timestamp('21-AUG-15 07.10.21.265000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Song',null,null,'A gathering place of the elithi, where they meet to sing the Song.  The floors are crafted from a''shanne, sacred sand which rises to represent the form of the Chorus.  Each city and Song has its own hall.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1429,1,to_timestamp('21-AUG-15 07.11.04.751000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aoldain',null,null,'The Hall of Song in Rolar.  Its sands have been hardened glass since Gethin y Kam&icirc;l brought stillness to the Song.  Some blame the hall''s demise on the deeds of Cadeyrn yr Orain, who drew a blade in the hall to challenge Gethin''s accusations about Cyngen yr Orain and Cynyr yr Orain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1430,1,to_timestamp('21-AUG-15 07.12.11.656000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tree, The',null,null,'See the Great Tree.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1452,1,to_timestamp('22-AUG-15 09.36.58.489000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Huotha Ji''har',null,null,'Words found inscribed on the Archway, meaning "That realm which is seen by the blind", "The realm that is not", or "Nowhere."  It was also the answer to a s&oelign-aqar&aring; discovered by Malig in Rolar, but the question is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1215,1,to_timestamp('16-AUG-15 10.08.23.625000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Trog''lin',null,null,'A race of the Bel''caj Ged''don, created by Az-Azaal and N&aelig;lya in the Black Waters beneath Dagar Morduum.  Appearing as twisted humanoids with elongated limbs and distorted features, trog''lin have an intense desire to possess silver and gold, including a desire to consume it, but are strongly allergic to its touch.  They also have a natural repulsion to human flesh but can only survive off it. Trog''lin are possessed of keen intelligence, but are unable to read or write or form words that are comprehensible to a man.  They have their own language, spoken in a pitch that is barely audible to humans.  All trog''lin are bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  They were employed as basic soldiers during the Great War, sent in hordes to die on the fronts.  Several tribes of the trog''lin survive on the continent of Serinin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1248,1,to_timestamp('18-AUG-15 08.54.18.799000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'F&oacute;lk',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.  The rikr was Dagur Dagur-s&oslash;n as the time of Arastel''s assault, but he perished during the attack.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1252,1,to_timestamp('18-AUG-15 08.55.03.925000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Skjotla',null,null,'A clan of I&aring;rth, one of the magnr.  Currently holds a seat among the &thorn;engill.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1258,1,to_timestamp('18-AUG-15 10.19.36.209000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fagvaenn',null,null,'One of the Grod of the du&aring;rg, associated with beauty and vanity.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1263,1,to_timestamp('18-AUG-15 10.21.52.032000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gandr',null,null,'One of the Grod of the du&aring;rg, associated with study and knowledge.  The guild haven of the Hammar is known as Eslr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1310,1,to_timestamp('20-AUG-15 03.30.28.556000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dai''lok',null,null,'The third born of the Er&uacute;, per the Sundering of Anda Adalai&auml;, associated with the Prime Virtues of Intellect and Wisdom.  He extended the Gift of Da''kol to the Five Races.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1314,1,to_timestamp('20-AUG-15 03.33.13.676000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sundering of Anda Adalai&auml;, The',null,null,'One of the Poems of Lero, describing the early ages of the Universe.  It consists of nine stanzas.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1317,1,to_timestamp('20-AUG-15 03.40.10.493000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jardain',null,null,'An early world of the Universe, where Istaron and Veryn first dwelt.  The Children of Istaron were born here.  Later called Jardya by the people of Huaah''tiaah.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1326,1,to_timestamp('20-AUG-15 03.58.56.845000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aljaberin',null,null,'A city of the d&aelig;n''shee, long ago destroyed.  The city of I&aring;rth is built atop its ruins.  Also called the Sea Halls.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1431,1,to_timestamp('21-AUG-15 07.16.23.597000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Teloq-&icirc;plat',null,null,'One of two curved swords carried by Diarin since the Sphere.  It appears to be a Shiahd or Tal''Shiahd with the power to summon chill and frost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1339,1,to_timestamp('20-AUG-15 07.55.48.996000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Llearth',null,null,'A nearly indestructible stone that cannot be shaped by heat, pressure, labor, Da''kol, or Naming.  Some of the most talented Singers are able to shape llearth via the Depsangf.  In appearance, llearth is utterly black, seeming to pull in all surrounding light.  It has no reflection.  Also called dragonstone, godstone, truestone, deepstone, and black mithril.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1342,1,to_timestamp('20-AUG-15 07.56.38.285000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Godstone',null,null,'An alternative name for llearth.  Also a term for the Star Gem used in Demarge.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1357,1,to_timestamp('20-AUG-15 08.35.53.830000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mountain Home, The',null,null,'A vast llearth mountain which rises from the heart of I&aring;rth.  It is the symbol and heart of the Iron Kingdom, from which the &thorn;engill rule.  It was Spoken over the ruins of Magor-Balkr during the years following the Sons'' War, which brought untold devastation to the Iron Kingdom, and has since been raised higher every season by the efforts of the Hammar.  The Mountain Home has never fallen, and the Iron Born believe it never will.  This is reflected in the saying "A thousand hammers, a thousand years."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1365,1,to_timestamp('20-AUG-15 09.12.42.354000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Anda Adalai&auml;',null,null,'The heart of creation, where D&uacute;vinar dwells.  The Er&uacute; and the Ir&uacute; were born here; and Jh&aelig;''va&icirc;r was crafted.  Also called the Celestial Palace.  Surrounded by Qu&aelig;''lor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1377,1,to_timestamp('20-AUG-15 09.24.14.392000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Po-r&auml;th',null,null,'A weapon featuring double-bladed ax heads on both sides of a wooden shaft.  It is a symbol of the aq''Mar''Shein of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2476,1,to_timestamp('19-SEP-15 07.03.44.283000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Quintune',null,null,'One of five persons chosen from among the plebeians of the Empire of Demarge, meant to act as the voice of the people in the Imperial Senate.  They have the right to speak before the body but are not afforded a vote.  However, they can by four-fifths majority override the vote of the senators and by unanimous consent override the Divine Veto of the God Emperor.  This latter right has only been exercised twice in the history of the Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1382,1,to_timestamp('21-AUG-15 08.34.51.301000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'D&ucirc;n Mile''&ucirc;r',null,null,'The Great Tree of Rolar, which grew at the heart of Diris Alonne.  The seedlling of Mile''&ucirc;r.  D&ucirc;n Mile''&ucirc;r was wounded by Gethin y Kam&icirc;l, causing the Song to go silent, and then was murdered by Amber, causing the Song''s demise.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1402,1,to_timestamp('21-AUG-15 11.24.40.782000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Void, The',null,null,'A Fold of the Universe whose origins and purpose are lost to time.  It appears to be a vast grey realm populated by grey stars, vast spiders, and the gh&ucirc;l.  Zaryth dar Mistral''s attempts to restore the dead apparently opened a rift into the Void during the First &AElig;gan Empire, resulting in the Gh&ucirc;l War.  Also called Gahgra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1408,1,to_timestamp('21-AUG-15 03.58.26.174000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Above, the',null,null,'The ceiling, or literal roof, of a cavern of the Domains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1473,1,to_timestamp('22-AUG-15 05.53.48.265000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shaaro Plains, The',null,null,'A broad expanse of open plains to the north and east of Aljerbach and the Taunland, situated atop a plateau.  Isolated villages are scattered across the Shaaro Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1474,1,to_timestamp('22-AUG-15 05.58.06.429000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Anarawd yr Eldwr',null,null,'The head of Ryche Eldwr and uncle to Taliesin yr Eldwr.  He was a close friend and adviser to Delsar during much of his reign, but was later imprisoned when he failed to inform his king of his dreams, resulting (to Delsar''s mind) in Cadeyrn yr Orain''s death.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1475,1,to_timestamp('22-AUG-15 07.58.23.611000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taliesin yr Eldwr',null,null,'A prince of Ryche Eldwr and nephew of Anarawd yr Eldwr, friend to Delsar, captain of the Brische Calikahd.  He perished in the fighting pits of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1489,1,to_timestamp('23-AUG-15 06.09.08.156000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Veryn''s Song',null,null,'An ancient name for the Earth Song.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (941,1,to_timestamp('15-AUG-15 09.53.43.747000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Havuni',null,null,'One of the Ba-kwe, inhabitant of the town of Kanalokamu, and friend to Kain.  He accompanied the na''k&ouml;r on their journey to find Kain''s Sphere.  He perished in Uragar, where he inadvertently betrayed the na''k&ouml;r to Serrik Vey''ron via his dreams.  Eleanor absorbed his memories into herself before his death.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1222,1,to_timestamp('17-AUG-15 04.08.55.493000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Clever God, The',null,null,'An alternative name for Ai-Ani.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (657,1,to_timestamp('13-AUG-15 11.06.11.883000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mer''Tanal',null,null,'The motto of the Gamon Ju''g&aelig;, meaning "Without Error."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1227,1,to_timestamp('18-AUG-15 08.42.23.095000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;engill-rikr',null,null,'The king of I&aring;rth, also known as the High Thane or the Iron King.  Selected by unanimous election among the eight &thorn;engill.  No &thorn;engill-rikr has ruled from the Mountain Home in centuries.  The first &thorn;engill-rikr was Amlaith Hjalmar-s&oslash;n of magnr Khazin.  The most recent was Agnard Edvill-s&oslash;n of magnr Ginnheilag, who died during the War of East and West.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1301,1,to_timestamp('20-AUG-15 07.51.05.977000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;en',null,null,'The eight houses (out of a total of twenty-one) of the magnr which currently hold seats on the &thorn;engill in I&aring;rth.  Replacements are selected by the sitting thanes.  Currently, there are only seven &thorn;en.  No replacement was chosen after the disgrace of magnr Undlflos.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (692,1,to_timestamp('14-AUG-15 01.00.31.267000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Spear',null,null,'An alternative name for a Hierophant of the Caal Anuur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (671,1,to_timestamp('13-AUG-15 11.33.53.364000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ankr',null,null,'A term in the language of the Shug, meaning "strength through rage."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (436,1,to_timestamp('12-AUG-15 01.43.40.794000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Children of the Gift, The',null,null,'An alternative name for the Kalit’edon Emari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (446,1,to_timestamp('12-AUG-15 01.49.23.138000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Salt Folk, The',null,null,'An alternative name for the Ku-kuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (459,1,to_timestamp('12-AUG-15 02.05.43.990000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tower of Circles, The',null,null,'The name of the synern located in the ruins of the city of Uragar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1495,1,to_timestamp('23-AUG-15 06.31.41.659000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eshrella y''Gur Dao''Ast',null,null,'The current queen of Kailar, regarded as cruel and ruthless.  She has initiated countless wars with Calikar and the Great Roof over the centuries.  She is the mother to Diarin, Kiarin, and Liarin.  Dao''Ast has ruled the d&aelig;n''shee for well over a millennium, a lifespan that exceeds the average for her people by many times.  Also called simply Eshrella Dao''Ast or Dao''Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1496,1,to_timestamp('23-AUG-15 06.31.56.235000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dao''Ast',null,null,'A shortened form of Eshrella y''Gur Dao''Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1497,1,to_timestamp('23-AUG-15 11.54.32.277000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'God Emperor, The',null,null,'The emperor of Demarge, chosen from a dynastic imperial family, the Soluun.  Although there is theoretically a separation of power in the Empire of Demarge, the authority of the Emperor has grown over the centuries until it has become nearly absolute.  He appoints the Magistrates, exercises the Divine Veto, controls the Dun Mordra, and largely controls the Divine Host via the Triumvirs.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1498,1,to_timestamp('23-AUG-15 11.55.21.642000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'God Throne, The',null,null,'The seat of the God Emperor of Demarge.  Also known as the One Throne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1500,1,to_timestamp('23-AUG-15 12.03.08.828000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of the Waking Dream, The',null,null,'An alternative name for the city of Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1503,1,to_timestamp('23-AUG-15 12.09.27.808000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nara''s Seat',null,null,'An alternative name for the Throne of Nara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1505,1,to_timestamp('23-AUG-15 12.12.10.533000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Many Faces of Nara',null,null,'See the Faces of Nara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1506,1,to_timestamp('23-AUG-15 12.13.23.959000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nara',null,null,'See Nara do''Virt.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1507,1,to_timestamp('23-AUG-15 12.14.43.927000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Faces of Nara, The',null,null,'Stone visages which are carved in periodic intervals along the White Walls of Rolar.  Each is said to demonstrate a different mood or guise of the same woman, Nara do''Virt, the wife and love of Calik Twiceborn.  The Faces are said to protect the Emerald City.  Their eyes open in the presence of enemies of Calikar, inflicting paralysis and dread.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1511,1,to_timestamp('23-AUG-15 12.27.34.941000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Isles, The',null,null,'An alternative name for Srin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1385,1,to_timestamp('21-AUG-15 09.06.09.940000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Farlo',null,null,'A common name for the slums of the great cities of Puell&aelig;, generally located on the outskirts.  In modern, walled cities, farlos are most frequently found outside the outer walls.  Also called Sin Alleys.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1421,1,to_timestamp('21-AUG-15 06.40.33.331000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-&euml;l Mound',null,null,'Sacred hills carved by the Calikari, used as repositories or wells of water purified of the Grim.  Their interiors are hollow and planted with Eiluinar.  Their surfaces are carved with intricate, ornamental runes.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1394,1,to_timestamp('21-AUG-15 11.01.24.204000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ailar, The',null,null,'An alternative name for the elithi, most frequently used among human kingdoms of the Great Kingdom.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1307,1,to_timestamp('20-AUG-15 03.23.48.387000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Er&uacute;, The',null,null,'The first and greatest children of D&uacute;vinar, per the Sundering of Anda Adalai&auml;.  In the order of their creation:  Istaron, Veryn, Dai''lok, Jesr&aelig;''el, and Jarhya.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1490,1,to_timestamp('23-AUG-15 06.09.17.473000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Song of the Daughter, The',null,null,'An ancient name for the Earth Song.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1524,1,to_timestamp('23-AUG-15 12.50.51.403000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dun Mordra',null,null,'The private legions of the God Emperor of Demarge, bred and raised to kill among pits in the city of Dracol. They live their lives in cages and are are said to dine on the blood of dying men, beaten and twisted into terrifying warriors.  They are as feared as the ki''tahj.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1525,1,to_timestamp('23-AUG-15 12.51.06.180000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blood, The',null,null,'An alternative name for the Sangus of Demarge.  Also term for one who possesses the Gift as it relates to Shaping.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1526,1,to_timestamp('23-AUG-15 12.51.22.316000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'High Blood, The',null,null,'An alternative name for the Centarum of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1527,1,to_timestamp('23-AUG-15 01.05.11.883000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Skien',null,null,'A hurtt in the southern regions of I&aring;rth, located near a Deep Gate.  The na''k&ouml;r passed under its shadow on their journey to find the Black Woman.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1528,1,to_timestamp('23-AUG-15 01.08.11.774000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'V''g&aring;ndr',null,null,'A fortress in the southern regions of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1529,1,to_timestamp('23-AUG-15 01.20.17.162000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aust-Agder',null,null,'A hurtt in the southern regions of I&aring;rth.  Kaja''lar''s Sphere fell near the village, and she later murdered its inhabitants.  It was visited by Delsar, Diarin, Amber, and Durgin as they pursued rumors of the Black Woman.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1537,1,to_timestamp('23-AUG-15 01.42.38.387000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dark, The',null,null,'An alternative name for the Deep Dark.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1539,1,to_timestamp('23-AUG-15 01.43.32.564000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Westermere',null,null,'The name of a region of the kingdom of Rolar, located west of Aiun-Malach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1476,1,to_timestamp('22-AUG-15 08.01.42.658000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aneiron y Marel',null,null,'The son of Diarin yn Marel yn Ast and Therylin y Marel and prince of Ryche Marel. The only known child of a d&aelig;n''shee and elithin (her mother is a half-breed, but this is not known), Aneiron has entirely elithin characteristics.  He was murdered by Drolock''harka during the Grey War but resurrected by the power of the Laroch &Ucirc;l.  In the absence of his mother and father, he currently represents Ryche Marel in the Tiaryche.  He also acts as personal servant and voice of Delsar Unborn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1523,1,to_timestamp('23-AUG-15 12.49.06.481000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'God''s City, The',null,null,'An alternative name for the City of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1513,1,to_timestamp('23-AUG-15 12.31.09.989000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'I''Srin L&ucirc;n',null,null,'A gateway into Srin, manifesting as an empty, unadorned stone portal.  One enters the breach by passing beneath.  Also called an Arch, a Breach Gate, or a Gateway.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1395,1,to_timestamp('21-AUG-15 11.03.31.028000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Calikar',null,null,'The kingdom of the elithi in the Great Cavern.  It once covered the whole of the Roof, before the Land Gift and the Gloaming.  Among the Bright Lords, Calikar (or Uahd isch Calikahd), may also refer to Roof as a whole.  Also called the Bright Kingdom and Uahd isch Calikahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1510,1,to_timestamp('23-AUG-15 12.26.56.554000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'World Between Worlds, The',null,null,'An alternative name for Srin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2469,1,to_timestamp('18-SEP-15 11.52.20.372000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'N&aelig;lya',null,null,'The name assumed by N''koth Velar when she became one of the Mortuani.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1770,1,to_timestamp('29-AUG-15 11.01.47.882000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Key of Echoes, The',null,null,'The translation of Laroch &Ucirc;l in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1441,1,to_timestamp('21-AUG-15 08.50.47.087000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Belaan',null,null,'The name by which du&aring;rg originally called Kaja''lar after her discovery at Aust-Agder.  She adopted this name until her dreams eventually revealed the truth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1444,1,to_timestamp('22-AUG-15 08.28.50.298000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Kam&icirc;l',null,null,'A house of the elithi in Rolar, once accounted among the Tiaryche, known as the guardians of the Song.  Its current head is Aeden y Kam&icirc;l.  The Ryche is currently in a state of dishonor after the deeds of Gethin y Kam&icirc;l.  Their status as Tiaryche has been revoked, the only time such a punishment has been levied.  Their symbol is a cardinal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1771,1,to_timestamp('29-AUG-15 11.02.00.671000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Key to Echoes, The',null,null,'The translation of Laroch &Ucirc;l in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1772,1,to_timestamp('30-AUG-15 12.53.06.199000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seros',null,null,'A lesser god of Malkar, son of Kourix and Valeus.  Portrayed as a boy, he is associated with love/infatuation, particularly when it is urgent/young/overwhelming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1773,1,to_timestamp('30-AUG-15 12.53.47.505000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Phallus of Seros, The',null,null,'A mythological relic associated with the boy god, Seros, said to endow the possessor with the power to woo any woman.  Its theft was thought to be partially responsible for the cataclysm that struck Phera.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1471,1,to_timestamp('22-AUG-15 05.51.25.973000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taunland, The',null,null,'An area of farmland to the east of Aljerbach, whose populace breed Urda and harvest fungal wood, plon, and mushrooms.  It is separated from the Thrice City by the Forest of Ur.  Also called the Taun.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1493,1,to_timestamp('23-AUG-15 06.24.41.618000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Chorus, The',null,null,'An alternative name for the Song.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1407,1,to_timestamp('21-AUG-15 11.38.57.309000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Natalie',null,null,'A girl of indeterminate origin who descended with the Harbingers of Eta&ucirc;r in their Sphere.  She died in the prison of Ajer-D&aelig;n.  Later, she appeared to Malnos in a dream or vision and taught him the song by which he summoned the d&aelig;mon who brought ruin to Aljerbach.  Strangely, other na''k&ouml;l have encountered nearly identical girls who vary only in their age:  Ana and Sayori.  Natalie possessed a pendant (a golden disc on a silver chain, marked by an unfamiliar black rune:  later identified by Garani der''Annher as the Warden''s Sigil), discovered in the Temple of Thr&icirc;sh, strangely identical to one given to Durgin by Sayori. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2470,1,to_timestamp('19-SEP-15 06.40.21.983000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sindur Plains, The',null,null,'A district in the northeastern region of Aljerbach, adjacent to the Great River.  Several small villages are located here, as well as the city of Kellis.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2471,1,to_timestamp('19-SEP-15 06.42.46.590000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Garus',null,null,'A house of the Sangus and Centarum in the Empire of Demarge, one of two houses afforded three representatives in the Imperial Senate (with Tallium).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1486,1,to_timestamp('23-AUG-15 05.55.19.521000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sorain',null,null,'The city and kingdom of the corti in the Great Kingdom, built atop lands offered by the Calikari in the Land Grand.  It was closely allied with Calikar, Aljerbach, and I&aring;rth.  The city was protected by a wyrd of misdirection that kept its people safe from the ki''tahj for long centuries.  Eventually, however, it was destroyed by the d&aelig;n''shee in the War of the Final Dawning.  Afterward, the corti were thought extinct.  Sorain was once home to the Star Gem, which created periods of alternating light and darkness in the Great Cavern, and also several other artifacts entrusted to the keeping of the corti by Gai''lal.  The Star Gem is assumed to have been taken back to Kailar after the war.  The fate of the other artifacts is unknown.  Also called the Grey City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1461,1,to_timestamp('22-AUG-15 10.09.29.330000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dal Ammar',null,null,'A hilly region of southern I&aring;rth, known for its quarries and mines.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1478,1,to_timestamp('23-AUG-15 05.10.06.724000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aiel-m&ucirc;r',null,null,'The tower of the Eshthara in Rolar, crafted of gray stones.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1501,1,to_timestamp('23-AUG-15 12.03.20.430000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Emerald City, The',null,null,'An alternative name for the city of Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1477,1,to_timestamp('23-AUG-15 04.53.54.474000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'D&ucirc;n &AElig;mac&ucirc;r',null,null,'The Great Tree of Sorain, planted as gift from the elithi to the corti.  It was located in a  plaza on the southern side of the city, opposite the Star Gem.  It was seeded by Arienne yn Wuhlain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1479,1,to_timestamp('23-AUG-15 05.12.21.919000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Freyna yn Loutha',null,null,'One of the Eshthara who accompanied Delsar, Amber, and Diarin to I&aring;rth.  She was disfigured saving Delsar from the Black Wind in Oilar and later perished in the fighting pits of the Iron City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1775,1,to_timestamp('30-AUG-15 12.56.44.182000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sikyons',null,null,'A port town of medium size along the Forever Sea in Malkar, located between Namar and the fishing village of Sinope.  Malig set sail from here for Oikon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1776,1,to_timestamp('30-AUG-15 12.58.36.019000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sinope',null,null,'A small fishing village on the Forever Sea in Malkar, approximately equidistant between Hyele and Namar.  Malig visited here while seeking transportation to Oikon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1778,1,to_timestamp('31-AUG-15 09.50.42.104000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Endless Cavern, The',null,null,'An alternative name for Llua''la.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1485,1,to_timestamp('23-AUG-15 05.43.42.457000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Land Gift, The',null,null,'A grant of land offered by the lords of Calikar to the corti and du&aring;rg, creating the kingdoms of I&aring;arth and Sorain.  Technically, the lands offered were under the dominion of Kailar at the time.  Known as fae&eth;a to the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (796,1,to_timestamp('14-AUG-15 09.53.39.360000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Durem Erim&aelig;',null,null,'Chiromantic practitioners of the Gift in the modern age and spiritual successors of the N&aelig; Kisal.  The Durem Erim&aelig; have no knowledge of the underlying nature of Da''kol or the Pillars.  They jealously reserve magick for their members, scouring the Domains for children to join their order under the guise of religious rites.  They maintain their power by supplying pure water to the Roofs via the kbai.  AThe Durem Erim&aelig; follow a perverted form of the g''harra, eschewing direct political power.  Even Alanon, the heart of their influence, has a separate mundane government.  Members are identified along two separate axes, one representing "strength" and the other "role."  For example, zal-heru is a measure of strength while T''h&aelig;n is a role.  The measures of strength are, in order:  jal-kuru, nal-kuru, dal-naru, ral-naru, sal-naru, gal-heru, kal-heru, tal-heru, mal-heru, val-heru, zal-heru, and seral-heru.  More commonly known as the Order.  Individual members are known as Givers or magi (the former most common the human kingdoms of the Great Cavern).  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1202,1,to_timestamp('16-AUG-15 07.53.44.554000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Anlocht',null,null,'An ancient, white steel blade possessed by Calik.  According to the legends of the Calikari, the sword would eventually be reclaimed, thus fulfilling the prophecy of the Twiceborn''s return.  This did not happen for three thousand years.  The touch of Anlocht was death to all those who tried, causing a great tree to erupt from their abdomens.  However, the blade finally accepted Delsar during the Grey War.  Anlocht''s nature is ill-understood, and it is likely that the blade is a Tal''Shiahd.  Its history during and prior to the First &AElig;gan Empire is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1380,1,to_timestamp('21-AUG-15 08.30.38.045000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Diris Alonne',null,null,'The sacred grove of Rolar, surrounding D&ucirc;n Mil''e&ucirc;r.  Its saplings were born of a Great Tree, causing them to emit a luminescence that gave the Emerald City its name.  A wyrd or other spell caused madness in those not of the Bright Blood who walked beneath its boughs.  Elithi instead experienced intense communal memories.  The grove perished after Amber murdered D&ucirc;n Mil''e&ucirc;r during the Grey War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3075,1,to_timestamp('10-OCT-15 11.39.59.683000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Flames of Ravin, The',null,null,'The name by which the people of Aljerbach know the beacon towers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3076,1,to_timestamp('10-OCT-15 11.41.40.610000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Blarb&aacute;l, The',null,null,'The name by which the people of I&aring;rth know the beacon towers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3077,1,to_timestamp('10-OCT-15 11.44.14.056000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Beacon Towers, The',null,null,'A series of signal pyres set throughout the eastern lands of the Great Roof, running from southern I&aring;rth to Aljerbach.  They are set to signify the coming of threat; and burn blue to signal the coming of the ki''tahj.  Among the du&aring;rg, they are known as the Blarb&aacute;l, and among the Aljer they are known as the Flames of Ravin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1420,1,to_timestamp('21-AUG-15 06.37.29.148000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eiluinar',null,null,'A plant endemic to Calikar, which roots possess the power to cleanse the Grim.  Eiluinar develops in two distinct stages.  In the first, it grows as a parasite upon the trees of the Holy Groves, harvesting nutrients from their roots.  After it blossoms, the Eiluinar can be separated from its host and placed in a ka-&uml;el mound.  In its second form, the plant possesses the power to cleanse water that passes over its roots.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1432,1,to_timestamp('21-AUG-15 07.16.54.815000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&Icirc;n-Rak''shyr',null,null,'One of two curved swords carried by Diarin since the Sphere.  It appears to be a Shiahd or Tal''Shiahd with the power to summon flame.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1434,1,to_timestamp('21-AUG-15 07.27.03.923000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kabaal',null,null,'A race which currently dwells in the Modeira in the Great Cavern, a place they call Uushra''s Coil and which they consider to be a prison or penance they must suffer until their queen is freed.  Little is known of the their culture or history except the secrets imparted to Diarin by Arsshtra.  The kabaal have reptilian features and feed by absorbing the inner organs and fluids from their victims.  They also have the power to trespass the shadow realm, though in a lesser capacity than the kiril.  Their mythology appears to be a distorted history of Zaryth dar Mistral, whom they believe to be a wronged god they call Ar&icirc;sh-sh&ucirc;l.  They have a prophecy which involves the na''k&ouml;l freeing this goddess from her prison and saving their people.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3053,1,to_timestamp('10-OCT-15 10.53.07.771000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Innermost City, The',null,null,'The innermost district of the radially designed city of Aljerbach.  It is home to the royal palace, the temple district, the royal docks, the library of Aljerbach, the prison of Ajer-D&aelig;n, the Tower of Sages, the royal park, and a small number of personal estates of elder houses of the Aljerstraad.  The Innermost City is surrounded by the Innermost Wall, which is a ceremonial barrier of piled stone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1464,1,to_timestamp('22-AUG-15 10.18.31.168000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;dramar',null,null,'A name or title by which Irien has referred to Delsar.  Delsar has also on rare occasion dreamed as a man called by the same name.  Memories of these dreams are not clear on awakening.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1769,1,to_timestamp('29-AUG-15 10.45.12.405000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Delsar Unborn',null,null,'The name by which Cyngen yr Orain came to call himself after he called upon the Laroch &Ucirc;l to cleanse himself of his "humanity."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1416,1,to_timestamp('21-AUG-15 04.12.44.652000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sihte Alonne',null,null,'The ship which delivered the na''k&ouml;r from Aljerbach to Rolar, captained by Pryderi yr Erybin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1418,1,to_timestamp('21-AUG-15 04.15.37.962000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Qino-Porath',null,null,'A small village in the northern regions of the kingdom of Aljerbach.  Its inhabitants were slaughtered by Diarin yn Marel yn Ast on her path from Gh&icirc;lar to Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3054,1,to_timestamp('10-OCT-15 10.55.05.035000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Inner City, The',null,null,'A district of the radially designed city of Aljerbach, lying just outside the Innermost City.  It is primarily the home of elder houses of the Aljerstraad and wealthy merchant families.  Opulent plazas and shopping districts are also located here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3078,1,to_timestamp('10-OCT-15 11.46.45.093000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Comitatis, The',null,null,'The personal honor guard of the House of Klar.  Considered a great honor, its members are volunteers and are most commonly the second and third sons of great houses of the Aljerstraad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3079,1,to_timestamp('10-OCT-15 11.50.19.444000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ethereal Chorus, The',null,null,'A group of choralists in the Empire of Demarge, whose art has been passed down through their generations for long centuries.  They sing without words, and their voices are considered divine instruments.  Once reserved for the God-Emperor and his family, the Ethereal Chorus has performed on rare occasions before houses of the Sangus and the Milita in recent decades.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1962,1,to_timestamp('05-SEP-15 12.53.10.908000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Basilica',null,null,'An alternative name for a Shul.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1782,1,to_timestamp('31-AUG-15 10.01.09.040000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Maze, The',null,null,'An alternative name for the Unfinding of Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1568,1,to_timestamp('23-AUG-15 07.25.26.789000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ivy City, The',null,null,'An alternative name for the city of Harn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1482,1,to_timestamp('23-AUG-15 05.19.40.453000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Senyar',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Its current head is Ruari y Senyar.  The line of Senyar have acted as the High Kings of Calikar since Calik''s death and are considered descended of his blood.  Their symbol is a white owl.  Prior to the Ungloaming, Riordain y Senyar was their head.  Afterward, he was succeeded by his son, Ruari y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1443,1,to_timestamp('22-AUG-15 08.25.22.222000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Marel',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Its current head is Therylin y Marel.  With his disappearance, and the absence of his wife, Diarin, their son, Aneiron y Marel, currently holds the seat.  Their symbol is a white swan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1470,1,to_timestamp('22-AUG-15 03.18.17.406000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taabri&icirc;l',null,null,'A race of the Bel''caj Ged''don, created by Az-Azaal and N&aelig;lya in the Black Waters beneath Dagar Morduum.  Taabri&icirc;l resemble normal humanoids.  They are able to take on the shape of animals (an act that involves terrific pain) and also communicate with them.  Each taabri&icirc;l is bound to a specific species of animal, but can elect to change this affinity on nights when Kovar is full.  On nights when both Kovar and Augar are in the sky, taabri&icirc;l are reduced to mindless animals.  All taabri&icirc;l are bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  Taabri&icirc;l are natural loaners, generally only mating in their animal forms.  Several are known to inhabit the continent of Serinin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1460,1,to_timestamp('22-AUG-15 10.05.58.581000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lappsala',null,null,'A small hurtt located in Dal Hammar in the southern regions of I&aring;rth, visited by the na''k&ouml;r on their journey to the Iron City.  They remained here for several days.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1487,1,to_timestamp('23-AUG-15 05.57.43.936000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of the Final Dawning, The',null,null,'A brief war, or, more accurately, a battle, in which the d&aelig;n''shee inexplicably overcame the misdirecting wyrd that protected Sorain.  The city and kingdom, including all or most of the corti, were destroyed within hours.  So named because the Star Gem was broken or stolen during the conflict, forever ending its light in the Great Cavern.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1514,1,to_timestamp('23-AUG-15 12.31.29.690000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arch',null,null,'An alternative name for an i''Srin L&Ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1459,1,to_timestamp('22-AUG-15 10.03.36.945000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hjalgar',null,null,'A minor clan of the du&aring;rg.  They own the largest brewery in the hurtt of Lappsala and acted as host to Amber during the na''k&ouml;r''s visit to the town.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1564,1,to_timestamp('23-AUG-15 06.55.29.431000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Erinin''ar',null,null,'One of the Eight Pillars of Calikar, located in the heart of what is now the Inner Plains over the ruins of D''hnal.  Its Holy Grove was Sung by Leuen y Senyar.  The city was destroyed by the d&aelig;n''shee during the Slaves'' War approximately a hundred years before the Gloaming, never to be reclaimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1882,1,to_timestamp('01-SEP-15 03.01.32.110000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Awrywdd',null,null,'A term for an exile in ancient Calikar, and also the name for the act of banishment.  It was marked by the pal’n&icirc;r being bound to the neck with black Ilantuil.  Those banished were not to be heard or looked upon, nor could they join in the Song.  They were forbidden from entering a Holy Grove under pain of death.  Also called the Quietus.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1502,1,to_timestamp('23-AUG-15 12.03.23.435000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rolar',null,null,'One of the Eight Pillars of Calikar, the first city Sung by Calik himself after the elithi emerged from their long exile.  Rolar lies upon the Althrin, situated atop the plateau of Aiun-Malach in the Valley of Uanahd.  It is surrounded by the White Walls, which had never fallen prior to the Grey War.  The Great Tree of Rolar, D&ucirc;n Mile''&ucirc;r is dead, its Hall of Song still, and its Song silent.  Most of its people perished in the Grey War.  The kings of Rolar are the line of Orain, and their current head is Delsar Unborn.  For more than a millennium, Rolar as been the only known surviving city of Calikar, and its king has acted as High King.  However, the Gloaming has recently been lifted from over Lluagh Maguin, to unknown result.  Also called the Emerald City, the City of the Waking Dream, and, by the elithi, Rhulwyr Lluete mucht Istue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1883,1,to_timestamp('01-SEP-15 03.01.48.512000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Quietus, The',null,null,'An alternative term for awrywdd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1884,1,to_timestamp('01-SEP-15 03.03.08.787000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Feaght',null,null,'A term in Tuindil, meaning "fate-bound."  It referred to one who offered one’s life and honor as bond for another’s good behavior.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1885,1,to_timestamp('01-SEP-15 03.05.27.254000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Blysiog',null,null,'The madness, lethargy, and eventual death suffered by elithi who are separated from u’Vidain e’Cuhlhair.  Translates literally to "the Longing."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1886,1,to_timestamp('01-SEP-15 03.05.50.321000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Longing, The',null,null,'Literal translation of Blysiog.  Also an alternative term for the Grod.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1887,1,to_timestamp('01-SEP-15 03.13.31.880000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sight, The',null,null,'A rare gift of prophecy, possessed by only a handful of individuals throughout history.  Known examples include Eldrani do''Kiron and the lords of Ryche Rhuaidh.  It manifests in various forms, including visions, spoken prophecy, premonition, dreams, etc.  Among the elithi, it is known as Gwellwg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1888,1,to_timestamp('01-SEP-15 03.13.47.257000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gwellwg',null,null,'The name by which the Sight is known among the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1582,1,to_timestamp('24-AUG-15 10.10.01.961000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Liar''s Peace, The',null,null,'A peace treaty organized by Veomnis of Kailar and high king Fionn y Senyar of Calikar in an attempt to end the War of Ash, hosted at a''Tiernya.  The d&aelig;n''shee betrayed and murdered their hosts, simultaneously launching a series of assassinations and assaults throughout Calikar.  Twenty years later, Fionn''s grandson, Cuth&ucirc;l-erin, took his vengeance in the War of Measure, driving the kitahj back into Kailar, breaking their power, and deposing their king.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1556,1,to_timestamp('23-AUG-15 06.39.34.148000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rhuanedd',null,null,'One of the Eight Pillars of Calikar, located an island called Shiar in the Althrin in the heart of Alune.  It was renowned as a city of scholars and the heart of the order of the Eshthara.  Its Holy Grove was Sung by Leuen y Senyar and was called Diris Rhuan.  Its Great Tree was Nuchalan and its Hall of Song was Sendain.  It was lost to the Gloaming, and its survivors fled to Shar''ris y Rhuanedd.  The city was recently reclaimed.  Also called the City of Sages, the City of Scholars, and the City of Learning.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1706,1,to_timestamp('29-AUG-15 08.44.54.130000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black Mist, The',null,null,'An alternative name for Dubh Gaoth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1707,1,to_timestamp('29-AUG-15 08.45.07.949000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hunger, The',null,null,'An alternative name for Dubh Gaoth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1708,1,to_timestamp('29-AUG-15 08.45.12.647000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mist, The',null,null,'An alternative name for Dubh Gaoth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1709,1,to_timestamp('29-AUG-15 08.45.18.986000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black Hunger, The',null,null,'An alternative name for Dubh Gaoth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1800,1,to_timestamp('31-AUG-15 10.40.52.438000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Battle of Kuren''or',null,null,'The final battle of the War of East and West.  The forces of Calikar were victorious, but their king, Cynyr yr Orain, was lost and and presumed dead.  In fact, he was taken as prisoner to Kailar, where he endured torment at the hands of Eshrella y''Gur Dao''Ast for ten years.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1801,1,to_timestamp('31-AUG-15 10.42.27.432000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bright Legion',null,null,'The armies of Calikar, famed for their armor and blades of white stone, the m&aelig;lorwi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1802,1,to_timestamp('31-AUG-15 10.47.48.729000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Long, Short Years',null,null,'Literal translation of Shara Lach-Nach in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1803,1,to_timestamp('31-AUG-15 10.50.25.873000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Maliuch Beuen',null,null,'The hall of healing in Rolar.  Its foundation is credited to Sionyn y Randwch, though this legend is likely false.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1804,1,to_timestamp('31-AUG-15 10.52.49.001000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Huynduir',null,null,'A famed harbor and shipyard of Calikar, located several days'' journey west from Rolar along the Althrin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1807,1,to_timestamp('01-SEP-15 07.15.18.494000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Duri-Med&icirc;n',null,null,'The great hall of learning and performance for the song-smiths of Calikar, located in Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1809,1,to_timestamp('01-SEP-15 07.25.33.989000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Turin&aelig;',null,null,'The halls of learning at Rolar, consisting of emerald towers and deep vaults.  Tyrin lies at its heart.  The texts and scrolls at Turin&aelig; are in general more recent or replicas, with the original and ancient documents having been moved to the depths of Vild&acirc;n after its construction. According to legend, Turin&aelig; was constructed by Tyren y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1585,1,to_timestamp('24-AUG-15 10.25.28.363000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Blood Screamers',null,null,'Individual warriors of the ki''tahj.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1587,1,to_timestamp('24-AUG-15 10.30.09.061000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Myndrath',null,null,'An elithin village in Westermere.  Diarin sent Aneiron y Marel here when she journeyed to I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1588,1,to_timestamp('24-AUG-15 10.32.57.391000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Searah''s Fen',null,null,'A thick morass in Westermere.  The waters of the swamp are waist deep and drip from the Above, rendering it a deadly place (due to the Grim).  Eleanor''s Sphere fell here.  Diarin later visited the site.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1591,1,to_timestamp('24-AUG-15 10.45.57.846000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lords of Chaos',null,null,'A possible translation of na''k&ouml;r in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1592,1,to_timestamp('24-AUG-15 10.46.06.564000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Children of Chaos',null,null,'A possible translation of na''k&ouml;r in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1594,1,to_timestamp('24-AUG-15 10.52.19.450000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cazari, The',null,null,'A shortened form of Al&euml;''uriel Caz''ur Ve''&euml;ri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1595,1,to_timestamp('24-AUG-15 10.54.33.940000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eshrella Dao''Ast',null,null,'See Eshrella y''Gur Dao''Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1559,1,to_timestamp('23-AUG-15 06.46.59.665000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of Kings, The',null,null,'An alternative name for &AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1563,1,to_timestamp('23-AUG-15 06.50.30.105000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aduine',null,null,'One of the Eight Pillars of Calikar, located deep in the heart of Alune.  Most of Calikar''s ancient forges and smithies were located here after the fall of Shuon Ashhal, as well as the Hall of Tablets.  Its Holy Grove was Sung by Leuen y Senyar.  It was lost to the Gloaming.  The city was recently reclaimed.  Also called the City of the White Flame and City of the Stone Shield.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1810,1,to_timestamp('01-SEP-15 07.37.00.326000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Atriach isch Meileuhr',null,null,'A palace crafted by the famed Maker, K&aelig;ryeah yr Inythyn, in &AElig;lm&aelig;dris in an attempt to win the heart of Brean-M&icirc;in yn Senyar.  Said to be so beautiful that it brought tears to the eyes of its creator.  It required the labor of one hundred Makers over twenty years as was the tallest edifice in the Great Cavern.  The palace was crafted all of gold and silver, possessing a natural radiance that did not fail even with the coming of the Gloaming.  Its walls and doors were constructed to this dimensions of his beloved.  The Song was said to always echo in its halls, summoning old memories both sweet and melancholy.  Also called the Palace of Yesterday.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1521,1,to_timestamp('23-AUG-15 12.45.32.336000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Centarum, The',null,null,'The highest 100 houses of the Sangus, which are eligible to hold seats in the imperial senate of Demarge.  Each house is afforded two senators, exceptiong Tallium and Garus, which sends three, and Soluun, which sends none.  Also called the High Blood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1538,1,to_timestamp('23-AUG-15 01.42.46.860000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Underdeeps, The',null,null,'An alternative name for the Deep Dark.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1811,1,to_timestamp('01-SEP-15 07.37.17.161000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Palace of Yesterday, The',null,null,'Translation of Atriach isch Meileuhr in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1558,1,to_timestamp('23-AUG-15 06.46.46.116000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vieun Istue',null,null,'A name for the heart of Calikar, sometimes applied to the city of &AElig;lm&aelig;dris but more typically to the Elod&aelig;ne Palace at its heart.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1573,1,to_timestamp('23-AUG-15 07.47.32.185000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cuth&ucirc;l-erin y Senyar',null,null,'An elithin and former high king of Calikar.  Twenty years after the Liar''s Peace at a''Tiernya, he led the Bright Legions in vengeance against Kailar.  This assault ended in the near ruin of Kailar and the death of its king, Veomnis.  The Golden Age of Calikar followed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1596,1,to_timestamp('24-AUG-15 12.47.41.644000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Garani der''Annher',null,null,'The lord of the house of Annher in Aljerbach and father of Garath der''Annher.  The Harbingers of Eta&ucirc;ur visited his estate in the Golden Square after escaping from Ajer-D&aelig;n.  He is the first person to refer to them as na''k&ouml;r and seemed to have preordained their arrival, including having arranged rooms to each of their tastes.  He had had previous dealings with Aldar der''Annache (related to the murder of his son), who aided the Harbingers in the Great Prison.  He also possessed a room with a portrait of Natalie and a pennant over his estate with the same sigil marked on Natalie''s pendant.  His behavior was strange, and perhaps touched, throughout the time the na''k&ouml;r remained in his house, and shortly after they arrived, he mysteriously vanished.  When he reappeared, he was apparently fully mad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1597,1,to_timestamp('24-AUG-15 12.47.57.701000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Garani',null,null,'See Garani der''Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1598,1,to_timestamp('24-AUG-15 12.48.36.083000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aldar',null,null,'See Aldar der''Annache.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1599,1,to_timestamp('24-AUG-15 12.56.08.020000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aldar der''Annache',null,null,'A lord of Aljerbach, knight of the Comitatis, father of Sigmy der''Annache, lord of House Annache, and loyal friend to the royal family of Klar. He was responsible for the interrogation of the Harbingers of Eta&ucirc;r in Ajer-D&aelig;n.  Although initially hostile, in the end he gave them aid.  He died in the prison, murdered by the poisoned blade of Geshrin der''Baasmeir, the wife of Evram der''Baasmeir.  Dying, he professed to the na''k&ouml;r the truths that led them to the house of Garani der''Annher.  Although House Annache is not accounted among the Aljerstraad, Aldar was considered among their ranks.  He was named one of the Unblooded by Davram der''Klar, a title which he and his house held as long as he lived.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1600,1,to_timestamp('24-AUG-15 12.56.52.791000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Baasmeir',null,null,'A house of the Aljerstraad.  Its lord is Evram der''Bassmeir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1601,1,to_timestamp('25-AUG-15 09.43.55.506000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Moth',null,null,'Translation of Skor-Mut in the Iron Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1603,1,to_timestamp('25-AUG-15 09.47.44.998000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lure',null,null,'Human decoys/lures used by du&aring;r miners to distract swarms of Skor-Mut.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1701,1,to_timestamp('29-AUG-15 08.07.29.685000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'En Asara Dianhe',null,null,'Literally "In Asara, we bathe" in the Pure Tongue.  More accurately translated as "In Asara, we find tranquility." A common saying of the priests of Asara.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1702,1,to_timestamp('29-AUG-15 08.09.50.200000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Key, The',null,null,'Shortened literal translation of Laroch &Ucirc;l in the Pure Tongue.  (The full translation is "Key of Echoes" or "Key to Echoes.")',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1480,1,to_timestamp('23-AUG-15 05.15.05.330000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ajer-D&aelig;n',null,null,'A prison of Aljerbach, located on an island in the Great River.  The Harbingers of Eta&urirc;ur awoke here after the Sphere.  The prison was destroyed when a mysterious winged d&aelig;mon appeared as the na''k&ouml;r fled the city.  Also called the Great Prison.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1481,1,to_timestamp('23-AUG-15 05.15.21.845000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Great Prison, The',null,null,'An alternative name for Ajer-D&aelig;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1484,1,to_timestamp('23-AUG-15 05.40.40.886000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Te-Srin',null,null,'Seedstones used to grow new i''Srin L&ucirc;n.  They are Shiahd developed by Zaryth dar Mistral, and means of their creation has been lost since her banishment.  A te-Srin appears as stone acorn laced with hrest.  It will "grow" if placed in the earth and touched with any Pillar of Da''kol.  A mirrored island in Oilear will be created.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1491,1,to_timestamp('23-AUG-15 06.09.22.901000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Earth Song, The',null,null,'The source of a Singer''s power, by which he or she speaks the tongue of the earth and supplicates its obedience.  Some few believe that the Song can be used to command animals, rivers, and even the moonlight, but if so, this talent has been lost.  Known as the Depsangf among the du&aring;rg, Veryn''s Song and the Song of the Daughter among ancient peoples, and occasionally just the Song.  Per the Sundering of Anda Adalai&auml;, the Earth Song was crafted as Gift for the Children of Istaron by the Er&uacute;, Veryn.  Among the du&aring;rg, the act of (Earth-)Singing is known as Speaking, one who practices that art is known as an (Earth-)Speaker (the related Grod is called Hammar).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1751,1,to_timestamp('29-AUG-15 07.18.13.211000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Twin Gods, The',null,null,'The gods of Berak, who are not given name.  The Twin Caliphs are thought to be their earthly manifestations.  The church of the Twin Gods is located in the holy city of Tatt&ucirc;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1545,1,to_timestamp('23-AUG-15 03.31.26.926000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Erossi',null,null,'Formerly a minor house of the elithi in Rolar.  Due to the deeds of Meirchion yr Erossi, Delsar raised it to one of the Tiaryche, the second time such a thing had been done in three thousand years (after Ryche Erybin).  Meirchion yr Erossi is its head.  Their symbol was a blue jay.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1546,1,to_timestamp('23-AUG-15 03.34.22.725000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Marker',null,null,'Stone posts in Srin which mark the nature of the paths they adjoin.  There are up to six Markers and paths per isle.  Destroying or binding a Marker will destroy the path, at least temporarily.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1547,1,to_timestamp('23-AUG-15 06.06.02.834000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cara-aleach',null,null,'A pair of elithin warriors bound by a sacrament considered as sacred and binding as marriage.  Their oath compels them to fight until Kailar lies in ruins.  The first of the cara-aleach were Leuen y Senyar and &AElig;ronwy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1494,1,to_timestamp('23-AUG-15 06.28.07.136000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Star Gem',null,null,'A Tal''Shiahd which periodically radiates with a strong, white light, sufficient to illuminate a Roof of the First Domain.  They glow for a period approximately equal to the duration of sunlight on the surface above their location.  At least two Star Gems are known.  One is in the Empire of Demarge, located in the capital city.  The other was once possessed by the corti in Sorain.  Its current location is theorized to be Kailar but is unknown.  In Demarge, the Star Gem is also known as the Godstone and God''s Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1512,1,to_timestamp('23-AUG-15 12.27.51.557000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Oilear',null,null,'An alternative name for Srin, used primarily by the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2306,1,to_timestamp('16-SEP-15 10.35.18.130000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Atrium, The',null,null,'A great hollowed area in the heart of the Mountain Home, hundreds of feet across and taller than the eye can see, ringed with ancient staircases and corridors.  According to legend, the Atrium has been Spoken over the centuries by the Hammar as they search for the Eye of Phaelen lost by the mad &thorn;engill-rikr Ymir Ymin-s&oslash;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1516,1,to_timestamp('23-AUG-15 12.31.42.989000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gateway',null,null,'An alternative name for an i''Srin L&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1530,1,to_timestamp('23-AUG-15 01.21.57.038000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Porsgrunn',null,null,'A mine near the hurtt of Aust-Agder.  A collapse in the mine precipitated the dousing in which Belaan first manifested the Gargoyle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1533,1,to_timestamp('23-AUG-15 01.29.30.119000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Reaches, The',null,null,'An alternative name for the Deep Reaches of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1536,1,to_timestamp('23-AUG-15 01.42.23.253000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Deep Dark, The',null,null,'The name by which the peoples of the Domain refer to wild, unclaimed places of the Roofs, where light never touches.  Such places are claimed by deadly beasts and have no sources of drinkable water or edible food.  Also called the the Dark, the and Underdark.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1549,1,to_timestamp('23-AUG-15 06.14.34.602000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Val Analyth',null,null,'One of the Eight Pillars of Calikar, carved into valleys among hills of silver and gold, called the Living Hills.  It was destroyed by the d&aelig;n''shee more than two centuries after the Gloaming, never to be reclaimed.  Its Holy Grove was Sung by Leuen y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1551,1,to_timestamp('23-AUG-15 06.29.20.770000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Living Hills, The',null,null,'Hills of natural gold and silver radiance.  The elithin city of Val Analyth was carved into the valleys between the hills.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1552,1,to_timestamp('23-AUG-15 06.36.39.053000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shiar',null,null,'An island in Alune, on which the city of Rhuanedd is located.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1553,1,to_timestamp('23-AUG-15 06.38.05.459000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of Sages, The',null,null,'Alternative name for the city of Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1554,1,to_timestamp('23-AUG-15 06.38.13.919000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of Learning, The',null,null,'Alternative name for the city of Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1555,1,to_timestamp('23-AUG-15 06.38.35.771000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of Scholars, The',null,null,'Alternative name for the city of Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2477,1,to_timestamp('19-SEP-15 07.05.13.585000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Divine Veto',null,null,'The power of the God Emperor of the Empire of Demarge to veto any suggestion or law passed by the Imperial Senate.  This veto can be overridden by unanimous consent of the the Quintunes, although this has only happened twice in the history of the Empire. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1561,1,to_timestamp('23-AUG-15 06.50.07.545000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of the White Flame, The',null,null,'An alternative name for the city of Aduine.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1562,1,to_timestamp('23-AUG-15 06.50.28.245000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of the Stone Shield, The',null,null,'An alternative name for the city of Aduine.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1567,1,to_timestamp('23-AUG-15 07.25.07.764000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Harn',null,null,'A city and kingdom in the western regions of the Great Cavern.  It was once part of the kingdom of Aljerbach but long ago earned its independence.  It fell to the Empire of Demarge in the War of East and West and has since been a protectorate.  Its queen is Titiana Vrer.  Also called the Ivy City (named for its Ivy Walls, which have since been torn down).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2688,1,to_timestamp('24-SEP-15 09.14.48.144000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'F&aelig;l&ucirc;n, The',null,null,'Great jagged cliffs which surround the plateau of Auin-Malach.  Great watch towers ward its expanse, which according to legend were Spoken by Peredur y Senyar, eldest son of Calik Twiceborn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1578,1,to_timestamp('24-AUG-15 09.36.57.943000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lluagh Maguin',null,null,'The name by which Alune has been known since it was lost to the Gloaming, meaning literally the Dark Path.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1579,1,to_timestamp('24-AUG-15 09.37.13.669000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dark Path, The',null,null,'Literal meaning of Lluagh Maguin in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1581,1,to_timestamp('24-AUG-15 10.05.37.081000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Stone Glass, The',null,null,'The literal translation of a''Tiernya in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1541,1,to_timestamp('23-AUG-15 01.56.14.979000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Pryderi yr Erybin',null,null,'An elithin, lord of Ryche Erybin, and captain of the Sihte Alonne. He encountered the na''k&ouml;r in Aljerbach and transported them to Rolar.  He later became a close friend and confidante to Delsar, named marshal during the Grey War and regent of Calikar when Delsar departed for I&aring;rth.  He and his house were elevated to the Tiaryche for his deeds, the first such ascension in three thousand years.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1519,1,to_timestamp('23-AUG-15 12.44.02.539000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Empire of Demarge',null,null,'A great empire of the First Domain, which occupies most of Bel Can’nar and Frelham, as well as parts of Zamoch and the Great Cavern.  It has complicated political structure which consists of a dynastic imperial bloodline, feudal houses called the Sangus, the Milita, and an imperial senate.  Formerly a republic, Demarge has been an empire for more than a thousand years, with power having grown gradually more centralized on the God Emperor over that time.  It is militaristic and expansionist in nature, but allows autonomy among its conquered states in the form of the Milita.  Also called the Eternal and Glorious Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1745,1,to_timestamp('29-AUG-15 07.04.29.073000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mad King Teras',null,null,'Lord of the Asvana and king of Berak during the civil war known as the Mad King''s War.  His madness and cruelty are attributed with bringing about the end of the monarchy in Berak.  Also known as the Butcher King.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1748,1,to_timestamp('29-AUG-15 07.08.37.469000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tatt&ucirc;l',null,null,'A holy city in Berak, where priests choose children to be elevated to the station of Twin Caliph.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1754,1,to_timestamp('29-AUG-15 07.39.57.246000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Calik',null,null,'The name assumed by Ym du''Seraa when he became a Triumvir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1757,1,to_timestamp('29-AUG-15 07.41.43.485000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Twiceborn, The',null,null,'A simplified version of Calik Twiceborn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1758,1,to_timestamp('29-AUG-15 07.42.22.626000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Calik Twiceborn',null,null,'The name by which Ym du''Seraa was known after he perished at Erinin and was resurrected by the sacrifice of Nara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1767,1,to_timestamp('29-AUG-15 10.04.57.944000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Uushra',null,null,'A person or thing which, according to the kabaal, was companion to their queen, Ar&icirc;sh&ucirc;l.  He seems to have adopted the role of a god or demi-god in their faith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (456,1,to_timestamp('12-AUG-15 02.02.20.880000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Sentinel, The',null,null,'Translation of the corti title, Gres''Mahar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1557,1,to_timestamp('23-AUG-15 06.41.08.779000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Srawain',null,null,'One of the Eight Pillars of Calikar, built to ward the boundary between Alune and the Modeira.  Its kings were descended from Shen’r&aelig;th yn Senyar.  It was lost to the Gloaming, and its current fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1566,1,to_timestamp('23-AUG-15 07.22.43.976000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ki''tahj',null,null,'The armies of Kailar and the d&aelig;n''shee.  Regarded as ruthless berserkers, they often fight naked or clothed in the blood of their enemies.  They are known to butcher, dismember, and rape their fallen foes, often making displays of their bodies.  They also salt lands through which they pass and ruin kbai-stations.  The ki''tahj was first seen during the Sons'' War, but its modern incarnation was born under the rule of Zjeryn e''Gur Dao''Ast.  Warriors of the ki''tahj are known as Blood Screamers.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1509,1,to_timestamp('23-AUG-15 12.26.46.086000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Breach, The',null,null,'An alternative name for Srin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1515,1,to_timestamp('23-AUG-15 12.31.35.496000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Breach Gate',null,null,'An alternative name for an i''Srin L&Ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1531,1,to_timestamp('23-AUG-15 01.24.55.936000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gargoyle, The',null,null,'A term used by Durgin to describe the change that overcomes Kaja''lar when she is exposed to Da''kol.  See "The Other."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (691,1,to_timestamp('14-AUG-15 01.00.09.055000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hierophant',null,null,'The highest priest/leader of one the Seven Faiths of the Caal Anuur.  Also called a Spear, e.g. the Spear of Irien.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1499,1,to_timestamp('23-AUG-15 12.02.42.423000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rhulwyr Lluete mucht Istue',null,null,'The name by which the elithi refer to Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1520,1,to_timestamp('23-AUG-15 12.45.05.210000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sangus, The',null,null,'The noble houses of the Empire of Demarge, numbering more than 500.  The highest of these houses, which number 100, are called the Centarum and are eligible to hold seats in the imperial senate.  Also called the Blood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1522,1,to_timestamp('23-AUG-15 12.48.03.882000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eternal and Glorious Empire, The',null,null,'An alternative name for the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2930,1,to_timestamp('06-OCT-15 07.12.49.732000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aurain',null,null,'A kingdom during the time of the Second &AElig;gan Empire, known for its great forests and mountains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1532,1,to_timestamp('23-AUG-15 01.29.10.517000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Deep Reaches, The',null,null,'A series of elaborate tunnels that run beneath the kingdom of I&aring;rth.  They are filled with dead ends, traps, and deliberately misleading passages, rendering them passable only by trained guides among the du&aring;rg, the Visa. Entrances into the Deep Reaches, called Deep Gates, are carefully hidden and are never located within hurtts, cities, or redoubts.  Also called the Reaches.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1760,1,to_timestamp('29-AUG-15 07.53.46.568000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'High Throne',null,null,'One of the Twin Caliphs of Berak and also the name of the city from which he rules, so named for its relative elevation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1543,1,to_timestamp('23-AUG-15 03.27.23.538000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tiaryche',null,null,'The highest houses, or Ryche, of Calikar, and also the title of their heads who sit in council.  They originally numbered one hundred forty-five, but it is  unclear how many remain now.  There were ten lines of the Tiaryche in Rolar when the na''k&ouml;r arrived.  Since then, Ryche Re''nan has become extinct and Ryche Erybin and Ryche Erossi have been elevated.  According to legend, the original Tiaryche were selected because they chose to follow Calik when he summoned them for the first Chorus, requiring no explanation or evidence.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2931,1,to_timestamp('06-OCT-15 07.14.07.095000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gheira',null,null,'A young woman encountered by Amber in Aljerbach shortly after she landed in the Sphere.  She was a servant in an inn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2932,1,to_timestamp('06-OCT-15 07.15.29.594000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tevin der''Sorge',null,null,'A farlo boy of the Aljer, murdered by Amber during one of her fugue states after her Sphere landed in Aljerbach.  The son of Lambert der''Sorge and an unnamed woman, both of whom were killed by Amber in the same encounter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2935,1,to_timestamp('06-OCT-15 07.23.14.445000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dunmarche',null,null,'A small city of the Aljer, located on the Shaaro Plains.  Great coal mines are located here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2936,1,to_timestamp('06-OCT-15 07.28.32.384000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Jeril der''Klar',null,null,'A prince of the Aljer, captain of the Ruhmreich, son of Davram der''Klar and Biara der''Klar, brother of Medira der''Klar and Tavin der''Klar.  He died shortly after the Sphere fell in the farlo of Aljerbach, killed by Drolock''harka.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2938,1,to_timestamp('06-OCT-15 07.33.26.170000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tavin der''Klar',null,null,'A prince of the Aljer, son of Davram der''Klar and Biara der''Klar, brother of Medira der''Klar and Jeril der''Klar.  Following the murder of his parents after the fire-light, he fled Aljerbach.  His current fate is unknown, but he is suspected dead.  If alive, he is the heir to the Thrice Crown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2940,1,to_timestamp('06-OCT-15 07.37.37.493000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Stranger, The',null,null,'An appellation by which Malnos referred to Kisel before the latter''s name was learned.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1746,1,to_timestamp('29-AUG-15 07.04.45.544000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Butcher King, The',null,null,'An alternative name for Mad King Teras of Berak.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1756,1,to_timestamp('29-AUG-15 07.40.52.225000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Serpent Mage, The',null,null,'The literal translation of "Gai''lal" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1814,1,to_timestamp('01-SEP-15 08.00.53.085000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'K&aelig;ryeah yr Inythyn',null,null,'An elithin accounted by many to be the greatest of the Makers.  His first great work was Callain.  He later built the Lattice Bridge, Vildîn, and Atriach isch Meileuhr, among others.  A cousin of Amranwen yr Inythyn (removed by several generations).  K&aelig;ryeah is recalled for his great of love of Brean-M&icirc;in yn Senyar, in search of whose heart many of his great works were said to have been crafted.  He did eventually earn her hand but died soon thereafter, assassinated by the d&aelig;n''shee while resting in Lleah-roht.  His sister-in-law, An-Creirwy yn Senyar, died in the same assault.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1816,1,to_timestamp('01-SEP-15 08.05.29.325000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Inythyn',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Many of the great Makers were born to this house.  Their line is considered extinct since the Gloaming.  Their symbol was a bower bird.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1817,1,to_timestamp('01-SEP-15 08.07.41.962000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Hwnwn',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Their line is considered extinct since the Gloaming.  Their symbol was a tern.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1818,1,to_timestamp('01-SEP-15 08.14.02.544000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shuonne',null,null,'A sword master among the elithi.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1548,1,to_timestamp('23-AUG-15 06.09.52.421000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Thir Eris',null,null,'The great cities of Calikar:  Rhulwyr Lluete mucht Istue, &AElig;lm&aelig;dris, Aduine, Rhuanedd, Erinin''ar, Val Analyth, Shuon Ashhal, and Srawain.  Of those cities, only Rhulwyr Lluete mucht Istue has not fallen.  Rhuanedd, &AElig;lm&aelig;dris, and Aduine have recently been reclaimed from Lluagh Maguin.  The fate of Srawain is unknown.  Also known as the Eight Pillars.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1504,1,to_timestamp('23-AUG-15 12.11.19.316000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'White Walls, The',null,null,'The walls of the city of Rolar, said to have been Sung by Calik from coral stone beneath the Althrin.  They are tall and white, radiating an inner light.  Stone visages are carved in periodic intervals along the wall, called the Faces of Nara, which are thought to protect the city with a powerful wyrd.  The walls have four gates:  the Gate of Mindal&ucirc;n to the south, the Gate of Calik to the west, the Gate of Chara to the north, and the Gate of Erinin to the east.  None of the gates, or the wall itself, had ever fallen before the Grey War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1725,1,to_timestamp('29-AUG-15 10.51.05.321000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Stealer of Lives, The',null,null,'The name given by the Calikari to an assassin who murdered Cyngen yr Orain and his court in Urek-Un&ucirc;n.  Later, it was discovered that he was Kisel Gres''Mehar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1768,1,to_timestamp('29-AUG-15 10.06.36.078000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Uushra''s Coil',null,null,'The name which the kabaal give to their current home, the Modeira, which they consider to be a prison or punishment that they must suffer until Ar&icirc;sh-sh&ucirc;l is released from her prison.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1749,1,to_timestamp('29-AUG-15 07.16.12.030000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Twin Caliph',null,null,'One of two leaders of the nation of Berak, who are considered to be living manifestations of the Twin Gods of Berak.  They are always chosen as children, always male, always twins, and never related by blood to the prior Caliphs.  Selection is performed by the patriarchs of the Twin Gods in the holy city of Tatt&ucirc;l.  They are castrated upon being risen to avoid the temptation of ancestral power, i.e. a return to the line of the Asvana.  They rule from separate cities with separate administrations, and power over the caliphate is divided equally among them.  It has been this way since the Mad King''s War, when the people of Berak wrested power from the Mad King Teras.  According to the faith of the Beraki, the Twin Caliphs perish naturally in the same moment.  One is known as the High Throne and the other as the Low Throne, though this seems to represent a difference in the elevation of their respective capitals rather than authority.  Also called the God Brothers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1787,1,to_timestamp('31-AUG-15 10.08.36.674000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shara Lach-Nach',null,null,'A period in elithin history when they had been spared the full wrath of the Withering by the birth of the Song but had not yet achieved their immortality.  According to the mythology of Calikar, immortality was not achieved until D&ucirc;n Mile''&ucirc;r reached its full maturity.  During this period, the effects of the Withering were suppressed for 25-30 years, then progressed very quickly, ending in death within 1-2 weeks.  Many quickened generations passed among the elithin during these years, through which they were protected by the cara-aleach.  Literally, the "Long, Short Years."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1791,1,to_timestamp('31-AUG-15 10.17.37.567000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shadow of Rhuanedd',null,null,'Literal translation of Shar''ris y Rhuanedd in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1793,1,to_timestamp('31-AUG-15 10.20.27.299000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gloaming, The',null,null,'The name given to the coming of Dubh Gaoth, by which Alune was transformed to Lluagh Maguin.  The people of the Gloam associated it with the Dark Herald, whom they assumed to be a servant of Eshrella Dao''Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1795,1,to_timestamp('31-AUG-15 10.23.01.275000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wraith',null,null,'A simplified term for a Mist Wraith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1518,1,to_timestamp('23-AUG-15 12.38.27.738000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'City of Demarge',null,null,'The capital city of the Empire of Demarge, built among the Seven Hills.  It is the largest city of the First Domain and is home to the imperial senate, the God Emperor and his court, the majority of the houses of the Sangus, and several among the Milita.  Also called the God''s City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1762,1,to_timestamp('29-AUG-15 07.57.06.696000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Askam',null,null,'A small hurtt in southern I&aring;rth, located near Aust-Agder.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1742,1,to_timestamp('29-AUG-15 06.50.43.672000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Milita',null,null,'A "Military House", a political entity within the Empire of Demarge.  Unlike the Sangus, which were original bloodlines of the Empire, the Milita were joined to the Empire via conquest.  The Milita are considered separate from the God Legions and are tasked with serving Empire rather than the Emperor.  They do not number among the Centarum.  They, like the Senate, were intended to provide a form of checks and balances, but over the centuries this division has become more and more ceremonial as power has been centralized in the God Emperor.  Each MIlita has its own history and structure.  Some bear significantly more influence than others.  A few Houses remain connected to ethnicity or region, but many others have transformed over the years to behave more like mercenary groups.  In very rare cases, a house of the Milita has been raised to the Sangus.  Known Milita include Dom''Vanari, Rej''Cajere, Aq''Mar''Shein, Agra''dalolth''Dakaj, S&icirc;n''Kanar, Jres''Mah&aelig;, En''Zaman''Tal, Torilen''Vidar, Lin''Ajor, and Lin''Beraal.  Also known as a Legio Filres, which translates as "Faith''s Legion", or more informally as a Military House.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1958,1,to_timestamp('05-SEP-15 12.35.03.249000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'First Harnese Rebellion, The',null,null,'A failed attempt by the people of Harn to gain independence from Aljerbach, waged in the years after Gelastir was razed during the War of Salt and Sorrow, likely in response to perceived weakness from the Aljer.  Despite the circumstances, the rebellion was quickly quashed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1759,1,to_timestamp('29-AUG-15 07.48.05.055000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ki''lan',null,null,'The name assumed by Neatham do''Sema''ar when he became a Triumvir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1761,1,to_timestamp('29-AUG-15 07.53.54.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Low Throne',null,null,'One of the Twin Caliphs of Berak and also the name of the city from which he rules, so named for its relative elevation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1729,1,to_timestamp('29-AUG-15 04.22.48.414000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'War of Seven Roofs, The',null,null,'The name of Arastel''s war of dominion against the First Domain, applied years later.  Also called the War of Three Roofs, and, when referring to the first strikes against Calikar, the Grey War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1959,1,to_timestamp('05-SEP-15 12.38.16.146000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Second Harnese Rebellion, The',null,null,'The second attempt of the Harnese to win their independence from Aljerbach, waged less than twenty years after the first rebellion.  Aided by financial and later military support from the Empire of Demarge, this revolution was successful after six years of conflict.  A hero from the war, Joram Vrer, becomes the king of Harn and creates a dynasty.  Known among the Harnese as the War of Liberation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1960,1,to_timestamp('05-SEP-15 12.38.39.526000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Joram Vrer',null,null,'A hero of the Second Harnese Rebellion and the first king of Harn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1961,1,to_timestamp('05-SEP-15 12.39.51.701000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Liberation, The',null,null,'The name by which the Second Harnese Rebellion is known among the Harnese.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1733,1,to_timestamp('29-AUG-15 04.31.38.515000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ilur&icirc;n Mar&ucirc;l',null,null,'A title by which Cyngen yr Orain was known in his youth.  It means "Mist Walker" in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1711,1,to_timestamp('29-AUG-15 09.03.20.948000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Thrice City, The',null,null,'An alternative name for the city of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1712,1,to_timestamp('29-AUG-15 09.03.37.381000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Thrice Kingdom, The',null,null,'An alternative name for the kingdom of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1713,1,to_timestamp('29-AUG-15 09.04.03.355000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Thrice Crown, The',null,null,'The crown of the kings of Aljerbach, historically possessed by the royal line of Klar.  Also a metaphor for the authority/current liege of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1589,1,to_timestamp('24-AUG-15 10.38.33.572000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Triurg, The',null,null,'The symbol of Klar, the royal house of Aljerbach:  three golden crowns, drawn in an overlapping triangle.  Its shape and history owes to the mythical tale of the Gr&icirc;th.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1714,1,to_timestamp('29-AUG-15 09.18.59.919000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Salt and Sorrow, The',null,null,'A war between Kailar and the nations of the Great Cavern, initiated by Eshrella Dao''Ast two hundred years after the fall of Sorain.  It was intended to be the ruin of her enemies, and the aim was, even in the case of failure, to leave those lands taken destroyed by reclamation.  Val Analyth and Gelastir were lost during this conflict, which lasted more than fifty years.  The power of Aljerbach was broken, such that the First Harnese Rebellion began shortly thereafter. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1716,1,to_timestamp('29-AUG-15 09.29.08.325000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black Lady, The',null,null,'The name by which the du&aring;rg refer to Kaja''lar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1797,1,to_timestamp('31-AUG-15 10.27.44.736000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Muir Auread',null,null,'A golden pool located in Alune, predating the arrival of the elithin.  It has the appearance and consistency of liquid metal, but is lukewarm to the touch.  Muir Auread generates a golden radiance which casts no shadows and does not summon Dubh Gaoth.  After the Gloaming, the survivors of &AElig;lm&aelig;dris lived for a time on the shores of Muir Auread before Camryn''s Pride.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1717,1,to_timestamp('29-AUG-15 09.38.50.901000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Phaelen',null,null,'A du&aring;rg born possessing two or more of the Grod.  (According to the beliefs of the Iron Born, a Phaelen is born possessing all 16 Grods, but this is not true.)  The first was the son of Amlaith, and his name was Phaelen, for whom all the others have been named.  This is an exceptionally rare occurrence, and almost always ends in madness before the person''s twenty-sixth naming day.  If hes does not succumb to insanity, Phaelen generally becomes a symbol and puppet of the &thorn;engill, who fear the rising of a &thorn;engill-rikr-rikr.  The most recent Phaelen is Durgin.  Also known as the Grod-rikr-rikr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1719,1,to_timestamp('29-AUG-15 09.55.53.896000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hl&aelig;ja Egill-s&oslash;n',null,null,'The born name of Durgin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1798,1,to_timestamp('31-AUG-15 10.37.03.710000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Luer',null,null,'A brown stone common to the First Domain, known to be both lightweight and strong.  Being the only known buoyant stone, it is a common component in ships of the Roofs, where fungal woods are quickly dissolved by extended exposure to Grim-tainted waters.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1799,1,to_timestamp('31-AUG-15 10.38.03.458000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Forbidden Gift, The',null,null,'The name by which the inhabitants of Lluagh Maguin refer to the practice of all forms of magick, so called for the risk of attracting Dubh Gaoth.  It is punishable by death.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1722,1,to_timestamp('29-AUG-15 10.19.29.968000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eleanor',null,null,'One of the na''k&ouml;r and an apparently human child.  Eleanor''s Sphere fell in Searah''s Fen in Calikar.  She dreams as N''koth Velar, who later became the Mortuani, N&aelig;yla.  Little is known of her history except that she once lived with Malnos in the Grey Tower and that she was once a slave in the possession of Malig.  It seems likely that, although she appears as a child, Eleanor is in fact one of the mentej, a race of monstrous creatures created by the woman as whom she dreams (in conjunction with Az-Azaal).  She is able to Shape wyrds connected with Life and the mind instinctively (and sometimes unaware that she is doing so) but is also able to Shape via her chara with effort.  She has also apparently possessed her currently form for some time (at least several decades) without aging.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1727,1,to_timestamp('29-AUG-15 10.54.31.661000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eshthara',null,null,'A sect of chiromantic Shapers among the elithin.  They are separate from the Durem Erim&aelig;.  Once, the heart of the heart of the Eshthara was in  Ruel-uchain in Rhuanedd but since the Gloaming has been the tower of Aiel-m&ucirc;r in Rolar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1730,1,to_timestamp('29-AUG-15 04.23.23.162000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'War of Three Roofs, The',null,null,'An alternate term for the War of Seven Roofs, used contemporaneously during the middle years of the war.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1731,1,to_timestamp('29-AUG-15 04.25.53.078000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey War, The',null,null,'A term that can be used to describe either the full war between Gethin y Kam&icirc;l (and those that continued to follow after his death) and Delsar yr Orain or the final battle in which the Song was silenced and the Grey Mist rose to consume Rolar.  The Grey War can be considered part of the greater War of Seven Roofs.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1438,1,to_timestamp('21-AUG-15 08.48.35.635000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Delsar',null,null,'A name assumed by Cyngen yr Orain in his youth.  Its source and meaning are unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1732,1,to_timestamp('29-AUG-15 04.29.41.741000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Delsar yr Orain',null,null,'A name assumed by Cyngen yr Orain in his youth.  Its source and meaning are unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1734,1,to_timestamp('29-AUG-15 04.32.14.205000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mist Walker, The',null,null,'Literal translation of Ilur&icirc;n Mar&ucirc;l in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1550,1,to_timestamp('23-AUG-15 06.27.59.420000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shuon Ashhal',null,null,'One of the Eight Pillars of Calikar, within close proximity to Aljaberin.  It was famed as the heart of the Bright Legions and housed the quarries where their weapons and armor were forged.  Its queen was M&aelig;lorwen yn Ash''shwl, the Warrior Queen.  She crafted the first m&aelig;lorwi and credited as the Mother of the Bright Legions.  Shuon Ashhal was destroyed during the War of Ash, not to be reclaimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1576,1,to_timestamp('24-AUG-15 09.33.58.299000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ruahd',null,null,'The elithin name for the internal wall that divides the Great Cavern into its eastern and western parts.  Alune and the Modeira are located within Ruahd.  Ruahd is known as the Great Stone among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1735,1,to_timestamp('29-AUG-15 04.48.00.599000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Argoel',null,null,'See Argoel y Rhuaidh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1737,1,to_timestamp('29-AUG-15 05.03.57.568000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Twisted',null,null,'An adjective used to refer to one of the Twisted Ones.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1738,1,to_timestamp('29-AUG-15 05.30.02.174000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Drolock''harka',null,null,'One of the na''k&ouml;r and a dar''lak.  Drolock''harka''s Sphere fell in Aljerbach.  Drolock''harka dreams as Antoni do''Ch&aelig;ron, who later became known as Tymian.  According to his dreams, Drolock''harka was once tasked with finding and assassinating Diarin yn Marel yn Ast by her mother, Eshrella Dao''Ast.  He was also apparently once under the service of Arastel and somehow came to be in the Empire of Demarge, where he was first a gladiator and then a warrior of the Rej''Cajere.  After awakening in Ajer-D&aelig;n, he traveled northward with the na''k&ouml;r to Rolar.  On the path, he claimed a mysterious blade on a dark citadel atop the island of Kardu&icirc;n.  The blue steel coursed with red veins and caused him extreme pain, but he was unable to rid himself of it.  If he did not feed the blade fresh blood, he grew progressively weaker.  In Lluagh Maguin, he was overcome by the blade and fell into a state of catatonia.  Later, the blade seemed to have been purified when he and it were submerged in the waters of Muir Airgin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1739,1,to_timestamp('29-AUG-15 06.40.46.538000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Military House',null,null,'An alternative name for one of the Milita of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1740,1,to_timestamp('29-AUG-15 06.41.25.194000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Legio Filres',null,null,'An alternative name for one of the Milita of Demarge, meaning Faith''s Legion.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1741,1,to_timestamp('29-AUG-15 06.41.47.860000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Faith''s Legion',null,null,'An alternative name for one of the Milita of Demarge, literal translation of Legio Filres.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1743,1,to_timestamp('29-AUG-15 07.01.37.192000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Asvana, The',null,null,'A royal bloodline of early Berak which believed itself descended from the God Emperor of Demarge.  The last king of the Asvana, Teras, was a madman whose cruelty brought about a civil war in Berak, called the Mad King''s War, and eventually broke the power of the monarchy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1744,1,to_timestamp('29-AUG-15 07.02.59.730000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Asvanari, The',null,null,'Noble houses of early Berak which were loyal to the Asvari and Mad King Teras during the Mad King''s War.  Many human exiles in I&aring;rth are thought to be descended from Asvanari that fled Beark after the war.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1724,1,to_timestamp('29-AUG-15 10.49.48.291000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kisel Gres''Mehar',null,null,'One of the na''k&ouml;r, a corti, and a Harbinger of Eta&ucirc;r.  Kisel''s Sphere fell in Aljerbach.  Diarin dreams as Alvain Nu&icirc;n, who later became Mayar.  Kisel wears black robes which cannot be forcibly removed.  Beneath those robes, his flesh is pale and covered with pulsing veins of shadow.  He is hairless and eyeless, lacking nostrils.  His mouth is a lipless circle, and he has no teeth.  Kisel was once a leader of the corti, known by the title Gres''Mehar, of Sorain and is apparently several centuries or even millennia old.  It is unclear now whether he is corti, kiril, or something else entirely.  He had a brother and co-ruler, Jan&icirc;l Hwe''leri, and at one point a wife, Marylla.  It is unknown what happened to Kisel during and after the fall of Sorain, but he evidently did not participate in the Gloaming.  Centuries later, he arrived in Rolar as the Stealer of Lives and murdered Cyngen yr Orain and his court in Urek-Un&icirc;n before being spared by his son, Cyngen yr Orain.  Kisel perished in Uragar, sacrificing himself to drag Serrik Vey''ron through the Egra Door.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1763,1,to_timestamp('29-AUG-15 08.10.24.708000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Du&aring;rg',null,null,'A race and the people of I&aring;rth, who according to their own mythology are descended from Khijak the Khazjad via his only surviving son, Rajnvald. They are a short, stocky people of great strength, with black skin and dark hair and eyes.  There is no historical or dream evidence of the du&aring;rg during the Second &AElig;gan Empire.  After the Sundering, they lived for a time in a land called Bagr-Danr, believed to lie in the Roof of Bel Can''nar.  Eventually, they came to Calikar, where they first dwelt in the land of Yjalmar, located near Myramaal and modern-day Harn.  Since the time of the f&aelig;&eth;a, the du&aring;rg have been the masters of Irongate, built atop the d&aelig;n''shee city of Aljaberin.  Being the most proximate nation to Kailar, they have suffered the most assaults from the ki''tahj.  They attribute their strength and survival to the discovery of the Eyes of Phaelen and the Grod.  On average, the du&aring;rg live 200-250 years, but some few have been known to survive to the age of 300.  Also called the Iron Born.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1752,1,to_timestamp('29-AUG-15 07.27.48.231000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Berak',null,null,'A nation in the southeastern corner of the Great Cavern, built atop the ruins of Sorain approximately six hundred and twenty years before the Spheres fall.  The inhabitants believe themselves refugees from Demarge, and the original kings, the Asvana, believed themselves descended from the line of the God Emperor.  The kings were overthrown in a civil war, the Mad God''s War, due to the cruelty and insanity of Mad King Teras.  Since that time, power has been divided between the Twin Caliphs, who are thought to be mortal manifestations of the Twin Gods of Berak.  Historically protected from Kailar by I&aring;rth, Berak has had historic tension with Aljerbach.  It sided with Kailar and Demarge in the War of East and West, and more recently engaged in the War of the Low Throne with Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1880,1,to_timestamp('01-SEP-15 02.55.21.375000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tryptych',null,null,'One of five elithin warlords who governed the Bright Legions of Calikar. Originally, they guided the legions from Shuon Ashaal, but after its fall they guided the host from the Hall of Tablets in Aduine.  Collectively referred to as the Sh''hein''Rein, or the Stone Fist.  The Tryptychs were presumed to have died during the Gloaming, though it seems Rawain y Keb’bryn survived and served Gysioch y Senyar at Asmaragaidhe.  His ultimate fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (586,1,to_timestamp('13-AUG-15 08.33.01.345000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shining Ones, The',null,null,'A people of legend among the du&aring;rg.  Supposedly, they are immortal servants of Gai''lal with bones, hair, and eyes of silver and blood of gem dust.  They are seen as fickle faerie creatures, with no aim except the satisfaction of their whim.  Durgin believes that Kaja''lar encountered the Shining Ones on the first occasion when she succumbed to the Other.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1572,1,to_timestamp('23-AUG-15 07.38.20.390000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Asmaal e''Gur Dao''Ast',null,null,'A former king of Kailar, grandson of Veomnis e''Gur Dao''Ast, son of Zjeryn e''Gur Dao''Ast, and father of Eshrella y''Gur Dao''Ast.  Learning from the failed wars of his father Zjeryn, he never assaulted Calikar during his reign.  He was murdered by his daughter, who considered him a weak man.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1893,1,to_timestamp('01-SEP-15 03.19.16.258000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shale, The',null,null,'An alternative term for Machuin Muil, used primarily in ka-&AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2312,1,to_timestamp('16-SEP-15 10.57.00.164000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Other, The',null,null,'A term used by Kaja''lar to describe the change that overcomes her when she is exposed to Da''kol.  She gains intense strength and endurance but is overcome with rage, specifically targeted at the Shaper but not limited in its scope.  All volition is lost, and her eyes turn black.  She does not remember the episode afterward.  Durgin refers to this state as "The Gargoyle."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2313,1,to_timestamp('16-SEP-15 11.01.29.549000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Order, The',null,null,'An alternative (and popular) name for the Durem Erim&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2314,1,to_timestamp('16-SEP-15 11.11.58.545000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gaithan Aiur',null,null,'The hall of relics in Rolar, which contains the many treasures and artifacts preserved by the elithi during their flight from Llua''la. The greatest of those treasures, prior to its reclamation during the Grey War, was the sword of Calik Twiceborn, Anlocht.  According to legend, Gaithan Aiur was constructed by Tyren y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2315,1,to_timestamp('16-SEP-15 11.12.51.624000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Su''od''he',null,null,'The name for the Essence of Death in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2316,1,to_timestamp('16-SEP-15 11.13.47.866000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shadow-souled, The',null,null,'A common term used to refer to the d&aelig;n''shee among the peoples of the Great Cavern.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2318,1,to_timestamp('16-SEP-15 11.19.32.746000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&Oslash;x',null,null,'A ceremonial battleaxe carried by the fell-drengr.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1828,1,to_timestamp('01-SEP-15 08.49.24.059000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'D&ucirc;n Calithar',null,null,'The name of three separate Great Trees, born of the Dunain &AElig;lwndwg&aelig;th of Calithar.  The first was located near the Muir Auread and perished during Camryn''s Pride.  The second was located in Diris Maguin near Muir Airgin.  The third was restored to the plaza outside the Elod&aelig;ne Palace when &AElig;lm&aelig;dris was relcaimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1957,1,to_timestamp('05-SEP-15 12.32.16.125000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fools'' War, The',null,null,'A brief war in which an alliance of nobles from Aljerbach assaulted Rolar in response to the elithi''s recognition of Harn''s independence.  Aljerbach was aflame and its king had surrendered within days of the war''s declaration.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1492,1,to_timestamp('23-AUG-15 06.24.28.582000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'U’Vidain e’Cuhlhair',null,null,'The means by which the elithi are bound to the Great Trees, originally created by Calik as his final deed to preserve his people from the Withering.  u’Vidain e’Cuhlhair is a communal experience, shared among a''shanne visions in a Hall of Song.  The experience cleanses them of hatred and spite, unites their people in a common purpose, shares their memories, and suppresses the Withering.  It is also thought to render the Calikari immortal and incapable of deceit.  During the early years of u’Vidain e’Cuhlhair, the elithi were not immortal, and it is thought that their agelessness was not realized until the first Tree, D&ucirc;n Mile''&ucirc;r reached its maturity.  This time is referred to as the Long, Short Years.  In the absence of u’Vidain e’Cuhlhair, an elithin will gradually lose his sanity, his will, and his life in what is known as Blysiog.  Depending on his age, this may or may not occur before he is claimed by the Withering.  Also called the Chorus and the Song.  (Note that there are elithi who dwell outside Calikar who do not suffer from the Withering and are not joined to u’Vidain e’Cuhlhair.  They are known as the Unsung.)',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1577,1,to_timestamp('24-AUG-15 09.36.29.500000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Alune',null,null,'A collection of caverns carved into the northern area of Ruahd, the great internal wall that separates the Great Cavern into its eastern and western parts.  The Althrin flows through Alune, and the heart of Calikar is located here, including the cities of Aduine, Rhuanedd, Srawain, and &AElig;lm&aelig;dris.  Since the Gloaming, Alune has been lost and referred to as Lluagh Maguin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1580,1,to_timestamp('24-AUG-15 10.05.14.550000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'A''Tiernya',null,null,'A place of meeting east of Rolar.  The Star Gem was located here for a brief time.  This was the location of the gathering known as the Liar''s Peace, a failed attempt to end the Age of Ash which led to the assassination of several lords of Calikar and its near ruin.  a''Tiernya has been abandoned since that time.  Delsar and the na''k&ouml;r used a Srin located at a''Tienrya to travel to I&aring;rth.  Also called the Hall of Stone Glass.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1583,1,to_timestamp('24-AUG-15 10.13.03.091000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Age of Ash, The',null,null,'An alternative name for the War of Ash.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1820,1,to_timestamp('01-SEP-15 08.17.38.237000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Ash''shwl',null,null,'A house of the elithi in Shuon Ashhal, accounted among the Tiaryche.  They were the royal line of Shuon Ashhal and have inaccurately been considered extinct since that city''s fall.  The last queen of Shuon Ashhal was M&aelig;lorwen yn Ash''shwl.  Their symbol was a bald eagle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1586,1,to_timestamp('24-AUG-15 10.28.17.804000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nurilin',null,null,'An elithin town in Westermere, visited by Diarin on her journey to find Eleanor''s Sphere.  An i''Srin L&ucirc;n is located on its outskirts.  A description from Book 4:  "The town lay in a ring of hills deep in Westermere. A river bubbled up from an enchanted well at the village’s heart, and emerald caves were carved into the surrounding bluffs. Towers rose among natural stalagmites, wreathed in white mosses. The central tower was two spires that rose from separate bases and entwined toward a single silver tip."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1590,1,to_timestamp('24-AUG-15 10.45.28.193000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Na''k&ouml;r',null,null,'A word used by Garani der''Annher to refer to the Harbingers of Eta&ucirc;r and possibly a reference to prophecy.  The persons who descended to Puell&aelig; in the Spheres later embraced the term.  Malnos attempted to research the na''k&ouml;r prophecies in both Aljerbach and Rolar, to no avail.  Translates to "Children of Chaos" or "Lords of Chaos."  The na''k&oumlr; share several unexplained traits:  they descended in Spheres, they are absent their memories before that descent, they dream of other ages (most commonly of the Second &AElig;gan Empire and their own prior lifetimes), and they are prevented from doing harm to one another by a phenomenon they refer to as lih''tin.  They also experience extreme, prolonged pain and disorientation when another of their member dies.  Durgin is a partial example:  he has the dreams and lacks any memory of a year of his life, but he recalls his youth and is not warded by the lih''tin.  The presumed na''k&ouml;r are Cyngen yr Orain, Eliryndil yn Orain, Diarin yn Marel yn Ast, Argoel y Rhuaidh, Drolock''harka, Kisel Gres''Mehar, Malnos, Malig Narz, Durgin, Kaja''lar, and Eleanor.  Some of them appear to have been connected and/or related in the time before the Spheres, but others appear to have no connection at all.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1704,1,to_timestamp('29-AUG-15 08.28.08.914000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Taulain Annor',null,null,'A high priest of Irien during the Second &AElig;gan Empire and close friend to both Calik and Nara.  He was briefly Hierophant of the faith after Calik''s death at Erinin, when Nara renounced the title.  Calik assumed the role after his resurrection.  Taulain spent years trying to earn Calik''s forgiveness for Nara''s sacrifice but never succeeded.  Eventually, he chose the life of a warrior-priest, dying in an unknown battle to be buried in a mass grave.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2601,1,to_timestamp('20-SEP-15 09.14.26.532000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Athaneum, The',null,null,'A place where exotic beasts are kept in the City of Demarge for public display, essentially a zoo.  Among the animals are a small number of the imperial serestum.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1705,1,to_timestamp('29-AUG-15 08.44.34.035000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dubh Gaoth',null,null,'A black mist which descended over the heart of Calikar during the Gloaming, claiming all of Alune.  The mist sought out all sources of light and life, leaving only shadow in its wake.  Within short days of the mist''s coming, the great cities of Aduine, Srawain, Rhuanedd, and &AElig;lm&aelig;dris were lost, and the few survivors of Calikar''s heartland were become refugees in the Deep Dark.  The lost land became known as Lluagh Maguin.  Those without presumed it and all its people lost.  The lives of those within were shaped by the mist for more than a thousand years.<br/><br/>The source of Dubh Gaoth was presumed for a millennium to be Eshrella Dao''Ast.  More recently, it seems that it may have been summoned by the corti, vengeance for Calikar''s failure to come to their aid during  the fall of Sorain.  There was not in fact a singular "Dubh Gaoth" but rather four distinct mists, one rooted in each of the Great Pillars which fell.  Each mist was undone when Malnos undid the heart of the ongoing ritual in &AElig;m&aelig;ldris.  Among the Calikari, credit for banishing Dubh Gaoth and ending the Gloaming is attributed to Riordian y Senyar.<br/><br/>The mist had living servants, known as Mist Wraiths.  These are now understood to have been corti who did not fully join with the mist.  They are thought to have perished with Dubh Gaoth.  Also known as the Hunger, the Black Mist, and Mist, and the Black Hunger.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1715,1,to_timestamp('29-AUG-15 09.28.46.972000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kaja''lar',null,null,'One of the na''k&ouml;r.  Kaja''lar''s Sphere fell in I&aring;rth near the hurtt of Aust-Agder.  She briefly lived here, taking the name Belaan, before she was overcome by the Other in the presence of the Shining Ones and murdered its inhabitants.  Kaja''lar dreams as Kerin Farryn-lar, who later became the Mortuani, Mariel.  Her dreams also suggest that she may have once been a member of a tribe called the Aiki who live in the wyrm-tunnels beneath the First Domain.  They also suggest that she has at least two siblings, brothers named Ash''gun and Gret''nul.  In the presence of Shaping, she is overcome by rage, losing all sense of time and self.  Her eyes turn black, she gains intense strength and stamina, and she murders all those in her presence, starting with the Shaper.  For this reason, the du&aring;rg named her the Black Lady.  She calls this state "The Other"; Durgin calls it "The Gargoyle."  Kaja''lar is not able to Shape herself, nor, due to her lack of awareness, is she able to perceive her chara during the Shaping of others.  Despite the Gargoyle, she is unable to harm other na''k&ouml;r due to the lih''tin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1750,1,to_timestamp('29-AUG-15 07.17.09.394000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'God Brothers, The',null,null,'An alternative name for the Twin Caliphs of Berak.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1721,1,to_timestamp('29-AUG-15 10.10.56.599000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Malnos',null,null,'One of the na''k&ouml;r, a man, and a Harbinger of Eta&ucirc;r.  Malnos'' Sphere fell in Aljerbach.  Malnos dreams as Ardran Myramar, who later became the Triumvir, Gai''lal.  According to his dreams, Malnos was born as Memar Korain near Camaar, where he was exiled as boy to die due to his mystical gifts.  In the Death''s Blades Mountains, he was saved by the Shining Ones and brought to Uragar.  Eventually, he betrayed his saviors and stole artifacts sacred to their people before fleeing southward.  He made his way to Malkar, where he nearly died in the Stone Desert before discovering the Grey Tower, which accepted him as its master.  There, he granted himself his name, which means literally "Great One."  He lived in the tower for several hundred years before his Sphere descended in Aljerbach, and for some period Eleanor was his ward.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1819,1,to_timestamp('01-SEP-15 08.16.32.450000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Rhyfelwr',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Many of the greatest warriors and shuonne of Calikar were born to this house.  The current head of the house is Kai y Rhyfelwr, but he renounced this honor to serve in the Bych''Tair.  Sharolyn yn Rhyfelwr sits in his place.  Their symbol is a cassowarry.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1838,1,to_timestamp('01-SEP-15 09.21.30.239000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tashyr y Srainne',null,null,'An elithin who was in his youth at the time of the Gloaming.  He was an acolyte of Duri-Med&icirc;n, student of Murigen yn Inythyn, and one of few to have visited the Grey City.  As a young boy, he was in love with his teacher, but after her death, he became lovers with Arienne yn Wuhlain.  He was a member of the Fellowship of the Truth, later known as the Black Company, which let to his becoming banished as awrywdd.    His lover, Arienne, died to the coming of Dubh Gaoth while bearing his child.  Afterward, he considered going to meet Calik on several occasions, remaining separate from the Song for long enough that it left a physical mark upon him.  Eventually, he returned to Shar''ris y Rhuanedd, where the lords of the city overlooked his banishment, allowing him to enter the Chorus, but not in the full Song.  Since that time, he has taken on a new name, Methuel, and dedicated himself to protecting the peoples of Shar''ris y Rhuanedd.  Those who follow him know that he is awrywdd but are unaware of the details.  He only ever enters the Song alone, which further contributes to his physical decline.  He has never shared his knowledge that the Withering no longer afflicts all the sons of Calik and that their people are responsible for the Grim.  Since he believes that Lluagh Maguin has laid claim to the whole Domain, he sees no cause to do so.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1387,1,to_timestamp('21-AUG-15 10.54.21.989000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Elithin',null,null,'A race and the people of Calikar, who according to their own mythology are descended from Calik and those who went into exile with him from Llua''la.  They are a fair, faerie people of great natural beauty.  There is no historical or dream evidence of the elithi during the Second &AElig;gan Empire.  They are afflicted by a curse called with the Withering which cripples their bodies and minds, eventually ending in their demise.  This curse is held at bay by the Song, which also renders them immortal, combines their thoughts into a single intent, and cleanses their souls of hatred.  The empire of Calikar once covered the whole of the Great Cavern, resulting in a golden age, before the Land Gift and the Gloaming.  Also called the Calikari, the Bright Ones, the Ailar, the Bright Lords, and the Fair Folk.  Pl. elithi.  There are elithi who live outside Calikar, in Demarge, who do not suffer from the Withering and are not bound to u’Vidain e’Cuhlhair.  They are referred to as the Unsung.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1584,1,to_timestamp('24-AUG-15 10.18.17.239000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Mist, The',null,null,'A gray cloud which descended over Rolar when the na''k&ouml;r attempted to restore their memories with the Laroch &Ucirc;l, inadvertently resurrecting Aneiron y Marel.  It descended over the city as a chill mist, lingering for several days and stealing the life from most it touched.  The coming of the Grey Mist was foretold in Irienya''s Vision.  During this time, many of the Twisted Ones unexpectedly appeared, climbing over the White Walls to perish in the mist.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1593,1,to_timestamp('24-AUG-15 10.51.41.946000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Al&euml;''uriel Caz''ur Ve''&euml;ri',null,null,'An order of sages among the elithin, frequently sought out for their wisdom.  They can be identified by their stark white robes.  Between Choruses, they are called upon to adjudicate disputes.  For the last three thousand years, the Al''Sinyar have been hidden among their ranks.  Until the Grey War, Myfyr was the lord of the Al&euml;''uriel Caz''ur Ve''&euml;ri.  The current lord is Darwych y Shalleagh.  Also called the Cazari.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1710,1,to_timestamp('29-AUG-15 09.03.06.812000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aljerbach',null,null,'A city of the Great Cavern and also a nation of the same name.  Its lands were granted to men by the lords of Calikar (but were not part of the original Land Gift).  Originally, the city was called Gelastir but was renamed after it was razed during the War of Salt and Sorrow.  The nation once held reign over all the western human kingdoms of the the Roof, including Harn and the Free Cities, but those kingdoms have long since earned their independence.  The crown now only has loose control over the far boundaries of the nation.<br/><br/>In recent times, Aljerbach has been plagued by pestilence, drought, the coming of the Harbingers of Eta&aucirc;r, and the devastation of the Firelight.  Its king and queen were murdered during the escape of the na''k&ouml;r from Ajer-D&aelig;n, and the fate of their heirs is unknown.  Evram der''Baasmeir, formerly a prisoner of the Great Prison, is now the acting regent.  The kingdom is also known as the Thrice Kingdom.  The city is also known as the Thrice City and is laid out in a radial pattern of spokes, radii, and rows.  Both names owe to the fable of the Gr&icirc;th.  The word, Aljerbach, means literally "Friend of the North."  The symbol of the nation and the royal line of Klar is the Triurg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1911,1,to_timestamp('03-SEP-15 11.16.44.837000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Changing Mirror, The',null,null,'An alternative label for Drauth''n&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2301,1,to_timestamp('16-SEP-15 10.02.56.268000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mikoll-holl',null,null,'The great hall of the Mountain Home.  It is crafted from llearth and contains the thrones of the eight &thorn;engill as well as the Fuligin Chair of the &thorn;engill-rikr.  The statues of ancient thanes line the walls, and a mosaic is set in a depression before the throne, crafted of precious gems.  Its surfaces portray visions that change moment to moment and person to person, generally reflections of the Grod.  The true image is of an ax and hammer drawn in rust and gold.  The deep halls of the magnr are accessible via the Mikoll-holl.  Also called the Great Hall.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2302,1,to_timestamp('16-SEP-15 10.03.35.910000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Hall, The',null,null,'An alternative name for the Mikill-holl in the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2303,1,to_timestamp('16-SEP-15 10.10.38.389000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grodphyr',null,null,'The act of surrendering entirely to the Grod, allowing it to consume one''s intellect and person.  Such a person is known as prj&oacute;ta.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2304,1,to_timestamp('16-SEP-15 10.11.42.970000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Prj&oacute;ta',null,null,'A du&aring;rg who succumbs to grodphyr, i.e. allows the Grod to consume his reason and person, becoming a slave to it.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2201,1,to_timestamp('14-SEP-15 09.45.05.465000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kayus Mireni',null,null,'An alias used shortly by Eleanor while she traveled to the Grey Tower aboard the White Gull.  It meant "Good Mireni."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2334,1,to_timestamp('17-SEP-15 08.32.26.817000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Law, The',null,null,'The system of jurisprudence in I&aring;arth.  Only the &thorn;engill and &thorn;engill-rikr have the authority to modify or append the Iron Law, which has remained largely unchanged since the early days of the Iron Kingdom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1720,1,to_timestamp('29-AUG-15 10.04.29.461000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Malig Narz',null,null,'One of the na''k&ouml;r, a frel, and a Harbinger of Eta&ucirc;r.  Malig''s Sphere fell in Aljerbach.  He is apparently blind, with one eye opaque and other absent, but somehow he is able to see.  He is hideous and misshapen.  Malig dreams as Merik do''Rehny, who later became the Mortuani, Az-Azaal.  Presumably, he was once an inhabitant of Frelham, but little else is known of his history except that at some time Eleanor was a slave in his possession.  Currently, Malig is masquerading as Idomeneus, a Malkarian merchant-cum-lord.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1726,1,to_timestamp('29-AUG-15 10.51.48.425000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cyngen yr Orain',null,null,'One of the na''k&ouml;r and an elithin.  More commonly known as Delsar, a name he assumed in his youth.  Cyngen''s Sphere fell in Aljerbach.  Cyngen dreams as Ym du''Serra, who later became the Triumvir, Calik Twiceborn.  After escaping from Aljerbach, Cyngen discovered himself to be the son of Cynyr yr Orain and Arianrhod yn Orain and rightful heir to Nara''s Throne.  Later, he also discovered himself to be brother to Eliryndil, but it is unclear whether he ever knew himself to be Diarin''s brother.  He returned to Rolar and assumed his crown but abdicated after the Grey War and the death of the Song.  After experiencing the fall of I&aring;rth, losing his sister, Eliryndil, and seeing the hopelessness of Lluagh Maguin, Cyngen surrendered all hope and became the Unborn.  Also known as Ilur&icirc;n Mar&ucirc;l, the "Mist Walker," and, later, as Delsar Unborn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1950,1,to_timestamp('05-SEP-15 12.01.09.600000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aljerstraad',null,null,'The noble houses of Aljerbach.  Also describes the council in which they meet.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1951,1,to_timestamp('05-SEP-15 12.03.14.969000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of the Dawn, The',null,null,'An early conflict between Calikar and the d&aelig;n''shee, before the foundation of Kailar.  The d&aelig;n''shee were united and led by the warlord, N''h&aelig;l.  Also called the Dawning War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1952,1,to_timestamp('05-SEP-15 12.03.37.431000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dawning War, The',null,null,'An alternative name for the War of the Dawn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1953,1,to_timestamp('05-SEP-15 12.08.06.264000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Morum Lumen',null,null,'An alternative name for Mera Rhin, used by its current occupants.  It means "Bright Legacy".',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1954,1,to_timestamp('05-SEP-15 12.08.07.613000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mera Rhin',null,null,'A fortress built by the elithi to watch over the Western Path, built in response to the appearance of the corti.  It was abandoned during Shanaran''s Plague, and its ruins were later occupied by Harn and the Empire of Demarge.  Also called Morum Lumen, meaning "Bright Legacy."  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1955,1,to_timestamp('05-SEP-15 12.08.24.897000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Bright Legacy',null,null,'Translation of "Morum Lumen."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1765,1,to_timestamp('29-AUG-15 09.59.53.577000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Rhuaidh',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the a''Tiaryche.  Its current head is Argoel y Rhuaidh.  The line of Rhuaidh have been blessed with the Sight since the time of Arawn y Rhuaidh and have been the trusted counselors of Ryche Senyar since long before the Gloaming.  Despite not being accounted among the Tiaryche, the lords of Ryche of Rhuaidh are considered among the most respected in the Bright Kingdom.  Their symbol is a hummingbird.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1821,1,to_timestamp('01-SEP-15 08.23.23.932000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cadf&aelig;l y Rhyfelwr',null,null,'An acclaimed elithin warrior who lived early in the history of Calikar.  He is a hero of the Dawning War, remembered for having led a legion into battle atop adur-&auml;l steeds.  He is credited as having killed Mi’shenrin (the son of the first king of the d&aelig;n’shee, Shenrin) in battle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1822,1,to_timestamp('01-SEP-15 08.25.04.021000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cadogen y Rhyfelwr',null,null,'An acclaimed elithin warrior, considered among the greatest of their military minds.   He was high captain of the Triptychs at the hour the Hunger fell and died in the defense of &AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1823,1,to_timestamp('01-SEP-15 08.30.35.726000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mi''Shenrin u''Gur Dao''Ast',null,null,'A d&aelig;n''shee and eldest son of the first king of Kailar, Shenrin e''Gur Dao''Ast.  He was beloved by his people.  Shenrin died during the Dawning War, killed by Cadf&aelig;l y Rhyfelwr in single combat just moments after his father''s death.  He never assumed the throne.  His brother, Sendar u''Gur Dao''Ast, surrendered.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1825,1,to_timestamp('01-SEP-15 08.37.13.505000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sendar e''Gur Dao''Ast',null,null,'The younger son of Shenrin e''Gur Dao''Ast.  During the War of Four Kingdoms, as a young child, he assumed the throne of Kailar when his father and brother, Mi''Shenrin u''Gur Dao''Ast, perished.  He surrendered and was dragged back to live the rest of his life as a crown-less king beneath Diris Alonne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1827,1,to_timestamp('01-SEP-15 08.47.14.119000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Calithar',null,null,'The Great Tree of &AElig;lm&aelig;dris, located in the plaza outside the Elod&aelig;ne Palace.  After the Gloaming, its seed was planted near Muir Auread.  After Camryn''s Pride, it was planted in Diris Maguin near Muir Airgin.  Both were named D&ucirc;n Calithar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1829,1,to_timestamp('01-SEP-15 08.51.35.768000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mithrdain',null,null,'The Hall of Song in &AElig;lm&aelig;dris.  Also referred to an area of Diris Maguin, where the exiles of ka-&AElig;lm&aelig;dris gathered to join in the Chorus.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1830,1,to_timestamp('01-SEP-15 09.01.54.823000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Essrwwr',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Their line is considered extinct since the Gloaming.  Their symbol was a nightingale.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1833,1,to_timestamp('01-SEP-15 09.10.06.543000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Blood Guard, The',null,null,'The personal guards of the kings and queens of Kailar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1834,1,to_timestamp('01-SEP-15 09.11.10.968000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Abyssal Halls',null,null,'The name of the palace from which the kings and queens of the d&aelig;n''shee rule in Kailar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1839,1,to_timestamp('01-SEP-15 09.24.13.260000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dunain &AElig;lwndwg&aelig;th',null,null,'Seeds offered by the Great Trees in premonition of their demise.  They are always three in number, and only one ever takes root.  Translates to "Seed of Rebirth" in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1841,1,to_timestamp('01-SEP-15 09.25.12.530000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Seed of Birth',null,null,'Literal translation of Dunain Genedwg&aelig;th in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1843,1,to_timestamp('01-SEP-15 09.31.22.481000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Muatha isch Lluhahdi',null,null,'A name applied to builders, architects, and creators among the elithi.  They are thought to be possessed by spirits, both a curse and a blessing.  Known more simply as a Maker.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1845,1,to_timestamp('01-SEP-15 11.29.31.760000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dear Child',null,null,'Literal translation of "Kaliara" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1847,1,to_timestamp('01-SEP-15 11.59.05.261000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Broach of Hibernation',null,null,'Literal translation of Mu''anshe Coh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1848,1,to_timestamp('01-SEP-15 12.01.30.479000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mu''anshe Coh',null,null,'Artifacts possessed by Jan&icirc;l Hwe''leri and Kisel Gres''Mehar, likely Shiahd or Tal''Shiahd, which apparently extended their lives by inducing long periods of sleep, called the Sleepless Slumber.  Crafted of green stone and silver, the broach shows a boy sleeping in field of poppies, his eyes open.  When the pin is drawn through flesh, the eyes close and the wearer sleep for 100 years (or until the pin is withdrawn).  For two months after use, the eyes of the boy remain dark, then turn a sapphire blue.  It can then be used again.   Also known as Broaches of Hibernation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1849,1,to_timestamp('01-SEP-15 12.02.23.440000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dreamless Slumber',null,null,'A long period of dreamless sleep, or hibernation, induced by the Mu''anshe Coh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1850,1,to_timestamp('01-SEP-15 12.07.56.978000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eight Pillars, The',null,null,'An alternative name for Thir Eris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1852,1,to_timestamp('01-SEP-15 12.11.47.333000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Diris Rhuan',null,null,'Name shared by Holy Groves of Rhuanedd and Shar''ris y Rhuanedd.  The former has been lost since the Gloaming. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1857,1,to_timestamp('01-SEP-15 12.27.07.787000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'D&ucirc;n Nuchalan',null,null,'The name of the Great Tree of Shar''ris y Rhuanedd, located in Diris Rhuan, born of the Dunain &AElig;lwndwg&aelig;th of Nuchalan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1860,1,to_timestamp('01-SEP-15 12.35.44.751000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mereith',null,null,'An ancient mining village of Calikar, abandoned years before the Gloaming when more rewarding veins were found near Aduine and Srwawain.  Ka-&AElig;lm&aelig;dris lies near its ruins, and the exiles use its ancient mines as a source of a’shanne and the minerals used to craft pal’n&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1835,1,to_timestamp('01-SEP-15 09.16.37.890000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Murigen yn Inythyn',null,null,'A great Maker among the elithi, famed for laying the tesserae in Urek-Un&icirc;n and the Elod&aelig;ne Palace as well as the murals in the Well of Dreams.  She was one of few Calikari invited to Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1794,1,to_timestamp('31-AUG-15 10.22.46.340000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mist Wraith',null,null,'A corporeal servant of Dubh Gaoth.  Their nature and origin are uncertain.  Also called a Wind Wraith or a Wraith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1805,1,to_timestamp('31-AUG-15 10.56.24.770000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sarylla',null,null,'A woman of the corti, sister to Marylla, and husband to Jan&icirc;l Hwe''leri.  During the ritual which gave birth to Dubh Gaoth, she betrayed Jan&icirc;l''s intent to Kisel and was murdered by her husband.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1864,1,to_timestamp('01-SEP-15 12.49.46.440000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mist Barrens, The',null,null,'The vast area of Lluagh Maguin which has been abandoned by the Calikari, now considered the domain of Dubh Gaoth and its Wraiths.  Also called the Shadowlands by the people of ka-&AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1865,1,to_timestamp('01-SEP-15 12.50.16.886000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shadowlands, The',null,null,'An alternative name for the Mist Barrens, used primarily by the exiles of ka-&AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1867,1,to_timestamp('01-SEP-15 12.52.51.213000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sh’hein’Rein',null,null,'The collective name for the Triptychs.  Also known as the Stone Fist or the Fist of Swords.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1869,1,to_timestamp('01-SEP-15 12.53.22.596000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stone Fist, The',null,null,'An alternative name for Sh’hein’Rein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1870,1,to_timestamp('01-SEP-15 12.54.55.636000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Many and Sacred Kingdoms, The',null,null,'An alternative name for Chara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1871,1,to_timestamp('01-SEP-15 02.34.47.502000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shara-aleach',null,null,'A gruop of e¿lithin warriors bound by a sacrament considered as sacred and binding as marriage. Their oath compels them to fight until K¿ailar lies in ruins.  Similar to the cara-aleach, except that the bonded groups include more than two persons, most frequently three.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1875,1,to_timestamp('01-SEP-15 02.47.44.573000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'In Darkness, Remembering [the Light]',null,null,'Literal translation of Maguur De''na in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1876,1,to_timestamp('01-SEP-15 02.48.05.516000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Still, The',null,null,'An alternative label for the Maguur De''na.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1877,1,to_timestamp('01-SEP-15 02.48.11.516000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silent, The',null,null,'An alternative label for the Maguur De''na.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1878,1,to_timestamp('01-SEP-15 02.48.48.696000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Brume, The',null,null,'An alternative name for the Gloam.<br /><br />Also a place mentioned by Minoch al''Fainar to Sarah der''Taber in the Tower of Sages, evidently not pleasant.  He implied that he dwelt here for some time, likely as a prisoner.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1766,1,to_timestamp('29-AUG-15 10.01.42.057000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ondran yr Eldwr',null,null,'An elithin and member of the Eshthara.  He sacrificed his life sealing the path to Rhuanedd from Oilear with a n&ucirc;l.  They Key to the n&ucirc;l was lost with him.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1890,1,to_timestamp('01-SEP-15 03.18.33.217000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hungering Earth, The',null,null,'An alternative term for Machuin Muil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1892,1,to_timestamp('01-SEP-15 03.18.52.334000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Last Battlefield, The',null,null,'An alternative term for Machuin Muil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1813,1,to_timestamp('01-SEP-15 07.44.40.757000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wenwryn yn Hwnwn',null,null,'An acclaimed Maker of the elithi, who built the Elod&aelig;ne Palace and Mithrdain in &AElig;lm&aelig;dris, as well the Hall of Tablets in Aduine.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1837,1,to_timestamp('01-SEP-15 09.18.27.002000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grey City, The',null,null,'An alternative name for the city of Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1397,1,to_timestamp('21-AUG-15 11.05.21.947000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Uudhuur',null,null,'A term used by the elithi to describe those who are not elithi, for example du&aring;rg and humans.  It is not used in reference to the d&aelig;n''shee.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1404,1,to_timestamp('21-AUG-15 11.28.30.068000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fold',null,null,'An alternative aspect, perspective, or realm of the Universe, most easily conceived of as a "dimension."  They do not occupy physical space, per s&eacute;.  The races of Puell&aelig; cannot, except in rare circumstances, perceive the Folds and are generally unaware of them.  Examples include Almad&eacute;n, Gahgra, Gor''Nahara, Eram Morgot, Urum Morgot, Huotha Ji''har, and Qu&aelig;''lor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1904,1,to_timestamp('03-SEP-15 10.53.57.594000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Breah-heod',null,null,'An alternative label for Aruth''eras y Senyar.  Literally the "Great King" in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1905,1,to_timestamp('03-SEP-15 10.54.10.252000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shadowspur, The',null,null,'An alternative label for Aruth''eras y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1906,1,to_timestamp('03-SEP-15 10.54.45.296000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great King, The',null,null,'Literal translation of Breah-heod.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1909,1,to_timestamp('03-SEP-15 11.16.23.452000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Midam''s Pome',null,null,'An alternative label for Drauth''n&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1910,1,to_timestamp('03-SEP-15 11.16.33.245000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Pardoning Stone, The',null,null,'An alternative label for Drauth''n&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1868,1,to_timestamp('01-SEP-15 12.53.07.729000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fist of Swords, The',null,null,'An alternative name for Sh’hein’Rein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1831,1,to_timestamp('01-SEP-15 09.03.52.959000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Gaardwr',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  Their line is considered extinct since the Gloaming.  Their symbol was a sparrow.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1832,1,to_timestamp('01-SEP-15 09.09.16.485000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Brochf&aelig;l y Rhyfelwr',null,null,'A legendary elithin considered by many to be the greatest of the shuonne to have lived.  He served beneath Cuth&ucirc;l-erin and is said to have fought an army of a thousand of the Blood Guard before killing Veomnis and four of his six sons in the Abyssal Halls in the final days of the War of Ash.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1836,1,to_timestamp('01-SEP-15 09.17.58.276000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Well of Dreams, The',null,null,'A hall and bath of great beauty in the Calikari city of Val Analyth.  Its famous murals were painted by Murigen yn Inythyn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1806,1,to_timestamp('31-AUG-15 10.59.24.179000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nexus, The',null,null,'The heart of Oilear, connected to the Egra Doors.  Little is known except that Malnos, Eleanor, and Kain reached Rhuanedd from the Grey Tower by entered i''Srin L&ucirc;n via the Nexus.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1808,1,to_timestamp('01-SEP-15 07.17.05.867000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tower of Tyrin, The',null,null,'The tallest tower of Rolar, a thin, tapering spire of ivory stone set in the heart of Turin&aelig;.  Supposedly, sacred texts or laws set by Calik are stored in its highest chambers.  According to legend, both Tyrin and Turin&aelig; were built by Tyren y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2190,1,to_timestamp('12-SEP-15 09.27.32.366000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Draodhe’agin',null,null,'A term in the Pure Tongue which has no direction translation in modern tongues: "eldritch" or "unknown" or "old magick" are close approximations.  It has a connotation of "dreadful" and "unknowable".',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1571,1,to_timestamp('23-AUG-15 07.36.22.351000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Zjeryn e''Gur Dao''Ast',null,null,'A former king of Kailar, son of Veomnis e''Gur Dao''Ast, father of Asmaal e''Gur Dao''Ast, and grandfather of Eshrella y''Gur Dao''Ast.  He watched his father murdered by Brochf&aelig;l as a small child and devoted his life to the ruin of Calikar, transforming the ki''tahj into into its modern incarnation.  Although he initiated many wars against Calikar, all were failures, and he reigned throughout much of what is known as the Golden Age of Calikar.  He perished in the Mountain Home, a prisoner of Grundr Sun&eth;un-s&oslash;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1844,1,to_timestamp('01-SEP-15 11.29.14.303000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kaliara',null,null,'A disease developed by Mariel during the Great War.  The disease has little noticeable effect for up to fifteen years after infection (generally puberty for newborns).  This is followed by 4-5 years of painless but terrible deformity, followed by 4-5 years of unbearable pain, followed by death.  Mariel deigned the illness out of curiosity.  She wanted to see how men dealt with generational illness and hopelessness.  It is thought that Kaliara is the basis of the Withering suffered by the eliti, likely communicated purposely to Calik by Az-Azaal during their duel at Erinin.  The word means "Dear Child" in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1856,1,to_timestamp('01-SEP-15 12.20.20.730000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Western Path, The',null,null,'The expanse of the Deep Dark which separates the Great Roof from the Roof of Bel Can’nar.  It is patrolled by the armies of the Empire of Demarge, in which it is known as the Eastern Path.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1812,1,to_timestamp('01-SEP-15 07.43.00.870000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Elod&aelig;ne Palace',null,null,'The palace at the heart of &AElig;lm&aelig;dris and the seat of power for Ryche Senyar, crafted by the acclaimed Maker, Wenwryn yn Hwnwn.  The Great Tree, Calithar was planted in the plaza outside its gates.  Also known as Vieun Istue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1862,1,to_timestamp('01-SEP-15 12.44.38.975000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'An Suragh',null,null,'One of two ancient Calikari villages renowned as a breeding ground for adur-&auml;l steeds.  It was abandoned after the adur-&auml;l were driven to extinction.  It lies two day''s walk from Muir Auread, where colts bred in the village were traditionally birthed.  The elithi of &AElig;lm&aelig;dris originally resettled in An Suragh after the Gloaming, but it was abandoned after they summoned Dubh Gaoth’s attentions during Camryn''s Pride.  It may have since been reclaimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1854,1,to_timestamp('01-SEP-15 12.16.35.960000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sendain',null,null,'The Hall of Song in Rhuanedd, abandoned since the Gloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1859,1,to_timestamp('01-SEP-15 12.33.42.424000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ruthelain',null,null,'A migrant village of the elithi of Shar''ris y Rhuanedd.  Its people were slaughtered by a pair of ugyr, and Ulryn y Marych was the only survivor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1534,1,to_timestamp('23-AUG-15 01.36.27.189000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gh&icirc;lar',null,null,'A town in northern Aljerbach, formerly a crossroads of significant wealth and significance.  It is built in the shadow of the Lattice Bridge and was once the site of a Calikari city (which name it assumed) that was abandoned during the Battle of Legacy in the Unseen War.  The na''k&ouml;r visited Gh&icirc;lar on their journey to Rolar, spending three days at the Festival of Gr&icirc;th.  Kisel murdered Heilyn here, and Diarin met her sister, Liarin a''Gur Dao''Ast.  Shortly after the na''k&ouml;r departed, Gh&icirc;lar was destroyed by some power or force.  Those who passed by the city afterward referred to it as a blackened crater.  It is unknown if there are any survivors.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2422,1,to_timestamp('18-SEP-15 03.41.47.822000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rajnvald Khijak-s&oslash;n',null,null,'A du&aring;rg and son of Khijak the Khazjad and Silvi.  He is one of seven sons of Khijak and the only one to survive the Unforging.  He was the leader of his people when they arrived in Bagr-Danr but died well before they came to Calikar and Yjalmar.  All the magnr of I&aring;rth claim to be the descendants of Rajnvald and ultimately the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2425,1,to_timestamp('18-SEP-15 04.03.05.555000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Murtl-sang',null,null,'The ceremony in which the Hammar speak llearth from the depths of the Mountain Home, both digging the Atrium deep and raising the height of the mountain.  The dragonstone can be seen flowing up the edges of the Atrium, where it divides into thousands of branches that over time flow into the mountain''s shell.  Some believe that this ritual is performed in an effort to discover and reclaim the Weeping Eye.  The murtl-sang occurs during the second month of each year.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2430,1,to_timestamp('18-SEP-15 04.33.50.682000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Charmers'' Grotto, The',null,null,'A district of the city of I&aring;rth, known as the dominion of the Hammar.  Sangf-havn is located here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2447,1,to_timestamp('18-SEP-15 08.32.11.743000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tower of Sages, The',null,null,'The tallest building in the Innermost City of Aljerbach, occupied by emissaries from Alanon, chief of whom is zal-Minoch al''Fainar.  The tower has no apparent entrance except when its masters desire it to appear.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2399,1,to_timestamp('17-SEP-15 06.59.21.125000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hl&aelig;ja Zaan-s&oslash;n',null,null,'A du&aring;rg boy, disinherited child of magnr Undlflos, drekka, son of Valkari Rowan-d&oslash;tter and Zaan Nafalti-s&oslash;n, younger (twin) brother to Folkvar Zaan-s&oslash;n.  His mother died in his birth, and he was named by his grandmother, Rowan Fylla-d&oslash;tter in honor of one of her brothers, before she too died shortly thereafter.  Now an orphan, he became a slave and was taken a young boy to the Mines of Orth, where he toiled with his brother for five years.  Eventually, he was rescued by Durgin, who named him first skyldr and then &thorn;egn alongside his brother.  He was murdered by Diarin yn Marel yn Ast during Arastel''s assault on the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2379,1,to_timestamp('17-SEP-15 02.49.54.589000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Khijak the Khazjad',null,null,'A legendary hero and supposed progenitor of the du&aring;rg, thought to have lived during the time of Llua''la and to have perished before the Iron Born fled the Endless Cavern.  Some Iron Born consider him to be a god and peer of Urag.  It is likely that he was the Councilor, Johra Kail.  Also known as the Stoneheart.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2381,1,to_timestamp('17-SEP-15 03.00.10.382000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;ingh&aacute;',null,null,'A wealthy district of I&aring;rth, where many high end merchants and houses of the magnr are located.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2812,1,to_timestamp('25-SEP-15 08.14.20.253000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valmwwr yn Ceonwe',null,null,'An elithin, former lady of Rolar, mother to Cadeyrn yr Orain, and wife to Brandynn yr Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2814,1,to_timestamp('25-SEP-15 08.17.15.620000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nerrwn yn Orain',null,null,'An elithin and former lady of Ryche Orain.  Mother to Bathwrwy yn Orain, sister of Arduen yr Orain, and daughter to Calin yr Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2816,1,to_timestamp('26-SEP-15 11.23.52.152000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Yaolair',null,null,'A house of the elithi in Rhuanedd, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was stork.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2833,1,to_timestamp('26-SEP-15 11.20.15.886000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'M&aelig;lgad yr Aloreth',null,null,'An elithin, former Tiaryche of Ryche Aloreth, finest shuonne in Rolar, and First Shield of Ire''nurine.  He served Gethin y Kam&icirc;l and betrayed Cyngen yr Orain during the Grey War, where he was killed by Diarin yn Marel yn Ast in single combat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2839,1,to_timestamp('26-SEP-15 11.35.49.112000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hyorn yr Aloreth',null,null,'An elithin, shuonne, and Tiaryche of of Ryche Aloreth since the end of the Grey War. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2840,1,to_timestamp('26-SEP-15 11.36.15.610000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eamon yr Idrien',null,null,'An elithin, loremaster of Turin&aelig;, and Tiaryche of of Ryche Idrien since the end of the Grey War. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2911,1,to_timestamp('05-OCT-15 08.35.01.831000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Forest of Ur, The',null,null,'A fungal wood that lies between Aljerbach and the Taunland.  Its heart is considered cursed and dangerous, the abode of deadly beasts and flora, but its edges are heavily harvested for fungal wood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2912,1,to_timestamp('05-OCT-15 08.35.51.697000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Barthur''s Light',null,null,'An expression of surprise, delight, or fear among the Aljer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2913,1,to_timestamp('05-OCT-15 08.37.34.953000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'God-sign',null,null,'A mark placed upon children of the Great Roof to indicate that they have gone through their Naming ceremony and been associated with a deity.  It generally takes the form of a brand upon the forehead or cheek.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2914,1,to_timestamp('05-OCT-15 08.38.55.838000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fire-stones',null,null,'Stones granted children of the Great Roof as part of their Naming ceremony.  When struck together, they produce flame and light.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2915,1,to_timestamp('05-OCT-15 08.39.25.498000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arenne Tools',null,null,'Implements given children of the Great Roof as part of their Naming ceremony, used in the ritual preparation of food.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2916,1,to_timestamp('05-OCT-15 08.46.55.017000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lu Root',null,null,'A root endemic to the First Domain.  It has no nutritional value but is often chewed for recreation, especially among the lower classes of the Aljer.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2815,1,to_timestamp('25-SEP-15 08.23.23.967000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Brandynn yr Orain',null,null,'An elithin and former lord of Ryche Orain.  Father to Cadeyrn yr Orain, brother to Eldeain yr Orain, and son of Carac&ucirc;l yr Orain and B&aelig;rin yn Nyamn. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2339,1,to_timestamp('17-SEP-15 09.10.42.856000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Khazjad''s Honor',null,null,'An alternative name for S&aelig;m&eth; Khazjadi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1777,1,to_timestamp('31-AUG-15 09.50.30.419000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Llua''la',null,null,'The name by which the elithin refer to the realm in which they lived before Calikar, i.e. the surface of Puell&aelig;.  Also known as the Endless Cavern.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1792,1,to_timestamp('31-AUG-15 10.19.02.514000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gloam, The',null,null,'The name by which Lluagh Maguin was known to its inhabitants.  Also called the Brume.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1774,1,to_timestamp('30-AUG-15 12.54.53.237000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Isokrates',null,null,'The captain of a sightseeing vessel which delivered Malig from Sikyons to Oikon.  He perished when Malig arranged to have his ship sunk.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1747,1,to_timestamp('29-AUG-15 07.07.08.758000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mad King''s War, The',null,null,'A civil war early in the history of Berak, where the people rose up against the madness and cruelty of Mad King Teras of the Asvana.  The war lasted seven years, and the people of Berak vowed thereafter to never allow power to consolidate in a single throne.  Thus were born the Twin Caliphs.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1359,1,to_timestamp('20-AUG-15 08.50.32.408000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'True Tongue, The',null,null,'A language comprised entirely of nouns, the proper and divine names of all things that have been, are, or ever will be.  According the Sundering of Anda Adalai&auml;, the True Tongue was brought into being when Jesr&aelig;''el offered a Gift to the Children of Istaron.  Speaking the Name of a thing in the True Tongue can grant a Namer power over it; or it can grant the thing power over the Namer.  In the case of the True Tongue, sound and language are only allegories.  A Name is more abstract in nature.  Each name must be taught or intrinsically known.  They cannot be discovered by accident.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2340,1,to_timestamp('17-SEP-15 09.13.13.806000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Champions'' Theater',null,null,'The great coliseum of I&aring;rth, where gladiators, slaves, and wild beasts fight for the entertainment of the Iron Born.  Also known as the Pit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1840,1,to_timestamp('01-SEP-15 09.24.44.650000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Seed of Rebirth',null,null,'Literal translation of Dunain &AElig;lwndwg&aelig;th in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1855,1,to_timestamp('01-SEP-15 12.17.57.174000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Diris Maguin',null,null,'The Holy Grove of ka-&AElig;lm&aelig;dris, grown on the banks of Muir Airgin.  Its sacred trees are coated with resin to conceal their light from Dubh Gaoth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2607,1,to_timestamp('20-SEP-15 09.34.36.895000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gandath Erland-s&oslash;n',null,null,'The eldest child and only son of Erland Hjalmar-s&oslash;n  He was taken as a hostage by Erland''s brothers in their attempt encourage his father to leave Sk&aring;n-Ule&aring; and join the battle against the d&aelig;n''shee.  He died while a hostage, taken by a d&aelig;n''shee blade.  Afterwards, Erland disowned his brothers and closed all the roads to Sk&aring;n-Ule&aring;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1540,1,to_timestamp('23-AUG-15 01.48.46.371000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Erybin',null,null,'Formerly a minor house of the elithi in Rolar.  Due to the deeds of Pryderi yr Erybin, Delsar raised it to one of the Tiaryche, the first time such a thing had been done in three thousand years.  Pryderi yr Erybin is its head.  Their symbol is a white gull.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2443,1,to_timestamp('18-SEP-15 08.17.48.349000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Soltinn',null,null,'A du&aring;rg whose skills/person do not match his Grod, for example a crippled Drengskapr or a scarred Fagvaenn.  The saddest of the soltinn, considered cursed and nearly equal to the drekka, are Hammar who lack the skill to Speak.  Others can at least pursue their purpose.  They can be what they are.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2479,1,to_timestamp('19-SEP-15 07.09.59.234000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Justicar',null,null,'An alternative term for a "warrior-judge" of the Dom''Vanari.  They have the right of passing judgment on any citizen of the Empire of Demarge, theoretically even members of the Centarum and the God Emperor himself.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2392,1,to_timestamp('17-SEP-15 06.18.55.048000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Liarin a''Gur Dao''Ast',null,null,'A d&aelig;n''shee woman, daughter of Eshrella y''Gur Dao''Ast and Jaarokk, sister of Diarin yn Marel yn Ast, Kiarin u''Gur Dao''Ast, Shiarin a''Gur Dao''Ast, Jiarin u''Gur Dao''Ast, Giarin u''Gur Dao''Ast, and Riarin u''Gur'' Dao''Ast.  She is apparently connected in some way to the Milita, Agra''Dalolth''Dakaj.  Physically similar to Diarin, except that her brows and cheekbones are silver rather than her knuckles, nails, and elbow.  Some wyrd seems to bind the two sisters, where wounds inflicted upon Liarin are suffered threefold by Diarin.  Liarin claims that this spell was laid by their mother, Eshrella, who hates all her children but Diarin most.  Liarin was last seen across the river from Gh&icirc;lar.  Her purpose there is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2442,1,to_timestamp('18-SEP-15 04.49.58.826000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dena''dana',null,null,'"He Who Ruled from the Basement" in the Pure Tongue.  A term used Xatran M&ucirc;r to describe Pithras D&aelig;m (a reference to Pithras'' cowardice, compared to the terms Tala''dana and Resa''dana employed by the Shugda).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2472,1,to_timestamp('19-SEP-15 06.43.03.493000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tallium',null,null,'A house of the Sangus and Centarum in the Empire of Demarge, one of two houses afforded three representatives in the Imperial Senate (with Garus).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2475,1,to_timestamp('19-SEP-15 06.58.16.272000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Imperial Senate, The',null,null,'The legislative body of the Empire of Demarge, which traces its roots back to the senate of the ancient republic.  It consists of two hundred and two members drawn from houses of the Centarum.  Once, the Senate held absolute power in Demarge, but its authority has been slowly eroded over the century, until many now consider it a puppet of the God Emperor.  The Emperor and the Quintunes have the authority to veto any vote passed on the Senate hall (the latter via the God Emperor''s Eye).  Technically, the Quintunes can also override the Divine Veto of the Emperor, but this has only happened twice in the history of the Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2934,1,to_timestamp('06-OCT-15 07.20.38.267000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lurik der''Wieck',null,null,'One of the guards on the cell of the na''k&ouml;r on their initial awakening in Ajer-D&aelig;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2842,1,to_timestamp('26-SEP-15 11.46.06.180000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gethin y Kam&icirc;l',null,null,'An elithin, Keeper of the Song, and former Tiaryche of of Ryche Kam&icirc;l.  He entered Aoldain against tradition and spoke against Cyngen yr Orain and Cynyr yr Orain.  Some believe that this act, or the conflict that subsequently rose between him and Cadeyrn yr Orain, resulted in the silence of the Song.  A subsequent vision shown by Myfyr to Delsar revealed that he may have more directly brought harm to D&ucirc;n Mile''&ucirc;r.  Gethin later waged the Grey War against Delsar, wherein he was killed by Caderyn (and vice-versa) while trying to lay claim to Anlocht.  For his deeds, Ryche Kam&icirc;l was stripped of its rank as a house of the Tiaryche, the first and only time such a punishment has been levied.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2918,1,to_timestamp('05-OCT-15 08.52.09.073000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gaidrin',null,null,'A children''s game among the Aljer, generally the more wealthy, where metal or wooden rings are guided by long poles called gaidrin sticks.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2919,1,to_timestamp('05-OCT-15 08.52.42.625000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gaidrin Sticks',null,null,'Long, wooden poles used in the children''s game called gaidrin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2920,1,to_timestamp('05-OCT-15 08.53.44.428000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Jerome',null,null,'A boy encountered by Trier der''Xylander in the Forest of Ur.  He spoke, appeared, and behaved strangely, claiming to be from a far-off place called Weyr a''Sherr&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2922,1,to_timestamp('05-OCT-15 08.57.54.330000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Farauk der''Xylander',null,null,'A boy of the Taunland and elder brother of Treir der''Xylander and Etri der''Xylander.  He entered his five years and was later conscripted to the Harnese border.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2923,1,to_timestamp('05-OCT-15 09.00.03.683000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dragons delve the deeps, and Tytans bestride the clouds',null,null,'Words written by Amber on a wall in Aljerbach, drawn in her own blood.  She did not understand their meaning or remember their scribing.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2924,1,to_timestamp('05-OCT-15 09.00.39.875000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dragon',null,null,'Dragons are?',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2925,1,to_timestamp('05-OCT-15 09.02.07.257000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fields of Erdrion, The',null,null,'An inn in Aljerbach, visited by Durgin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2926,1,to_timestamp('05-OCT-15 09.02.50.563000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Erdrion',null,null,'The realm of the gods in the mythology of the Aljer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2927,1,to_timestamp('05-OCT-15 09.03.19.711000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Karamus',null,null,'The realm of the gods in the cosmology of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2389,1,to_timestamp('17-SEP-15 03.46.36.518000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;egn',null,null,'The closest and most personal servant of a &thorn;engill or Phaelen, chosen from among his Skyldr.  Only one &thorn;egn can be named, but Durgin chose two:  Hlaeja Zaan-s&oslash;n and Folkvar Zaan-s&oslash;n.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (761,1,to_timestamp('14-AUG-15 09.09.47.939000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aiki',null,null,'A race of people who dwell between the First Domain and the Second Domain within the wyrm tunnels.  Little is known of them.  Kaja''lar is the most well-known member of this people.  Also known as the wyrm-blessed.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2929,1,to_timestamp('05-OCT-15 11.46.08.834000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Councilor',null,null,'A member of the Council of Twenty-one.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2836,1,to_timestamp('26-SEP-15 11.29.00.104000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sulwyn yr Idrien',null,null,'An elithin and Tiaryche of Ryche Idrien.  He served Gethin y Kam&icirc;l and betrayed Cyngen yr Orain during the Grey War, where he was killed during the Battle of Two Hills.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1842,1,to_timestamp('01-SEP-15 09.26.55.783000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dunain Gendwg&aelig;th',null,null,'Common seeds offered by the Great Trees.  They germinate to become not Great Trees but the lesser trees of the Holy Groves.  Translates to "Seed of Birth" in Tuindil (as opposed to Dunain &AElig;lwndwg&aelig;th).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1851,1,to_timestamp('01-SEP-15 12.10.29.967000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Diris M&aelig;rysh',null,null,'The ancient Holy Grove of &AElig;lm&aelig;mdris, lost since the Gloaming.  Note that the Great Tree of Diris M&aelig;rysh was not located in the Grove but instead in the plaza outside the Elod&aelig;ne Palace.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2613,1,to_timestamp('20-SEP-15 09.50.17.102000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'H&ouml;gval',null,null,'An eastern hurtt of the kingdom of I&aring;rth.  Her great hills were shattered by the Weapon of Arastel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1873,1,to_timestamp('01-SEP-15 02.40.03.704000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'T&aelig;ryn yr Aranrhod',null,null,'Captain of the first legion of the Shadow Legion in ka-&AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1872,1,to_timestamp('01-SEP-15 02.39.40.499000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shadow Legion',null,null,'The armies of ka-&AElig;lm&aelig;dris, derived from the Bright Legions.  Those who still possess their m&aelig;lorwi have painted them black, but most have adopted more primitive armament.  There are no active forges in Lluagh Maguin.  There are three separate legions of the Shadow Legion, each with its own captain.  T&aelig;ryn yr Aranrhod is captain of the first legion.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1908,1,to_timestamp('03-SEP-15 11.15.26.208000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tarrin',null,null,'A corti and childhood friend to Kisel Gres''Mehar.  After accidentally being exposed to the Umbra, he sank into madness and was executed by his people.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2445,1,to_timestamp('18-SEP-15 08.27.43.415000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Deep Towers, The',null,null,'An alternative name for Ruel-uchain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2446,1,to_timestamp('18-SEP-15 08.29.17.377000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Stone, The',null,null,'The name by which Ruahd is known among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1753,1,to_timestamp('29-AUG-15 07.30.01.688000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'War of the Low Throne, The',null,null,'A recent war between Aljerbach and Berak.  The armies of Berak were victorious in nearly every battle, but nonetheless they eventually surrendered, an act that was never understood by the lords of the Aljerstraad.  Despite the surrender of the Beraki, a tribute was levied against the Aljer, a move which later caused many of the Aljer people to consider their king, Davram derl''Klar, weak.  The smallfolk were never fully aware of the circumstances of the war or the surrender.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1780,1,to_timestamp('31-AUG-15 09.54.20.088000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grey Dome, The',null,null,'An iconic dome at the heart of the city of Sorain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2618,1,to_timestamp('20-SEP-15 01.59.00.540000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Midsday',null,null,'The middle day of the week in the calendar of the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2620,1,to_timestamp('20-SEP-15 02.03.34.591000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Go''avram',null,null,'A man seen by Drolock''harka in a vision provided by the Archway.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2622,1,to_timestamp('20-SEP-15 02.04.10.092000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Karalyn',null,null,'A woman seen by Drolock''harka in a vision provided by the Archway.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2623,1,to_timestamp('20-SEP-15 02.05.37.275000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tunir&aelig;on of the White Walls',null,null,'A city witnessed by Drolock''harka in a vision provided by the Archway.  It lies in the shadow of floating island called Vindil&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2485,1,to_timestamp('19-SEP-15 07.23.30.950000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'God''s Legions, The',null,null,'An alternative term for the Divine Legions of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2487,1,to_timestamp('19-SEP-15 07.28.39.684000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dwerin',null,null,'A mysterious person who has appeared in many places, including masquerading a guard to deliver food to the na''k&ouml;r in Ajer-D&aelig;n.  His identity and purpose are unclear.  He apparently has the power to duplicate his person, frequently as five separate entities, although this may be Mask of some kind.  Ariel durahl Durain believes him to be the dure''marg of the "Old Man."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2602,1,to_timestamp('20-SEP-15 09.15.47.775000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Circus Serimus',null,null,'A chariot racing stadium located in the City of Dermarge.  The largest stadium in the First Domain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2603,1,to_timestamp('20-SEP-15 09.17.13.926000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gylfi Mylfi-s&oslash;n',null,null,'A du&aring;rg of house Onnder who interrogated Delsar while the latter was a prisoner in the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2604,1,to_timestamp('20-SEP-15 09.17.55.484000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Onnder',null,null,'A minor house of the du&aring;rg in I&aring;rth.  Gylfi Mylfi-s&oslash;n of house Onnder interrogated Delsar while the latter was a prisoner in the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2606,1,to_timestamp('20-SEP-15 09.32.37.052000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Brothers'' Fountain, The',null,null,'An ancient fountain in the city of I&aring;rth, located atop Oddvar''s Way.  It was long ago drained of its water.  The empty pool is filled with fantastical shapes: shelled creatures of the deeps, mermaids, giant men holding pitted tridents, warriors astride strange hooved beasts. At its heart is a raised platform, suspended from great chains connected to statues at the pool’s periphery: great gods with the faces of animals. There is a raised dais on the platform, recessed at its center. That recess is empty but according to legend held a Star Gem for a short time.  Although the fountain was built in the time of Aljaberin, it is named for the sons of Hjalmar Tyr-s&oslash;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2608,1,to_timestamp('20-SEP-15 09.42.14.930000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tyrn',null,null,'A hurtt several days east of the city of I&aring;rth.  A great battle was fought here between two legions of the Iron Army and the shorn, but it was a ruse such that the Cobalt Legion could surprise Arastel''s forces at Vj&auml;kon and seize the Weapon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2610,1,to_timestamp('20-SEP-15 09.47.14.906000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ask&aring;',null,null,'An eastern hurtt of the kingdom of I&aring;rth, destroyed by the Weapon of Arastel.  Her walls had been shattered.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2611,1,to_timestamp('20-SEP-15 09.47.25.306000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arastel''s Weapon',null,null,'See the Weapon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2614,1,to_timestamp('20-SEP-15 09.54.16.524000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dj&auml;kol',null,null,'A word in the Iron Tongue meaning "d&aelig;n''shee."',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2615,1,to_timestamp('20-SEP-15 10.00.32.758000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eslr',null,null,'The guild haven of the Gandr, a library and museum located in the city of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2617,1,to_timestamp('20-SEP-15 01.58.08.042000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Merus do''Tawry',null,null,'An author, academic, and Shaper in the time of the Second &AElig;gan  Empire.  He fancied himself an archaeologist and historian and wrote extensive volumes on the First &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2619,1,to_timestamp('20-SEP-15 02.01.31.749000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valus Amphitheater',null,null,'An outdoor amphitheater located on the Sidus Siderum in the city of Demarge, where plays are performed for the wealthy and privileged.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2657,1,to_timestamp('23-SEP-15 10.50.49.338000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sundering, The',null,null,'One of many names applied to the cataclysm that struck at the end of the Great War during the Second &AElig;gan Empire.  The world was transformed and shattered, and most people were driven beneath the earth to find shelter.  The empire and its civilization never recovered.  This event is known by many names, including the Unforging (du&aring;rg), the Scouring (Demarge), the Havoc (Sho-renar), the Wrath (Drak-hnai), the Hecatombs (Malkar), the Hour of Heaven''s Shadow (Ashan), the Hundred Years of Sorrow (Her-lak Merg&ouml;l), the Burning Deluge (Ku-kuit), the Deluge (Ba-kwe), the Rancor (Serinin), the Avengment (kabaal), and the Defiling (elithi).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2841,1,to_timestamp('26-SEP-15 11.39.26.358000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aedan y Kam&icirc;l',null,null,'An elithin and lord of of Ryche Kam&icirc;l since the end of the Grey War.  He was next in line to be Tiaryche, but his house was stripped of its rank by Cyngen yr Orain due to the deeds of Gethin y Kam&icirc;l in the Grey War, the first and only time such a punishment has been levied.  He currently acts as servant and slave to Delsar, suffering daily torment.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2905,1,to_timestamp('05-OCT-15 08.28.20.531000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Treir der''Xylander',null,null,'A boy of the Taunland who was witness to many miracles, brother of Farauk der''Xylander and Etri der''Xylander.  He saw a leaf descend from the Above before encountering the izlie and a strange boy from a far-off land, Jerome.  Finally, he witnessed the Sphere fall into Aljerbach.  His current fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2917,1,to_timestamp('05-OCT-15 08.48.15.014000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Narth Steak',null,null,'Meat prepared from narth, considered a delicacy among the peoples of the Great Cavern.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2921,1,to_timestamp('05-OCT-15 08.56.49.821000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Five Years, The',null,null,'A period of mandatory military service among the Aljer.  So named not because it lasts five years but because there is a five year period (between seventeen and twenty-two) when it can be entered.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2928,1,to_timestamp('05-OCT-15 11.35.17.522000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Umbra, The',null,null,'A realm of shadow which can be manipulated and visited by the kiril.  It is likely a Fold.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2947,1,to_timestamp('06-OCT-15 08.01.18.647000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Quin Custore',null,null,'A sect of the Gamon Jug&aelig; who were dedicated to the preservation and study of ancient objects and artifacts, both mundane and magick.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2948,1,to_timestamp('06-OCT-15 08.03.02.319000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ansel der''Kaube',null,null,'A man of the Aljer who was for a short time imprisoned in the same cell as the na''k&ouml;r in Ajer-D&aelig;n.  He was murdered by Diarin yn Marel yn Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2949,1,to_timestamp('06-OCT-15 08.07.29.288000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ivy Throne, The',null,null,'The physical throne of Harn; or the current sovereign of Harn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1785,1,to_timestamp('31-AUG-15 10.04.44.741000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Charisi',null,null,'A clan of the cara-aleach, bound to Ryche Senyar.  When &AElig;lm&aelig;dris was built in the heart of Alune, the Charisi followed the line of kings, departing Chara to dwell in the new village of Aubin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1915,1,to_timestamp('04-SEP-15 09.21.47.828000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tonic, The',null,null,'An alternative term for Wizards'' Root.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1916,1,to_timestamp('04-SEP-15 09.21.57.498000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Black Tea, The',null,null,'An alternative term for Wizards'' Root.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1917,1,to_timestamp('04-SEP-15 09.22.07.239000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fools'' Elixir',null,null,'An alternative term for Wizards'' Root.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1918,1,to_timestamp('04-SEP-15 09.22.15.084000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shapers'' Venom',null,null,'An alternative term for Wizards'' Root.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1919,1,to_timestamp('04-SEP-15 09.24.25.487000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dreamroot',null,null,'The active ingredient in Wizards'' Root, originally found on the island of Tendron.  In its raw form, it is known as a mild narcotic and a cure for cramps and aches.  Also known as dreamseed (a more accurate description, since it is actually a seed).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1920,1,to_timestamp('04-SEP-15 09.24.45.795000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dreamseed',null,null,'An alternative label for dreamroot.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1881,1,to_timestamp('01-SEP-15 02.56.54.233000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unsung, The',null,null,'The name by which the Calikari refer to the elithi of Demarge, who do not suffer from the Withering and are not bound to u’Vidain e’Cuhlhair.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1788,1,to_timestamp('31-AUG-15 10.11.33.470000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Song',null,null,'A rare gathering in which elithi from multiple Songs join in a single Chorus.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1863,1,to_timestamp('01-SEP-15 12.45.56.439000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Caonwen',null,null,'One of two ancient Calikari villages renowned as a breeding ground for adur-&auml;l steeds.  It was abandoned after the adur-&auml;l were driven to extinction.  It lies two day''s walk from Muir Airgin, where colts bred in the village were traditionally birthed.  The elithi of &AElig;lm&aelig;dris settled in Caonwen afer Camryn''s Pride, but it was abandoned after they summoned Dubh Gaoth’s attentions during the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1866,1,to_timestamp('01-SEP-15 12.51.39.433000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hall of Tablets, The',null,null,'The meeting place of the Sh’hein’Rein and the ceremonial heart of the Bright Legions, designed and constructed by the famed Maker, Wenwryn yn Hwnwn.  It was located in Aduine and lost during the Gloaming. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1891,1,to_timestamp('01-SEP-15 03.18.42.001000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unclaimed Tombs, The',null,null,'An alternative term for Machuin Muil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1902,1,to_timestamp('03-SEP-15 10.44.22.593000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Basilisk, The',null,null,'An alternative name for Myrddin y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1433,1,to_timestamp('21-AUG-15 07.19.24.929000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nara do''Virt',null,null,'The wife and true love of Calik.  She was the Hierophant of Irien.  Nara died using the Laroch &Ucirc;l to restore Calik''s life after he and Az-Azaal killed each other in a mutual duel in Erinin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1912,1,to_timestamp('03-SEP-15 11.16.54.304000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Living Gift, The',null,null,'An alternative label for Drauth''n&icirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2347,1,to_timestamp('17-SEP-15 09.32.31.623000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sargarathi',null,null,'A term that sometimes appears among the dreams of the na''k&ouml;r.  It appears to refer to a group of people, but its meaning is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2349,1,to_timestamp('17-SEP-15 09.33.46.083000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yencari',null,null,'A term that sometimes appears among the dreams of the na''k&ouml;r.  It appears to refer to a group of people, but its meaning is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2625,1,to_timestamp('20-SEP-15 02.07.17.099000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eye of the Render, The',null,null,'A thing seen or imagined by Drolock''harka in a vision provided by the Archway.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2626,1,to_timestamp('20-SEP-15 02.09.28.033000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Land of the Dawn, The',null,null,'A place explored by Drolock''harka in a vision provided by the Archway.  In this place, he saw many things, including the river Verd, the city Tunir&aelig;on of the White Walls, the floating island Vindal&ucirc;n, the woman Karalyn, the man Go''avram, and other wonders.  The sun here appears to be rising from the west; but never actually changes position or sets.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2628,1,to_timestamp('20-SEP-15 02.17.17.105000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Inj&thorn;or Ymir-s&oslash;n',null,null,'A du&aring;rg and &thorn;engill, rikr of magnr  Khazin.  He perished during Arastel''s assault on the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2605,1,to_timestamp('20-SEP-15 09.22.43.271000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Oddvar''s Path',null,null,'Ancient stairs in I&aring;rth which trace their history back to Aljaberin, crafted of white stone and so old that they are worn with footprints, grooved and pocked like the surface of coral reef.  At their apex is the Brothers'' Fountain.  Although the path is named for an ancient du&aring;rg, the steps are sized for a d&aelig;n''shee.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2612,1,to_timestamp('20-SEP-15 09.49.14.706000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mite&aring;',null,null,'An eastern hurtt of the kingdom of I&aring;rth, destroyed by the Weapon of Arastel.  Her hills and walls had been shattered.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2616,1,to_timestamp('20-SEP-15 10.04.50.956000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Guild Haven',null,null,'The central location of one of the Grods of I&aring;rth, generally not open to outsiders.  Often, the Grod-rikr lives here.  They include Eslr, Sangf-havn, Gr&oacute;f, and Holfyfr.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2400,1,to_timestamp('17-SEP-15 07.04.43.781000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Folkvar Sijfinnur-s&oslash;n',null,null,'A du&aring;rg of magnr Undlflos, son of Sijfinnur Jaako-s&oslash;n and Ingjard Erica-d&oslash;tter, half-brother to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Honvir Sijfinnur-s&oslash;n, Signy Fylla-d&oslash;tter, and Flos Sijfinnur-s&oslash;n, brother to Mikhan Sijfinnur-s&oslash;n and Lell Ingjard-d&oslash;tter, half-brother to Valmiki Sijfinnur-s&oslash;n, and adopted brother to Hl&aelig;ja Egill-s&oslash;n.  Born Phaelen, he succumbed to prj&oacute;ta and eventually madness before his twenty-sixth birthday.  He was exiled to the depths of Ymir''s Abyss to die.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2942,1,to_timestamp('06-OCT-15 07.40.57.511000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Biara der''Klar',null,null,'A queen of the Aljer, wife of Davram der''Klar, mother of Jeril der''Klar, Medira der''Klar and Tavin der''Klar.  She and Davram were murdered during the fire-light by conspirators attempting to free Evram der''Baasmeir from Ajer-D&aelig;n, a deed that was later blamed on the na''k&ouml;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2950,1,to_timestamp('06-OCT-15 08.10.39.515000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Patriarch',null,null,'A high priest of one of the Greater Seven of the Pantheon of Aljerbach.  Recently, the Patriarchs have taken advantage of calamities in the Thrice Kingdom in order to seize power from the Aljerstraad and the king, blaming pestilence, war, drought, and the coming of the na''k&ouml;r on a failure of faith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2951,1,to_timestamp('06-OCT-15 08.12.01.005000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Harbingers, The',null,null,'A shortened form of Harbingers of Eta&uuml;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2952,1,to_timestamp('06-OCT-15 08.15.11.785000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Friend of the North',null,null,'Literal translation of "Aljerbach" in the tongue of the Aljer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2953,1,to_timestamp('06-OCT-15 08.17.22.961000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Travelers, The',null,null,'An alternate name for the Harbingers of Eta&ucirc;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2954,1,to_timestamp('06-OCT-15 08.18.43.205000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Changeling Stone',null,null,'An alternative name for a Shaping Stone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2955,1,to_timestamp('06-OCT-15 08.20.17.248000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Zaln',null,null,'A city of the Empire of Demarge, likely named after the ancient city of Xaln.  It is known as a place of great learning, containing the universities and libraries of the Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2956,1,to_timestamp('06-OCT-15 08.22.01.232000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dracol',null,null,'A city of the Empire of Demarge, known as the location of the great temples.  It is also the training ground of the Dun Mordra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2957,1,to_timestamp('06-OCT-15 08.23.32.048000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Farron',null,null,'A unit of currency in Aljerbach.  The denominations are:  farron, quin, shill, aljer, talon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2958,1,to_timestamp('06-OCT-15 08.23.45.159000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Quin',null,null,'A unit of currency in Aljerbach.  The denominations are:  farron, quin, shill, aljer, talon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2960,1,to_timestamp('06-OCT-15 08.24.05.774000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Talon',null,null,'A unit of currency in Aljerbach.  The denominations are:  farron, quin, shill, aljer, talon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2962,1,to_timestamp('06-OCT-15 08.32.44.758000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tin',null,null,'A unit of currency in Alanon which has been adopted as common coinage throughout the First Domain.  The denominations are cup, tin, and gallon, which roughly correspond to the value of a corresponding volume of water drawn from a kbai-spring.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2963,1,to_timestamp('06-OCT-15 08.32.51.000000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gallon',null,null,'A unit of currency in Alanon which has been adopted as common coinage throughout the First Domain.  The denominations are cup, tin, and gallon, which roughly correspond to the value of a corresponding volume of water drawn from a kbai-spring.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2964,1,to_timestamp('06-OCT-15 08.33.06.341000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Coins of Alanon',null,null,'See cup, tin, and gallon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2966,1,to_timestamp('06-OCT-15 08.39.40.939000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Thrice three is nine, and nine is thrice three',null,null,'Literal translation of S&oelig;n ri s&oelig;n sehr-in, sehr s&oelig;n s&oelig;n-ni.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2968,1,to_timestamp('06-OCT-15 08.42.03.702000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eastern Path, The',null,null,'The Western Path as known by the peoples of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2969,1,to_timestamp('06-OCT-15 08.56.41.294000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ik''fah lurien e''talre thor jaal',null,null,'Words in the tongue of the corti/kiril meaning "I hear what the shadows hear."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2970,1,to_timestamp('06-OCT-15 08.57.12.372000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'I hear what the shadows hear',null,null,'Literal translation of Ik''fah lurien e''talre thor jaal in the tongue of the corti/kiril.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2971,1,to_timestamp('06-OCT-15 08.57.46.134000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'I see what the shadows see',null,null,'Literal translation of Ik''fah lurien e''misere thor jaal in the tongue of the corti/kiril.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1903,1,to_timestamp('03-SEP-15 10.53.22.120000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aruth''eras y Senyar',null,null,'The final high king of united Calikar, his death to the Dark Herald marked the coming of the Gloaming and the loss of Alune to Lluagh Maguin.  Outside of the Dark Path, he is regarded as weak, ineffectual king, but within the Gloam, he is seen as a great sovereign, second only to Calik Twiceborn.  The Gloam is seen as a desperate strike by Eshrella Dao''Ast in response for the actions of Aruth''eras, whom they believe was near breaking the power of Kailar.  Also called Breah-heod, meaning the Great King, and the Shadowspur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1956,1,to_timestamp('05-SEP-15 12.09.26.772000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Myramaal',null,null,'An ancient city of the corti, located near modern-day Harn.  It was abandoned after the corti were granted the lands of Sorain in the Land Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2305,1,to_timestamp('16-SEP-15 10.26.39.956000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;&ucirc;l',null,null,'An honorific suffix applied to the name of a &thorn;engill, for example Einmyri-&thorn;&ucirc;l.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2319,1,to_timestamp('16-SEP-15 11.19.57.000000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hamarr',null,null,'A ceremonial battle hammer carried by the fell-drengr.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2353,1,to_timestamp('17-SEP-15 09.41.53.860000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Skjoldr',null,null,'The eastern fortresses of the kingdom of I&aring;rth, build to defend against the coming of the ki''tahj.  Those who serve here expect to die, and it is considered a great honor among the Iron Army.  Their duty is to summon the Tears of Urag before their deaths.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2632,1,to_timestamp('23-SEP-15 08.53.25.702000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Koloch',null,null,'A word in the tongue of the Dar''lak meaning "Strong."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2202,1,to_timestamp('14-SEP-15 09.45.40.637000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tiracles',null,null,'An alias used shortly by Malnos while he traveled to the Grey Tower aboard the White Gull.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2335,1,to_timestamp('17-SEP-15 08.38.34.341000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grod-a&eth;r',null,null,'According to legend, the fifteen du&aring;rg who were present when the Eyes of Phaelen were retrieved from their vault in Ska&aring;n-Ule&aring;.  They became the first to experience the Grod and the first Grok-rikr.  For their service, the Phaelen Amlaith-s&oslash;n offered them the gift of immortality by transforming them into the Grodinn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1790,1,to_timestamp('31-AUG-15 10.17.14.887000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shar''ris y Rhuandedd',null,null,'A city founded by the survivors of Rhuanedd after Gysioch''s Folly resulted in the destruction of Asmaraghaide.  To prevent future discovery by Dubh Gaoth from resulting in the complete ruin of their people, only a small portion of the population was present in the city at any given time, and the Song was always performed in small Choruses.  They became a divided people, divided into dozens of migrant villages.  Literally, "Shadow of Rhuanedd."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2361,1,to_timestamp('17-SEP-15 02.11.04.310000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cal-ganr',null,null,'The great cemetery of the city of I&aring;rth:  a collection of cairns, mausoleums, graves, and tombs set among twisting hills and valleys.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2368,1,to_timestamp('17-SEP-15 02.26.50.297000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silent Chair, The',null,null,'The symbolic chair/throne of Phaelen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2421,1,to_timestamp('18-SEP-15 03.35.10.237000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hjalmar Tyr-s&oslash;n',null,null,'A du&aring;rg, son of Tyr Gorbund-s&oslash;n, wife to Skoldjis Unn-Hild-d&oslash;tter, and father to twenty-four sons, including Amlaith Hjalmar-s&oslash;on, Oddvar Hjalmar-s&oslash;n, and Erland Hjalmar-s&oslash;n.  All the magnr of I&aring;rth claim to trace their lineage back to Skoldjis and Hjalmar; and through them to Khijak the Khazjad, their ancestor.  Hjalmar was born on the plains of Bagr-Danr and was the leader of the Iron Born when they arrived in Yjalmar.  He perished before his people came to the Iron Kingdom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2621,1,to_timestamp('20-SEP-15 02.03.59.639000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Verd',null,null,'A green river imagined by Drolock''harka in a vision provided by the Archway.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2805,1,to_timestamp('25-SEP-15 08.05.22.337000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arduen yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Son of Calin yr Orain,  father to Carac&ucirc;l yr Orain, and brother to Nerrwn yn Orain.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2809,1,to_timestamp('25-SEP-15 08.10.35.257000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sharwych yr Orain',null,null,'An elithin, former Tiaryche of Ryche Orain, and father of Irienya yn Orain.  He was the last lord of his Ryche before they became the kings of Rolar. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2813,1,to_timestamp('25-SEP-15 08.15.46.803000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bathwrwy yn Orain',null,null,'An elithin and former lady of Ryche Orain.  Mother to Arianrhod yn Orain and Search yn Orain and daughter to Nerrwn yn Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2423,1,to_timestamp('18-SEP-15 03.49.19.638000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Yjalmar',null,null,'The first kingdom of the du&aring;rg in the Great Cavern, located near Myramaal and modern-day Harn.  It was abandoned after the du&aring;rg were granted the lands of the Iron Kingdom in the f&aelig;&eth;a.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2439,1,to_timestamp('18-SEP-15 04.45.36.973000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Feldr',null,null,'Formal cloaks worn by the lords of the magnr in I&aring;rth.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2398,1,to_timestamp('17-SEP-15 06.59.19.838000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Folkvar Zaan-s&oslash;n',null,null,'A du&aring;rg boy, disinherited child of magnr Undlflos, drekka, son of Valkari Rowan-d&oslash;tter and Zaan Nafalti-s&oslash;n, elder (twin) brother to Hl&aelig;ja Zaan-s&oslash;n.  His mother died in his birth, and he was named by his grandmother, Rowan Fylla-d&oslash;tter in honor of one of her brothers, before she too died shortly thereafter.  Now an orphan, he became a slave and was taken a young boy to the Mines of Orth, where he toiled with his brother for five years.  Eventually, he was rescued by Durgin, who named him first skyldr and then &thorn;egn alongside his brother.  He was fatally wounded by the Albino, then given a merciful death by Diarin yn Marel yn Ast during Arastel''s assault on the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2635,1,to_timestamp('23-SEP-15 09.31.01.302000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Amlaith Hjalmar-s&oslash;n',null,null,'A du&aring;rg, youngest of twenty-four sons of Hjalmar Tyr-s&oslash;n and Skoldjis Unn-Hild-d&oslash;tter, and father of the first Phaelen. Born in the time of Yjalmar, Amlaith was one of three leaders of the du&aring;rg at the time of the f&aelig;&eth;a.  With his brother, Oddvar Hjalmar-s&oslash;n, he embraced warfare with the d&aelig;n''shee and was committed to occupying their former capital, Aljaberin.  His eldest brother, Erland Hjalmar-s&oslash;n, however, departed Aljaberin and constructed a hidden city, Sk&aring;n-Ule&aring;, which he intended to keep separate from the war.  Many of his people followed him, including those considered the most educated and skilled.  Subsequently, the war went poorly for the du&aring;rg, and many laid blame on Erland, believing that he had taken the secrets of his people with him into exile.  Oddvar and Amlaith took his son, Gandath Erland-s&oslash;n hostage, resulting in the boy''s death.  This resulted in Erland closing the Veiled City altogether to his people.  Eventually, Amlaith and Oddvar faked their own deaths to draw Erland out of hiding.  Erland and Oddvar died in the ensuring conflict, with Amlaith becoming the first &thorn;engill-rikr.  Many treasures were found in Sk&aring;n-Ule&aring;, including the Eyes of Phaelen.  Amlaith is credited as the father of the first and greatest Phaelen and the father of the Grod, the man du&aring;rg who created the Iron Born from his people (although the name was adopted later).  Also known as the First King.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3080,1,to_timestamp('10-OCT-15 01.51.16.857000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Murrbach',null,null,'A village of Aljerbach that was hard stricken by the recent pestilence and deep-drought.  Its people regard Minoch al''Fainar, who provided them with a salve to to cure their Kodras'' thinning hides, with the salvation of their town. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3081,1,to_timestamp('10-OCT-15 01.52.27.638000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Haalach',null,null,'A town of the kingdom of Aljerbach whose primary trade is the cultivation of choca for the use in purifying edelberry wine.  Their crops and worms were dying to the deep-drought before the intervention of Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3082,1,to_timestamp('10-OCT-15 01.53.14.263000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gallen',null,null,'A farlo boy of Aljerbach.  Durgin watched his Naming ceremony in a burnt out temple.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3119,1,to_timestamp('12-OCT-15 01.03.55.926000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kar-wood',null,null,'A breed of fungal wood endemic to the First Domain, known for its strength and sheen.  It is harvested in very few places, among them the forest of Vi&eth;r-mork in I&aring;rth.  Furniture crafted from kar-wood is considered extravagant and a sign of great wealth.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3005,1,to_timestamp('08-OCT-15 10.42.11.329000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Edelberry',null,null,'A berry (actually, a fungal seed) endemic to the First Domain which is used in the preparation of wine.  When grown using pure water, edelberry is not afflicted by the Grim.  Unfortunately, it carries its own toxin, which is equally fatal to humans.  The peoples of the Domain long ago discovered that this taint could be removed using choca, a type of flat worm.  The cultivation of edelberry, and the process by which it is converted to wine, is a carefully guarded secret of House Baasmeir.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1560,1,to_timestamp('23-AUG-15 06.47.09.248000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;lm&aelig;dris',null,null,'One of the Eight Pillars of Calikar, located deep in the heart of Alune.  It was the city of the high kings and the heart of the power of Ryche Senyar since the time of Thierin y Senyar.  Its Holy Grove was Sung by Leuen y Senyar and was called Diris M&aelig;rysh.  Its Great Tree was Calithar, which was located in the plaza outside the Elod&aelig;ne Palace rather than in the heart of the grove.  Its Hall of Song was Callain.  The city was lost to the Gloaming, and its survivors fled to Muir Auread, Caonwen, and eventually to ka-&AElig;lm&aelig;dris.  The city was recently reclaimed.  Also called Vieun Istue and the City of Kings.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2188,1,to_timestamp('11-SEP-15 02.10.24.660000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Callain',null,null,'The Hall of Song in &AElig;lm&aelig;dris, abandoned since the Gloaming. It was significantly larger and more elaborate than the other halls of Calikar, intended to hold the Great Song when necessary.  It was the first work of K&aelig;ryeah yr Inythyn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1853,1,to_timestamp('01-SEP-15 12.13.17.463000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ruel-uchain',null,null,'A district dedicated to the Eshthara in Rhuanedd, once the heart of their order.  It was comprised of both standard and inverted towers covered with glass-stone.  It has been abandoned since the Gloaming.  Also known as the Deep Towers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1779,1,to_timestamp('31-AUG-15 09.53.18.739000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Marylla',null,null,'A woman of the corti, likely the spouse or lover of Kisel Gres''Mehar according to his dreams, and sister to Sarylla.  She perished during the War of the Final Dawning.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2362,1,to_timestamp('17-SEP-15 02.13.10.249000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grodhol',null,null,'A district of I&aring;rth where du&aring;rg go to indulge in their Longing.  There are different tunnels, buildings, and plazas devoted to varying Grod.  This is an accepted part of Iron Born society and is not considered lapsing into  grodphyr.  Nonetheless, most du&aring;rg consider the Grodhol shaming and conceal their identities when visiting the district.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2367,1,to_timestamp('17-SEP-15 02.25.28.903000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seneschal',null,null,'A du&aring;rg bound to the service of the Mountain Home and the &thorn;engill as a whole via the Iron Oath.  They are universally selected from among the Drekka.  By law, the seneschals are not bound to specific magnr or persons, but this law is not universally observed.  (The servants of individual thanes are known as the Skyldr; or Sworn.)',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1858,1,to_timestamp('01-SEP-15 12.28.14.791000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Nuchalan',null,null,'The Great Tree of Rhuanedd, seedling of D&ucirc;n Mile’&ucirc;r, located in Diris Rhuan.  It perished during the Gloaming, and its seed gave root to D&ucirc;n Nuchalan in Shar''ris y Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2629,1,to_timestamp('20-SEP-15 08.55.43.781000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Signy Alva-d&oslash;tter',null,null,'A du&aring;rg of magnr Undlflos, daughter of Fylla Alva-d&oslash;tter, sister to Lyall Sijfinnur-s&oslash;n, Flos Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, and Honvir Sijfinnur-s&oslash;n, half-brother to Mikhan Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, and adopted brother to Hl&aelig;ja Egill-s&oslash;n.  She was born when her mother was unwed, some believe fathered by Sijfinnur.  She was only a small child during during the hunt of Durgin''s twenty-sixth birthday.  She died short years later, killed alongside her mother in an attempt on the life of her brother, Honvir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2636,1,to_timestamp('23-SEP-15 09.31.44.999000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'First King, The',null,null,'An alternative name for Amlaith Hjalmar-s&oslash;n, the first &thorn;engill-rikr of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2638,1,to_timestamp('23-SEP-15 09.34.37.511000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hidden City, The',null,null,'An alternative name for Sk&aring;n-Ule&aring;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2440,1,to_timestamp('18-SEP-15 04.47.55.337000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tala''dana',null,null,'"He Who Ruled from the Fore" in the Pure Tongue.  A term used by the Shugda to describe a manner of general.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2624,1,to_timestamp('20-SEP-15 02.06.16.173000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vindil&ucirc;n',null,null,'A floating island seen by Drolock''harka in a vision provided by the Archway.  It lies above the city known as Tunir&aelig;on of thee White Walls.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2943,1,to_timestamp('06-OCT-15 07.45.44.003000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Davram der''Klar',null,null,'A king of the Aljer, husband of Biara der''Klar, father of Jeril der''Klar, Medira der''Klar and Tavin der''Klar.  He and Biara were murdered during the fire-light by conspirators attempting to free Evram der''Baasmeir from Ajer-D&aelig;n, a deed that was later blamed on the na''k&ouml;r.  Prior to his death, Davram''s reign was mixed. Loved and respected by many as a measured sovereign and war hero, others held him to be a weak king:  responsible for the Harbingers, the failures of the War of the Low Throne and the ensuring tribute, the deep drought, and the pestilence/famine of recent seasons.  The patriarchs of the great churches and the Aljerstraad held greater power under his reign than under any earlier king.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2944,1,to_timestamp('06-OCT-15 07.55.06.272000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yem Xocar',null,null,'An organization dedicated to the preservation of language in its spoken and written form.  Originally a sect of the Gamon Jug&aelig;, the Yem Xocar evolved into a separate entity during the Second &AElig;gan Empire.  For a time, there were two organizations bearing the same name.  Two factions of the Yem Xocar were the Yem Tuul and the Yem Nos. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2945,1,to_timestamp('06-OCT-15 07.57.50.526000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yem Tuul',null,null,'A sect of the Gamon Jug&aelig; and subsect of the Yem Xocar, the Yem Tuul were dedicated to the preservation of the Earth Song.  This was an academic institution and most keepers did not possess the Gift of the Song.   Originally a sect of the Gamon Jug&aelig;, the Yem Tuul evolved into a separate entity during the Second &AElig;gan Empire.  For a time, there were two organizations bearing the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2639,1,to_timestamp('23-SEP-15 09.42.01.960000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sk&aring;n-Ule&aring;',null,null,'A hidden city created by Erland Hjalmar-s&oslash;n shortly after the f&aelig;&eth;a.  Erland was one of three leaders of the du&aring;rg at this time, along with his brothers, Amlaith Hjalmar-s&oslash; and Oddvar Hjalmar-s&oslash;n, and, unlike his siblings, wanted no part of the war with the d&aelig;n''shee.  He withdrew into seclusion with many of the wisest of his people, along with their great artifacts and secrets.  Sk&aring;n-Ule&aring; was considered a place of great wonder and magick, veiled with secrets descended from Khijak the Khazjad and gifted by the corti.  For a time, it was open to all du&aring;rg, but after Erland was betrayed by his brothers (his son, Gandath Erland-s&oslash;n, was taken hostage to force his hand in the war), it was closed to all.  Eventually, Amlaith and Oddvar faked their own death to move Erland from hiding.  In the ensuing conflict, Oddvar and Erland perished as well as many of the lords of Sk&aring;n-Ule&aring;.  The city was sacked and its treasures stolen, including the Eyes of Phaelen.  The current fate and location of the city are unknown, although it is suspected to be nothing more than a ruin.  The du&aring;rg have been a single people since its fall, united by the Grod, the &thorn;engill, and the &thorn;engill-rikr (of whom Amlaith was the first), which institutions were born in the aftermath.  Also called the Veiled City and the Hidden City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1781,1,to_timestamp('31-AUG-15 10.00.52.889000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unfinding, The',null,null,'A spell or wyrd laid over the kingdom of Sorain which rendered it undiscoverable by its enemies.  Even those guided to the city forgot the path afterwards.  The kings of Calikar were gifted with the Amity Rings which possessed the power to pierce the Unfinding.  Also referred to as the Grey Maze.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1718,1,to_timestamp('29-AUG-15 09.52.17.288000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Durgin',null,null,'Perhaps one of the na''k&ouml;r and a du&aring;rg.  Durgin is a special case in that he does not seem to be protected by the lih''tin, he possesses his memories excepting the last year, and that he did not descend in a Sphere.  He dreams as Johra Kail, who later became Khijak the Khazjad, progenitor and hero to the Iron Born.  Born as  Hl&aelig;ja Egill-s&oslash;n, son of Egill Modi-s&oslash;n (his mother died in his birth).  Possessing the symbol of the Streichen, he would have been sacrificed except that he did not manifest any other symptom of the Twisted Ones.  He was also Phaelen and eventually came to be the ward of the magnr of Undlflos, who had another son who was Phaelen.  Eventually, Durgin''s pride ended in the death of his adopted brother and father, and he fled in shame into the west, leaving those behind to assume his death.  For a time, he succumbed to the Grod, becoming a brutal warlord in Quaul, but was "saved" by Sayori (Yori) and became a warrior of the Aq''Mar''Shein in Demarge, where he served for over a century.  When she died, she began appearing to him in visions.  This eventually led him to seek out the na''k&ouml;r in Aljerbach and later to return to I&aring;rth.  He is still waiting for the Sign to show him what he must do.  In his youth, Durgin was known as the Softheart, particularly among his adopted siblings and Rowan Fylla-d&oslash;tter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1815,1,to_timestamp('01-SEP-15 08.01.56.986000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Amranwen yr Inythyn',null,null,'An elithin account first of the Makers and also learned in the arts of the Eshthara.  Born in the Long, Short Years, he did not live long but in his time crafted Huynduir and Tyrin as well as developed the art by which luer is transformed to i-luer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2189,1,to_timestamp('11-SEP-15 02.45.30.144000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jan&icirc;l Hwe''leri',null,null,'One of two rulers of the corti, who apparently reigned for many centuries in the city of Sorain.  He was brother to Kisel Gres''Mehar and husband to Sarylla.  His fate after the War of the Final Dawning is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2685,1,to_timestamp('23-SEP-15 09.38.35.357000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Njorthrbiatr Morten-s&oslash;n',null,null,'An ancient  du&aring;rg and lord of magnr Undlflos, former &thorn;engill-rikr of I&aring;arth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2640,1,to_timestamp('23-SEP-15 09.42.52.971000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wishing Stones, The',null,null,'An alternative name for the Eyes of Phaelen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2641,1,to_timestamp('23-SEP-15 09.46.35.417000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sveinn Ulmer-s&oslash;n',null,null,'A du&aring;rg seneschal encountered by the na''k&oslash;n in the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2644,1,to_timestamp('23-SEP-15 10.23.54.354000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Weeping Eye, The',null,null,'One of a pair of artifacts known as the Eyes of Phaelen, so named because it is cracked and drips with grey "tears."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2645,1,to_timestamp('23-SEP-15 10.24.15.280000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Bright Eye, The',null,null,'One of a pair of artifacts known as the Eyes of Phaelen, so named because it it is whole.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2646,1,to_timestamp('23-SEP-15 10.36.39.868000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ter Anai',null,null,'A Lodge at the University of Xaln during the Second &AElig;gan Empire, associated with the healing arts.  It was modeled after the Ter Graz Daan, and many of the greatest servants of that order were drawn from its ranks.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2647,1,to_timestamp('23-SEP-15 10.40.18.917000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Unforging, The',null,null,'An alternative name for the Sundering, used by the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2648,1,to_timestamp('23-SEP-15 10.41.16.118000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Scouring, The',null,null,'An alternative name for the Sundering, used by the men of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2649,1,to_timestamp('23-SEP-15 10.41.35.083000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Defiling, The',null,null,'An alternative name for the Sundering, used by the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2650,1,to_timestamp('23-SEP-15 10.41.49.166000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Havoc, The',null,null,'An alternative name for the Sundering, used by the Sho''renar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2651,1,to_timestamp('23-SEP-15 10.42.01.903000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wrath, The',null,null,'An alternative name for the Sundering, used by the Drak-hnai.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2652,1,to_timestamp('23-SEP-15 10.42.15.829000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hecatombs, The',null,null,'An alternative name for the Sundering, used by the Malkarans.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2653,1,to_timestamp('23-SEP-15 10.43.08.965000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hundred Years of Sorrow, The',null,null,'An alternative name for the Sundering, used by the men of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2654,1,to_timestamp('23-SEP-15 10.43.39.129000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hundred Years, The',null,null,'A shortened form of "The Hundred Years of Sorrow," an alternative name for the Sundering used by the men of Her-lak Merg&ouml;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2655,1,to_timestamp('23-SEP-15 10.44.13.211000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Burning Deluge, The',null,null,'An alternative name for the Sundering, used by the men of the Ba-kwe.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2656,1,to_timestamp('23-SEP-15 10.46.42.661000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Deluge, The',null,null,'An alternative name for the Sundering, used by the men of the Ku-kuit.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2658,1,to_timestamp('23-SEP-15 10.52.05.655000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Hour of Heaven''s Shadow, The',null,null,'An alternative name for the Sundering, used by the men of Ashan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2659,1,to_timestamp('23-SEP-15 10.54.29.227000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rancor, The',null,null,'An alternative name for the Sundering, used by the men of Serinin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2660,1,to_timestamp('23-SEP-15 10.54.48.323000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Avengment, The',null,null,'An alternative name for the Sundering, used by the kabaal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2643,1,to_timestamp('23-SEP-15 10.22.27.347000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eyes of Phaelen, The',null,null,'Artifacts and likely Shiahd or Tal''Shiahd possessed by the du&aring;rg.  Their origin is unclear:  some believe they were preserved from Llua''la by Khijak the Khazjad.  Some believe that they are in fact the eyes of Khijak preserved.  Others believe they were gifts from the corti.  Discovered among the ruins of Sk&aring;n-Ule&aring; after the betrayal and murder of Erland Hjalmar-s&oslash;n, it is believed that the Eyes of Phaelen possess the power to fulfill wishes (one each) and are the original source of the Grod, their answer to Amlaith Hjalmar-s&oslash;n''s desire to save his people from the d&aelig;n''shee.<br/><br/>In appearance, the Eyes are worn grey spheres the size of a woman''s fist, slightly oblong like a stone egg. White traces of mineral run over the surface without pattern, suggesting veins. Ancient runes are carved in tiny, elegant script.  One of the two Eyes is cracked and oozes gray droplets which appear like tears.  It is known as the Weeping Eye (and is thought to be the source of the wish that brought the Grod).  They other is called the Bright Eye.<br/><br/>After Sk&aring;n-Ule&aring;, the Eyes were added for a time to Iron Crown, then built into the statue of Khijak in S&aelig;m&eml; Khazjadi, then returned to the crown.  In the time of Ymir Ymin-s&oslash;n, the Weeping Eye was lost, falling into the heart of the Mountain Home.  According to legend, the Atrium is Spoken deeper every year in search of it.  The Bright Eye came into the possession of magnr Khazin and was invoked by Durgin during the War of Seven Kingdoms.  To his surprise, it shattered, and a naked man appeared in its place, the Albino.  Shortly thereafter, the Grod abandoned his people.  Also known as the Wishing Stones.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2939,1,to_timestamp('06-OCT-15 07.35.23.193000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Medira der''Klar',null,null,'A princess of the Aljer, daughter of Davram der''Klar and Biara der''Klar, sister of Jeril der''Klar and Tavin der''Klar.  She was taken hostage by the na''k&ooml;r and for a short time accompanied them before being murdered by Diarin yn Marel yn Ast.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1728,1,to_timestamp('29-AUG-15 10.55.11.953000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eliryndil yn Orain',null,null,'One of the na''k&ouml;r, daughter of Cynyr yr Orain and Arianrhod yn Orain, sister to Cyngen yr Orain and Diarin yn Marel yn Ast.  Eliryndil''s Sphere fell in Aljerbach, but it was a separate Sphere from the one which carried the Harbingers of Eta&ucirc;r.  In Aljerbach, she sought the Harbingers for a time, suffering periodic states of fugue.  Eventually, she awoke in the house of Garani without any clear memory of this time period.  Eliryndil dreamed as Elaina J&aelig;s''n&aelig;, who later became the Mortuani, Kitira.  Born as Eliryndil yn Orain, she was kidnapped on her first birthday and, according to her dreams, came to be raised among the Die Nearal in the city of Demarge, herself becoming an acolyte of Baragur.  She lived her life under an illusion, a Mask of Mirrors so perfect that it responded to changes in her environment.  She and others believed herself to be a human with green eyes and auburn hair.  Under this guise, she was known as Amber (and she continued to refer to herself by this name even after the truth was known).  Later, in the company of the na''k&ouml;r, she returned to Rolar and, led by visions of the future she beheld in the Archway, murdered D&ucirc;n Mile''&ucirc;r and the Song, effectively dooming her own people.  It was only then that her Mask was broken, revealing her identity.  Eliryndil died in the Mountain Home, her life claimed by the touch of Anlocht.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2336,1,to_timestamp('17-SEP-15 08.53.53.743000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Droginn',null,null,'The Droginn are a collection of fifteen obelisks found in S&aelig;m&eth; Khazjadi in the Mountain Home in I&aring;rth.  Six are set against the western wall, six against the eastern, and three against the northern.  They face inward toward the Circle.  According to legend, they were crafted by Amlaith Hjalmar-s&oslash;n and later imbued with the souls of the Grodinn by Phaelen Amlaith-s&oslash;n.  They are now used to mark members of their respective Grods.  In appearance, they are great, twisted pillars of iron embedded with iridescent minerals.  According to Durgin, "They varied in subtly in size and arch.  They suggested a shape that was not there, a reflection of an iron body.  They suggested movement that must have been his imagination.  It seemed that they were on the verge of sprouting arms or legs, and each had a pair of jewels embedded in its surface. Those gems were all sizes and shapes. Some were set side by side like glittering eyes and some were set far apart.  They seemed to turn, to watch him."  In the presence of one marked by their Grod, the cracks of the crystal eyes become veins, and they shed tears of "blood and fire."  Since the time of Ymir Ymin-s&oslash;n, it has been Iron Law that all du&aring;rg must be judged by the Droginn, but this law is only enforced with respect to the sons and daughters of the magnr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2661,1,to_timestamp('23-SEP-15 11.00.33.057000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Einmyria Alva-d&oslash;tter',null,null,'A du&aring;rg woman, esteemed Speaker and paragon of the Hammar, and member of the house of Asfird.  Although not the creator of the ghola, her life''s work was their refinement, including imbuing them with the blood of the d&aelig;n''shee to grant them directed purpose and limited autonomy.  Last seen by Diarin yn Marel yn Ast during Arastel''s assault on the Mountain Home, her fate is uncertain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2637,1,to_timestamp('23-SEP-15 09.34.16.431000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Veiled City, The',null,null,'An alternative name for Sk&aring;n-Ule&aring;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2627,1,to_timestamp('20-SEP-15 02.13.15.189000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dagur Dagur-s&oslash;n',null,null,'A du&aring;rg and &thorn;engill, eldest of that body at the time of Arastel''s assault on the Mountain Home, rikr of magnr  F&oacute;lk.  He perished during the assault.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2803,1,to_timestamp('25-SEP-15 07.58.38.198000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eldeain yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Husband of Athorea yn Dalornmn, son of Carac&ucirc;l yr Orain and B&aelig;rin yn Nyamn, brother of Brandynn yr Orain,  and father to Cynyr yr Orain.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2420,1,to_timestamp('18-SEP-15 03.32.03.688000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Skoldjis Unn-Hild-d&oslash;tter',null,null,'A du&aring;rg, husband to Hjalmar Tyr-s&oslash;n, and mother to twenty-four sons, including Amlaith Hjalmar-s&oslash;n, Oddvar Hjalmar-s&oslash;n, and Erland Hjalmar-s&oslash;n.  All the magnr of I&aring;rth claim to trace their lineage back to Skoldjis and Hjalmar; and through them to Khijak the Khazjad, their ancestor.  Also known as the Great Mother.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2424,1,to_timestamp('18-SEP-15 03.54.12.917000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'F&aelig;&eth;a',null,null,'The name by which the du&aring;rg refer to the Land Gift.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2427,1,to_timestamp('18-SEP-15 04.16.36.951000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seffi',null,null,'The mere''marg of Johra Kail according to the dreams of Durgin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2804,1,to_timestamp('25-SEP-15 08.03.14.339000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Carac&ucirc;l yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Husband of B&aelig;rin yn Nyamn, son of Arduen yr Orain,  and father to Eldeain yr Orain and Brayndynn yr Orain.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2807,1,to_timestamp('25-SEP-15 08.07.50.571000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Garuen yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Son of Irienya yn Orain,  father to Calin yr Orain, and husband to Iliana yn Orain.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2946,1,to_timestamp('06-OCT-15 07.58.56.136000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yem Nos',null,null,'A sect of the Gamon Jug&aelig; and subsect of the Yem Xocar, the Yem Nos were dedicated to the preservation of the True Tongue.  This was an academic institution and most keepers did not possess the Gift of Naming.   Originally a sect of the Gamon Jug&aelig;, the Yem Nos evolved into a separate entity during the Second &AElig;gan Empire.  For a time, there were two organizations bearing the same name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2959,1,to_timestamp('06-OCT-15 08.23.52.884000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shill',null,null,'A unit of currency in Aljerbach.  The denominations are:  farron, quin, shill, aljer, talon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2961,1,to_timestamp('06-OCT-15 08.32.33.857000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cup',null,null,'A unit of currency in Alanon which has been adopted as common coinage throughout the First Domain.  The denominations are cup, tin, and gallon, which roughly correspond to the value of a corresponding volume of water drawn from a kbai-spring.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2965,1,to_timestamp('06-OCT-15 08.33.37.560000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Coins of Aljerbach',null,null,'See farron, quin, shill, aljer, and talon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2967,1,to_timestamp('06-OCT-15 08.39.41.959000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'S&oelig;n ri s&oelig;n sehr-in, sehr s&oelig;n s&oelig;n-ni',null,null,'Words spoken by an apparition of Natalie after her death, meaning literally, "Thrice three is nine, and nine is thrice three."  The significance of these words is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2972,1,to_timestamp('06-OCT-15 08.58.16.270000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ik''fah lurien e''misere thor jaal',null,null,'Words int eh tongue of the corti/kiril meaning "I see what the shadows see."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2337,1,to_timestamp('17-SEP-15 09.09.15.073000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'S&aelig;m&eth; Khazjadi',null,null,'A great and sacred hall/temple located in the Mountain Home and crafted from llearth.  It measures nearly three hundred spans in its length and half that in its width.  It contains the Droginn, the statue of Khazjad, and the Circle.  The Weapon of Arastel was kept here during the War of Seven Roofs, as was Anlocht after it was confiscated from Cyngen yr Orain.  Eliryndil yn Orain perished here, sacrificing herself to destroy the Weapon by embracing Calik''s blade.  Also known as Khazjad''s Honor and Khazjad''s Hall.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2338,1,to_timestamp('17-SEP-15 09.10.31.552000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Khazjad''s Hall',null,null,'An alternative name for S&aelig;m&eth; Khazjadi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2341,1,to_timestamp('17-SEP-15 09.13.33.168000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pit, The',null,null,'And alternative name for the Champions'' Theater in I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2342,1,to_timestamp('17-SEP-15 09.14.48.558000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Streichen',null,null,'An alternative term for the Twisted Ones, employed among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2345,1,to_timestamp('17-SEP-15 09.27.55.235000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nameless Temple, The',null,null,'A temple at the heart of Diris Alonne in Rolar, in the clearing known as Ilur&icirc;n, built atop the Tomb of Calik.  It was built by Peredur y Senyar and is the home of the Al&euml;''uriel Caz''ur Ve''&euml;ri.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2346,1,to_timestamp('17-SEP-15 09.30.23.149000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pass of Servants, The',null,null,'A street/plaza in Xaln, surrounded by buildings, towers, and sky bridges that touched the clouds.  Prospective students gathered here on their day of induction (into the University of Xaln) during the Second &AElig;gan empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2348,1,to_timestamp('17-SEP-15 09.32.38.582000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sargarath',null,null,'A term that sometimes appears among the dreams of the na''k&ouml;r.  It appears to refer to a place, but its meaning is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2350,1,to_timestamp('17-SEP-15 09.33.58.576000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yencour',null,null,'A term that sometimes appears among the dreams of the na''k&ouml;r.  Its meaning is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2352,1,to_timestamp('17-SEP-15 09.37.57.747000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Citadel, The',null,null,'An alternative name for Magor-Balkr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2355,1,to_timestamp('17-SEP-15 09.46.31.004000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lone Isle, The',null,null,'An alternative name for Kardu&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2356,1,to_timestamp('17-SEP-15 09.48.13.770000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gnar-fly',null,null,'An insect endemic to the First Domain.  They are known to burrow into small animals and corpses, draining them of fluids.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2357,1,to_timestamp('17-SEP-15 09.49.14.562000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'God, The',null,null,'The name by which Arastel, and his followers, refer to him.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2358,1,to_timestamp('17-SEP-15 09.51.46.197000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'&THORN;engill-gar&eth;r',null,null,'An abstract term for an area or thing reserved for the &thorn;engill.  One example is a seating area in the Champions'' Theater.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2359,1,to_timestamp('17-SEP-15 02.06.49.821000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Einmyri Aldolf-s&oslash;n',null,null,'The current head of Magnr Haldororf and one of the &thorn;engil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2360,1,to_timestamp('17-SEP-15 02.08.24.092000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Khazjad''s Path',null,null,'The western gate of the city of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2363,1,to_timestamp('17-SEP-15 02.17.28.591000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silent Mother, The',null,null,'A goddess of the pantheon of Demarge, associated with piety and service.  She is associated with the Lesser Circle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2365,1,to_timestamp('17-SEP-15 02.22.46.848000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sworn, The',null,null,'An alternative name for the skyldr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2366,1,to_timestamp('17-SEP-15 02.23.57.986000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Oath, The',null,null,'The oath sworn by the seneschals of I&aring;rth.  It binds them to the service of the Mountain Home and the &thorn;engill as a body.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2838,1,to_timestamp('26-SEP-15 11.35.08.721000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lorweth yn Inymuus',null,null,'An elithin and Tiaryche of of Ryche Inymuus since the end of the Grey War.  She was a close friend to Freyna yn Loutha.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2609,1,to_timestamp('20-SEP-15 09.44.56.724000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vj&auml;kon',null,null,'A crossroads of the Iron Road in the kingdom of I&aring;rth, famously the location where the Cobalt Legion seized the Weapon from Arastel, overcoming a host of d&aelig;n''shee and shorn.  The operation was enabled by a distraction at Tyrn, where two full legions gave their lives.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2817,1,to_timestamp('26-SEP-15 11.24.24.540000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Nynwynwr',null,null,'A house of the elithi in Rhuanedd, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was woodpecker.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2818,1,to_timestamp('26-SEP-15 11.25.34.063000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Daind',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was a crane.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2819,1,to_timestamp('26-SEP-15 11.25.48.191000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Gwillym',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was a quail.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2431,1,to_timestamp('18-SEP-15 04.34.54.719000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sangf-havn',null,null,'The guild haven of the Hammar, located deep in the heart of the Charmers'' Grotto in I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2435,1,to_timestamp('18-SEP-15 04.40.21.801000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Urag Tug Me',null,null,'An epithet among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2811,1,to_timestamp('25-SEP-15 08.13.25.136000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'B&aelig;rin yn Nyamn',null,null,'An elithin and former queen of Rolar.  Mother to Eldeain yr Orain and Brandynn yr Orain, and wife to Carac&ucirc;l yr Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2933,1,to_timestamp('06-OCT-15 07.17.21.872000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lambert der''Sorge',null,null,'A farlo man of the Aljer, interrogated and then murdered by Amber during one of her fugue states after her Sphere landed in Aljerbach.  The father of Lambert der''Sorge and husband to an unnamed wife, who were also murdered.  It was he who revealed that Amber was seeking the Harbingers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2937,1,to_timestamp('06-OCT-15 07.30.27.226000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ruhmreich, The',null,null,'The "Glorious Warriors," an elite and ceremonial military company of the Aljer comprised primarily of lords of the Aljerstraad.  It was massacred by the na''k&ouml;r, including its captain, Jeril der''Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2369,1,to_timestamp('17-SEP-15 02.29.19.511000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bagr-Danr',null,null,'A plain where the du&aring;rg shortly lived during their flight from Llua''la, before they came to Calikar.  It is thought to have been located in the Roof of Bel Can''nar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2370,1,to_timestamp('17-SEP-15 02.34.28.510000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kykr',null,null,'The legendary hammer of Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2374,1,to_timestamp('17-SEP-15 02.42.13.500000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hellforge, The',null,null,'An alternative name for Onlendr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2376,1,to_timestamp('17-SEP-15 02.44.59.606000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Du&aring;rg-Fa&eth;ir',null,null,'A name by which the du&aring;rg refer to Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2377,1,to_timestamp('17-SEP-15 02.45.12.948000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'All-fa&eth;ir',null,null,'A name by which the du&aring;rg refer to Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2378,1,to_timestamp('17-SEP-15 02.45.47.943000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Khazjad, The',null,null,'A shortened form of Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2384,1,to_timestamp('17-SEP-15 03.30.11.728000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Trennus Hill',null,null,'One of the Seven Hills of Demarge, known for its brothels and gambling and black market establishments.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2385,1,to_timestamp('17-SEP-15 03.31.18.402000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seven Hills, The',null,null,'A collection of hills around which the city of Demarge was founded.  Each hill has its own history and culture.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2391,1,to_timestamp('17-SEP-15 03.50.17.340000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'God''s Gift',null,null,'A term for the Star Gem used in Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2396,1,to_timestamp('17-SEP-15 06.29.21.528000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sere''marg',null,null,'"Protector of the soul" in the Pure Tongue.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2397,1,to_timestamp('17-SEP-15 06.29.39.520000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mere''marg',null,null,'"Companion of the Soul" in the Pure Tongue.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1889,1,to_timestamp('01-SEP-15 03.17.53.411000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Machuin Muil',null,null,'A plain of brittle stone covering vast pits and chasms, supposedly created by a battle between the Eshthara and Dubh Gaoth.  It is located between Rhuanedd and &AElig;lm&aelig;dris.  Also called the Hungering Earth, the Unclaimed Tombs, the Last Battlefield, the Shale, and (in ka-&AElig;lm&aelig;dris) the Brittle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1921,1,to_timestamp('04-SEP-15 09.25.22.558000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tendron',null,null,'A small island from the time of the Second &AElig;gan Empire, primarily known as the source of dreamroot.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2351,1,to_timestamp('17-SEP-15 09.37.46.053000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Magor-Balkr',null,null,'A fortress of black iron built in the heart of Irongate, intended to be impenetrable.  It fell during the Sons'' War, and shortly thereafter the Mountain Home was Spoken over its ruins.  Also known as the Iron Citadel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1069,1,to_timestamp('15-AUG-15 08.17.56.051000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chara',null,null,'An idiom by which a Shaper perceives the Pillars of Da''kol.  The most common chara are visual, but they can be auditory, olfactory, or even gustatory.  Builders perceive the wyrds of others according to their own chara.<br /><br />Also a name for the collective lands of the dluth-chara.  Also called the Many and Sacred Kingdoms.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2633,1,to_timestamp('23-SEP-15 09.21.19.056000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Erland Hjalmar-s&oslash;n',null,null,'A du&aring;rg, eldest of twenty-four sons of Hjalmar Tyr-s&oslash;n and Skoldjis Unn-Hild-d&oslash;tter, and father to Gandath Erland-s&oslash;n. Born in the time of Bagr-Danr, Erland was one of three leaders of the du&aring;rg at the time of the f&aelig;&eth;a.  Unlike his brothers, Amlaith Hjalmar-s&oslash;n and Oddvar Hjalmar-s&oslash;n, he was opposed to continued to warfare with the d&aelig;n''shee.  As a result, he departed Aljaberin and constructed a hidden city, Sk&aring;n-Ule&aring;, which he intended to keep separate from the war.  Many of his people followed him, including those considered the most educated and skilled.  Subsequently, the war went poorly for his people, and many laid blame on Erland, believing that he had taken the secrets of his people with him into exile.  His brothers took his son, Gandath Erland-s&oslash;n hostage, resulting in the boy''s death.  This resulted in Erland closing the Veiled City altogether to his people.  Eventually, Amlaith and Oddvar faked their own deaths to draw Erland out of hiding.  Erland and Oddvar died in the ensuring conflict, with Amlaith becoming the first &thorn;engill-rikr.  Many treasures were found in Sk&aring;n-Ule&aring;, including the Eyes of Phaelen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3001,1,to_timestamp('08-OCT-15 10.24.28.879000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Vrer',null,null,'The ancestral royal house of the city and nation of Harn.  Its current head is the queen, Titiana Vrer.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3002,1,to_timestamp('08-OCT-15 10.25.55.255000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Titiana Vrer',null,null,'The head of House Vrer and current queen of the nation of Harn.  Due to the status of the state as an occupied territory of the Empire of Demarge, this title is largely ceremonial.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3003,1,to_timestamp('08-OCT-15 10.28.10.382000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gelastir',null,null,'The first great city built by men in the Great Cavern and capital of their nation.  The modern city of Aljerbach is built atop its ruins.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3004,1,to_timestamp('08-OCT-15 10.36.29.089000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kodra',null,null,'Domesticated animals of the First Domain, common to human kingdoms in both Bel Can''nar and the Great Cavern.  They resemble sheep and are likely descended from stock preserved from Llua''la.  (Kodra, unlike sheep, are blind; they are also omnivorous, with razor fangs.)  Kodra cannot survive the Grim and thus no longer are found in the wild.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3006,1,to_timestamp('08-OCT-15 10.42.51.547000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Choca',null,null,'A type of flat worm endemic to the First Domain, used in the purification of wine fermented from edelberry.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3007,1,to_timestamp('08-OCT-15 10.44.09.780000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gendra',null,null,'A fungus which fronds are consumed as vegetable among the peoples of the First Domain.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1488,1,to_timestamp('23-AUG-15 06.08.46.412000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Depsangf',null,null,'An name for the Earth Song among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3010,1,to_timestamp('09-OCT-15 12.26.48.822000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Naroch',null,null,'The "venom" or "curse" which is responsible for the Grim.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3011,1,to_timestamp('09-OCT-15 12.29.33.159000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rel&icirc;th',null,null,'A curse in the tongue of the d&aelig;n''shee.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3013,1,to_timestamp('09-OCT-15 12.31.34.391000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sigmy derAnnache',null,null,'The son of Aldar der''Annache.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3015,1,to_timestamp('09-OCT-15 12.37.47.174000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gold Square, The',null,null,'An affluent square in the Inner City of Aljerbach and the location of many great estates, including the House of Annher.  It is located at the intersection of Sepulch Radius and Sharon Spoke.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3016,1,to_timestamp('09-OCT-15 12.38.45.624000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kheira',null,null,'The head mistress and maid of House Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3018,1,to_timestamp('09-OCT-15 12.42.25.180000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Balish der''Veiss',null,null,'An old man and servant in the house of Annher and father of Klara der''Veiss.  He displayed a great admiration for Calikar and the Bright Folk.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3021,1,to_timestamp('09-OCT-15 08.21.33.831000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Merton',null,null,'A servant boy of house Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3023,1,to_timestamp('09-OCT-15 08.22.52.528000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vallon',null,null,'A servant and cook of House Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3026,1,to_timestamp('09-OCT-15 08.30.46.302000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Taryeah',null,null,'A trusted servant man of House Annher, whose duties include household purchases.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3027,1,to_timestamp('09-OCT-15 09.47.26.316000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Baasmeir',null,null,'A great house of Aljerbach and member of the Aljerstraad, second in history and esteem only to the royal house of Klar.  Its current lord is Evram der''Baasmeir.  Although the formal heraldry of the house displays a griffon, it is often identified by a sprig of red berries:  edelberry, which has for centuries been the source of line''s wealth and power.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3029,1,to_timestamp('09-OCT-15 09.55.28.962000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Annher',null,null,'A great house of Aljerbach and member of the Aljerstraad.  Once greatly esteemed, its wealth and influence have been diminished by the decisions of recent generations.  With its lord, Garani der''Annher, seemingly mad or dead, this bloodline is likely spent.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2401,1,to_timestamp('17-SEP-15 07.20.12.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mines of Orth',null,null,'A mine for iron and copper located just outside the city of I&aring;rth, within sight of the Iron Walls.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2403,1,to_timestamp('17-SEP-15 07.27.04.081000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Si&eth;astr',null,null,'A du&aring;rg watch post set approximately three days'' east of the city of I&aring;rth. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2404,1,to_timestamp('17-SEP-15 07.35.40.743000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Eram Morgot',null,null,'A realm described in the teachings of the Caal Anuur, perhaps mythological or perhaps a Fold, where the souls of good men go after death, roughly equivalent to a "heaven."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2406,1,to_timestamp('17-SEP-15 07.38.20.102000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grod-rikr',null,null,'The leader of one of the Grod of the du&aring;rg; or the collection of leaders.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2407,1,to_timestamp('17-SEP-15 07.38.35.497000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grod-rikr-rikr',null,null,'An alternative name for Phaelen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2408,1,to_timestamp('17-SEP-15 07.42.01.903000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Here or Beneath the Tree',null,null,'A common expression of farewell among the elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2409,1,to_timestamp('18-SEP-15 02.59.16.565000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ash''gun',null,null,'One of the Aiki and the brother of Kaja''lar according to her dreams.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2411,1,to_timestamp('18-SEP-15 03.05.26.374000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ol''var',null,null,'A shaman among the Aiki who appears in the dreams of Kaja''lar.  He apparently molested her younger brother, Gret''nul, when Gret''nul he was a small boy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2413,1,to_timestamp('18-SEP-15 03.09.36.105000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrm-chosen',null,null,'A shaman among the Aiki.  There is only one wyrm-chosen per tribe.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2416,1,to_timestamp('18-SEP-15 03.16.05.668000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrm-resin',null,null,'A deposit left by the passage of wyrms:  a thick mucus.  It is employed as a food source, fuel, healing agent, alchemical agent, lubricant, and insulating agent by the Aiki.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2418,1,to_timestamp('18-SEP-15 03.19.16.169000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Firerock',null,null,'A red-orange mineral known by the Aiki to unpleasant to wyrms.  A wyrm will not pass through an area rich in firerock.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2426,1,to_timestamp('18-SEP-15 04.07.49.397000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;thel''mierre',null,null,'A sacred tree located atop Cielin-al&ucirc;n during the Second &AElig;gan Empire.  It possessed leaves which were silver on one side and golden on the other and was an important symbol for the Ter Graz Daan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2428,1,to_timestamp('18-SEP-15 04.21.52.820000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Supplicants'' Hour, the',null,null,'A period of one hour that occurs every three days, in which twenty supplicants are chosen to gain entrance to the Mountain Home.  Tens of thousands of du&aring;rg gather on each occurrence for the honor, which is assigned by random chance.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2429,1,to_timestamp('18-SEP-15 04.28.50.190000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mistval, The',null,null,'A district of the city of I&aring;rth, known for its great tanning furnaces.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2432,1,to_timestamp('18-SEP-15 04.36.31.915000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Holfyfr',null,null,'The guild haven of the Grysthiir, located in I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2433,1,to_timestamp('18-SEP-15 04.37.08.324000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gr&oacute;f',null,null,'The guild haven of the Myrkyr, located in I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2434,1,to_timestamp('18-SEP-15 04.40.14.183000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Urag''s Beard',null,null,'An epithet among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1363,1,to_timestamp('20-AUG-15 08.55.17.177000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Children of Istaron, The',null,null,'The five races created by Istaron against the wishes of his father, D&uacute;vinar:  Humans, the Bangrey, the Elistle, the N&icirc;mra, and the Elistle.  Also known as the Five Races.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1400,1,to_timestamp('21-AUG-15 11.21.43.006000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gor''Nahara',null,null,'A Fold of the Universe which apparently allowed travel between all other Folds.  Also called the Celestial Sea.  Long ago sealed by D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2642,1,to_timestamp('23-SEP-15 10.03.38.769000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ariel',null,null,'A human woman who helped Durgin enter the Tower of Sages in Aljerbach (where she claimed to be a lady of house Taber, Ariel der''Taber), was later encountered by the na''k&ouml;r in Rolar, was eventually pursued by Delsar, Amber, and Diarin to I&aring;rth, and now has taken up residence in the City of Demarge.  She has some connection to the War of Seven Kingdoms, although its nature is unclear, and was apparently bound to the will of Arastel (via the Bracelet of Yagnoll) before deceiving Delsar into breaking the bond.  She has a strong resemblance physically and personally to Ariel durahl Durain from the Second &AElig;gan Empire, including an affinity for high art.  Again, the nature of this connection is unclear.  Aru''darian appears to be her dure''marg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2820,1,to_timestamp('26-SEP-15 11.26.35.588000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Yeorathe',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was a condor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2821,1,to_timestamp('26-SEP-15 11.26.48.672000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Gronoe',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was a blue bird.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2631,1,to_timestamp('20-SEP-15 10.51.15.906000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valmiki Sijfinnur-s&oslash;n',null,null,'A du&aring;rg and son of Ingjard Erica-d&oslash;tter after her exile from magnr Undflos, brother to Folkvar Sijfinnur-s&oslash;n, Mikhan Sijfinnur-s&oslash;n, adn Leel Ingjard-d&oslash;tter.  Although named in honor of Sijfinnur Jaak-so&oslash;n, he is almost certainly not the son of the rikr, who was deceased at the time of his birth.  His mother was a prostitute at the time.  He survived the assassination of his sister, Lell, and mother by his brother-in-law, Honvir Sijfinnur-s&oslash;n.  His current fate is unknown.  Excepting Lell, the other children of Ingjard never knew of his existence.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3032,1,to_timestamp('09-OCT-15 10.01.47.372000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stone, The',null,null,'A great fortress north of Aljerbach, which wards the narrow pass between the Shaaro Plains and the Great River.  Historically, its wardens have been the lords of House Taber.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3033,1,to_timestamp('09-OCT-15 10.02.36.344000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sarah der''Taber',null,null,'A woman encountered by Durgin in Aljerbach, who guided him to the Tower of Sages.  She claimed to be a lady of House Taber, but it seems likely that her name and identify were false.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3035,1,to_timestamp('09-OCT-15 10.09.13.452000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sigmy der''Annache',null,null,'The younger brother of Aldar der''Annache and current lord of House Annache.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3037,1,to_timestamp('09-OCT-15 10.16.01.548000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Ausseller',null,null,'A great house of Aljerbach and member of the Aljerstraad.  Its current lord is Saffroy der''Aussaller.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3039,1,to_timestamp('09-OCT-15 10.18.15.441000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fairon Tainus',null,null,'See gal-Fairon Tainus.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3041,1,to_timestamp('10-OCT-15 03.31.13.342000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'House Mering',null,null,'A great house of Aljerbach and member of the Aljerstraad.  Its current lord is Anson der''Mering.  A ship owned by Anson was in the Royal Port of Aljerbach when the na''k&ouml;r were fleeing the city and was considered as a possible route of escape.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3042,1,to_timestamp('10-OCT-15 03.31.23.939000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Anson der''Mering',null,null,'The lord of House Mering.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3044,1,to_timestamp('10-OCT-15 03.34.52.414000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sten der''Gan',null,null,'The lord of House Gan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3045,1,to_timestamp('10-OCT-15 03.35.55.079000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arissa der''Gan',null,null,'A lady of House Gan and cousin to its patriarch, Stan der''Gan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3047,1,to_timestamp('10-OCT-15 03.41.20.342000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Senkull',null,null,'A fortress which wards the western approach to Aljerbach, warding the narrow pass that rises over the Dwimmer Vale.  It has never fallen to an invader.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3049,1,to_timestamp('10-OCT-15 03.49.53.970000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Bakhor',null,null,'A word drawn by a thousand hands in a thousand tongues in a vision shown to Malnos by Natalie''s apparition.  The word, and the bleak mountain doors into which they were carved, filled him with existential dread.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3050,1,to_timestamp('10-OCT-15 03.51.23.790000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gauthrin, The',null,null,'An order of historians and scholars among the Aljer, mostly drawn from younger sons of houses of the Aljerstraad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3052,1,to_timestamp('10-OCT-15 03.54.56.282000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Path of Tears, The',null,null,'The name by which the people of Aljerbach know Duthra Cein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3008,1,to_timestamp('09-OCT-15 12.16.33.513000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Merla',null,null,'A waitress in the inn, Fields of Erdrion, encountered by Durgin on his visit to Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3009,1,to_timestamp('09-OCT-15 12.20.57.576000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fire-light, The',null,null,'A searing light that mysteriously appeared in Ajer-D&aelig;n while the na''k&ouml; were captives in the prison.  It left only ash in its wake, sparing none but the Harbingers.  No explanation for the fire-light has been offered.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3014,1,to_timestamp('09-OCT-15 12.35.38.097000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Myrianda',null,null,'The base-born and vulgar wife of Davian Lamar during the Second &AElig;gan Empire.  She died of natural causes in her eighth decade.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3017,1,to_timestamp('09-OCT-15 12.40.06.091000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rollan',null,null,'A boy servant of House Annher.  He came to be the personal attendant of Malig while the na''k&ouml;r were visitors at the house and was later murdered by the frel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3022,1,to_timestamp('09-OCT-15 08.21.46.050000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Adalie',null,null,'A servant girl of House Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2436,1,to_timestamp('18-SEP-15 04.42.13.105000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Beraki',null,null,'An adjective describing things or persons from Berak.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2437,1,to_timestamp('18-SEP-15 04.42.59.694000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Host, The',null,null,'An alternative term for the Iron Army.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2438,1,to_timestamp('18-SEP-15 04.44.45.059000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Amlaith''s Gate',null,null,'The eastern gate of the city of I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2441,1,to_timestamp('18-SEP-15 04.48.21.498000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Resa''dana',null,null,'"He Who Ruled from the Rear" in the Pure Tongue.  A term used by the Shugda to describe a manner of general.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2386,1,to_timestamp('17-SEP-15 03.33.37.710000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Freki',null,null,'A term among the du&aring;rg for an outcast, specifically one considered beholden to foreign lands.  The pejorative is applied to Iron Born who voluntarily elect to live beyond the boundaries of the Iron Kingdom.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2382,1,to_timestamp('17-SEP-15 03.16.08.728000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Battle of Three Queens, The',null,null,'A famous battle in the War of Four Kingdoms waged near the borders of Sorain and I&aring;rth, so-named because the host of the d&aelig;n''shee was led at different points by three different wives of Shenrin, each of whom fell during the struggle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2383,1,to_timestamp('17-SEP-15 03.23.37.699000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Drekka',null,null,'One of the du&aring;rg who is not touched by the Grod.  Being drekka is considered a source of great shame among the Iron Born, such that most become servants or slaves.  Being drekka is not a purely genetic trait, but it does seem to run in bloodlines.  Children born of two drekka are almost always (but not ALWAYS) drekka.  Among the magnr, women who give birth to drekka are considered cursed and are often put aside as partners; this is almost universally true of women who give birth to two drekka.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2388,1,to_timestamp('17-SEP-15 03.41.15.666000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Not with a thousand hammers, not if given a thousand years',null,null,'A common saying among the du&aring;rg.  It refers to the belief that the Mountain Home will never fall, even if her enemies were given a thousand hammers and thousand years.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2390,1,to_timestamp('17-SEP-15 03.48.38.031000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Garsh''jk''k',null,null,'A word in the tongue of Drolock''harka''s people.  It means "One who is not prey" or "one who is not enemy."  Also known as a friend.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1535,1,to_timestamp('23-AUG-15 01.39.57.193000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lot Rhuedd',null,null,'A village of the elithi in Westermere.  Eleanor walked here after she woke from her Sphere.  She does not remember this journey or her time here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1755,1,to_timestamp('29-AUG-15 07.40.26.889000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gai''lal',null,null,'The name assumed by Ardran Myramar when he became a Triumvir.  It means Serpent Mage in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1879,1,to_timestamp('01-SEP-15 02.49.25.052000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Guardians, The',null,null,'An alternative term for the dluth-chara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2012,1,to_timestamp('07-SEP-15 08.04.10.206000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fae&eth;a',null,null,'The name by which the du&aring;rg refer to the Land Gift.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2417,1,to_timestamp('18-SEP-15 03.16.24.962000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Father',null,null,'An alternative term by which the Aiki refer to wyrms.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2822,1,to_timestamp('26-SEP-15 11.28.28.295000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iorwerth',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the gloaming.  Their symbol was a magpie.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2823,1,to_timestamp('26-SEP-15 11.30.26.829000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vanaollyn',null,null,'The line of kings among the elithi in Aduine, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the gloaming.  The current lady is Aibreann yn Vanaollyn.  Their symbol was a golden eagle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2824,1,to_timestamp('26-SEP-15 11.31.08.591000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aibreann yn Vanaollyn',null,null,'Current queen of the peoples of Aduine and Tiaryche of Ryche Vanaollyn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2825,1,to_timestamp('26-SEP-15 11.31.48.961000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wendran y Dulain',null,null,'The current Tiaryche of Ryche Dulain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2826,1,to_timestamp('26-SEP-15 11.32.07.069000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Blathnaid yn Branna',null,null,'The current Tiaryche of Ryche Branna.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2827,1,to_timestamp('26-SEP-15 11.32.31.234000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Killian y Ther''ras',null,null,'The current Tiaryche of Ryche Ther''ras.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2828,1,to_timestamp('26-SEP-15 11.32.45.178000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grainne yn Faolain',null,null,'The current Tiaryche of Ryche Faolain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2829,1,to_timestamp('26-SEP-15 11.33.09.029000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Saiorse yn Wuhlain',null,null,'The current Tiaryche of Ryche Wuhlain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2830,1,to_timestamp('26-SEP-15 11.13.52.638000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Meirchion yr Erossi',null,null,'An elithin, lord of Ryche Erossi, and former regent of Rolar.  He and his house were elevated to the Tiaryche by Cyngen yr Orain for services rendered, the second such ascension in three thousand years.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2831,1,to_timestamp('26-SEP-15 11.16.58.944000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Erilys yn Re''nan',null,null,'An elithin, Tiaryche of Ryche Re''nan, harbor-mistress of Huynduir, ail-U&icirc;n, and esteemed member of the Eshthara.  She served Gethin y Kam&icirc;l and betrayed Cyngen yr Orain during the Grey War, where she and the last of her blood were killed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2832,1,to_timestamp('26-SEP-15 11.19.07.292000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cadwallen yr Inymuus',null,null,'An elithin, former Tiaryche of Ryche Inymuus, shuonne, and guardian of the White Walls.  He served Gethin y Kam&icirc;l and betrayed Cyngen yr Orain during the Grey War, where he was killed by Diarin yn Marel yn Ast in single combat.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2834,1,to_timestamp('26-SEP-15 11.22.19.570000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kirnin yn Tanyrin',null,null,'An elithin, Tiaryche of Ryche Tanyrin, and renowned minstrel of Duri-Med&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2835,1,to_timestamp('26-SEP-15 11.27.47.907000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aelwen yn Enelorn',null,null,'An elithin, former Tiaryche of Ryche Enelorn, and esteemed lady of the Eshthara.  She died in the service of Cyngen yr Orain during the Grey War, killed by the fathaigh at Btul Erym.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2837,1,to_timestamp('26-SEP-15 11.33.09.802000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vanwyra yn Enelorn',null,null,'An elithin and Tiaryche of of Ryche Enelorn since the end of the Grey War.  A distant cousin to Aelwen yn Enelorn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3024,1,to_timestamp('09-OCT-15 08.25.45.616000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hatal',null,null,'An elderly servant woman in the service of Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3025,1,to_timestamp('09-OCT-15 08.29.43.722000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Tuliar',null,null,'A lake in the Great Cavern which feeds and is fed by a branch of the Great River.  Several townships of the kingdom of Aljerbach are situated on its banks.  A series of waterfalls prevents direct travel between the Thrice City and the lake.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3028,1,to_timestamp('09-OCT-15 09.53.19.409000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Klar',null,null,'The royal house of Aljerbach.  According to legend, this bloodline was formed via the divine interdiction known as the Gr&icirc;th, where three great but feuding families were combined into one.  Their symbol is the Triurg.  The current lord of the house is uncertain.  It may be Tavin der''Klar if he survived the fire-light; or the line may be extinct.  In this state of confusion, Evram der''Baasmeir sits as regent on the Thrice Throne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3030,1,to_timestamp('09-OCT-15 09.59.29.734000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Taber',null,null,'A great house of Aljerbach and member of the Aljerstraad, ancestral wardens of the fortress known as the Stone.  Its current lord is Davyd der''Taber.  In Aljerbach, Durgin met a woman who claimed to be Sarah derTaber, but it seems likely that this was a false name.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3036,1,to_timestamp('09-OCT-15 10.14.38.060000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Unblooded, The',null,null,'A station among the Aljer, where a lord is ordained to the Aljerstraad for deeds for king or country.  This honor extends as long as the lord lives but is not inherited along bloodlines.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3040,1,to_timestamp('09-OCT-15 10.23.07.686000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gal-Fairon Tainus',null,null,'A magus of the Durem Erim&aelig; encountered by the na''k&ouml;r in Ajer-D&aelig;n, scorned by those who knew him as a twisted sadist.  Before she was taken, Natalie had a series of dreams where she was tormented at his hands.  These dreams were realized.  Fairon attempted to flee the fire-light by submerging himself in water and was subsequently boiled alive.  He was dying a horrible death when Malnos and Delsar found him.  Delsar grudgingly offered him a merciful death.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3046,1,to_timestamp('10-OCT-15 03.40.19.287000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dwimmer Vale',null,null,'A fungal wood to the south and west of Aljerbach.  Passage through the wood and then along a narrow ledge that winds along the Wall of the Roof offers the only approach to the Thrice City from the west.  This pass is warded by a great fortress known as Senkull, which has never fallen to an invader.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3051,1,to_timestamp('10-OCT-15 03.54.39.667000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Duthra Cein',null,null,'A treacherous region of the Great River believed to be cursed.  Human vessels do not pass through this area, where winds and currents turn on a whim.  Even the tributaries of the river, itself, are said to twist and change path.  Among the Aljer, Duthra Cein is known as the Path of Tears.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1569,1,to_timestamp('23-AUG-15 07.28.42.728000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fionn y Senyar',null,null,'An elithin and former high king of Calikar, known for calling the Liar''s Peace at a''Tiernya, which ended in his murder and the near ruin of Uahd isch Calikahd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2402,1,to_timestamp('17-SEP-15 07.23.25.379000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grod-sm&aacute;r',null,null,'A "guild" within the context of a Grod.  For example, among the Gresthiir there are many forms of smith, including silver-smiths, gold-smiths, iron-smiths, etc, each with their own grod-sm&aacute;r.  The are fifteen grod and forty-seven grod-sm&aacute;r. ',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2405,1,to_timestamp('17-SEP-15 07.35.59.107000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Urum Morgot',null,null,'A realm described in the teachings of the Caal Anuur, perhaps mythological or perhaps a Fold, where the souls of wicked men go after death, roughly equivalent to a "hell."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2410,1,to_timestamp('18-SEP-15 03.02.17.987000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gret''nul',null,null,'One of the Aiki and the younger brother of Kaja''lar according to her dreams.  Apparently, he was molested by a shaman called Ol''var in his youth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2412,1,to_timestamp('18-SEP-15 03.07.52.332000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrm-blessed',null,null,'An alternative name by which the Aiki refer to themselves.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2414,1,to_timestamp('18-SEP-15 03.11.20.741000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wyrm-chaser',null,null,'A privileged and respected class among the Aiki, responsible for tracking the wyrm and collecting wyrm-resin.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2415,1,to_timestamp('18-SEP-15 03.13.18.362000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Wyrm, The',null,null,'A mythological creature among the Aiki, believed to be the first and greatest of the wyrms, from which all other wyrms are descended.  They Great Wyrm is seen as a deity.  The Aiki believe that the Great Wyrm carved the Domains with its passage.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2419,1,to_timestamp('18-SEP-15 03.27.21.851000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Mother, The',null,null,'The name by which the du&aring;rg refer to Skoldjis Unn-Hild-d&oslash;tter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2901,1,to_timestamp('05-OCT-15 08.21.40.352000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cantos of Lero, The',null,null,'An alternative term for the Poems of Lero.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2902,1,to_timestamp('05-OCT-15 08.23.39.884000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Doom of D&uacute;vinar, The',null,null,'A terrible and eternal curse laid by D&uacute;vinar upon his son in the Cantos of Lero.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2372,1,to_timestamp('17-SEP-15 02.40.01.412000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'B&aacute;l',null,null,'A mountain mentioned in the legends of Khijak the Khazjad, described a mountain of flame.  A collection of forges was located at its base, known as Sl&aacute;-mork.  The greatest of these forges was known as Onlendr, the Hellforge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2394,1,to_timestamp('17-SEP-15 06.25.55.223000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Di''nar',null,null,'A name mentioned to Diarin yn Marel yn Ast by Liarin a''Gur Dao''Ast during their meeting near Gh&icirc;lar.  His identify and importance are unclear.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2903,1,to_timestamp('05-OCT-15 08.24.34.990000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gara-d&ucirc;n',null,null,'According to the Cantos of Lero, a place where the son of D&uacute;vinar was cursed to dwell for all eternity as part of the Curse of D&uacute;vinar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2906,1,to_timestamp('05-OCT-15 08.29.37.304000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'House Xylander',null,null,'A minor family of farmers in the Taunland of Aljerbach.  A child of the family, Treir der''Xylandar, was witness to the fall of the Sphere after encountering Izlie and Jerome in the Forest of Ur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2907,1,to_timestamp('05-OCT-15 08.31.58.608000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Weyr a''Sherr&aelig;',null,null,'A place mentioned by Jerome to Treir der''Xylander, evidently his homeland.  It is described as being high in elevation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2908,1,to_timestamp('05-OCT-15 08.32.31.362000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Harnese',null,null,'An adjective describing a thing or person of Harn origin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2909,1,to_timestamp('05-OCT-15 08.33.17.243000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Etri der''Xylander',null,null,'A boy of the Taunland and the younger brother of Treir der''Xylander and Farauk der''Xylander.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2910,1,to_timestamp('05-OCT-15 08.33.49.342000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ur',null,null,'A shortened form of the Forest of Ur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2843,1,to_timestamp('26-SEP-15 11.53.21.587000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shar''ris y Rhuanedd',null,null,'A city formed by the survivors of Rhuanedd after the loss of Asmaraghaidhe during Gysioch''s Folly.  Determined that such complete calamity would never strike their people again, they became a migrant nation.  The Song is never joined except in small groups, and its peoples are divided among hundreds of small villages, only coming to Shar''ris y Rhuanedd when the Lament requires.  Literally, "Shadow of Rhuanedd."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1914,1,to_timestamp('04-SEP-15 09.21.29.188000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wizards'' Root',null,null,'A tea which makes it difficult for the drinker to Shape but does not affect his ability to perceive Da''kol.  The active ingrdient in Wizard''s Root is dreamroot.  The tea is prepared by boiling the seed in water.  The seed can also be incorporated in foodstuffs, which can mask the strong flavor.  The Tea was discovered in the prior age among the peoples of the isle of Tendron, who used it as a folk remedy to calm the “dream magick” of infants and toddlers (they included the leaf, roots, and seeds in the preparation, which yielded the name).  In the modern era, the lords of Demarge, fearing magick and the wyrm, introduce dreamseed directly into the water supply and granaries, as well as including it in the diet of meat animals.  It was introduced to the Domain by the survivors of Xaln.  Dreamseed requires light in which to grow (Groves/Star Gem) but can be preserved in a dry form indefinitely.  The effect of Tea on the drinker is inversely proportional to his strength in the Gift.  Chiromancers are incapacitated by small dosages, which might cause a Shaper to experience a light headache or fatigue.  The dosage required to affect a Builder would likely kill a Chiromancer unless highly condensed.  Most without the Gift do not experience any effect at all, though some suffer headaches or chills, likely due to allergies.  It has been known to soothe some cramps and aches.  Wizards'' Root can induce illness, weakness, or death among creatures created via Shaping.  In ka-Ælmædris, the Calikari grow dreamseed as grafts to the Holy Trees.  Also called Shapers'' Venom, the Tonic, the Black Tea, and Fools'' Elixir.
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1453,1,to_timestamp('22-AUG-15 09.50.30.970000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Archway, The',null,null,'An object discovered by Malig, Amber, and Eleanor in Gaithan Aiur.  Its touch bring strong visions, apparently of the future.  According to the histories of the elithi, the Archway was among the artifacts carried by the Calikari during their exile from Llua''la.  It was rediscovered by Arawn y Rhuaidh centuries later and supposedly granted him and his descendants the Sight.  It also drove Arawn to madness and eventual suicide.  Amber discarded the Archway before she departed Rolar, believing it culpable for her deeds in the Grey War.  It was later discovered by Drolock''harka and guided him to Rhuanedd.  It is currently in the possession of Malnos. In appearance the Archway is a portal of black stone, of the size to fit in a man’s hand, with violet stars carved into its surface.  The words "Huotha Ji''har" are carved into its granite base.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1602,1,to_timestamp('25-AUG-15 09.46.48.002000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Skor-Mut',null,null,'A worm endemic to the Great Cavern.  They are known to migrate in great swarms, seeking out sources of iron and other precious metals, which they consume.  The du&aring;rg follow these swarms to find mineral sources.  Skor-Mut will also seek out iron in human and Iron Born bodies, digging their way through the flesh.  This process is painful, ending in anemia and death within a few weeks.  For this reason, the du&aring;rg employ Lures when following the swarms.  Also called Iron Moths.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2941,1,to_timestamp('06-OCT-15 07.38.10.957000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mr. Shadow',null,null,'An appellation by which Natalie referred to Kisel in Ajer-D&aelig;n, before his name was learned.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3012,1,to_timestamp('09-OCT-15 12.30.40.273000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tal-Oriman al''Aman',null,null,'A magus of the Durem Erim&aelig; encountered by the na''k&ouml;r in Ajer-D&aelig;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3019,1,to_timestamp('09-OCT-15 12.44.00.688000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Klara der''Veiss',null,null,'An Aljer woman and daughter of Balish der''Veiss.  After the burning of Aljerbach, she fled in exile to Rolar, where she was murdered by Eliryndil yn Orain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3020,1,to_timestamp('09-OCT-15 12.45.22.523000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Stag Narz',null,null,'A frel and the brother of Malig Narz according to the visions shown Malig in the red fragments.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3031,1,to_timestamp('09-OCT-15 10.00.00.471000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Davyd der''Taber',null,null,'The lord of House Taber.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1861,1,to_timestamp('01-SEP-15 12.37.12.348000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lleah-Roht',null,null,'A fungal grove located far west of &AElig;lm&aelig;dris in Alune, once sacred to the Calikari, now lost to the Deep Dark.  Hidden in a deep cavity, it was spared by Dubh Gaoth, though this is only known to a select few among the survivors of ka-&AElig;lm&aelig;dris.  Considered both a blessed and cursed place.  According to legend, it was the inspiration for the Lattice Bridge, but K&aelig;ryeah yr Inythyn, An-Creirwy yn Senyar, and Leuen of the Silver Tongue were all assassinated here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2478,1,to_timestamp('19-SEP-15 07.08.10.666000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Magistrate',null,null,'A judge in the judicial system of the Empire of Demarge, charged with executing and interpreting the laws set by the Imperial Senate and God Emperor.  Magistrates are appointed by the God Emperor and are generally chosen from among the Centarum, frequently from the Emperor''s own house, Soluun.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2801,1,to_timestamp('25-SEP-15 07.50.22.940000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Searah yn Orain',null,null,'An elithin and former lady of Ryche Orain.  Wife of Cadeyrn yr Orain, sister to Arianrhod yn Orain, daughter to Bathwrwy yn Orain, and mother to Anatai yr Orain.  Considered an exceptional beauty, she was similar in temperament and appearance to her sister, the queen of Rolar.  She perished during the birth of her son, who was born Twisted.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1764,1,to_timestamp('29-AUG-15 09.52.50.240000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dluth-Chara',null,null,'A race of men who descended from Lluala alongside the elithi, loyal to both Gai''lal and Calik Twiceborn.  They helped preserve the Calikari during the exile and later during the Long, Short Years, when the Bright Lords were afflicted by the Withering.  For this service, they earned the eternal friendship of the Sons of Calik.  They have lived sheltered in the heart of Nermere for millennia, secret from the other peoples of the Roof.  Their collective nations are referred to as Chara.  The elithi are bound by sacred oath to protect the dluth-chara and do them no harm.  One tribe of the dluth-chara, the Charisi, were bound to Ryche Senyar and followed when the royal line moved from Rolar to &AElig;lm&aelig;dris, founding the city of Aubin in the heart of Alune.  Also called the Guardians.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2802,1,to_timestamp('25-SEP-15 07.53.09.387000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Anatai yr Orain',null,null,'A former elithin lord of Ryche Orain, child to Searah yn Orain and Cadeyrn yr Orain.  Born Twisted, he was exiled to his presumed death while still an infant.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1456,1,to_timestamp('22-AUG-15 09.57.31.044000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Heilvir',null,null,'A small hurtt in the southern regions of I&aring;rth, visited by the na''k&ouml;r on their journey to the Iron City.  Amber, Diarin, and Delsar first encountered Durgin here (although they were unaware of it).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2630,1,to_timestamp('20-SEP-15 08.58.47.281000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lell Ingjard-d&oslash;tter',null,null,'A du&aring;rg of magnr Undlflos, daughter of Sijfinnur Jaako-s&oslash;n and Ingjard Erica-d&oslash;tter, half-sister to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Honvir Sijfinnur-s&oslash;n, Signy Fylla-d&oslash;tter, and Flos Sijfinnur-s&oslash;n, sister to Folkvar Sijfinnur-s&oslash;n and Lell Ingjard-d&oslash;tter, half-sister to Valmiki Sijfinnur-s&oslash;n, and adopted sister to Hl&aelig;ja Egill-s&oslash;nr.  She was a young girl during the events of Durgin''s twenty-sixth naming day hunt.  Later she went into exile with her mother, living in a brothel until she was murdered by Honvir''s agents.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2634,1,to_timestamp('23-SEP-15 09.26.05.108000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Oddvar Hjalmar-s&oslash;n',null,null,'A du&aring;rg, second youngest of twenty-four sons of Hjalmar Tyr-s&oslash;n and Skoldjis Unn-Hild-d&oslash;tter. Born in the time of Yjalmar, Oddvar was one of three leaders of the du&aring;rg at the time of the f&aelig;&eth;a.  With his brother, Amlaith Hjalmar-s&oslash;n, he embraced warfare with the d&aelig;n''shee and was committed to occupying their former capital, Aljaberin.  His eldest brother, Erland Hjalmar-s&oslash;n, however, departed Aljaberin and constructed a hidden city, Sk&aring;n-Ule&aring;, which he intended to keep separate from the war.  Many of his people followed him, including those considered the most educated and skilled.  Subsequently, the war went poorly for the du&aring;rg, and many laid blame on Erland, believing that he had taken the secrets of his people with him into exile.  Oddvar and Amlaith took his son, Gandath Erland-s&oslash;n hostage, resulting in the boy''s death.  This resulted in Erland closing the Veiled City altogether to his people.  Eventually, Amlaith and Oddvar faked their own deaths to draw Erland out of hiding.  Erland and Oddvar died in the ensuring conflict, with Amlaith becoming the first &thorn;engill-rikr.  Many treasures were found in Sk&aring;n-Ule&aring;, including the Eyes of Phaelen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3034,1,to_timestamp('09-OCT-15 10.08.56.281000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Annache',null,null,'A lesser house of Aljerbach.  Its lord, Aldar der''Annache, was named to the Aljerstraad for life (Unblooded) for services rendered to his king and friend, Davram der''Klar; but he is now deceased, murdered in Ajer-D&aelig;n by Geshrin der''Baasmeir.  The current lord of the house is his younger brother, Sigmy der''Annache.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3038,1,to_timestamp('09-OCT-15 10.17.23.005000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Saffroy der''Ausseller',null,null,'The lord of House Ausseller.  He was involved in the plot to free Evram der''Baasmeir from Ajer-D&aelig;n and implicate the na''k&ouml;r for the deed.  He survived the fire-light but was later interrogated and executed by Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3043,1,to_timestamp('10-OCT-15 03.33.42.689000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'House Gan',null,null,'A great house of Aljerbach and member of the Aljerstraad.  Its current lord is Sten der''Gan.  A ship owned by a lady of House Gan, Arissa der''Gan, was in the Royal Port of Aljerbach when the na''k&ouml;r were fleeing the city and was considered as a possible route of escape.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3048,1,to_timestamp('10-OCT-15 03.46.58.750000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Frel',null,null,'A people of short but proportionate stature, endemic to the Roof of Frelham.  Although once an independent people ruled by a theocracy, they have in recent centuries fallen under the power of the Empire of Demarge.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3055,1,to_timestamp('10-OCT-15 10.56.56.090000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Outer City, The',null,null,'A district of the radially designed city of Aljerbach, lying between the Inner City and the Outermost City.  It is primarily the home of lesser noble houses, merchant families, and successful tradesmen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3056,1,to_timestamp('10-OCT-15 10.57.51.512000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Outermost City, The',null,null,'A district of the radially designed city of Aljerbach, lying between the Outer City and the Farlo.  It is primarily the home of tradesmen, career military, and base laborers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3057,1,to_timestamp('10-OCT-15 10.59.25.867000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'King''s Wall, The',null,null,'An alternative name for the Innermost Wall in Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3058,1,to_timestamp('10-OCT-15 10.59.34.617000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wall of Klar, The',null,null,'An alternative name for the Innermost Wall in Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3059,1,to_timestamp('10-OCT-15 10.59.39.803000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Innermost Wall, The',null,null,'A low wall of piled stone that surrounds the Innermost City of Aljerbach.  Also known as the King''s Wall and the Wall of Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3060,1,to_timestamp('10-OCT-15 11.01.38.953000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Inner Wall, The',null,null,'A tall, stone wall that separates the Inner City and Outer City of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3061,1,to_timestamp('10-OCT-15 11.02.03.054000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Outer Wall, The',null,null,'A tall, stone wall that separates the Outer City and Outermost City of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3062,1,to_timestamp('10-OCT-15 11.02.49.460000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Outermost Wall, The',null,null,'A stone wall that separates the Outermost City and Farlos of Aljerbach.  It is the tallest and thickest of the walls of Aljerbach and is patrolled at all hours.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3063,1,to_timestamp('10-OCT-15 11.06.11.572000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Great Circle, The',null,null,'A philosophical belief common among Aljer nobility, similar to karma:  every act of valor has a reward and every sin has a consequence.  The echo may not be evident or immediate, but what is sewed is reaped.  (In the Aljer belief system, the echo can return on someone other than the actor.)',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3064,1,to_timestamp('10-OCT-15 11.07.46.874000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Jaan der''Rettig',null,null,'A lord of Aljerbach and member of the Gauthrin who questioned Malnos and the na''k&ouml;r in Ajer-D&aelig;n.  He was an inquisitive and bookish character; his current fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3065,1,to_timestamp('10-OCT-15 11.09.52.352000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dachma der''Baasmeir',null,null,'The elderly matron of House Baasmeir and mother of Evram der''Baasmeir.  It is believed that her machinations were responsible for the attempt to free her son from Ajer-D&aelig;n, leading to the murder of the House of Klar and the implication of the na''k&ouml;r in the deed.  Renowned as a woman of merciless guile, she was better known as the Spider of Baasmeir.  Following the events of the fire-light, she was interrogated and executed by Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3066,1,to_timestamp('10-OCT-15 11.10.31.572000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Spider of Baasmeir, The',null,null,'An appellation often applied to Dachma der''Baasmeir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3067,1,to_timestamp('10-OCT-15 11.13.23.170000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Grenna der''Baasmeir',null,null,'The daughter of Evram der''Baasmeir and Geshrin der''Baasmeir.  She died of the flux at the age of seven.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3068,1,to_timestamp('10-OCT-15 11.17.17.853000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Evram der''Baasmeir',null,null,'A lord of Aljerbach, master of House Baasmeir, husband to Geshrin der''Baasmeir, father of Grenna der''Baasmeir, and son of Dachma der''Baasmeir.  As the lord of the second house of Aljerbach, and as a renowned war hero and champion of the people, he is greatly loved by the base-born of the Thrice City.  This acclaim caused him to first be exiled and later imprisoned in Ajer-D&aelig;n, which fate he accepted.  During the fire-light, his mother, Dachma der''Baasmeir arranged his freedom and the murder of the lords of Klar.  Unaware of these events, he has grudgingly acceptance regency while he searches for Tavin der''Klar, the last rightful heir of the Thrice Crown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2317,1,to_timestamp('16-SEP-15 11.18.44.664000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fell-drengr',null,null,'Elite warriors of the Mountain Home, the &thorn;engill, and the magnr.  They wear ceremonial black armor, and carry symbolic weapons:  the hamarr and the &oslash;x.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2343,1,to_timestamp('17-SEP-15 09.20.26.131000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Circle, The',null,null,'A circle of gray granite set into the heart of S&aelig;m&eth; Khazjadi, measuring ten spans in diameter.  It is even with the rest of the floor, but the surrounding llearth creates the illusion of a ridge.  The circumference of the Circle is marked by nine carvings: nine engraved circles drawn enameled with silver and black to show different phases of fullness. Within the nine orbits is a pattern of twenty-one stars, and within that pattern is engraved an elaborate runic symbol.  According to legend, the twenty-one stars represent the twenty-one brothers whose blood became the magnr. Children are brought to the Circle to be judged by the Droginn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2364,1,to_timestamp('17-SEP-15 02.22.28.178000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Skyldr',null,null,'A servant among the du&aring;rg sworn to the service of a &thorn;engill or Phaelen.  Such service is considered a great honor and is generally not offered to the Drekka.  This is as opposed to the seneschals, who serve the &thorn;engill as a body and are universally Drekka.  Also known as the Sworn.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1907,1,to_timestamp('03-SEP-15 11.06.41.749000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-&AElig;lm&aelig;dris',null,null,'A city founded by the survivors of &AElig;lm&aelig;dris after the Nameless Hour led them to abandon their settlement at Caonwen.  It was built near the ruins of the ancient mining and smithing town of Mereith.  The Holy Grove of ka-&AElig;lm&aelig;dris, Muir Airgin, was not located in the city itself but on the banks of Muir Airgin.  ka-&AElig;lm&aelig;dris was abandoned when Dubh Gaoth was defeated and the Gloam lifted.  Literally, "Not &AElig;lm&aelig;dris."',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2444,1,to_timestamp('18-SEP-15 08.26.55.050000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Jarrus',null,null,'An apparently invented land in a story told by Durgin to Hl&aelig;ja Zaan-s&oslash;on while the latter was dying in the Mountain Home.  The story was an allegory for the tale of Durgin and magnr Undlflos.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1874,1,to_timestamp('01-SEP-15 02.47.26.590000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Maguur De’na',null,null,'A variant of the shara-aleach which arose after the Gloaming, warriors bound in a martial bond.  They have sworn oaths of silence, to be abandoned only when Dubh Gaoth is vanquished, when "water is fire and earth is air".  They consider themselves bound to Calikar and elithi as a whole rather than to individual Ryche.  Literally translates to "In Darkness, Remembering [the Light]."  Also called the Silent or the Still.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2375,1,to_timestamp('17-SEP-15 02.44.08.481000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Khijak',null,null,'A shortened form of Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2674,1,to_timestamp('23-SEP-15 01.30.30.979000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cadeyrn yr Orain',null,null,'An elithin and lord of Ryche Orain, son of Brandynn yr Orain and Valmwwr yn Ceonwe, husband of Searahyn Orain, cousin of Cynyr yr Orain, father of Anatai yr Orain, lord of Ryche Orain, and famed shuonne.  His son was born Twisted, and his wife died in childbirth.  Famed for his love and devotion to both Cynyr yr Orain and Cyngen yr Orain, Cadeyrn was known as an unrelenting, uncompromising character.  He drew a weapon in Aoldain and some believed him responsible for silencing the Song.  Caderyn perished in the depths of Gaithan Aiur, attempting to prevent Gethin y Kam&icirc;l from laying claim to Anlocht.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2806,1,to_timestamp('25-SEP-15 08.06.44.408000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Calin yr Orain',null,null,'An elithin, lord of Ryche Orain, and former king of Rolar.  Son of Garuen yr Orain and Iliany yn Orain, and father to Arduen yr Orain and Nerrwn yn Orain.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2808,1,to_timestamp('25-SEP-15 08.08.38.923000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Iliana yn Orain',null,null,'An elithin, lady of Ryche Orain, and former queen of Rolar.  Mother to Calin yr Orain, and wife to Garuen yr Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2810,1,to_timestamp('25-SEP-15 08.12.16.615000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Athorea yn Dalornmn',null,null,'An elithin and former queen of Rolar.  Mother to Cynyr yr Orain and wife to Eldeain yr Orain. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1703,1,to_timestamp('29-AUG-15 08.18.58.838000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Laroch &Ucirc;l',null,null,'An artifact created by Zaryth dar Mistral during the Second &AElig;gan Empire.  It may have been the only Ta''lShiahd crafted during that era.  In appearance, it seems to be a crude statuette crafted of brown granite, shaped as a small boat with a crooked mast and an unfurled mast.  There is an unidentifiable object on its deck, shaped vaguely like a box.  When used, gray stars dance along its sails.  The Laroch &Ucirc;l was crafted from a fragment of the Grey Gem and like many of Zaryth''s creations was intended to restore the dead.  It apparently met with success in this regard, though the exact limits and nature of the Laroch &Ucirc;l''s power are not clear.  Its use is widely attributed with opening the gap into Gahgra which started the Gh&ucirc;l War.  After Zaryth''s banishment, the Laroch &Ucirc;l was given into the custody of Ym du''Seraa and was later used by Nara to resurrect him, earning him the appellation Calik Twiceborn.  In doing so, she sacrificed her own life to prevent the rising of the gh&ucirc;l.  Eventually, the artifact was taken into exile by the Calikari and used by the na''k&ouml;r (inadvertently) to restore the life of Aneiron y Marel, perhaps unleashing a new gh&ucirc;l on the world.  Translate to the Key of Echoes or the Key to Echoes (or, more simply, the Key).',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3069,1,to_timestamp('10-OCT-15 11.19.49.336000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Geshrin der''Baasmeir',null,null,'The lady of House Baasmeir, wife to Evram der''Baasmeir, and mother to Grenna der''Baasmeir.  After her husband''s imprisonment in Ajer-D&aelig;n, she plotted his release and vengeance upon the line of Klar with her mother-in-law, Dachma der''Baasmeir.  Shortly before the fire-light, she murdered Aldar der''Annache, stabbing him in the back with a blade coated in naroch.  Later, she was interrogated and executed by Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3070,1,to_timestamp('10-OCT-15 11.22.20.454000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Theobold',null,null,'An elderly servant of House Annher.  He told Kisel Gres''Mehar the tale of Garath der''Annher, son of Garani der''Annher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3071,1,to_timestamp('10-OCT-15 11.26.18.688000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Garath der''Annher',null,null,'A young lord of House Annher and son of Garani der''Annher.  He was murdered by base-born criminals in the town of Alsfeld approximately a year before the coming of the Spheres, who forced him to drink waters infected by the Grim.  His death led to the meeting of Aldar der''Annache and Garani; and some believe to the slow madness of his father.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3072,1,to_timestamp('10-OCT-15 11.32.12.972000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kamu''heir',null,null,'An expression among the Aljer, meaning roughly ''Good Luck.''  The connotation is that luck will be required.<br />A battle during the Great War, in which Arastel betrayed his captains and allies to their deaths because he found the challenge unworthy of him.  Prior to his betrayal, the battle was assumed lost by the Triumvirate.  (The battle and the Aljer word are likely connected.)',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3073,1,to_timestamp('10-OCT-15 11.35.20.364000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Festival of Gr&icirc;th, The',null,null,'An annual three day festival among the Aljer, celebrating the Gr&icirc;th.  It is celebrated throughout the Thrice Kingdom, but the most famous celebration occurs at Gh&icirc;lar, which is believed to have been the original site of the Gr&icirc;th.  The celebration includes dancing, carnival activities, a masquerade, and a ritual procession into the Deep Dark.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3074,1,to_timestamp('10-OCT-15 11.37.37.085000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kauthon der''Mrier',null,null,'A Seeker charged with extracting information from the na''k&ouml;r in Ajer-D&aelig;n.  He was a cruel, sadistic man.  It is unknown whether he survived the fire-light.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2371,1,to_timestamp('17-SEP-15 02.38.18.871000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sl&aacute;-mork',null,null,'A collection of forges mentioned in the legends of Khijak the Khazjad.  They were located at the base of the mountain called B&acute;l.  The greatest forge was known as Onlendr, the Hellforge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2373,1,to_timestamp('17-SEP-15 02.42.02.024000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Onlendr',null,null,'A famed forge mentioned in the legends of Khijak the Khazjad, located in a collection of forges known as Sl&aacute;-mork at the base of the fiery mountain called B&acute;l.  Also known as the Hellforge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2380,1,to_timestamp('17-SEP-15 02.52.29.140000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Urag''s Anvil',null,null,'A small inn in the city of I&aring;rth, located near the &thorn;ingh&aacute;.  It caters primarily to the sons of wealthy merchants from the outer hurtts.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2387,1,to_timestamp('17-SEP-15 03.40.44.457000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grundr Sun&eth;un-s&oslash;n',null,null,'An ancient king of I&aring;rth.  He is credited as the king who broke the power of Zjeryn e''Gur Dao''Ast and first uttered the words, "Not with a thousand hammers, not if given a thousand years."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2393,1,to_timestamp('17-SEP-15 06.22.43.120000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kinthrak',null,null,'A human man encountered by Diarin yn Marel yn Ast in Gh&icirc;lar.  He followed her, and it was later revealed by his tattoo that he was a member of Agra''Dalolth''Dakaj.  He was in Gh&icirc;lar in service to Liarin a''Gur Dao''Ast, to whom he led Diarin after their meeting.  His current fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2354,1,to_timestamp('17-SEP-15 09.46.10.427000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kardu&icirc;n',null,null,'An island in the Great River covered with a great, sheer plateau.  An elithin village, Anduil, is crafted into caverns carved into its base.  A strange citadel lies atop the plateau, predating Calikar.  It is considered cursed by the elithi.  There is a warding stone warning against entering the citadel, written in the Pure Tongue and located near an Egra Door.  While traveling between Aljerbach and Rolar, the na''k&ouml;r visited Kardu&icirc;n and entered the citadel, where they saw what appeared to be visions of themselves.  Drolock''harka also claimed the Serpent Blade here.  Also known as the Lone Isle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2395,1,to_timestamp('17-SEP-15 06.27.55.526000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dure''marg',null,null,'"Soul-slave" in the Pure Tongue.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2344,1,to_timestamp('17-SEP-15 09.24.59.676000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tomb of Calik, The',null,null,'The place where the body of Calik Twiceborn was entombed after his death, supposedly constructed and concealed by his son, Peredur y Senyar.  The tomb contained the Door of Eternity, which protected the path back to Llua''la, as well as several of Calik''s most sacred treasures, including the Laroch &Ucirc;l.  Its location was a carefully guarded secret, until the tomb was opened by Cyngen yr Orain during the Grey War.  The tomb lies beneath the Nameless Temple in Diris Alonne, and both it and the paths leading to it are crafted from llearth.  Also known as the Grave of Calik, although this term is more frequently employed to refer to Uahd isch Calikahd as a whole.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2102,1,to_timestamp('09-SEP-15 11.02.47.303000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kayla yn Lar''ryn',null,null,'The former wife of Argoel y Rhuaidh.  She perished during the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2103,1,to_timestamp('09-SEP-15 11.03.39.551000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kauna yn Rhuaidh',null,null,'The daughter of Argoel y Rhuaidh and Kayla yn Lar''ryn.  She died during the Nameless Hour while still a young child.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2104,1,to_timestamp('09-SEP-15 11.09.18.551000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Thearah yn Junwyr',null,null,'The wife of Vindain y Rhuaidh.  After her daughter, Saura yn Rhuaidh, was born Twisted, and her son, Mathwyn y Rhuaidh (as well as many of her loved ones) died during the Nameless Hour, she went off alone into the Gloam to meet her end.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2105,1,to_timestamp('09-SEP-15 11.11.30.494000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Saura yn Rhuaidh',null,null,'The daughter of Vindhain y Rhuaidh and Thearah yn Junwyr.  She was born Twisted and left to die in the Gloam.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2110,1,to_timestamp('09-SEP-15 12.38.06.548000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Black Company, The',null,null,'The name by which the Fellowship of Truth was labeled by the Veiled Tribunal.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2111,1,to_timestamp('09-SEP-15 12.40.51.519000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Niara yn Wuhlain',null,null,'Daughter of Arienne yn Wuhlain, lover of Tashyr y Srainne, member of the Fellowship of Truth, and Speaker of considerable skill.  She perished during the Gloaming while pregnant with Tashyr''s child.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2112,1,to_timestamp('09-SEP-15 12.47.03.745000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sharolyn yn Rhyfelwr',null,null,'A current member of the Tiaryche among the survivors of ka-&AElig;lm&aelig;dris.  The head of the Ryche is Kai y Rhyfelwr, but he renounced the honor to stand among the Bych''Tair.  She is a distant cousin of the main bloodline.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2113,1,to_timestamp('09-SEP-15 12.52.41.867000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Srainne',null,null,'A house of the a''Tiaryche in Rhuanedd.  Their last known member is Tashyr y Srainne.  Their symbol is a barn swallow.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2114,1,to_timestamp('09-SEP-15 12.55.44.063000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Wuhlain',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  The current head of the house is Saoirse y Wuhlain.  Their symbol is a rock wren.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2115,1,to_timestamp('09-SEP-15 12.57.16.820000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Faolain',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  The current head of the house is Grainne yn Faolain.  Their symbol is a wood thrush.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2117,1,to_timestamp('09-SEP-15 01.03.29.917000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Keb''bryn',null,null,'A house of the elithi in Rhuanedd, accounted among the Tiaryche.  It is inaccurately presumed to have been extinct since the Gloaming.  Their symbol was an osprey.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2118,1,to_timestamp('09-SEP-15 01.06.42.920000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Methuel',null,null,'The name assumed by Tashyr y Srainne after the Gloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2120,1,to_timestamp('09-SEP-15 01.41.16.831000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Silence the Wind',null,null,'See Go To Meet Calik.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2122,1,to_timestamp('09-SEP-15 01.43.01.995000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Wind Wraith',null,null,'An alternative term for a Mist Wraith.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2124,1,to_timestamp('09-SEP-15 03.29.22.961000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Amuuth',null,null,'A house of the elithi in Aduine, accounted among the Tiaryche.  They are considered extinct since Camryn''s Pride.  Their symbol was a kingfisher.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2455,1,to_timestamp('18-SEP-15 09.10.52.742000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Veiled God, The',null,null,'An alternative name for Kaylus among the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2489,1,to_timestamp('19-SEP-15 07.30.07.338000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mask of Mirrors',null,null,'A Crowned illusion placed over a person.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2490,1,to_timestamp('19-SEP-15 07.33.09.135000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Old Man, The',null,null,'A name by which Ariel durahl Durain, Serrik Very''ron, and Davian Lamar (and possibly others) refer to a common person.  It seems likely that this person is the former Headmaster of the University of Xaln, Garg amar Mrel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2491,1,to_timestamp('19-SEP-15 07.33.52.198000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Urag''s Tears',null,null,'An alternative term for the Tears of Urag.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2492,1,to_timestamp('19-SEP-15 07.38.50.134000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'V&auml;xj&ouml;',null,null,'A hurtt approximately one hundred miles north of the Dal Ammar in the kingdom of I&aring;rth.  It is the southernmost village on the Iron Road.  There is a significant Deep Gate located here, a nexus of the Deep Reaches.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2493,1,to_timestamp('19-SEP-15 07.44.31.686000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tears of Urag, The',null,null,'An omen, or beacon, summoned by the Hammar of the kingdom of I&aring;rth, which takes the form of the Roof weeping sediment of brown and gray flakes, coating the land and creating a light mist.  It marks the coming of the ki''tahj.  If the Blood Screamers reach the Iron City, mold-varnaan is sounded, the Tears turn red, and the beacon towers of southern I&aring;rth are lit blue, creating a chain that extends through Berak to Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2495,1,to_timestamp('19-SEP-15 07.49.48.840000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-deer',null,null,'Wild, reptilian creatures of the F¿irst D¿omain. Translates literally to “Not Deer.”',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2496,1,to_timestamp('19-SEP-15 07.51.59.161000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'T''nar',null,null,'Wild, insectoid creatures of the F¿irst D¿omain, approximately the size of fox. Naturally reclusive, they are known to spray those who frighten them with a slightly caustic, foul smelling ichor that is difficult to remove.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2497,1,to_timestamp('19-SEP-15 07.54.30.613000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ilin Resin',null,null,'A dark compound used by the du&aring;rg to polish wood.  When mixed with salt and acid, it creates a deadly poison.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2501,1,to_timestamp('19-SEP-15 08.00.37.051000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Finder, The',null,null,'A moniker applied to Edim''yl Moriel during the Second &AElig;gan Empire. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2502,1,to_timestamp('19-SEP-15 08.02.46.408000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'He Who Seeks',null,null,'A potential translation for Lukifan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2503,1,to_timestamp('19-SEP-15 08.02.59.966000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'He Who Finds',null,null,'A potential translation for Lukifan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2505,1,to_timestamp('19-SEP-15 08.13.31.417000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Corander',null,null,'A name or title by which Baragur has referred to Amber.  Amber has also on rare occasion dreamed as a woman called by the same name.  Memories of these dreams were not clear on awakening.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2506,1,to_timestamp('19-SEP-15 08.17.36.843000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Asfird',null,null,'A minor house of the kingdom of I&aring;rth, mentioned among notes left by Ariel in Rolar.  Its significance is unclear.  Einmyria Alva-d&oslash;tter is daughter of this house.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2509,1,to_timestamp('19-SEP-15 08.19.40.226000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Atra',null,null,'An alternative name for Aru''darian.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2467,1,to_timestamp('18-SEP-15 11.37.49.155000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gan-mork',null,null,'A region of the Iron Kingdom several days southeast of Irongate.  The Iron Army achieved a victory here during the War of Seven Roofs, but it was a ruse.  A hidden army of shorn rose from the surrounding hills and slaughtered the host.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2014,1,to_timestamp('07-SEP-15 08.24.40.452000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Heima-So&thorn;astr',null,null,'The first city established by the du&aring;rg in the Iron Kingdom.  It still exists as a minor hurtt but was largely abandoned when I&aring;rth was built over the ruins of Aljaberin.  It means "The Last Home."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1963,1,to_timestamp('05-SEP-15 08.46.24.341000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lattice Bridge, The',null,null,'An alternative name for Areach isch Brean-M&icirc;in, used primarily among the uudhuur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1922,1,to_timestamp('04-SEP-15 09.42.21.739000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'I-luer',null,null,'A white stone that is hard as steel but lighter than water.  Its use is common in the great works of the elithi.  I-luer can be exposed to Grim-tainted water for decades without suffering decay and is thus used in the construction of the Bright Fleet.  The stone is created from luer by a process of transformation, discovered by the great maker, Amranwen yr Inythyn.  This alchemy is a carefully guarded secret no known outside the Bright Kingdom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1934,1,to_timestamp('04-SEP-15 10.26.58.557000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'M&aelig;lorwen yn Ash''shwl',null,null,'The first and only queen of Shuon Ashhal, acclaimed as both Maker and warrior.  She crafted the first stone swords and armor, the m&aelig;lorwi, which now bear her name.  During her reign, the heart of the Bright Legions stood in Shuon Ashhal and she was first among the Tryptychs.  She is credited as the Mother of the Bright Legions and is commonly remembered as the "Warrior Queen."  She perished when Shuon Ashhal was abandoned during the War of Ash, refusing to depart the city. There is a legend that more than a thousand d&aelig;n''shee fell before her life was claimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1964,1,to_timestamp('05-SEP-15 08.47.02.788000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Path of the Bright Face, The',null,null,'Literal translation of Areach isch Brean-M&icirc;in.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2680,1,to_timestamp('23-SEP-15 02.20.10.367000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Coward Councilor, The',null,null,'An alternative name for Pithras D&aelig;m during the Second &AElig;gan Empire.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2025,1,to_timestamp('07-SEP-15 01.17.03.653000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'C&aelig;rwyn yn Faor&ucirc;n',null,null,'One of the Bych''Tair, she wields a battle axe.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2702,1,to_timestamp('24-SEP-15 09.45.01.762000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gate of Chara, The',null,null,'One of the four gates of Rolar.  It connects the city with the northern regions of Nermere and the lands of Chara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2458,1,to_timestamp('18-SEP-15 09.14.39.078000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Greater Circle, The',null,null,'One of three echelons, or pantheons, among the gods of Demarge:  the Greater Circle, Lesser Circle, and Least Circle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2498,1,to_timestamp('19-SEP-15 07.56.04.600000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yendryn',null,null,'A small village during the time of the Second &AElig;gan Empire and birthplace of Edim''yl Mori''el.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2500,1,to_timestamp('19-SEP-15 08.00.25.028000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'True Sighted, The',null,null,'A moniker applied to Edim''yl Moriel during the Second &AElig;gan Empire. ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2704,1,to_timestamp('24-SEP-15 09.46.10.145000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gate of Erinin, The',null,null,'One of the four gates of Rolar.  It connects the city with the eastern region of Edelmere.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (828,1,to_timestamp('14-AUG-15 10.50.56.646000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kalit’edon Emari',null,null,'A people of the Death''s Blades Mountains, who live in the halls of Udmar Cajar in the ruins of Uragar.  They keep their existence carefully secret from the People of the Brown Plains and the nations south of the Death''s Blades Mountains.  Their veins run with silver blood, and they are imbued with a natural silver aura which they can suppress.  According to the legends of the Kalit’edon Emari, they are descended from the Silver Legion of Uragar and were given the Silver Gift during after saving the life of the son of the Storm Lord during the Great War (from the assassins of Az-Azaal).  They further believe that during the last days of the war, they were entrusted to ward the city while the Great Lord gathered his armies.  After the Storm Lord''s sons faltered and Marianthala, the wife of the Great Lord, perished, the majority of the Silver Legion betrayed the Great Lord and turned to the service of Arastel.  The remainder fled into hiding.  The Kalit’edon Emari believe themselves the descendants of those who fled.  They consider themselves cursed for that cowardice (they are infertile) and have devoted themselves to waiting for the day of the Great Lord''s return, hoping for his forgiveness.  (Note that the Shining Ones are only infertile in the sense that they cannot reproduce among themselves.  They can reproduce with humans, which is why they preserve children left in the Valley of the Cursed and bring them to Aul''dyn.  The men who are used in this capacity are unaware of their service.  Only Memar Korain was ever brought to Udmar Cajar and made aware of the Kalit’edon Emari.)  Literally means "Children of the Silver Gift."  Also known as the Children of the Gift, the Unredeemed, the Blessed, and the True-Blooded.  They believe that they will become the Redeemed when the Great Lord returns.  The Kalit’edon Emari are sworn enemies of the Tholar, whom they believe were created by Az-Azaal by corrupting one of their member, an act of retaliation for their stalwart defense of Uragar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2126,1,to_timestamp('09-SEP-15 03.31.12.240000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'War of Tears, The',null,null,'A period of nearly a hundred years in which the Bradwr, those responsible for Camryn''s Pride, were hunted to their extinction.  Also known as the Long Hunt.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2127,1,to_timestamp('09-SEP-15 03.31.27.774000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Long Hunt, The',null,null,'An alternative name for the War of Tears.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2128,1,to_timestamp('09-SEP-15 03.32.01.602000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Apostate',null,null,'An alternative term for one of the Bradwr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2131,1,to_timestamp('09-SEP-15 03.42.05.800000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aderyn yn Valuch',null,null,'An elithin woman, wife of Meilyr y Senyar and father to Riordan y Senyar and Meirwen yn Senyar.  She survived the death of her husband and Camryn''s Pride to later perish in the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2132,1,to_timestamp('10-SEP-15 02.44.41.223000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Spark, The',null,null,'An alternative term for the Gift as it applies to Shaping.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2133,1,to_timestamp('10-SEP-15 02.54.16.785000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Dulain',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  The current head of the house is Wendran y Dulain.  Their symbol is an egret.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2134,1,to_timestamp('10-SEP-15 03.06.04.632000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Branna',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  The current head of the house is Blathnaid yn Branna.  Their symbol is a meadowlark.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2136,1,to_timestamp('10-SEP-15 09.22.32.607000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Realm of Peers, The',null,null,'Translation of Kal''k''zh&ucirc;m.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2137,1,to_timestamp('10-SEP-15 09.36.16.785000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shard of Reflection',null,null,'The name used to refer to the Shiahd which allow access to Kal''k''zh&ucirc;m.  They take the form of assorted jewelry, shaped or marked with both a crescent moon and the symbol of Hestil.  Use of a Shard is restricted to Shapers.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2139,1,to_timestamp('10-SEP-15 11.12.17.951000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Llewyn y Fohl',null,null,'An elithin, husband of Niamh yn Rhuaidh and father of Daryn y Rhuaidh.  Guided by a prophecy by his wife that he would "die in the dark depths", he came to live in the eternal light of Diris Maguin, but perished falling into the depths of Muir Airgin while playing with his son.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2140,1,to_timestamp('10-SEP-15 11.13.31.573000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Daryan y Rhuaidh',null,null,'An elithin boy and the son of Niamh yn Rhuaidh and Llewyn y Fohl.  It is unknown whether he possesses the Sight.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2141,1,to_timestamp('10-SEP-15 11.15.12.596000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Breanyn yn Uurmyr',null,null,'An elithin, wife of Rhydian y Rhuaidh and father to Suian yn Rhuaidh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2143,1,to_timestamp('10-SEP-15 11.21.16.747000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rhydian y Rhuaidh',null,null,'An elithin, son of Ceridwen yn Rhuaidh and Gahain y Orynuch, husband to Breanyn yn Uurmyr, and father of Suain y Rhuaidh.  He died in the Ungloaming.  He did not possess the Vision.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1824,1,to_timestamp('01-SEP-15 08.35.21.388000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shenrin e''Gur Dao''Ast',null,null,'The first king to unite the d&aelig;n''shee under a single banner, creating the united nation of Kailar, likely in response to the Land Gift.  He initiated the War of Four Kingdoms, in which he and his son, Mi''Shenrin u''Gur Dao''Ast, perished.  His second son, Sendar e''Gur Dao''Ast surrendered and was dragged back to live the rest of his life as a crown-less king beneath Diris Alonne.  Shenrin had five wives, three of whom died at the Battle of Three Queens.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2510,1,to_timestamp('19-SEP-15 08.28.38.652000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sayori',null,null,'A girl encountered by Durgin in war-torn nation of Quaul during the time when he succumbed to grodphyr.  She bore a strong physical resemblance to the girl Natalie encountered by the na''k&ouml;r in Ajer-D&aelig;n and gave Durgin a pendant identical to the one later given to Malnos by Natalie in Aljerbach.  Durgin credits the child with "saving him," and later she became his adopted daughter.  She died of natural illness in Demarge. leading him to sink for a time into despair and drunkenness (when he gambled away the pendant), but she later returned to him in the form of dreams; eventually she guided him to Aljerbach, where he was meant to seek out the na''k&ouml;r and wait for a Sign.  She has not appeared to him since his arrival in the Thrice City.  He also calls her Yori and Mistress.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2511,1,to_timestamp('19-SEP-15 08.28.52.165000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Yori',null,null,'A diminutive form of Sayori.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2461,1,to_timestamp('18-SEP-15 09.32.08.095000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mikhan Sijfinnur-s&oslash;n',null,null,'A du&aring;rg of magnr Undlflos, son of Sijfinnur Jaako-s&oslash;n and Ingjard Erica-d&oslash;tter, half-brother to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Honvir Sijfinnur-s&oslash;n, Signy Fylla-d&oslash;tter, and Flos Sijfinnur-s&oslash;n, brother to Folkvar Sijfinnur-s&oslash;n and Lell Ingjard-d&oslash;tter, half-brother to Valmiki Sijfinnur-s&oslash;n, adopted brother to Hl&aelig;ja Egill-s&oslash;n, husband of Thelma Liv-d&oslash;tter, and father to Hormund Mikhan-s&oslash;n, Lyting Mikhan-s&oslash;n, and Ragnhild Thelma-d&oslash;tter.  Despite the deeds of his mother and her eventual exile from the magnr, he remained in the house until his death from natural causes.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2494,1,to_timestamp('19-SEP-15 07.47.32.076000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mold-varnann',null,null,'An alarm sounded in the Mountain Home to mark the coming of the ki''tahj to the Iron City.  The Hammar cause the mountain to tremble and be filled with the sound of deep drums.  When the mold-varnann is summoned, the Tears of Urag weep red throughout the Iron Kingdom, the beacon towers of Dal Ammar are lit blue, starting a chain that extends throughout Berak and Aljerbach, awarning that death is coming.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2465,1,to_timestamp('18-SEP-15 09.57.26.911000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Weapon, The',null,null,'A magick cauldron apparently used by Arastel to break the outer hurrts and fortresses of the Iron Kingdom.  It seemed to have the power to reduce hills and walls to rubble.  It was in fact a ruse and trojan horse, intended to be captured by the Iron Army and brought back to the Mountain Home.  It was a Shiahd, a Vahhki which unleashed a horde of Gaarok in the Mountain home, nearly leading to its downfall.  The Weapon was eventually destroyed by Eliryndil yn Orain, who sacrificed her life to do so.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1736,1,to_timestamp('29-AUG-15 05.03.39.585000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Argoel y Rhuaidh',null,null,'One of the na''k&ouml;r and an elithin born into the darkness of Lluagh Maguin.  More commonly known as Kain, a name he assumed after departing the Dark Path.  He dreams as Eldrani do''Kiron, who later later became known as Delsi.  Argoel''s Sphere fell in the Death''s Blades Mountains, where he was saved by the Shining Ones.  Later, Kain came to live among the Ba-kwe in the village of Kanalokamu, where he lived with Wemi, his wife, and his children, including Ana.  It was here that he encountered the na''k&ouml;r and decided to go in search of his past and Sphere in the mountains.  Eventually, he returned to the place of his birth, Lluagh Maguin, where he was nearly killed by Dubh Gaoth before being restored by the power of Midam''s Pome.  Argoel, like many in his bloodline, has the power of Sight.  This manifests as both multi-sensory auras and, more rarely, spoken prophecy.  These visions show both the future and the past, although the former is more common.  They cannot be controlled or interpreted accurately, and they are present even when he tries to block his senses.  He does possess a mirror given to him by his sister in which the visions are absent.  He has also encountered two people who did not possess auras:  Ana and Delsar after he became the Unborn.  Argoel is the brother of Iestyn y Rhuaidh, Niamh y Rhuaidh, Vindhain y Rhuaidh, and Briallen y Rhuaid, son and fourth born child to Myfanwy yn Rhuaidh and Arthud y Mallonwyr, and uncle to Daryan y Rhuaidh. He had a wife, Kayla, and a daughter, Kauna, as well as a niece and nephew to his brother, but they perished during the Nameless Hour (excepting the niece, Saura, who was born Twisted).  He does not recall them, nor has he dreamed of them.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2326,1,to_timestamp('16-SEP-15 11.45.29.671000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ka-grass',null,null,'White, faintly luminescent grass endemic to the First Domain.  Ka-grass can grow from water tainted with the Grim and is deadly to consume.  It is mostly commonly found in villages, towns, and other places where there is a source of light.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2327,1,to_timestamp('16-SEP-15 11.57.56.276000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vi&eth;r-mork',null,null,'A dark and vast fungal wood located a few days'' journey north of the city of I&aring;rth.  Ironwood is harvested here; it is also the den of many dangerous wild beasts.  Durgin attended a hunt here on the day he was formally named Phaelen.  His adopted brother and father, along with more than a score of the family''s honor guard, were murdered by a do''Drak as a result of his hubris.  Subsequently, he fled Irongate, not to return for more than a century.  Magnr Undlflos was disgraced and its seat among the &thorn;en was lost.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2331,1,to_timestamp('17-SEP-15 12.06.01.654000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sign, The',null,null,'A promised portent for which Durgin was supposed to wait in Aljerbach; and for which he still waits.  The promise was made my Yori and somehow relates to the na''k&ouml;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1723,1,to_timestamp('29-AUG-15 10.28.30.320000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Diarin yn Marel yn Ast',null,null,'One of the na''k&ouml;r, a d&aelig;n''shee, and a Harbinger of Eta&ucirc;r.  Diarin''s Sphere fell in Aljerbach.  Diarin dreams as Neatham do''Semaar, who later became the Triumvir, Ki''lan.  Although she physically appears as a d&aelig;n''shee, Diarin is the half-breed child of Eshrella Dao''Ast and Cyngen yr Orain.  The joints of her left arm are silver below the elbow, exposed through the flesh, as are her nails.  She has the tattoo of a cat etched across her chest and abdomen in black and silver.  She is the wife of Therylin y Marel, and their son, Aneiron y Marel, has the characteristics of an elithin.  Apparently, at some point Diarin fled Kailar.  After some indeterminate period of time, she arrived wounded and nearly dead in Calikar and was preserved by Cyngen, who presumably recognized his daughter.  Eventually, she became a respected lord of Calikar.  Currently, Diarin is wandering the Empire of Serinin on the trail of Ceygei dar Mistral.  She has at least two siblings:  Kiarin a''Gur Dao''Ast and Liarin y''Gur Dao''Ast.  At some point, she was associated with the Milita, Agra''dalolth''Dakaj, but the nature of this relationship is unclear.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1574,1,to_timestamp('23-AUG-15 07.51.53.642000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Irienya''s Vision',null,null,'A prophecy spoken by Irienya yn Orain, first queen of Rolar after Ryche Senyar occupied &AElig;lm&aelig;dris, which foretold the fall of the city of Rolar  to the Grey Mist when twins were born to one of the houses of the Emerald City.  The words were:  "First shall ruin visit the City of Song. The daughters of Meril will lament, and then their voices will fade to silence. Her sons will don their forsaken aegis; and thus shall they succumb. Then of the high blood of Calik shall be born two babes of one womb. This shall be the shade of Calik returned and the seed of his betrayal. Last shall descend the gray shadow, cold hunger laid claim from emerald roof to sapphire mist. When the soulless one lays claim to the Throne of Nara, Ash and blood shall be the legacy of the Lord of Erinin; and the cause shall be half the whole. And when that day has come, let him love nothing who does not love sorrow.”  Cyngen yr Orain and Eliryndil yn Orain are thought to be twins mentioned in the prophecy, and the events of the Grey War are thought to be its realization.  It is suspected that Cyngen fled his throne after his father''s death as a result of the omen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3093,1,to_timestamp('10-OCT-15 10.47.59.774000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rallis der''Annher',null,null,'A former lord of House Annher and grandfatherof Garani der''Annher.  Known as a gambler and rake, he squandered much of the fortune and good name of the house.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3094,1,to_timestamp('11-OCT-15 05.26.11.877000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Dwie',null,null,'A kingdom in the Roof of Welst, built atop flows of molten lava.  It is ruled by a class of prophets known as the Talraaga.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3095,1,to_timestamp('11-OCT-15 05.27.28.756000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Melsh',null,null,'Fauna endemic to the Great Cavern.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2149,1,to_timestamp('10-SEP-15 11.48.17.845000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Niamh y Rhuaidh',null,null,'The fifth born of Myfanwy yn Rhuaidh and Arthwydd y Mallonwyr, wife to Llewyn y Fohl, mother to Daryan y Rhuaidh, sister to Iestyn y Rhuaidh, Vindhain y Rhuaidh, Argoel y Rhuaidh, and Briallen yn Rhuaidh,  She possesses the Sight in the form of foretellings, which require touch and which she does not afterward recall.  Niamh was a child during the Nameless Hour, when her mother and father perished.  She was at the Great Tree and did not witness it.  Shortly thereafter, her brothers and sisters went to Meet Calik.  Her husband died when she attempted to save his life via prophecy.  She has lived most of her life over the last centuries in isolation in Muir Maguin with only her son at her side, believing herself cursed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2150,1,to_timestamp('10-SEP-15 11.53.29.306000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arawn y Rhuaidh',null,null,'An elithin and father to Ceridwen yn Rhuaidh and Lahahd y Rhuaidh.  He is considered the progenitor of Ryche Rhuaidh and was among the first of its lords after the Long, Short Years.  According to legend, the Sight was granted to his line when he touched an artifact known as the Archway.  The Sight led him to madness and eventually suicide, the only of the Bright Folk known to have taken his own life.  His prophecies were recorded by his son, Lahahd, who did not himself possess the Gift, and include references to the na''k&ouml;r.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2154,1,to_timestamp('11-SEP-15 12.30.24.382000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Smiling Wolf',null,null,'Literal translation of Ib''yadah.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2155,1,to_timestamp('11-SEP-15 12.30.30.107000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ib''yadah',null,null,'A race of the Bel''caj Ged''don, created by Az-Azaal and N&aelig;lya in the Black Waters beneath Dagar Morduum.  The Ib''yadah appear as normal men and women but are bound completely to the will of their master.  All Ib''yadah are bound by a Zjar Gad&ucirc;r which compels their obedience to Merik and N''koth and prevents their doing harm to their masters.  Translates literally to Smiling Wolf.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2006,1,to_timestamp('06-SEP-15 10.37.55.291000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Edelmere',null,null,'The name of a region of the kingdom of Rolar, located east of Aiun-Malach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1826,1,to_timestamp('01-SEP-15 08.43.40.365000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Four Kingdoms, The',null,null,'A war in the early history of Calikar between the d&aelig;n''shee, elithi, corti, and du&aring;rg.  It was the first conflict in which the d&aelig;n''shee were united under a single king, Shenrin e''Gur Dao''Ast, likely in response to the Land Gift.  Shenrin and his son, Mi''Shenrin u''Gur Dao''Ast, perished in the final battle of the war, the latter in a famed duel with Cadf&aelig;l y Rhyfelwr.  The second son, Sendar e''Gur Dao''Ast surrendered and was dragged back to live the rest of his life as a crown-less king beneath Diris Alonne, thus ending the conflict.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2504,1,to_timestamp('19-SEP-15 08.11.55.321000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Edim''yl Moriel',null,null,'A famed personality during the Second &AElig;gan Empire, one of the discovers of Xaln, a famed explorer responsible for the finding of Chamoch and Semaar (among other wonders), a Headmaster of the University of Xaln, and one of the Councilors of the Council of Twenty-one.  Born Beroth Kwin in the small village of Yendryn (a carpenter''s son), he assumed the name Edim''yl upon becoming a Headmaster.  He informally came to be known as Lukifan, meaning "He Who Seeks" or "He Who Finds", and also as the True Sighted and the Finder.  He perished under suspicious circumstances during the expedition known as the Karl Party and was replaced by N''koth Velar on the Council.  According to legend, Edim''yl possessed a Tal''Shiahd known as the Dousing Rod or Eye of Lukifan, which he carried since a small boy.  How he came into possession of the artifact is unclear.  Malnos believed that the Dousing Rod was brought by the elithi to Calikar.  He searched for it in Rhuanedd but never found it.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2508,1,to_timestamp('19-SEP-15 08.19.31.118000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Opal',null,null,'An alternative name for Aru''darian.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1923,1,to_timestamp('04-SEP-15 09.44.24.044000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bright Fleet, The',null,null,'The fleet of Calikar, great white vessels crafted from i-luer.  The harbor and shipyard of the Bright Kingdom, where the fleet is constructed, is located at Huynduir.  There was once a second great shipyard located at Rhuanedd, but it has been lost since the Gloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1924,1,to_timestamp('04-SEP-15 09.45.34.621000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Emrys yn Esswwr',null,null,'A famed poet and author, of such skill that she was invited to live permanently among the corti.  She presumably died when Sorain fell.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1925,1,to_timestamp('04-SEP-15 09.50.00.081000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arianell yn Essrwwr',null,null,'An elithin acclaimed for beauty and skill as a musician.  She was the founder of Duri-Med&icirc;n.  Arianell was the wife of Leuen of the Silver Tongue, and her slow death to the ravages of Shanaran''s Plague led him to take up the blade and devote his years to vengeance against the d&aelig;n''shee.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1926,1,to_timestamp('04-SEP-15 09.56.30.146000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;ronwy y Rhyfelwr ',null,null,'An elithin warrior, famed as the first female shuonne and also the first of the cara-aleach.  Her bound partner was Leuen of the Silver Tongue.  After Leuen was murdered during the Liar''s Peace, &AElig;ronwy went alone to seek revenge in Kailar.  She is assumed to have perished there.  Also known as the Daughter of the Blades.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1927,1,to_timestamp('04-SEP-15 09.58.58.846000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Daughter of the Blades, The',null,null,'An alternative label for &AElig;ronwy y Rhyfelwr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1928,1,to_timestamp('04-SEP-15 10.03.57.157000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mudwyn y Gaarddwr',null,null,'An elithin and the Caretaker of Diris M&aelig;rysh during the time of the Gloaming.  In the years afterward, he devised a method of grafting breeds of tree and plant with the Holy Trees to preserve strains of fruit and nut that otherwise would have been lost in the absence of light.  He perished during the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1930,1,to_timestamp('04-SEP-15 10.10.00.849000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Child of Grace, The',null,null,'An alternative label for Shanwyn yn Qunur.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1931,1,to_timestamp('04-SEP-15 10.11.47.963000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Qunur',null,null,'A minor house of the elithi in Rolar, primarily known as the house of Shanwyn yn Qunur.  Their symbol is a pigeon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1932,1,to_timestamp('04-SEP-15 10.12.40.041000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Leuen y Senyar',null,null,'See Leuen of the Silver Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1935,1,to_timestamp('04-SEP-15 10.27.38.826000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Warrior Queen, The',null,null,'An alternative label for M&aelig;lorwen y Ash''shwl.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1936,1,to_timestamp('04-SEP-15 10.28.02.971000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mother of the Bright Legions, The',null,null,'An alternative label for M&aelig;lorwen y Ash''shwl.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1937,1,to_timestamp('04-SEP-15 10.29.29.327000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'M&aelig;lorwi',null,null,'The name of the stone armor and weapons of the Bright Legion of Calikar, so-called in honor of M&aelig;lorwen yn Ash''shwl, who according to legend was their Maker.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2013,1,to_timestamp('07-SEP-15 08.22.55.831000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Qehreir',null,null,'The first city built by the corti in the Kingdom of Sorain, abandoned when the Grey City was founded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2015,1,to_timestamp('07-SEP-15 08.25.01.633000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Last Home, The',null,null,'Literal translation of Heima-So&thorn;astr.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2016,1,to_timestamp('07-SEP-15 08.43.12.467000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Stone Brothers, The',null,null,'The name given an early attempt by the d&aelig;n''shee to reclaim the lands lost in the Land Gift, fought primarily between the d&aelig;n''shee and the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3096,1,to_timestamp('11-OCT-15 05.27.51.401000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Farrin',null,null,'Fauna endemic to the Great Cavern.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3097,1,to_timestamp('11-OCT-15 05.29.43.644000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rot, The',null,null,'A mysterious pestilence which befell the Great Cavern in recent years, predominantly the lands of Aljerbach.  It manifested in the withering of plants and the thinning of the hides of beasts.  Minoch al''Fainar was credited with ending this plague.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3098,1,to_timestamp('11-OCT-15 05.31.36.439000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lisomnia',null,null,'A fungal plant used for various medicinal and alchemical purposes.  In its raw form, its touch causes terrible itching.  Also known as Itch Bloom.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3099,1,to_timestamp('11-OCT-15 05.31.52.782000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Itch Bloom',null,null,'An alternative name for lisomnia.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3100,1,to_timestamp('11-OCT-15 05.34.23.862000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Egrid''s Shoppe',null,null,'A storefront in the Inner City of Aljerbach, located on Entder Radius.  It sells herbs, powders, minerals, and other alchemical and arcane substances.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3101,1,to_timestamp('11-OCT-15 05.35.40.839000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Debt Token',null,null,'A coin offered by one of the Durem Erim&aelig; in recognition of a debt of blood.  It can be exchanged with any member of the Order in return for a favor.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3102,1,to_timestamp('11-OCT-15 05.37.15.668000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Il''Catar',null,null,'A word in the Pure Tongue meaning "the Betrayer."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3103,1,to_timestamp('11-OCT-15 05.37.32.249000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Betrayer, The',null,null,'The literal translation of Il''catar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3104,1,to_timestamp('11-OCT-15 05.39.29.392000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aksel der''Ochten',null,null,'A minor lord of Aljerbach and member of the Gauthrin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3105,1,to_timestamp('11-OCT-15 05.42.33.439000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Karrin Square',null,null,'An area of Aljerbach known for its tanneries.  It is located in the Farlo and is the place where the Sphere of Malnos, Diarin, Delsar, Kisel, Malig, and Drolock''harka descended.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3106,1,to_timestamp('11-OCT-15 05.45.38.016000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Death-drinkers',null,null,'A deleterious label applied by the Aljer to those who purposefully imbibe water tainted with the Grim.  They believe that those who survive the Taint are blessed by Eta&ucirc;r himself, thus becoming immortal.  This rite is considered a perversion of the Talinth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3107,1,to_timestamp('11-OCT-15 11.00.58.406000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gr&icirc;th',null,null,'A mythological stone given to Eduril by the child goddess, Ediram, at a time when the clans of man were at war and nearly extinguished.  By its magick, both Eduril of Dindran and Methey of Lun lay with Narianne of Eriol.  Of their union, two twin children were born:  Malen and Terincia.  Both fathers died in the union, and the mother died in childbirth.  The twins were protected by Ediram in a stone hovel called Klar and later bore a child called Agrin, giving birth to the royal clan of the Aljer and uniting their dying people.<br /><br />A ritual in which the Aljer given thanks to Ediram and her father, Eta&ucirc;r, for their salvation.<br /><br />Also see Festival of Gr&icirc;th.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3108,1,to_timestamp('11-OCT-15 11.04.34.570000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Dindran',null,null,'One of three houses that led the people who later became to Aljer to the Great Cavern.  The lords of Dindran were once a high house in the Ninth Nation of Man.  The war between these houses nearly destroyed their people, only ending with the Gr&icirc;th and the birth of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3109,1,to_timestamp('11-OCT-15 11.04.43.005000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Lun',null,null,'One of three houses that led the people who later became to Aljer to the Great Cavern.  Their lords were once the kings of a land of the same name.  The war between these houses nearly destroyed their people, only ending with the Gr&icirc;th and the birth of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3110,1,to_timestamp('11-OCT-15 11.05.00.547000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Eriol',null,null,'One of three houses that led the people who later became to Aljer to the Great Cavern.  Their lords were once the kings of a land of the same name.  The war between these houses nearly destroyed their people, only ending with the Gr&icirc;th and the birth of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3111,1,to_timestamp('11-OCT-15 11.05.53.252000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Keris',null,null,'The brother of Rollan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3112,1,to_timestamp('12-OCT-15 12.45.36.071000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Minoch',null,null,'See zal-Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3113,1,to_timestamp('12-OCT-15 12.45.44.243000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Minoch al''Fainar',null,null,'See zal-Minoch al''Fainar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3114,1,to_timestamp('12-OCT-15 12.48.56.561000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Zal-Minoch al''Fainar',null,null,'A zal-heru and a''serr&aelig; of the Durem Erim&aelig;, assigned as ambassador from Alanon to the kingdom of Aljerbach.  His counsel was instrumental in the na''k&ouml;r being sealed in Ajer-D&aelig;n rather than facing immediate execution.  He is considered as a hero among many of the Aljer for his role in easing the pain of the recent famines, deep-droughts, and the Rot. He bears a striking physical resemblance to the Councillor, Davian Lamar, and there is likely more to his story than is evident.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3115,1,to_timestamp('12-OCT-15 12.51.12.245000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ariel der''Taber',null,null,'See Ariel.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3116,1,to_timestamp('12-OCT-15 12.51.58.518000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Seramus Aurentius',null,null,'A man from the Empire of Demarge.  Little else is known.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3117,1,to_timestamp('12-OCT-15 12.54.29.661000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Talinth',null,null,'The seventh day of the week in the Aljer calendar, a holy day in which the people celebrate their delivery from pestilence by the gods.  Urda and plon are prepared using the arenne tools the manner taught by the god, Kaidor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (746,1,to_timestamp('14-AUG-15 09.01.45.450000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Urda',null,null,'Domesticated animals of the First Domain and smaller, faster, and more aggressive relatives of the bk&aring;r.  Urda consume large quantities of soil and their flesh is rough, resembling stone.  They have broad, shielded heads with five facial horns.  They are consumed as a primary foods source in every Roof of the First Domain but are worshiped as holy beasts among the Aljer.  In other lands, they are commonly employed as beasts of burden.  Urda cannot survive the Grim and thus no longer are found in the wild.  According to the legends of the Thrice Kingdom, their people were once dying of a terrible plague.  They were near extinction when the gods took pity upon their plight and descended from Erdrion.  Barthur guided their ancestors to the urda, G&auml;r taught the making of the arenne tools, and Kaidor guided them to plon and educated them on how to prepare both the meat and plant.  Only then did the illness pass.  The Aljer continue to eat urda and plon on the holy day of Talinth; and only on the Talinth.  The illness does not return for those who observe the ritual.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3120,1,to_timestamp('12-OCT-15 01.07.42.386000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Spoke',null,null,'One of the primary radial avenues of Aljerbah, which extend from the heart of the city toward the outer walls.  The great cross-streets are known as radii (a radius), and the minor streets are known as rows.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3121,1,to_timestamp('12-OCT-15 01.09.01.449000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Radius',null,null,'One of the primary cross-streets of Aljerbach.  The great radial avenues of Aljerbah, which extend from the heart of the city toward the outer walls, are known as spokes, and the minor streets are known as rows.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3122,1,to_timestamp('12-OCT-15 01.10.36.237000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Row',null,null,'A lesser street of Aljerbach.  The great radial avenues of Aljerbah, which extend from the heart of the city toward the outer walls, are known as spokes, and the primary cross-streets are known as radii (a radius).',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3123,1,to_timestamp('12-OCT-15 01.13.58.247000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Radii',null,null,'Plural of radius.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2162,1,to_timestamp('11-SEP-15 10.57.41.156000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'An-Creirwy yn Senyar',null,null,'An elithin, daughter of Cuth&ucirc;l-erin y Senyar and sister to Brean-Mi&icirc;n yn Senyar.  She was assassinated by the d&aelig;n''shee in Lleah-roht during the same assault that took the life of her brother-in-law, K&aelig;ryeah yr Inythyn, only having recently reached her adulthood.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2164,1,to_timestamp('11-SEP-15 11.12.47.025000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cymidei yn Branna',null,null,'An elithin and wife to Manonwy y Senyar.  She was murdered during the Liar''s Peace.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2165,1,to_timestamp('11-SEP-15 11.20.11.685000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ifanna yn Senyar',null,null,'Elithin, last queen of the Long, Short Years, last high queen to rule from Rolar, mother of Leuen y Senyar, Thierin y Senyar, and Shen''r&aelig;th yn Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2168,1,to_timestamp('11-SEP-15 11.28.42.730000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nerys yn Hannur',null,null,'An elithin, wife to Riordain y Senyar, and mother to Ronan y Senyar, Ruari y Senyar, and Tristwen yn Senyar.  She perished during the Nameless Hour, an event witness by her daughter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2172,1,to_timestamp('11-SEP-15 11.44.04.646000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Wynne yn Ainne',null,null,'An elithin, first wife of Meilyr y Senyar, and mother to Myrddin y Senyar.  She perished during the Gloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2174,1,to_timestamp('11-SEP-15 11.48.28.780000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Banhymh yn Senyar',null,null,'An elithin and prince of Ryche Senyar.  He was the younger son of Aruth''eras y Senyar and the father of Swn''Turan yn Senyar.  As a child, he was bound to the song of Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2177,1,to_timestamp('11-SEP-15 11.57.38.151000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shearah yn Senyar',null,null,'An elithin, lord of Ryche of Senyar, dauther of Larwyth y Senyar, and mother to Ganahahd y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2181,1,to_timestamp('11-SEP-15 01.43.55.491000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cariad yr Yeorathe',null,null,'An elithin, member of the Apostates, husband of Meirwen yn Senyar, and father to Llarfor y Senyar.  He was a leader among the Apostates until he perished in the Ungloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2182,1,to_timestamp('11-SEP-15 01.44.53.876000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Llarfor y Senyar',null,null,'An elithin, member of the Apostates, son of Meirwen yn Senyar and Cariad yr Yeorathe.  He was a leader among the Apostates until the Ungloaming. He survived that day, but his current fate is unknown.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2146,1,to_timestamp('10-SEP-15 11.31.52.123000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gahain yr Orynuch',null,null,'An elithin, husband to Ceridwen yn Rhuaidh (the younger) and father to Rhydian y Rhuaidh and Myfanwy yn Rhuaidh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2153,1,to_timestamp('11-SEP-15 12.01.37.303000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tyren y Fohl',null,null,'An elithin, husband to Ceridwen yn Rhuaidh (the elder) and father to R&aelig;wyn yn Rhuaidh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2158,1,to_timestamp('11-SEP-15 10.30.45.952000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ganahahd y Senyar',null,null,'An elithin, lord of Ryche Senyar, and son of Shearah y Senyar.  He was wife to Brean-Mi&icirc;n y Senyar after the death of K&aelig;ryeah yr Inythyn.   Aruth''eras y Senyar was born of their brief, loveless union.  He and his wife perished at the Battle of Legacy in Gh&icirc;lar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2665,1,to_timestamp('23-SEP-15 12.39.24.714000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ymir''s Treachery',null,null,'A name used to describe the dying actions of Ymir Ymin-s&oslash;n, &thorn;engill-rikr of I&aring;rth, where in a bout of prj&oacute;ta-induced madness, he murdered his Skyldr and invoked the Eyes of Phaelen, thus creating Ymir''s Abyss, losing the Weeping Eye, and ultimately perishing.  His house, magnr Khazin, has since that day been disgraced, never again being accounted among the &thorn;en.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2666,1,to_timestamp('23-SEP-15 12.42.26.248000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ymir''s Abyss',null,null,'A pit opened in the Mountain Home during the events of Ymir''s Treachery.  The Weeping Eye is thought to be lost in its depths.  It has since been expanded by the Hammar in the form of the murtl-sang (in search of the eye) to form the Atrium.  Exiles, such as the Streichen, are tossed into Ymir''s Abyss to die.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2667,1,to_timestamp('23-SEP-15 12.44.01.610000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ghola',null,null,'The plural form of gholam.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2668,1,to_timestamp('23-SEP-15 12.52.18.620000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gholam',null,null,'A statue or stone likeness imbued with mobility via the Depsangf.  Plural form is ghola.  The ghola were created by the Hammar over the course of centuries, their existence carefully hidden from the d&aelig;n''shee, using the legend of the fathaigh as a template.  The ghola do not possess souls, intellect, or intent.  They are tools and must be controlled and directed by the ghola, who must be in close proximity to do so.  Einmyria Alva-d&oslash;tter, a paragon of the Hammar, developed a mechanism by which the ghola were imbued with the blood of the d&aelig;n''shee, giving them a taste for the shadow-souled and limited independence.  She used stone taken from Torkel''s Treason to do so.  Durgin later suggested using the blood of a live donor, Diarin yn Marel yn Ast.  The ghola were first revealed during Arastel''s assault on the Mountain Home during the War of Seven Roofs, proving themselves formidable instruments of war.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2669,1,to_timestamp('23-SEP-15 12.53.03.639000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Red Hall, The',null,null,'An alternative name for the hall known as Torkel''s Treason.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1938,1,to_timestamp('04-SEP-15 10.32.49.891000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mertin',null,null,'A famed chiromancer or Shaper among the dluth-chara, remembed for sharing secrets with the elithi which later led to the foundation of the Eshthara.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1940,1,to_timestamp('04-SEP-15 10.36.53.224000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Founder, The',null,null,'An alternative label for Thierin y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1942,1,to_timestamp('04-SEP-15 10.40.22.505000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lowri yn Gaardwr',null,null,'See Lowri the Green.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1944,1,to_timestamp('04-SEP-15 10.59.05.717000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shanaran e''Gur Dao''Ast',null,null,'A king of Kailar, credited with the creation and/or dissemination of Shanaran''s Plague.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1948,1,to_timestamp('04-SEP-15 11.41.29.541000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sons'' War, The',null,null,'An early war between Calikar and Kailar, so named became it lasted several generations, nearly 170 years of conflict.  This was the first war in which the d&aelig;n''shee were organized into the kit''ahj, although it was far from its current incarnation, which was realized under Zjeryn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1570,1,to_timestamp('23-AUG-15 07.33.33.144000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Veomnis e''Gur Dao''Ast',null,null,'A former king of Kailar, father to Zjeryn e''Gur Dao''Ast, grandfather of Asmaal e''Gur Dao''Astl, and great-grandfather of Eshrella y''Gur Dao''Ast.  He orchestrated the War of Ash, the Liar''s Peace at a''Tiernya, and the series of assassinations/wars that followed.  During the War of Measure, he was killed by Brochf&aelig;l in the Abyssal Halls of Kailar while his son watched.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1949,1,to_timestamp('04-SEP-15 11.49.20.701000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'N''h&aelig;l',null,null,'A warlord among the d&aelig;n''shee in the early years of the modern age, before the foundation of Kailar.  He briefly united and led his people against Calikar in the War of the Dawn.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1979,1,to_timestamp('05-SEP-15 09.50.46.702000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Innocents'' War, The',null,null,'A war of reprisal against Kailar following Shanaran''s Plague.  So-named because only the children of Calikar survived the illness.  In actuality, the conflict was mostly waged by the d¿luth-c¿hara and Calikar''s allies among the c¿orti and duårg.  The war ends when the du&aring;rg take and occupy Aljaberin.  The d&aelig;n''shee flee eastward and are presumed undone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3083,1,to_timestamp('10-OCT-15 10.23.56.724000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Elar',null,null,'One of the Free Cities of the Great Cavern.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3084,1,to_timestamp('10-OCT-15 10.25.19.858000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Myrrish Sugar',null,null,'A sweet substance created by insects of the First Domain, similar to honey.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3085,1,to_timestamp('10-OCT-15 10.29.46.590000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vrai''gal',null,null,'An ancient prophet who may have been a historical figure or a myth.  If a man of flesh and blood, he likely lived at least several thousand years prior to the restoration of &AElig;gea.  During the Second &AElig;gan Empire, his name was commonly used as an epithet among members of the Council of Twenty-one, generally with an ironic undertone.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3086,1,to_timestamp('10-OCT-15 10.32.37.469000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Softheart, The',null,null,'A term of endearment by which Durgin''s adopted siblings referred to him in his youth, particularly Rowan Fylla-d&oslash;tter.  This is in opposition to the Stoneheart, an appellation for Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3087,1,to_timestamp('10-OCT-15 10.35.29.331000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Stoneheart, The',null,null,'A title for Khijak the Khazjad.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3088,1,to_timestamp('10-OCT-15 10.38.12.410000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Chara Juiice',null,null,'A sweet spiced beverage of the First Domain, generally reserved for children on days of festival.  It is often served hot.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3089,1,to_timestamp('10-OCT-15 10.40.10.142000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'House Naubein',null,null,'A great house of Aljerbach and member of the Aljerstraad.  Its current lord is Theissen der''Naubein.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3090,1,to_timestamp('10-OCT-15 10.41.26.836000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Theissen der''Naubein',null,null,'The young lord of House Naubein.  After the fire-light, he turned to Minoch al''Fainar for guidance but eventually became his own man and a close friend and adviser of Evram der''Baasmeir.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3091,1,to_timestamp('10-OCT-15 10.42.40.647000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'My conscience as stone, my thoughts as water',null,null,'The first creed of the Al&euml;’uriel Caz’ur Ve’&euml;r&icirc;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3124,1,to_timestamp('12-OCT-15 11.27.55.682000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mathey of Lun',null,null,'One of three lords of the men of the Great Cavern at the time of their arrival in the Great Cavern.  A feud between himself, House Dindran, and House Eriol nearly destroyed their race, only ending with the Gr&icirc;th and the foundation of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3125,1,to_timestamp('12-OCT-15 11.28.58.636000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Narianne of Eriol',null,null,'One of three lords of the men of the Great Cavern at the time of their arrival in the Great Cavern.  A feud between herself, House Dindran, and House Lun nearly destroyed their race, only ending with the Gr&icirc;th and the foundation of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3126,1,to_timestamp('12-OCT-15 11.30.10.549000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Three Bloodlines, The',null,null,'Three ancestral houses whose war nearly destroyed the race of man in the Great Cavern:  House Dindran, House Lun, and House Eriol.  Their union in the Gr&icirc;th created House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3127,1,to_timestamp('12-OCT-15 11.34.16.810000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eduril of Dindran',null,null,'One of three lords of the men of the Great Cavern at the time of their arrival in the Great Cavern, considered by many to be the greatest, father of Vendrick of Dindran, and once-owner of the legendary blade, Ilendur.  In his youth, he was an acclaimed warrior and sage in the Ninth Nation of Man.  He fought the Ajg&ouml;r which brought ruin to that land and wounded it.  He was prepared to fall in that battle but was saved by his friend, Pyrith the Sure.  Later, a feud between himself, House Lun, and House Eriol nearly destroyed their race, only ending when his vision guided his people to the Gr&icirc;th and the foundation of House Klar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3128,1,to_timestamp('12-OCT-15 11.36.55.197000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Azjg&ouml;r',null,null,'A winged d&aelig;mon of flame and shadow that according to the cosmology of the Aljer destroyed the Ninth Nation of Man.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3129,1,to_timestamp('12-OCT-15 11.38.00.141000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ilendur',null,null,'The mythical blade of Eduril of Dindran in the cosmology of the Aljer.  According to legend, the blade was lost in the duel between Eduril and the Azjg&ouml;r that marked the end of the Ninth Nation of Man.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3130,1,to_timestamp('12-OCT-15 11.39.30.239000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pyrith the Sure',null,null,'A mythical figure of the Aljer, who according to legend saved Eduril of Dindran during the latter''s duel with the Azjg&ouml;r, wounding him in the leg with and arrow and dragging him from the field.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3131,1,to_timestamp('12-OCT-15 11.47.02.572000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ninth Nation of Man, The',null,null,'A mythical western kingdom from which the Aljer believe their people originate.  Its formation on the plains of Etheras marked the end of an age of darkness and the beginning of a long era of peace and enlightenment.  According to their legends, it was destroyed by the coming of an Azj&ouml;r, which caused the broken remnants of its people to flee into exile, led by Eduril of Dindran.  In that exile, his clan was joined with House Lun and House Eriol.  Together, they arrived in the Great Roof.  Later, a feud between their houses nearly destroyed the race of man in the Roof, only ending with the Gr&icirc;th and the foundation of House Klar.  The Ninth Nation of Man, if it existed, likely was located in the Roof of Bel Can''nar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3132,1,to_timestamp('12-OCT-15 11.48.09.479000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Etheras',null,null,'A mythical plain in the cosmology of the Aljer, where seven clans joined to form the Ninth Nation of Man.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3133,1,to_timestamp('12-OCT-15 11.49.56.196000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Harl and Hoop, The',null,null,'An inn in Aljerbach, where Geron, the elder brother of Balish worked as a boy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3134,1,to_timestamp('12-OCT-15 11.50.05.124000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Geron',null,null,'The elder brother of Balish.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3135,1,to_timestamp('12-OCT-15 11.51.14.622000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Danin Row',null,null,'A minor side street in Aljerbach, located on Entder Radius.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3136,1,to_timestamp('12-OCT-15 11.51.52.802000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Entder Radius',null,null,'One of the primary cross-streets of Aljerbach, located in the Inner City.  Egrid''s Shoppe is located here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3137,1,to_timestamp('12-OCT-15 11.54.26.109000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sepulch Radius',null,null,'One of the primary cross-streets of Aljerbach, located in the Inner City.  TThe Gold Square is situated at the intersection of Sharon Spoke and Sepulch Radius.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3138,1,to_timestamp('12-OCT-15 11.58.05.084000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sharon Spoke',null,null,'One of the primary radial streets of Aljerbach.  The Gold Square is situated at the intersection of Sharon Spoke and Sepulch Radius.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2184,1,to_timestamp('11-SEP-15 01.55.17.988000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Aloreth',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Prior to the Grey War, their head was M&aelig;lgad yr Aloreth.  Afterwards, he was succeeded by Hyorn yr Aloreth.  Their symbol is a horned owl.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2186,1,to_timestamp('11-SEP-15 01.57.05.842000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Tanyrin',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Their head is Kirnin yn Tanyrin.  Their symbol is a peacock.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2108,1,to_timestamp('09-SEP-15 11.25.53.076000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Arienne yn Wuhlain',null,null,'An elithin Speaker and Healer of great renown, considered a near equal to both Leuen of the Silver Tongue and Sionyn y Randwch.  She was also considered a great friend by the corti and one of few elithi welcome in the Grey City; she dwelt their for many decades.  It was she who grew the Great Tree, D&ucirc;n &AElig;mac&ucirc;r, in Sorain.  She died while traveling between I&aring;rth and Sorain, captured and murdered by agents of the ki''tahj.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2109,1,to_timestamp('09-SEP-15 12.37.40.064000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fellowship of Truth, The',null,null,'A group of elithi who discovered the truth that some among the elithi no longer suffered from the Withering.  They intended to bring this knowledge to the Song, but their plan was discovered.  They were brought before the Veiled Tribunal and exiled as awrywdd, renamed the Black Company.  Their crimes were not publicly declared (and the Gloaming descended before the next Song was summoned), but most assumed that they were sympathizers with or betrayed Calikar to Kailar, as all prior exiles were banished for this cause.  The only surviving member of the Fellowship of Truth is Tashyr y Srainne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2116,1,to_timestamp('09-SEP-15 12.58.53.812000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Ther''ras',null,null,'A house of the elithi in &AElig;lm&aelig;dris, accounted among the Tiaryche.  The current head of the house is Killian y Ther’ras.  Their symbol is an albatross.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2119,1,to_timestamp('09-SEP-15 01.41.06.577000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Go To Meet Calik',null,null,'A phrase among the elithi, generally used to refer to ritual suicide.  Among the people of the Gloam, the phrase Silence the Wind is also used.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2121,1,to_timestamp('09-SEP-15 01.42.18.126000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lyrrin',null,null,'A Wind Wraith encountered by Malnos when he initially arrives in Lluagh Maguin and later killed by him when he returns to &AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2125,1,to_timestamp('09-SEP-15 03.30.21.169000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Camryn yn Amuuth',null,null,'A lord of the elithin, member of the Tiaryche of Aduine, Eshthara, and among the exiles who gathered at An Suragh after the Gloaming.  She is considered responsible for Camryn''s Pride.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2671,1,to_timestamp('23-SEP-15 01.12.40.359000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Thyra Merla-d&oslash;tter',null,null,'A du&aring;rg woman and daughter of magnr Khazin.  Born Eigavald and female, she has been forced to suppress her nature.  Living in the heart of the Mountain Home, this has resulted in eventual prj&oacute;ta and madness, including megalomania.  Thyra swore herself to Arastel and Baragur in return for promises of power and betrayed her people during Arastel''s assault on the Mountain Home, using the Weapon against her brothers and sisters.  She was killed by Eliryndil yn Orain.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2507,1,to_timestamp('19-SEP-15 08.19.19.908000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aru''darian',null,null,'Apparently the dure''marg of Ariel durahl Durain.  He appears to be a tall man with completely black eyes (no pupils) and is utterly devoted to his master.  Also known as Opal and Atra.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2672,1,to_timestamp('23-SEP-15 01.22.16.994000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shorn, The',null,null,'The name applied to the human citizens of Drieria and warriors of Arastel''s army by the elithi and du&aring;rg, so called because they shave their heads and bodies in honor of their God.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2673,1,to_timestamp('23-SEP-15 01.25.32.331000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Irienya yn Orain',null,null,'An elithin woman, daughter of Sharwych yr Orain, mother of Garuen yr Orain, lady of Ryche Orain, and first queen of Rolar after Ryche Senyar occupied &AElig;lm&aelig;dris.  She is famed for having spoken the words of Irienya''s Prophecy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2676,1,to_timestamp('23-SEP-15 02.13.54.832000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Gaarok',null,null,'A form of d&aelig;mon, apparently connected to Baragur in some manner.  In shape, they are winged imps with envenomed tails, but they veil themselves in mist and illusion, concealing their dimension and form.  See also Vahhki and the Weapon.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2677,1,to_timestamp('23-SEP-15 02.18.59.907000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Weaver, The',null,null,'A possible translation of Izmorah.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2678,1,to_timestamp('23-SEP-15 02.19.19.507000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'He Who Lays the Web',null,null,'A possible translation of Izmorah.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2679,1,to_timestamp('23-SEP-15 02.19.24.561000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Izmorah',null,null,'The name assumed by Pithras D&aelig;m when he became one of the M¿ortuani. It means literally "The Weaver" or "He Who Lays the Web" in the in the P¿ure T¿ongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2681,1,to_timestamp('23-SEP-15 02.21.00.449000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aurum',null,null,'A shortened form of Aura''Sarion.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2683,1,to_timestamp('23-SEP-15 02.31.30.516000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Hedwen y Nynwynwr',null,null,'An elithin and lord of Ryche Nynwynwr, brother to the Tiaryche.  He was encountered (and later murdered) by Kaja''lar and Drolock''harka.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2684,1,to_timestamp('23-SEP-15 02.32.17.854000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ezre''el',null,null,'A person or entity mentioned by the Neraphin, Noimead.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2512,1,to_timestamp('19-SEP-15 08.31.09.310000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sigfinnur',null,null,'A du&aring;rg, drekka, and seneschal encountered by the na''k&ouml;r in the Mountain Home.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2663,1,to_timestamp('23-SEP-15 12.28.41.600000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Well of MIrrors',null,null,'A place or cavity where Mirror Stones are kept, in particular the well in the Mountain Home in I&aring;rth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1508,1,to_timestamp('23-AUG-15 12.26.29.326000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Srin',null,null,'A Fold which connects distant places, created by Zaryth dar Mistral during the Second &AElig;gan Empire by the power of the Grey Gem, using Egra Doors as a template.  It manifests as a series of islands floating in a void, connected by varying passages (bridges, strips of land, etc).  Each island has up to eight connections, marked by sigil stones, or Markers.  The pattern of the islands varies according to the order in which they are traversed.  Some islands contain gates, or Srin, which connect to locations in Puell&aelig;.  Some also contain rivers or streams.  The islands float on various different levels, and not all are oriented to same axis.  New islands/gates can be grown via a Seedstone te-Srin.  Paths can also be broken by shattering or binding a Marker.  In the former case, the paths may re-grow over time of their own accord.  Patterns, or sequences, have also been known to shift over time.  There is an entrance into the heart of Srin via the Egra Doors, evidently known to Malnos.  How this manifests is unknown.  Two statues always appear on the first island visited in Srin.  Their significance is unknown.  Also called Oilear, the Isles, the World Between Worlds, and the Breach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2513,1,to_timestamp('19-SEP-15 08.40.05.365000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Warden''s Sigil, The',null,null,'A runic symbol:   two curved lines, one larger than the other, with three circles between and connecting them. Above these symbols, part of and yet separated from the rune, is a jagged line.  The symbol was found on a pendant that once belonged to Natalie, as well on a pendant given to Durgin by Sayori.  It was also found in the house of Garani der''Annher, although the jagged line (the Hunter) was found marked twice in that circumstance.  Garani described it as identifying the Protector, the Ward, and the Hunter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1939,1,to_timestamp('04-SEP-15 10.34.19.211000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Faralla',null,null,'A famed chiromancer or Shaper among the dluth-chara, remembered as the creator of the Tu&euml;ndar.  He was among those who survived the exile but perished in the early years of Calikar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1941,1,to_timestamp('04-SEP-15 10.38.08.567000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Thierin y Senyar',null,null,'An elithin, prince of Ryche Senyar, and middle child of Ifanna yn Senyar.  He was the first high king to rule from Vieun Istue and the first elithin to be bound to a new Song, which occurred while he was a small boy. He was younger brother to Leuen of the Silver Tongue, who renounced his throne, elder brother to Shen''r&aelig;th yn Senyar, and father to Mathonwy y Senyar.  Also known as the Founder and the Boy King.  He perished in battle during the Sons'' War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2307,1,to_timestamp('16-SEP-15 10.38.36.241000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Speaking',null,null,'An alternative name among the du&aring;rg for practicing the Earth Song.  Also known as Earth-Speaking.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2135,1,to_timestamp('10-SEP-15 09.22.17.046000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kal''k''zh&ucirc;m',null,null,'An alternate Fold accessible via a collection of Shiahd that take the form of jewelry:  bracelets, broaches, rings, hairpins, etc.  Each is shaped or marked with a crescent moon and the symbol of Hestil.  These Shiahd are known as Shards of Reflection, and their number is uncertain.  They can only be used by Shapers.  The Fold appears as an alternate world veiled in mist, filled with places and objects and places familiar and strange, a combination of modern locales and ancient memories.  It is respondent to thought: the places, travel, even personal experience.  To enter Kal''k''zh&ucirc;m, one must sleep while wearing one of the Shiahd.  Historically, it has been used as a meeting place for the Council of Twenty-one when physical meeting was impossible, but this can represent its own challenge.  Finding a common place in the Fold can be challenging and few among the Yencari have truly mastered the realm.  Translates to Realm of Peers in the Pure Tongue.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2145,1,to_timestamp('10-SEP-15 11.29.26.949000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Myfanwy yn Rhuaidh',null,null,'An elithin, wife to Arthwydd y Mallonwyr, child of Ceridwen yn Rhuaidh and Gahain yr Orynuch, sister of Rhudian y Rhuaidh, and mother to Iestyn y Rhuaidh, Briallen yn Rhuaidh, Argoel y Rhuaidh, Vindhain y Rhuaidh, and Niamh y Rhuaidh.  She perished during the Nameless Hour.  She possessed the Sight in the form of visions and foretellings.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2308,1,to_timestamp('16-SEP-15 10.40.30.924000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Yen',null,null,'An alternative name for an individual Grod of the du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2138,1,to_timestamp('10-SEP-15 11.10.17.121000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Iestyn y Rhuaidh',null,null,'An elithin and first child of Myfanwy yn Rhuaidh and Arthwydd y Mallonwyr.  Born Twisted in the early years of the Gloaming. he was exiled to die near An Suragh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2142,1,to_timestamp('10-SEP-15 11.16.15.102000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Suian yn Rhuaidh',null,null,'An elithin, daughter of Rhydian y Rhuaidh and Breanyn yn Uurmyr.  She has the Vision in the form of dreams.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2144,1,to_timestamp('10-SEP-15 11.24.00.853000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Arthwydd y Mallonwyr',null,null,'An elithin, husband to Myfanwy yn Rhuaidh and father to Iestyn y Rhuaidh, Briallen yn Rhuaidh, Argoel y Rhuaidh, Vindhain y Rhuaidh, and Niamh y Rhuaidh.  He perished during the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2309,1,to_timestamp('16-SEP-15 10.42.00.008000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Cobalt Legion, The',null,null,'One of the great hosts of the Iron Army of I&aring;rth.  During the War of Seven Kingdoms, they seized the Weapon of Arastel at Vj&auml;kon.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2310,1,to_timestamp('16-SEP-15 10.43.25.282000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Earth-Speaking',null,null,'See Speaking.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2311,1,to_timestamp('16-SEP-15 10.43.34.267000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Earth-Speaker',null,null,'See Speaker.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2682,1,to_timestamp('23-SEP-15 02.25.50.589000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Aura''Sarion',null,null,'A child born of what appeared to be a silver sphere, initially discovered among the possessions of the na''k&ouml;r in the Temple of Thr&icirc;sh in the Innermost City of Aljerbach.  Much later, the light of the sphere guided Delsar, Durgin, and Kaja''lar to Rhuanedd, where the "egg" hatched in the presence of Malnos.  The child was born a golden drake, but transformed into a human child with pure golden eyes, surrounded by a golden nimbus.  He appears to be mentally and spiritually bound to Malnos via his Providenta and is perhaps what is known as a sere''marg.  Known more informally as Aurum.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2664,1,to_timestamp('23-SEP-15 12.36.47.254000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ymir Ymin-s&oslash;n',null,null,'A du&aring;rg, lord of magnr Khazin, and former &thorn;engill-rikr of I&aring;rth.  Born Myrkr, he tried to live his life as Eigaval.  He suffered grodphyr and eventually prj&oacute;ta.  In his madness and paranioa, he betrayed his people.  He disbanded the &thorn;gill, fornicated with his skyldr, and restored the Eyes of Phaelen to the Iron Crown, never allowing them to leave his presence.  Thinking all were conspiring against him, he ordered the fell-drengr to first murder the Hammar (which they refused) and then turn their &oslash;x on themselves.  They would have done so, except his Phaelen, also his dear friend, pleaded for their lives.  Short days later, Ymir called upon the Eyes of Phaelen, murdering his Skyldr to fule their power.  The Mountain Home broke, creating Ymir''s Abyss.  Ymir fell into the abyss and was loss, taking the Weeping Eye with him.  This event was known as Ymir''s Treachery.  Magnr Khazin, in their shame, has never been a house of the &thorn;en since that day.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2687,1,to_timestamp('24-SEP-15 09.13.11.475000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aiun-Malach',null,null,'The plateau upon which Rolar is built, located in the northern regions of the Great Cavern adjacent to the Althrin and Mindal&ucirc;n.  It is surrounded by the great cliffs known as the F&aelig;l&ucirc;n, which expanse is warded by dozens of great watch towers.  Three paths lead to the heights:  the Pass of Meganin, the Pass of Orimys, and Aihte Kamu&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1947,1,to_timestamp('04-SEP-15 11.23.10.709000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Randwch',null,null,'A minor house of Calikar, initially of Rolar and later of Rhuanedd and Shar''ris y Rhuanedd.  It is best known for Sionyn y Randwch.  Their symbol is a golden duck.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1218,1,to_timestamp('16-AUG-15 03.03.33.574000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Corti',null,null,'A people of the First Domain and lords of the nation and city of Sorain.  The corti were known as an artistic, whimsical people with close ties to the kingdom of Calikar.  Once the kiril, they had forgotten their origins before they arrived in the Domain.  They came to the Great Roof from the west with several artifacts entrusted to them by Gai''lal, including the Star Gem, Gai''lal''s Providenta, the Eyes of Phaelen, and Drauth''n&icirc;r.  Originally, they occupied the city of Myramaal near modern-day Harn, but after the Land Gift they founded the city and nation of Sorain.  One of the lords of the corti was entitled Gres''Mahar, indicating that they may have possessed the Grey Gem or some relic crafted of it.  This is supported by the wyrd that protected their Sorain detection,known as the Unfinding.  The corti have been thought extinct since their kingdom fell to the d&aelig;n''shee during the War of the Final Dawning.  They live on average 100-150 years.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2151,1,to_timestamp('10-SEP-15 11.55.06.157000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lahahd y Rhuaidh',null,null,'An elithin and son to Arawn y Rhuaidh, born years before his father touched the Archway.  He is known for having recorded his father''s prophecies and documenting his last, mad years.  He did not possess the Sight.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2152,1,to_timestamp('11-SEP-15 12.00.04.372000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ceridwen yn Rhuaidh',null,null,'An elithin, daughter of Arawn y Rhuaidh (born in his mad years after touching the Artifact), husband to Tyren y Fohl, and mother of R&aelig;wyn yn Rhuaidh.  Ceridwen was the first of Ryche Rhuaidh to be born with the Sight, which took the form of Dreams and the reading of omens.  She was also the first to become seer to Ryche Senyar.<br/><br />An elithin, daughter of R&aelig;wyn y Senyar (named for her grandmother), husband to Gahain yr Orynuch, and mother to Rhydian y Rhuaidh and Myfanwy yn Rhuaidh.  She was born during the early years of the Gloam and never bound to the Song, eventually dying to the Withering at An Suragh.  It is not known whether she possessed the Sight.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2156,1,to_timestamp('11-SEP-15 10.27.26.544000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Brean-Mi&icirc;n yn Senyar',null,null,'An elithin and high queen of Calikar, renowned as the most beautiful of her people to have lived throughout the history of Calikar.  She was briefly married to K&aelig;ryeah yr Inythyn, but he was murdered by the d&aelig;n''shee shortly after their nuptials.  Later, for duty, she was wed to a distant cousin, Ganahahd y Senyar.  Aruth''eras y Senyar was born of their union, the last high king of a united Calikar.  Both she and her husband died at Gh&icirc;lar in the Battle of Legacy.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2163,1,to_timestamp('11-SEP-15 11.12.11.399000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mathonwy y Senyar',null,null,'An elithin, high king of Calikar, son of Theirin y Senyar, father to Fionn y Senyar, and husband to Cymidei yn Branna.  He perished in battle during the Sons'' War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2166,1,to_timestamp('11-SEP-15 11.23.00.083000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Boy King, The',null,null,'An alternative name for Thierin y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2167,1,to_timestamp('11-SEP-15 11.27.36.545000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tristwen yn Senyar',null,null,'An elithin, youngest child of Riordain y Senyar and Nerys yn Hanuur, sister to Ronan y Senyar and Ruari y Senyar.  Considered beautiful and kind, she has rarely spoken since witnessing the events of the Nameless Hour, including the death of her mother, while a small girl.  ',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2169,1,to_timestamp('11-SEP-15 11.32.01.217000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ronan y Senyar',null,null,'An elithin, firstborn of Riordain y Senyar and Nerys yn Senyar, and brother to Ruari y Senyar and Tristwen yn Senyar.  He is known for his physical and temperamental similarity to his uncle, Myrddin y Senyar, particularly his striated eyes and short manner.  When his father perished in the Ungloaming, Ronan abdicated his throne and went into voluntary exile, an act seemingly against his nature and which no one anticipated.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2670,1,to_timestamp('23-SEP-15 01.04.32.378000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Torkel''s Treason',null,null,'An act of betrayal by Torkel Unwin-s&oslash;n of magnr Ostogr.  Born Drekka, his condition was hidden by his father (he was named Eigavald), who was &thorn;engill.  Unequal to his position and envious of those around him, he eventually betrayed his people, conpsiring the d&aelig;n''shee and opening a path for the them into the Mountain Home.  This event was known as Torkel''s Treachery.  The ki''tahj were eventually replled, but Ostogr was dishonored, not being accounted among the &thorn;en since that day.  Torkel perished on that day, murdered by his own father.<br /><br />Also the area in which this treason and the ensuring battle occurred, located in the Mountain Home.  Called the Red Hall because the stone was stained red by the blood of the d&aelig;n''shee.  Stone from this area was subsequently employed by Einmyria Alva-d&oslash;tter to "teach" the ghola to hate the shadow-souled.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2675,1,to_timestamp('23-SEP-15 02.12.27.965000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Vahhki',null,null,'Shiahd of indeterminate purpose, believed by the Die Nearal to be conduits to a realm where the Gaarok dwell.  Whether they are in fact representation of a Fold, conduits to a Fold, or something else altogether is unclear.  It is also unclear whether they are limited to summoning/holding Gaarok, but it is evident that those things linked to the Vahhki are unable to wander far from its physical location.  In shape, they generally resemble a container of some kind, such as a cage or box.  Arastel''s Weapon was a cauldron.  They require active manipulation.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2689,1,to_timestamp('24-SEP-15 09.18.08.252000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aihte Kamu&icirc;n',null,null,'A narrow staircase which leads from Nermere to the heights of Aiun-Malach, said to be defensible by a single warrior.  One of three paths allowing entrance to the plateau.  The others are the Pass of Meganin and the Pass or Orimys.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2690,1,to_timestamp('24-SEP-15 09.19.43.930000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pass of Orimys, The',null,null,'An eastern pass on the border of Edelmere and Nermere which leads to the heights of Aiun-Malach.  One of three paths allowing entrance to the plateau.  The others are the Pass of Meganin and Aihte Kamu&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2691,1,to_timestamp('24-SEP-15 09.20.38.280000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Pass of Meganin, The',null,null,'An western pass in Westermere which leads to the heights of Aiun-Malach.  One of three paths allowing entrance to the plateau.  The others are the Pass of Orimys and Aihte Kamu&icirc;n.  The Pass of Meganin is warded by the great fortress known as Btul Erym.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2692,1,to_timestamp('24-SEP-15 09.21.35.246000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Btul Erym',null,null,'An elithin fortress which guards the Pass of Meganin, controlling access to Aiun-Malach.  Also known as the Last Wall.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2693,1,to_timestamp('24-SEP-15 09.23.13.623000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Valley of Uanahd, The',null,null,'A natural depression in the heights of Aiun-Malach.  The city of Rolar lies in its southern regions.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2694,1,to_timestamp('24-SEP-15 09.31.28.397000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gandar&icirc;n',null,null,'See the Watchtower of Gandar&icirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1943,1,to_timestamp('04-SEP-15 10.55.19.656000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lowri the Green',null,null,'An elithin and lord of Ryche Gaardwr.  She is said to have planted the gardens of Iny&ucirc;n and to have introduced the many species of flowers and trees that could grow from the light of the Sacred Groves.  Legend claims that trees flowered her presence and seeds grew from the barren earth.  Leuen of the Silver Voice was her student in his youth.  According to the legends of the Calikari, Lowri was the initial carrier of Shanaran''s Plague and died to its ravages.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1965,1,to_timestamp('05-SEP-15 08.58.15.834000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Areach isch Brean-M&icirc;in',null,null,'An arched  bridge constructed by K&aelig;ryeah yr Inythyn over the Althrin near the elithin city of Gh&icirc;lar.  It now lies beside a human city of the same name.  Legend speaks that K&aelig;ryeah conceived the bridge in order to win the heart of his beloved, Brean-M&icirc;in y Senyar.  It is constructed entirely of iendur, with no strand thicker than a spider''s thread.  Its surface is always cool to the touch but never cold.  It appears fragile, with the seeming of glass, but it is strong enough to bear the weight of a legion atop adur-&auml;l steeds.  The bridge''s prisms capture and scatter localized light, creating the impression of rainbows.  Areach isch Brean-M&icirc;in is considered one of the great wonders of the Golden Age of Calikar.  It is said that the bridge is greatly feared by the d&aelig;n''shee, who have never crossed its length.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1966,1,to_timestamp('05-SEP-15 08.59.29.169000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iendur',null,null,'A semi-transparent, crystallized stone, noted for its tendency to fracture light patterns.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1967,1,to_timestamp('05-SEP-15 09.08.37.387000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Golden Age, The',null,null,'An era of the Second &AElig;gan Empire after the Gh&ucirc;l War and before the Great War, known as an extended period of peace, prosperity, and intellectual and cultural progress.  It lasted nearly two centuries.<br /><br />An era in the history of Calikar between the end of the War of Measure and the beginning of the Slaves'' War, lasting nearly four centuries.  It is marked by relative peace, expansion, and the creation of many of the artistic wonders of ancient Calikar.  Humans also arrive during this era and found Gelastir.  Also known as the Long Peace.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1968,1,to_timestamp('05-SEP-15 09.11.25.299000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Measure, The',null,null,'A war of reprisal following the Liar''s Peace, in which the armies of Calikar, led by Cuth&ucirc;l-erin y Senyar, invade and decimate Kailar.  It ends when Brochf&aelig;l y Rhyfelwr kills King Veomnis e''Gur Dao''Ast in the Abyssal Halls.  The king''s son, Zjeryn e''Gur Dao''Ast, witnesses their duel but is spared.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1971,1,to_timestamp('05-SEP-15 09.22.14.191000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kbai-spring',null,null,'An alternative term for a kbai-well.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1973,1,to_timestamp('05-SEP-15 09.33.00.232000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Long Peace, The',null,null,'An alternative name for the Golden Age of Calikar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1974,1,to_timestamp('05-SEP-15 09.34.36.939000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shame''s War',null,null,'See the War of Shame.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2171,1,to_timestamp('11-SEP-15 11.42.45.433000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Riordain y Senyar',null,null,'An elithin, high king of Calikar, firstborn of Meilyr y Senyar and Aderyn yn Vythyl, brother to Myrddin y Senyar (his elder brother by another mother) and Meirwn yn Senyar, and father to Ronan y Senyar, Ruari y Senyar, and Tristwen yn Senyar.  He was known for his great patience, tendency toward forgiveness, and unwillingness to commit to rash decisions.  He was loathe to gainsay the decisions of the Chorus, which some few saw as weakness.  However, he was loved and respected by most.  Riordain became high king after the death of his father in Camryn''s Pride and his brother''s unexpected abdication.  He later perished in the Ungloaming, where his people believe he was responsible for destroying Dubh Gaoth and the ending the Gloam.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2176,1,to_timestamp('11-SEP-15 11.56.58.190000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Larwyth y Senyar',null,null,'An elithin, prince of Ryche of Senyar, son of Shen''r&aelig;th yn Senyar, and father to Shearah yn Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2178,1,to_timestamp('11-SEP-15 12.03.49.172000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shen''r&aelig;th yn Senyar',null,null,'An elithin, lord of Ryche Senyar, mother to Larwyth y Senyar and Osgurd y Senyar, and youngest child of Ifanna yn Senyar.  She chose to bind both her sons to the Songs of remote cities (Aduine and Srawain), extending the power of Ryche Senyar throughout Alune.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2173,1,to_timestamp('11-SEP-15 11.45.19.760000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Aderyn yn Vythyl',null,null,'An elithin, second wife to Meilyr y Senyar, and mother to Meirwen yn Senyar and Riordain y Senyar.  She survived her husband during Camryn''s Pride but later perished during the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1901,1,to_timestamp('03-SEP-15 10.44.02.916000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Myrddin y Senyar',null,null,'A prince of the Ryche of Senyar, eldest son of Meilyr y Senyar and Wynne yn Ainne, half-brother of Riordain y Senyar and Meirwen yn Senyar.  He is likely the greatest living shuonne and is well-known for his striated eyes, short manner, and unflinching dedication to duty.  When his father died during Camryn''s Pride, Myrddin, to the great surprise of all who knew him, abdicated his throne and went into voluntary exile.  He is one of few surviving elithin in Lluagh Maguin to have known times before the Gloaming.  He is greatly respected among his people but not well liked.  He very rarely visits ka-&AElig;lm&aelig;dris, only as the Song requires, and almost never speaking with anyone except his niece and nephews.  The shara-aleach known as the Mych''Tair are bound to him.  Also known as the Basllisk.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2183,1,to_timestamp('11-SEP-15 01.53.28.061000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Enelorn',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Prior to the Grey War, their head was Aelwen yn Enelorn.  Afterwards, she was succeeded by Vanwyra yn Enelorn.  Their symbol is a finch.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2695,1,to_timestamp('24-SEP-15 09.33.06.901000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Watchtower of Gandar&icirc;n, The',null,null,'A fabled tower in the eastern regions of the city of Rolar, where an emerald beacon always glows, facing in the direction of Kailar.  It is the second tallest building in the Emerald City after the Tower of Tyrin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2696,1,to_timestamp('24-SEP-15 09.33.42.339000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tyrin',null,null,'See the Tower of Tyrin.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2697,1,to_timestamp('24-SEP-15 09.35.23.716000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Calik''s Drift',null,null,'A region of natural hills in the city of Rolar, lying between the Gardens of Iny&ucirc;n and Diris Alonne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2698,1,to_timestamp('24-SEP-15 09.36.16.058000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iny&ucirc;n',null,null,'See the Gardens of Iny&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2699,1,to_timestamp('24-SEP-15 09.38.10.870000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gardens of Iny&ucirc;n, The',null,null,'A great expanse of cultivated gardens, hedges, and orchards which lies at the base of Ire''nurine in the city of Rolar.  This plant life is dependent on the light of Diris Alonne to survive, and since the Grey War the gardens have begun to wither and die.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2700,1,to_timestamp('24-SEP-15 09.40.52.883000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ilur&icirc;n',null,null,'A clearing in the grove of Diris Alonne in Rolar and the location of the Nameless Temple.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2701,1,to_timestamp('24-SEP-15 09.41.44.249000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mindal&ucirc;n',null,null,'See the Harbor of Mindal&ucirc;n.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2703,1,to_timestamp('24-SEP-15 09.45.49.541000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gate of Calik, The',null,null,'One of the four gates of Rolar.  It connects the city with the western region of Westermere.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1975,1,to_timestamp('05-SEP-15 09.37.25.540000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Shame, The',null,null,'A brief war between the du&aring;rg and the corti following the arrival of the Iron Born in the Great Roof.  It ended when the elithi intervened.  Holding themselves responsible for the conflict, the corti offered permanent custody of the Star Gem to the Calikari and gave the Eyes of Phaelen as a gift to the du&aring;rg.  More commonly known as Shame''s War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1976,1,to_timestamp('05-SEP-15 09.41.27.019000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Coverters'' War, The',null,null,'The second recorded conflict between the d&aelig;n''shee and the elithi, in which the d&aelig;n''shee, thinking the Star Gem some manner of weapon, try to invade the Bright Kingdom and reach a''Tiernya.  Their assault is crushed by the Bright Legions and significant portions of their lands are lost, driving them back to Aljaberin.  Also known as the War of the Covetous.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1977,1,to_timestamp('05-SEP-15 09.41.51.100000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of the Covetous, The',null,null,'An alternative name for the Coverters'' War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1978,1,to_timestamp('05-SEP-15 09.45.04.501000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Slaves'' War, The',null,null,'A war between Calikar (and her allies) and Kailar, initiated by the d&aelig;n''shee.  It marked the end of the Golden Age of Calikar.  Erinin''ar was lost during this conflict.  So-called because the ki''tahj employed slaves as the bulk of its forces, gathered and bred over the long centuries of the Golden Age into a great horde.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1981,1,to_timestamp('05-SEP-15 10.02.23.816000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Sh''hein''Rein',null,null,'The collective name of the Tryptychs of Calikar, who guided the Bright Legions from the Hall of Tablets in Aduine.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1946,1,to_timestamp('04-SEP-15 11.20.48.645000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sionyn y Randwch',null,null,'An elithin and the first to rediscover the art of healing after Shara Lach-Nach.  He is largely recalled as a boy, but he lived several hundred years.   Though accredited with the foundation of Maliuch Beuen, it was almost certainly the work of his ancestors.  Sionyn was the first to identify the Grim.  He also bred Eiluinar before its need was apparent.  It seems likely based upon his deeds that he was a true Shaper.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1985,1,to_timestamp('05-SEP-15 10.30.26.918000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Selpstone',null,null,'A natural stone with the appearance of milky wax flecked with silver glitter.  It reacts in the presence of those with the Gift, causing the "stars" to glow and migrate.  The reaction is proportional to the strength of the Shaper.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1987,1,to_timestamp('05-SEP-15 11.01.00.456000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shareach',null,null,'An ancient port village of Calikar, located in the heart of Alune and abandoned during the Gloaming.  It was visited by Malnos, Eleanor, and Kain on their journey between &AElig;lm&aelig;dris and Rhuanedd.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2017,1,to_timestamp('07-SEP-15 12.48.13.651000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Agnard Edvill-s&oslash;n',null,null,'The last &thorn;engill-rikr of I&aring;rth, who died surrounded by a host of the d&aelig;n''shee during the War of East and West.  He was a son of Magnr Ginnheilag.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2018,1,to_timestamp('07-SEP-15 01.02.54.023000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mych''Tair',null,null,'A renowned bonded group of shara-aleach bound to Meilyr y Senyar while still children.  After Meilyr''s death during Camryn''s Pride, it was expected they would bind themselves to the new king, Riordain y Senyar but instead went into exile with Myrddin y Senyar.  They are surrounded by a hundred tales and legends.  Prior to the Ungloaming, they were thought the only elithi to have killed a Wind Wraith.  The Mych''Tair are Peadar y Llewyn, Orla yn Naruch, and Sharwyn yn Naruch.  Also know as the "Great Three."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2101,1,to_timestamp('09-SEP-15 10.55.21.026000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Veiled Tribunal, The',null,null,'A secret meeting of the lords of Calikar, generally the Charisi, the masters of Ryche Senyar, and their trusted lieutenants, afterwards hidden from the Song by the art of Drauth''n&icirc;r.  The most "well-known" example is the Veiled Tribunal short years before the Gloaming, where the fate and betrayal of Sorain was decided.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2106,1,to_timestamp('09-SEP-15 11.12.25.634000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mathwyn y Rhuaidh',null,null,'The son of Vindhain y Rhuaidh and Thearah yn Junwyr.  He died during the Nameless Hour while still a young boy.  He had the Forbidden Gift, which truth was known to his father but concealed from the other elithi.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2107,1,to_timestamp('09-SEP-15 11.16.42.661000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vindhain y Rhuaidh',null,null,'The third born of Myfanwy yn Rhuaidh and Arthwydd y Mallonwyr, brother to Iestyn y Rhuaidh, Briallen yn Rhuaidh, Argoel y Rhuaidh, and Niamh yn Rhuaidh, husband to Thearah yn Junwyr and father to Saura yn Rhuaidh and Mathwyn y Rhuaidh.  He was the only of his siblings not to possess the Sight.  After his daughter was born Twisted, his son perished at the Nameless Hour, his wife sacrificed her life to the Gloam, and he watched the greater part of his people perish to Dubh Gaoth, he surrendered his will to live and went off into the Deep Dark to die.  He believed himself responsible for the events of the Nameless Hour, the Hunger having been summoned by his son''s Forbidden Gift, which he kept secret from his people.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1913,1,to_timestamp('04-SEP-15 09.14.22.034000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Drauth''n&icirc;r',null,null,'A Tal''Shiahd discovered and named by Lukifan during the Second &AElig;gan Empire.  Its name translates to “The Pardoning Stone” in the Pure Tongue.  Drauth''n&icirc;r appears as a fruit, similar in size and shape to a pomegranate.  Separated, its husk is brown and withered, with a black, twisted stem.  When attached to a tree, the flesh turns green-blue and semi-transparent.  Thousands of seeds are visible in the interior, seemingly moving.  If examined closely, those seeds seem infinite:  some are dark and some golden.  The latter twinkle like stars.  Over a period of centuries, the golden seeds naturally turn dark, until only the light remained.  The tree begins to grow similar fruit, which although they do not seem great in number are never exhausted.  Their skins are opaque and they have no glow-y seeds (or seeds at all).  When eaten, the Sacred Fruit draw “ill” from the recipient:  this is generally in the form of negative memories and emotions, largely uncontrollable.  (The man who originally attached the artifact to a tree was named Midam, giving Drauth''n&icirc;r its common name, Midam''s Pome.) <br/><br/>At some point during the First &AElig;gan Empire,  Drauth''n&icirc;r came into the possession of the Ter Graz Daan, who used it therapeutically.  During the Great War, it came into the possession of Mariel, who twisted its nature to torment.  She discovered that she could extract a man’s pleasant memories and feelings; and also infect one man with another’s despair.  Near the end of the War, it came into the possession of Gai''lal.  He used it, in combination with his staff, to draw the darkness from the kiril, creating the corti.  Drauth''n&icirc;r was one of the sacred treasures guarded by the corti when they descended to the Domain.  They eventually offered it as a gift to the Calikari (after their first gift, the Star Gem, was returned to them).  It was attached to D&ucirc;n Mile''&ucirc;r.  Accidentally, Lowri the Green transformed the nature of the Song such that it became bound with Drauth’n&icirc;r’s power.  The old Song was lost, resulting in the death of the elderly elithi, but a new Song was born, blessing those elithi who survived with immortality and tranquility.  After Leuen sang the ew Groves, Drauth''n&icirc;r was relocated to &AElig;lm&aelig;dris. After the Gloaming, it was taken to Muir Auread, where it was lost during Camryn''s Pride.  Drauth''n&icirc;r''s fate was unknown for a millennium, but it was recently rediscovered in the town of An Suragh.  Its seed was used to preserve Argoel y Rhuaidh from exposure to Dubh Gaoth, and it is currently in the custody of Malnos.  Also called Midam''s Pome, the Pardoning Stone, the Changing Mirror, and the Living Gift.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1988,1,to_timestamp('05-SEP-15 11.12.42.792000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nameless Hour, The',null,null,'The name given to the coming of Dubh Gaoth and its Wraiths to Caonwen, resulting in the deaths of more than half of the exiles of &AElig;lm&aelig;dris.  It was preceded by an earthquake, which somehow resulted in in several ancient lanterns in the city streets taking light.  Fires followed, spreading throughout the city.  This was attributed to the Forbidden Gift, though a specific source was never identified.  The high king, Meilyr y Senyar, perished, as well as Nerys, wife of Riordain y Senyar, and more than half of Ryche Rhuaidh.  Briallen yn Rhuiadh lost her sight saving her brothers Argoel y Rhuaidh and Vindhain y Rhuaidh. The Holy Grove of Diris Maguin, located days'' journey from Caonwen on the banks of Muir Airgin, was not discovered or lost.  After the Nameless Hour, the exiles built ka-&AElig;lm&aelig;dris near the ruins of Mereith.  Also called the Dread Pyre.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1993,1,to_timestamp('06-SEP-15 09.28.42.897000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Quaul',null,null,'A region of Bel Can’nar visited by Durgin during his exile from I&aring;rth.  It is a region torn by ancestral conflicts, and Durgin became a mercenary warlord, known for his cruelty and ruthlessness.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1996,1,to_timestamp('06-SEP-15 09.57.45.007000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black City, The',null,null,'An alternative name for the city of Kailar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1969,1,to_timestamp('05-SEP-15 09.18.19.473000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gysioch y Senyar',null,null,'A lord of the Ryche of Senyar and cousin to the line of kings, son of Swn''Turan yn Senyar.  He became the king of the exiles of Rhuanedd after the Gloaming and led them to Asmaraghaide.  Years later, thinking Dubh Gaoth vanquished, he lit a light in the highest tower of the city.  Those few who survived his folly settled in Shar''ris y Rhuanedd and determined that their people would never be gathered in one place at the same time.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1998,1,to_timestamp('06-SEP-15 10.04.17.854000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Last Wall, The',null,null,'A a great wall constructed to seal the breach between Alune and the Modeira which precipitated the Unseen War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2000,1,to_timestamp('06-SEP-15 10.15.50.738000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Mierah yn Senyar',null,null,'An elithin and lord of Ryche Senyar.  She was briefly high queen of Calikar after her father, Fionn y Senyar, was murdered at the Liar''s Peace.  She sued for peace with the lords of Kailar, ending the War of Ash, and was assassinated by agents of the ki''tahj approximately a year later.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2001,1,to_timestamp('06-SEP-15 10.26.21.617000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Day of Orihieyte',null,null,'The name given to the day on which Eliryndil yn Orain was abducted from her nursery in the town of Eny&ucirc;r.  Her nurse, Maethrin, was murdered, rent by claw and tooth.  No sign of the child remained except a blanket soaked in blood.  So named for the claims of others to have seen a "winged shadow."  Orihieyte means "Winged Shadow" in Tuindil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2003,1,to_timestamp('06-SEP-15 10.26.59.098000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Winged Shadow',null,null,'Translation of Orihieyte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2129,1,to_timestamp('09-SEP-15 03.35.27.109000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bradwr',null,null,'A group of elithi who challenged Dubh Gaoth at An Suragh, resulting in Camryn''s Pride.  Also known as the Apostates.  They were thought hunted to their extinction during the War of Tears, but have apparently survived to return to An Suragh.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2130,1,to_timestamp('09-SEP-15 03.38.35.232000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Camryn''s Pride',null,null,'The name for the coming of Dubh Gaoth to An Suragh and the near extinction of the survivors of &AElig;lm&aelig;dris.  After the Gloaming, the people of &AElig;lm&aelig;dris gathered on the shores of Muir Auread, where they seeded a new Holy Grove.  They built a home in the ruins of An Suragh.  This process of gathering the survivors took more than a decade, and some were afflicted by Blysiog or the Withering before the Calikari were unified.  Still others gathered at An Suragh hailed from Aduine, Srawain, or Rhuanedd and could not be joined with the Song of Ælmædris.  Others were Makers or Eshthara who railed against their new lives lived without their arts.  These people were without hope for a future and, led by Camryn yn Amuuth, convinced themselves that Calikar had fallen only because the Bright Folk were not prepared.  They believed that a gathered host of the Bright Folk could defeat Dubh Gaoth and wrest Alune from its grasp.  In their foolishness, they summoned the Black Hunger to An Suragh in order to challenge it, ending in the near extinction of their people.  Later, this became known as Camryn''s Pride.  The survivors fled to Caonwen.  For a hundred years afterward, they hunted down Camryn''s followers, known as the Bradwr, in the War of Tears.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2147,1,to_timestamp('10-SEP-15 11.36.04.097000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'R&aelig;wyn yn Rhuaidh',null,null,'An elithin, daughter of Ceridwen yn Rhuaidh and mother of Ceridwen yn Rhuaidh (grandmother and granddaughter shared same name).  She was prophetess to Aruth''eras at the time of the Gloaming and advised against his meeting with the Dark Herald.  Later, she was struck down by the Herald when he murdered the king.  The poison rendered her comatose but did not kill her.  She eventually perished during Camryn''s Pride, taken by the Black Hunger.  She possessed the Sight in the form of foretellings.  R&aelig;wyn is remembered among the people of the Gloam with great reverence.  Her guidance is associated with many of Aruth''eras y Senyar''s victories against Kailar and the Gloaming is ascribed at least in part to the d&aelig;n''shee''s fear of her power.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2187,1,to_timestamp('11-SEP-15 01.58.46.967000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Inymuus',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Prior to the Grey War, their head was Cadwallen yr Inymuus.  Afterwards, he was succeeded by Lorweth yn Inymuus.  Their symbol is a black swan.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (454,1,to_timestamp('12-AUG-15 01.55.42.593000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Seeker',null,null,'Servants of the N&aelig; Kisal tasked with seeking out children with the Gift.  Also a title used among the Aljer to describe a torturer or inquisitor.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2007,1,to_timestamp('06-SEP-15 10.38.19.357000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Nermere',null,null,'The name of a region of the kingdom of Rolar, located north of Aiun-Malach.  Chara is located here.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2008,1,to_timestamp('06-SEP-15 10.41.33.571000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'S&aelig;mere',null,null,'The name of a region of the kingdom of Rolar, located south of Aiun-Malach and the Althrin.  This region has largely been abandoned to the Inner Plains.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2010,1,to_timestamp('06-SEP-15 10.56.50.709000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Maleagh Ini',null,null,'An alternative name for the d&aelig;n''shee among the elithi, meaning "Cursed Ones."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2011,1,to_timestamp('06-SEP-15 10.57.04.762000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Cursed Ones, The',null,null,'Translation of Maleagh Ini.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1483,1,to_timestamp('23-AUG-15 05.34.46.894000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'N&ucirc;l',null,null,'One of few Shiahd developed during the Second &AElig;gan Empire.  It was originally designed as tool for binding a Shaper from Da''kol and later utilized as a means of temporarily repressing the nature of a Shiahd.  It was not particularly effective in either capacity once Shapers discovered means of disrupting the n&ucirc;l''s effect without the rune-word.  In theory, each n&ucirc;l is bound with a phrase, and the only way of breaking the n&ucirc;l is by touching the n&ucirc;l with a wyrd counteracting each Pillar (a weak wyrd comprised of eight parts, each touching its opposite) while speaking the original rune-word.  In practice, they are trivial to unravel.  A n&ucirc;l can be used multiple times if properly removed, but is broken if forcibly removed.  In appearance, a n&ucirc;l is prismatic stone the size of pebble.  It disappears on activation, and reappears in the palm of the un-binder when deactivated.  Items bound by a n&ucirc;l are apparent to Shapers, but the means varies by the chara of the individual.  It includes all eight Pillars.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1929,1,to_timestamp('04-SEP-15 10.09.11.443000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shanwyn yn Qunur',null,null,'The first elithin to survive the Withering past the age of forty and considered to be the first immortal.  Her one hundredth birthday is accounted as the end of the Long, Short Years.  Shanwyn was murdered by the d&aelign;n''shee during the Liar''s Peace.  Also known as the Child of Grace.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2026,1,to_timestamp('07-SEP-15 01.18.37.621000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Bych''Tair',null,null,'An informal name for a paired group of the shara-aleach, so named because they idolize and model themselves after the Mych''Tair.  They are Kai y Rhyfelwr, Awan yn Pallanwi, and C&aelig;rwyn yn Faor&ucirc;n.  All are in their youth (under twenty-five) and have yet to adopt the ageless look of their people.  Prior to Peadar''s death, they each wore two necklaces, each adorned by three pebbles.  Now they wear three.  Their hair is cut short.  The symbol of Aruth’eras is carved into their left hands (a rune).  They are bound to Riordan and later Ruari.  Also known as the "Little Three."  (These titles are not used among the three, nor do they look kindly upon them.)',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2148,1,to_timestamp('10-SEP-15 11.40.52.060000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Briallen yn Rhuaidh',null,null,'The second born of Myfanwy yn Rhuaidh and Arthwydd y Mallonwyr, sister to Iestyn y Rhuaidh, Vindhain y Rhuaidh, Argoel y Rhuaidh, and Niamh yn Rhuaidh,  She possessed the Sight in the form of visions.  Briallen lost her vision while saving her brothers during the Nameless Hour.  Later, she went into exile beside her brother, Argoel, apparently to Silence the Wind, and somehow appeared in Rolar, where she became a counselor to Ryche Orain.  She perished during the Grey War.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2157,1,to_timestamp('11-SEP-15 10.28.19.947000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Battle of Legacy, The',null,null,'A battle in the Unseen War in which Gh&icirc;lar fell.  Brean-Mi&icirc;in yn Senyar and Ganahahd y Senyar both fell at the battle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2159,1,to_timestamp('11-SEP-15 10.41.22.600000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Peredur y Senyar',null,null,'The eldest son of Calik Twiceborn and Nara do''Virt, brother to Shara yn Senyar and Tyren y Senyar, and first true high king of Calikar.  The names of his heirs were lost to the Long, Short Years.  Peredur is remembered for Speaking the watch towers of F&aelig;l&ucirc;n, sealing his father''s tomb, erecting the Door of Eternity, establishing the Al&euml;''uriel Caz''ur Ve''&euml;ri, and building the Nameless Temple at the heart of Diris Alonne.  Born in the last years of the exile, his years were short.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2160,1,to_timestamp('11-SEP-15 10.46.39.348000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tyren y Senyar',null,null,'The youngest son of Calik Twiceborn and Nara do''Virt, brother to Peredur y Senyar and Shara yn Senyar.  It is not recorded that he wed or was sire to any children.  Tyren is remembered for building the vaults and towers of Turin&aelig; and the hall of Gaithan Aiur to retain the memories of Llua''la (Note: later, historic documents were moved from Turin&aelig; to Vild&icirc;n).  He also built the tower of Tyrin at the heart of Turin&aelig; to hold the sacred writings of his father, commanding that it would ever be the tallest spire in Calikar.  Born in the last years of the exile, his years were short.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2161,1,to_timestamp('11-SEP-15 10.52.42.933000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Shara yn Senyar',null,null,'The middle child of Calik Twiceborn and Nara do''Virt, sisterer to Peredor y Senyar and Tyren y Senyar.  She is not recorded to have wed or to have mothered children.  Shara is remembered as a great Speaker and for adapting a''shanne for use in the Song and in many of the wonders with which it is associated, for example the Hall of Viewing in Ire''nurine.  Born in the last years of the exile, her years were short.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1984,1,to_timestamp('05-SEP-15 10.19.06.578000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Glowstone',null,null,'One of few Shiahd craftable by the wyrd-smiths of the Second &AElig;gan Empire.  They were common baubles in the Empire. In response to touch, they produced light which lingered long after contract was broken. The light was variable according to the wielder, and for those with the Gift it often took on shapes: memories and pleasing fantasies.  Such instruments were frequently employed to soothe the children of privilege and guide them to their dreams.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1986,1,to_timestamp('05-SEP-15 10.50.56.321000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Tu&euml;ndar',null,null,'Shiahd possessed by the lords of Calikar.  Taking the form of white chairs or thrones, there are eleven pairs.  Each seat is veined with opaque crystal.  One of each pair is kept in a room atop the spire of Ire''nurine in Rolar.  The others are scattered across Calikar.  When a chair is occupied, its mate''s crystal glows  a subtle yellow.  Those sitting are apparently transported to another place, or Fold, which is a replica of Urek-Un&icirc;n except that there is a yellow altar at its heart, filling the chamber with a ghostly light.  This allows distant communication.  According to legend, the Tu&euml;ndar were created by the cara-aleach, Faralla.  Eight of the eleven pairs are now lost or broken.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1989,1,to_timestamp('05-SEP-15 11.12.57.713000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dread Pyre, The',null,null,'An alternate name for the Nameless Hour.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1990,1,to_timestamp('05-SEP-15 11.21.14.817000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Meilyr y Senyar',null,null,'An elithin and prince of Ryche Senyar.  He became high king after his father, Llarfor, died in battle with the Forlorn.  He perished during the Nameless Hour.  He had two wives:  Wynne, who was mother to Myrddin y Senyar, and Aderyn, who was mother to Meirwen yn Senyar and Riordan y Senyar.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1992,1,to_timestamp('05-SEP-15 11.37.06.280000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Serpent Blade, The',null,null,'A two-handed bastard sword wrought of blue metal, with red veins that run through the blade.  The hilt is also blue with silver orb set into each side.  It is currently in the possession of Drolock''harka, who discovered the weapon driven into a pillar on the citadel atop Kardu&icirc;n.  It is likely Tal''Shiahd.  The touch of the blade initially caused Drolock''harka intense pain and consumed him with a desire for blood.  Failure to "feed" the blade induced more intense pain and eventually weakness.  However, he found himself unable to discard the weapon.  The nature of the blade was apparently changed when Drolock''harka was submerged in Muir Airgin.  The veins turned from red to silver, and the hunger faded.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1995,1,to_timestamp('06-SEP-15 09.57.22.742000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of East and West, The',null,null,'A relatively recent war between Kailar and the kingdoms of the Great Roof.  In this conflict, Berak and the Empire of Demarge allied themselves with ki''tahj.  Eventually, the Blood Screamers were driven back into the east, but not before Berak set flame to Aljerbach and the Empire permanently seized Harn and her subject states as a protectorate.  The king of Calikar, Cynyr yn Orain, was lost in the last battle of the war, the Battle of Kuren''or, and taken as a prisoner to the Black City.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2170,1,to_timestamp('11-SEP-15 11.36.18.650000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Ruari y Senyar',null,null,'An elithin, current high king of Calikar, secondborn of Riordain y Senyar and Nerys yn Senyar, and brother to Ronan y Senyar and Tristwen yn Senyar.  He is known for his physical and temperamental similarity to his father, particularly his great patience, tendency toward forgiveness, and unwillingness to commit to rash decisions.  Ruari became high king after the death of his father in the Ungloaming and his brother''s unexpected abdication.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2175,1,to_timestamp('11-SEP-15 11.50.17.883000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Swn''Turan yn Senyar',null,null,'An elithin and lord of Ryche Senyar.  She was the daughter of Banhymn yn Senyar and the mother of Gysioch y Senyar.  Swn''Turan was considered one of the greatest Eshthara to have ever lived, and might become the greatest if the Gloaming had not fallen during her youth.  She died leading the Eshthara at what later became known as Machuin Muil.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2180,1,to_timestamp('11-SEP-15 01.41.02.654000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Meirwen yn Senyar',null,null,'An elithin, lord of Ryche Senyar, daughter of Meilyr y Senyar, brother to Riordain y Senyar, half-brother to Myrddin y Senyar, wife to CAriad yr Yeorath, and mother to Llarfor y Senyar.  After Camryn''s Pride, her father confided to her the truth about the Grim and her family''s crimes.  In response, she took possession of Drauth''n&icirc;r and fled in the the darkness.  Most suspected that she had gone to Meet Calik, but instead she gathered the survivors of the Apostates and Calikari and became a leader among them.  She shared with them the truth but made them swear never to move against Caonwen or her brother''s people.  She was preserved for several centuries by the power of Drauth''n&icirc;r, but eventually succumbed to the Withering.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2185,1,to_timestamp('11-SEP-15 01.55.58.453000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ryche Idrien',null,null,'A house of the elithi in Rolar, accounted among the Tiaryche.  Prior to the Grey War, their head was Sulwyn yr Idrien.  Afterwards, he was succeeded by Eamon yr Idrien.  Their symbol is a dove.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2179,1,to_timestamp('11-SEP-15 12.05.26.056000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Osgurd y Senyar',null,null,'An elithin, lord of Ryche Senyar, son of Shen''R&aelig;th yn Senyar, and brother to Larwyth y Senyar.  Bound as a child to the Song of Srawain, he became that city''s first king.  His heirs ruled Srawain until the city fell in the Gloaming.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2473,1,to_timestamp('19-SEP-15 06.49.32.220000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Soluun',null,null,'A house of the Sangus and Centarum in the Empire of Demarge, the only house afforded no representatives in the Imperial Senate.  This is the house of the God Emperor, they royal dynasty of the Empire of Demarge.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1999,1,to_timestamp('06-SEP-15 10.12.55.346000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'War of Ash, The',null,null,'One of many wars between Calikar and Kailar, initiated by Veomnis e''Gur Dao''Ast.  The nation of I&aring;rth was decimated, Shuon Asshal was abandoned, and the great queen, M&aelig;lorwen perished.  At an attempt to bring peace to the conflict, the d&aelig;n''shee, treacherously murdered many of the lords of Calikar at the Liar''s Peace.  Eventually ended when Mierah yn Senyar sued for peace a year after the Liar''s Peace.  She was assassinated short years later.  Also called the Age of Ash.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2002,1,to_timestamp('06-SEP-15 10.26.46.811000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Orihieyte',null,null,'A word meaning "Winged Shadow" in Tuindil.  See the Day of Orihieyte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2005,1,to_timestamp('06-SEP-15 10.28.14.562000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Eny&ucirc;r',null,null,'An elithin town in Edelmere, where Eliryndil yn Orain was abducted during the Day of Orihieyte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1983,1,to_timestamp('05-SEP-15 10.15.00.069000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vacuole',null,null,'A Shiahd capable of holding an arbitrary number of objects in its interior.  Such objects "disappear" and apparently have no mass.  Each vacuole has a "key" by which its contents can be summoned, which varies in nature by vacuole.  It might be a word, a phrase, symbol, a touch, etc.  They take all manner of shapes and are all manner of sizes.  A vacuole cannot hold a a Tal''Shiahd.  Their making was not understood in the Second &AElig;gan Empire, and they were considered among the most valuable of Shiahd.  Only a few dozen are known to exist.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1789,1,to_timestamp('31-AUG-15 10.14.26.502000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Asmaraghaidhe',null,null,'A city formed by the survivors of Rhuanedd after the Gloaming.  According to legend, it was lost when Gysioch y Senyar, thinking Dubh Gaoth vanquished, lit a light in the highest tower of the city.  This is known as Gysioch''s Folly, and those who survived his imprudence founded Shar''ris y Rhuandedd.  Determined that such complete calamity would never strike their people again, they became a migrant nation, never gathering in one place at the same time.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1796,1,to_timestamp('31-AUG-15 10.25.41.864000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Muir Airgin',null,null,'A silver pool located in Alune, predating the arrival of the elithin.  It has the appearance and consistency of liquid metal, but is lukewarm to the touch.  Muir Airgin generates a silver radiance which casts no shadows and does not summon Dubh Gaoth.  The exiles of &AElig;lm&aelig;dris, when they fled from Camryn''s pride, planted the seeds of Diris Maguin on the shores of Muir Airgin.  They originally intended to craft their new city on the banks, but the eldritch light eventually drove them to settle further away, first among the ruins of Caonwen then in ka-&AElig;lm&aelig;dris.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (3092,1,to_timestamp('10-OCT-15 10.46.50.732000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Narauk der''Annher',null,null,'A former lord of House Annher and ancestor of Garani der''Annher.  He lived approximately five centuries before the Spheres fell, during a time when his House was among the wealthiest and most prominent in the Thrice Kingdom.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2320,1,to_timestamp('16-SEP-15 11.27.53.170000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Twisted Ones, The',null,null,'Twisted, deformed children found among every race of Puell&aelig;.  They are born with the art of speech but are insane and brutally violent.  Most peoples either ritually sacrifice or banish the Twisted.  There is no cure for their madness.  Different peoples have different mythologies to explain the origin of the Twisted, but two things are certain:  (1) the curse of the Twisted has existed for at least several thousand years, and (2) no Twisted has been born for more than three decades.  During the last days of the Grey War, hundreds of Twisted Ones mysteriously appeared outside the walls of Rolar and rushed to their deaths in the Grey Mist.  Among the du&aring;rg, the Twisted Ones are known as Streichen.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2321,1,to_timestamp('16-SEP-15 11.35.29.302000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Grod-mark',null,null,'A runic symbol representing one of the Grod of the du&aring;rg.  Iron Born commonly (but not universally) wear the grod-mark upon their person, often as carved flesh or a brand but sometimes as a mark upon jewelry or clothing.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2322,1,to_timestamp('16-SEP-15 11.38.04.892000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lyceum, The',null,null,'A collections of halls in the Mountain Home, their principle feature being a library that houses iron books.  They were constructed as guest halls for emissaries of the Durem Erim&aelig;.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2323,1,to_timestamp('16-SEP-15 11.38.38.421000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kalrych der''Klar',null,null,'An ancient king of Aljerbach.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2324,1,to_timestamp('16-SEP-15 11.41.33.125000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ar&ucirc;',null,null,'A red-brown vine commonly found in the interior of Aljer buildings.  It is coarse to the touch, with no fronds, spines, or leaves.  It has a distinct odor which some find unpleasant.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2325,1,to_timestamp('16-SEP-15 11.42.45.748000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Barlith',null,null,'A grain endemic to the Great Cavern.  It cannot survive on water tainted with the Grim.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2328,1,to_timestamp('17-SEP-15 12.00.57.708000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Vidhun Vidhul-s&oslash;n',null,null,'A du&aring;rg, healer of beasts by trade, wood-carver in his heart, resident of the hurtt of Aust-Agder in southern I&aring;rth.  He found and cared for Kaja''lar when her Sphere descended; later, she murdered him while overcome by the Other.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2329,1,to_timestamp('17-SEP-15 12.02.13.991000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'A thousand hammers, a thousand years',null,null,'A common saying among the du&aring;rg.  It refers to the belief that the Mountain Home will never fall, even if her enemies were given a thousand hammers and thousand years.  It is a shortened form of "Not with a thousand hammers, not if given a thousand years."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2330,1,to_timestamp('17-SEP-15 12.04.24.378000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Fuligin Chair, The',null,null,'The throne of the &thorn;engill-rikr, located in the Mikill-holl in the Mountain Home.  It is crafted of llearth and is separated from (and slightly larger than) the thrones of the &thorn;engill  The Iron Crown rests on its seat, unclaimed.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2332,1,to_timestamp('17-SEP-15 12.07.11.147000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Iron Lords, The',null,null,'The masters of I&aring;rth, may refer to the magnr or the &thorn;gill depending on the speaker.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2333,1,to_timestamp('17-SEP-15 12.17.47.223000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'One Throne, The',null,null,'An alternative name for the God Throne.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1846,1,to_timestamp('01-SEP-15 11.44.15.436000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Withering, The',null,null,'A disease which afflicts the elithi.  Those who are infected are asymptomatic until late puberty/early adulthood, when the illness begins to twist the body, resulting in 4-5 years of painless deformity.   This is followed by 4-5 years of unbearable pain, madness, then death.  For those who contract the disease late, or those for whom it is suppressed, symptoms can be more severe and progress more rapidly.  (Note that the condition does not currently seem transmissible except by birth.)  According to legend, Calik originally planted the Great Tree, D&ucirc;n Mile''&ucirc;r, and bound it to u''Vidain e''Cuhlhair in order to contain the Withering.  No elithin has thus perished from its effects for millennia, at least no elithin known to the lords of Rolar.  (Excepting the very old, withdrawal from the Song, known as Blysiog, will claim his life long before the Withering.)  The elithi believe that the Withering is a curse placed upon their people for their role in defying him during the wars that broke Llua''la.  More likely it is a form of the plague, Kaliara, perhaps purposefully communicated to Calik by Az-Azaal.  There are elithi in Demarge who do not seem to suffer from the Withering, called the Unsung.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1933,1,to_timestamp('04-SEP-15 10.21.37.257000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Leuen of the Silver Tongue',null,null,'An elithin, prince of Ryche Senyar, child of Ifanna yn Senyar, and elder brother of Thierin y Senyar and Shen''r&aelig;th yn Senyar.  He renounced his throne when his father died, not believing in the wars of his people (and also possibly because he was too old to be bound to the new Song of &AElig;lm&aelig;dris).  As a very young boy, he was an acolyte of Lowri the Green.  Blessed with the art of speaking in the tongue of trees, he discovered a means by which the Great Trees could be induced to give Seeds of Birth without their death.  He personally laid the seeds of five of the Thir Eris:  &AElig;lm&aelig;dris, Aduine, Rhuanedd, Erinin’ar, and Val Analyth.  In his youth, Leuen was a pacifist who sought peace with the d&aelig;n''shee, but after the death of his wife, Arianell yn Essrwwr, to Shanaran''s Plague, he devoted himself to vengeance.  He became a shuonne and the first of the cara-aleach, bound with &AElig;ronwy y Rhyfelwr.  Feared by all of Kailar, he was later assassinated during the Liar''s Peace while meditating in Lleah-roht.  Leuen is considered by the Calikari to be among the greatest and most tragic of their heroes, accounted alongside Calik Twiceborn and the great kings.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2450,1,to_timestamp('18-SEP-15 08.45.49.570000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Rowan Fylla-d&oslash;tter',null,null,'A du&aring;rg of magnr Undlflos, daughter of Sijfinnur Jaako-s&oslash;n and Fylla Alva-d&oslash;tter, sister to Lyall Sijfinnur-s&oslash;n, Flos Sijfinnur-s&oslash;n, Signy Fylla-d&oslash;tter, and Honvir Sijfinnur-s&oslash;n, half-brother to Mikhan Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, adopted brother to Hl&aelig;ja Egill-s&oslash;n, husband to Padraig Taavi-s&oslash;n, and mother to Valkari Rowan-d&oslash;tter.  Born drekka, she lived a sad life.  Her half-brother, Folkvar, was Phaelen and succumbed to madness.  Her adopted brother, Hl&aelig;ja and father apparently died to a do''Drak, and her brother Lyall, apparently fled afterwards, leading many to him responsible for the deed.  House Undlfos fell into disgrace, and she became a common laborer.  For many years afterwards, she remained faithful to the memory of Hl&aelig;ja, whom she had loved, but eventually she married.  She named her grandchildren Folkvar and Hl&aelig;ja in honor of her brothers.  Rowan died of natural causes shortly after their birth.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2452,1,to_timestamp('18-SEP-15 08.53.44.716000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Sijfinnur Jaak-s&oslash;n',null,null,'A du&aring;rg and rikr of magnr Undlflos, former &thorn;engill, husband to first Fylla Alva-d&oslash;tter and later Ignjard-d&oslash;tter, father to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Honvir Sijfinnur-s&oslash;n, Flos Sijfinnur-s&oslash;n, Mikhan Sijfinnur-s&oslash;n, Signy Fylla-d&oslash;tter, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, and adopted father to Hl&aelig;ja Egill-s&oslash;n.  He set aside his first wife, Fylla, because she bore two drekka children, but he never stopped loving her, continuing to maintain her in his household.  He was killed by a do''Drak during the twenty-sixth naming day hunt of Hl&aelig;ja, an accident for which his son, Lyall, was later held responsible, resulting in the disgrace of his house.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2453,1,to_timestamp('18-SEP-15 09.03.26.050000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valkari Rowan-d&oslash;tter',null,null,'A du&aring;rg woman, disinherited child of magnr Undlflos, drekka, daughter of Rowan Fylla-d&oslash;tter and Padraig Taavi-s&oslash;n, wife to Zaan Naftali-s&oslash;n, and mother of twin sons Hl&aelig;ja Zaan-s&oslash;n and Folkvar Zaan-s&oslash;n.  She lived as a mean servant throughout her life and died during the birth of her sons.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2454,1,to_timestamp('18-SEP-15 09.09.34.864000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Albino, The',null,null,'The name by which the na''k&ouml;r refer to a mysterious albino man who appeared when the Bright Eye was sundered.  He bore a strong resemblance to Ceygei dar Mistral from their dreams and carried a twisted staff.  Later, he disappeared into the depths of the Mountain Home, never having spoken.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2457,1,to_timestamp('18-SEP-15 09.14.31.304000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Least Circle, The',null,null,'One of three echelons, or pantheons, among the gods of Demarge:  the Greater Circle, Lesser Circle, and Least Circle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1945,1,to_timestamp('04-SEP-15 11.06.20.393000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Shanaran''s Plague',null,null,'A sudden and terrible plague which struck Calikar near the end of the Long, Short Years.  All those of age to be bound to the Song perished within a few short years, reducing Calikar to a shell.  Lowri the Green is accounted as its first victim and likely carrier.  She had recently traveled as an emissary to Kailar.  Named for the contemporary king of Kailar, Shanaran e''Gur Dao''Ast, who was suspected to be its origin.  In retaliation, Calikar launches the Innocents'' War, mostly waged by the dluth-chara and its allies among the corti and du&aring;rg.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1970,1,to_timestamp('05-SEP-15 09.21.55.821000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kbai',null,null,'A term which can refer either to a kbai-well or to the key which grants access to it.  Most often it is the latter.  In appearance, a kbai looks like a skeleton key with a need protruding from its shaft.  Generally, it is crafted of simple metal, but there are more elaborate versions.  The citizens of the First Domain are granted kbai by Durem Erim&aelig;, generally as children, in elaborate rituals that vary by Roof and nation.  These objects are considered sacred, bound to life and death.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1972,1,to_timestamp('05-SEP-15 09.29.24.023000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Kbai-well',null,null,'A source of water untainted by the Grim.  One or more kbai-wells are found in every human settlement of Demarge, the great Cavern, or Kailar and in many areas of Zamoch and Frelham.  The Durem Erim&aelig; once possessed the art to "grow" new wells, but this skill has long since been lost.  The civilizations of the Domain depend on the wells for survival, and the lords of Alanon control them absolutely through fear, ritual, and superstition, granting them the title of Givers.  In appearance, a kbai-well is a well crafted of gray stone.  It is only as deep as it emerged from the earth and is empty.  There is a key-hole in which a kbai can be inserted and turned, at which point the interior fills with water.  When the kbai is withdrawn, it empties.  The magick by which the kbai-wells function is not understood, and there is neither Shivers nor a visible wyrd in response to its operation.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2459,1,to_timestamp('18-SEP-15 09.23.22.317000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Fylla Alva-d&oslash;tter',null,null,'A du&aring;rg, first wife Sijfinnur Jaako-s&oslash;n, mother to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Honvir Sijfinnur-s&oslash;n, Flos Sijfinnur-s&oslash;n, and Sygny Fylla-d&oslash;tter, and adopted mother to Hl&aelig;ja Egill-s&oslash;n.  She was set aside by her husband after bearing two drekka children but remained in his household; and many believe she remained his only true love after he re-married to Ingjard Erica-d&oslash;tter.  Signy was born in this period.  She died in an assassination attempt on her son, Honvir, after the events of Durgin''s twenty-sixth naming day hunt, many believe by the designs of Ingjard.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2460,1,to_timestamp('18-SEP-15 09.24.59.140000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Ingjard Erica-d&oslash;tter',null,null,'A du&aring;rg, second wife Sijfinnur Jaako-s&oslash;n, mother to Folkvar Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, Valmiki Sijfinnur-s&oslash;n, and Mikhan Sijfinnur-s&oslash;n, and adopted mother to Hl&aelig;ja Egill-s&oslash;n.  She was deeply envious of Sijfinnur''s first wife and her children and is thought by many to have been responsible for the murder of Fylla Alva-d&oslash;tter and her daughter, Signy Alva-d&oslash;tter.  After Sijfinnur''s death, she was cast from the magnr by the new rikr, Honvir Sijfinnur-s&oslash;n and became a base prostitute.  Eventually, Honvir arranged her murder.  Lell died with her.  Valmiki was born while she was a prostitute, and although given Sijfinnur''s name, was almost certainly not his son.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2462,1,to_timestamp('18-SEP-15 09.39.16.590000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Egill Modi-s&oslash;n',null,null,'The father of Hl&aelig;ja Egill-s&oslash;n.  His wife died in the birth of his son, Hl&aelig;ja Egill-son, who was born with the mark of the streichen but did not otherwise seem Twisted. He wanted to leave his son to die, but as an oddity, Hl&aelig;ja was instead taken to I&aring;rth, where he was identified as Phaelen.  Egill became an alcoholic, lost his hand in a mill accident on his son''s twenty-first birthday, and died shortly thereafter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2463,1,to_timestamp('18-SEP-15 09.45.33.526000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Raker, The',null,null,'The name by which Drolock''harka referred to the creature whose path he followed on the way from Rolar to Rhuanedd, likely a gh&ucirc;l.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2464,1,to_timestamp('18-SEP-15 09.48.45.886000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'&AElig;&eth;ra',null,null,'A du&aring;rg who lives a lie, concealing or lying about his/her true Grod.',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2466,1,to_timestamp('18-SEP-15 09.58.17.614000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mistress',null,null,'A name by which Durgin refers to Sayori.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2468,1,to_timestamp('18-SEP-15 11.38.39.788000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Valley, The',null,null,'A shortened form of the Valley of Sema.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2456,1,to_timestamp('18-SEP-15 09.14.20.971000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lesser Circle, The',null,null,'One of three echelons, or pantheons, among the gods of Demarge:  the Greater Circle, Lesser Circle, and Least Circle.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1980,1,to_timestamp('05-SEP-15 10.00.16.902000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Rawain y Keb’bryn',null,null,'An elithin and one of the Tryptychs during the time of the Gloaming.  A rarity among the lords of Sh''hein''Rein, he was not a great warrior, only a great strategist.  In fact, he was crippled, having suffered a wound in his youth which left him with a profound limp and the loss of two fingers on his right hand.  He was presumed to have perished to the coming of Dubh Gaoth but later appeared in the service of Gysioch y Senyar at Asmaragaidhe.  Legend claims that he attempted to prevent his king from lighting the beacon which brought Gysioch''s Folly but failed.  His current fate is unknown.  Due to the divided nature of the Song of Shar''ris y Rhuanedd, he may have survived in relative anonymity, only known to a few.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1982,1,to_timestamp('05-SEP-15 10.05.36.268000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Gysioch''s Folly',null,null,'The name given to the fall of Asmaraghaidhe.  The king of the exiles of Rhuanedd, Gysioch y Senyar, was convinced that Dubh Gaoth was vanquished.  To prove this misguided belief to his people, he lit a torch in the highest tower of the city, resulting in the coming of the Black Hunger.  Most of his people perished in that hour, and the survivors founded Shar''ris y Rhuanedd.  Determined that such calamity would never strike again, they became a migrant, divided people, never gathered in one place at the same time.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1991,1,to_timestamp('05-SEP-15 11.32.08.076000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Black Blade, The',null,null,'A two-handed bastard sword wrought of black metal, with golden veins that run through the blade.  The hilt is also black with a golden orb set into each side.  It is currently in the possession of Arastel and is likely a Tal''Shiahd.  Little else is known.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1997,1,to_timestamp('06-SEP-15 10.03.42.025000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Unseen War, The',null,null,'A war between the lords of Calikar and an unknown enemy, which began after elithin stonesmiths accidentally dug into the Modeira.  The nature of this conflict has been lost since the Gloaming.  Gh&icirc;lar and several other towns near Ruahd were lost during this conflict.  Rhuanedd was besieged for several years.  Eventually, the Lost Wall was erected to seal the breach, and Srawain was constructed nearby to ward over it.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2004,1,to_timestamp('06-SEP-15 10.27.37.793000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Maethrin',null,null,'The nurse of Eliryndil yn Orain at the time she was abducted on the Day of Orihieyte.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2662,1,to_timestamp('23-SEP-15 12.27.53.035000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Mirror Stones',null,null,'Stones enchanted by the Depsanf such that they reflect a distant location.  Speakers and Mirror Stones are required in both locations.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2449,1,to_timestamp('18-SEP-15 08.38.43.543000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Lyall Sijfinnur-s&oslash;n',null,null,'A du&aring;rg of magnr Undlflos, eldest son of Sijfinnur Jaako-s&oslash;n and Fylla Alva-d&oslash;tter, brother to Flos Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Signy Fylla-d&oslash;tter, and Honvir Sijfinnur-s&oslash;n, half-brother to Mikhan Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, and adopted brother to Hl&aelig;ja Egill-s&oslash;n.  Lyall was killed by a do''Drak during the twenty-sixth naming day hunt of his half-brother, Hl&aelig;ja.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2451,1,to_timestamp('18-SEP-15 08.48.23.722000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Honvir Sijfinnur-s&oslash;n',null,null,'A du&aring;rg of magnr Undlflos, son of Sijfinnur Jaako-s&oslash;n and Fylla Alva-d&oslash;tter, brother to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Signy Fylla-d&oslash;tter, and Flos Sijfinnur-s&oslash;n, half-brother to Mikhan Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, adopted brother to Hl&aelig;ja Egill-s&oslash;n, husband of Erika Magna-d&oslash;tter, and father to Sindri Erika-d&oslash;tter and Ingmar Honvir-s&oslash;n.  After the death of his father and elder brother during Hl&aelig;ja''s twenty-sixth naming day hunt, he became the rikr of magnr Undlflos, which he remains to this day.  He rules a disgraced house.  He exiled his step-mother, Ingjard Erica-d&oslash;tter.  It is believed that he later arranged her death, as well as that of her daughter, Lell Ingjard-d&oslash;tter, believing her responsible for the murder of his mother and sister, Signy Alva-d&oslash;tter.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2499,1,to_timestamp('19-SEP-15 07.59.01.996000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Lukifan',null,null,'A self-ascribed moniker for Edim''yl Moriel during the Second &AElig;gan Empire.  It means "He Who Seeks" or "He Who Finds."',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2448,1,to_timestamp('18-SEP-15 08.37.14.616000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Flos Sijfinnur-s&oslash;n',null,null,'A du&aring;rg of magnr Undlflos, youngest son of Sijfinnur Jaako-s&oslash;n and Fylla Alva-d&oslash;tter, brother to Lyall Sijfinnur-s&oslash;n, Rowan Fylla-d&oslash;tter, Signy Fylla-d&oslash;tter, and Honvir Sijfinnur-s&oslash;n, half-brother to Mikhan Sijfinnur-s&oslash;n, Lell Ingjard-d&oslash;tter, and Folkvar Sijfinnur-s&oslash;n, and adopted brother to Hl&aelig;ja Egill-s&oslash;n.  Born drekka, he became a servant in his household, where he eventually died of natural causes.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2009,1,to_timestamp('06-SEP-15 10.56.26.204000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'D&aelig;n''shee',null,null,'A race and the people of Kailar. They are a fair, lithe people of feral beauty, with pale skin and silver hair and eyes.  There is no historical or dream evidence of the d&aelig;n''shee during the Second &AElig;gan Empire.  The d&aelig;n''shee were the original inhabitants of the Great Roof and had been present for at least several generations when the elithi arrived.  Conflict arose, and the d&aelig;n''hsee, lacking unity, quickly ceded land.  Eventually, their city of Aljaberin was lost and they were driven altogether from the Great Cavern into the Roof of Kailar.  Over the centuries, they became a united, savage people bent on the ruin of Calikar.  The ki''tahj and the Blood Screamers were born.  Thousands of years of war have followed, and the people of the Great Roof, human, elithin, and du&aring;rg alike, now view the d&aelig;n''shee as creatures of nightmare and ruin.  On average, the d&aelig;n''shee live 300-400 years, but some few have been known to survive to the age of 500.  Commonly known as the shadow-souled among the people of the Great Cavern.  Called the Maleagh Ini by the elithi, meaning "Cursed Ones."',1);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (1994,1,to_timestamp('06-SEP-15 09.52.27.592000000 AM','DD-MON-RR HH.MI.SS.FF AM'),'Dark Herald, The',null,null,'A messenger who came to the Elod&aelig;ne Palace shortly before the Gloaming.  Although R&aelig;wyn yn Rhuaidh, the king''s counselor, advised sending him away, Aruth''eras y Senyar agreed to meet with him.  What words were spoken, none know.  At some later point, the herald returned and murdered both the prophetess and the monarch.  The king died immediately and R&aelig;wyn survived another fifty years in deep slumber before perishing at Camryn''s Pride.  The Dark Herald survived and fled.  His identity was never known.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2019,1,to_timestamp('07-SEP-15 01.03.10.239000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Great Three, The',null,null,'Literal translation of Mych''Tair.',0);
Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2020,1,to_timestamp('07-SEP-15 01.04.08.609000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Peadar y Llewyn',null,null,'One of the Mych''Tair, a giant among the elithi and a shuonne.  He was devoted utterly to Myrddin y Senyar, whom he considers to be his only better on the field of battle.  He did not wear armor and bore a great wooden quarterstaff.  He perished at &AElig;lm&aelig;dris, killed by Malnos.',0);
