Insert into GLOSSARYAPPLICATION.TERMS (ID,USERID,CREATIONDATE,NAME,SDATE_DATE,SDATE_EPOCH,DEFINITION,MATCHLOWERCASE) values (2023,1,to_timestamp('07-SEP-15 01.15.47.184000000 PM','DD-MON-RR HH.MI.SS.FF AM'),'Kai y Rhyfelwr',null,null,'One of the Bych''Tair.  He is the youngest and least skilled, despite his Ryche being the most famed; he wields a stone spear.  He has denied his seat on the Tiaryche.',0);
